#!/usr/bin/env node
/**
 * just load the `@tapjs/run` module, which does all the work
 */
//@ts-ignore
process.setSourceMapsEnabled(true)
import { resolve } from 'node:path'
import { pathToFileURL } from 'node:url'

// Try to load the tap runner that's installed in the current project,
// if present. Running plugin builds etc from the global space will
// never ever work as expected, but people do global installs to be
// able to run tap without `npm exec` or `npm run-script`,
// so may as well make it do the right thing.

import { resolveImport } from 'resolve-import'
// import('@tapjs/run')
await import(
  String(
    await resolveImport(
      '@tapjs/run',
      await resolveImport('tap', pathToFileURL(resolve('x'))).catch(
        () => import.meta.url,
      ),
    ),
  )
)

/**
 * Exported as `'resolve-import/get-all-conditions'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

/**
 * Given an `exports` or `imports` value from a package, return the list of
 * conditions that it is sensitive to.
 *
 * `default` is not included in the returned list, since that's always
 * effectively relevant.
 *
 * Note that a condition being returned by this method does not mean
 * that the export/import object actually has a *target* for that condition,
 * since it may map to `null`, be nested under another condition, etc. But it
 * does potentially have some kind of conditional behavior for all the
 * conditions returned.
 *
 * Ordering of returned conditions is arbitrary, and does not imply precedence
 * or object shape.
 */
export const getAllConditions = (
  importsExports: Imports | Exports
): string[] => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: string[] = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getAllConditionsFromCond(
            importsExports as ConditionalValue
          )
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(...getAllConditionsFromCond(v))
    }
    return [...new Set(conditions)]
  }

  return getAllConditionsFromCond(importsExports as ConditionalValue)
}

const getAllConditionsFromCond = (cond?: ConditionalValue): string[] => {
  if (!cond || typeof cond === 'string') return []
  if (Array.isArray(cond)) {
    const conditions: string[] = []
    for (const e of cond) {
      if (!e || typeof e === 'string') break
      conditions.push(...getAllConditionsFromCond(e))
    }
    return [...new Set(conditions)]
  }
  const conditions: string[] = []
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    // anything after 'default' isn't relevant
    conditions.push(...getAllConditionsFromCond(v))
    if (k === 'default') break
    else conditions.push(k)
  }
  return [...new Set(conditions)]
}

import { getConditionalValuesList } from './get-conditional-values-list.js'
import { Exports, Imports } from './index.js'

/**
 * Get the minimal set of conditions that can potentially produce different
 * resolution values for a given imports or exports object from a package
 * manifest.
 *
 * For example:
 *
 * ```json
 * {
 *   ".": [{"import":[{"types":"x.d.ts"},"x.mjs"], "require":"y.js"}]
 *   "./a": {"browser":{"require":"./a.js"}},
 *   "./b": {"browser":"./b.js"},
 *   "./c": {"require":{"browser":"./c.js"}}
 * }
 * ```
 *
 * would return:
 * ```js
 * [
 *   ['import','types'],
 *   ['import'],
 *   ['require'],
 *   ['browser'],
 *   ['browser', 'require'],
 * ]
 * ```
 *
 * With the `['require', 'browser']` condition set omitted, as it is already
 * covered by `['browser', 'require']`.
 *
 * Condition ordering is arbitrary and not guaranteed to be consistent.
 */
export const getUniqueConditionSets = (
  importsExports: Imports | Exports
): string[][] => {
  const list = getConditionalValuesList(importsExports)
  let results: string[][] = []
  for (const [_, conditions] of list) {
    if (!results.some(arr => arrayIsEquivalent(arr, conditions))) {
      results.push([...conditions])
    }
  }
  return results
}

const arrayIsEquivalent = (arr: string[], sup: Set<string>) => {
  if (arr.length !== sup.size) return false
  for (const c of arr) {
    if (!sup.has(c)) return false
  }
  return true
}

/**
 * Exported as `'resolve-import/resolve-all-exports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { getNamedExportsList } from './get-named-exports-list.js'
import { Exports, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'
import { starGlob } from './star-glob.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible export path is mapped to the file URL that it would resolve to.
 *
 * Invalid exports are omitted. No errors are raised as long as the file is a
 * valid `package.json`.
 *
 * Note: in cases like `"./x/*": "./file.js"`, where the list of possible
 * import paths is unbounded, the returned object will contain `"./x/*"` as the
 * key, since there's no way to expand that to every possible match.
 */
export const resolveAllExports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllExports)
  }

  const results: Record<string, string | URL> = {}

  const { exports } = pkg
  for (const sub of getNamedExportsList(exports)) {
    let res

    // this can't shouldn't be able to actually throw, because we're
    // pulling the list from the set itself.
    /* c8 ignore start */
    try {
      res = resolveExport(sub, exports as Exports, pjPath, pjPath, options)
    } catch {}
    if (!res) continue
    /* c8 ignore stop */

    // if it contains a *, then we have to glob,
    // in package.json exports * is actually **, but only
    // relevant if there is exactly ONE star
    const sres = res.split('*')
    const ssub = sub.split('*')
    if (sres.length === 2 && ssub.length === 2) {
      for (const [rep, target] of await starGlob(
        sres as [string, string],
        pjDir
      )) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
    } else {
      results[sub] = pathToFileURL(resolve(pjDir, res))
    }
  }

  return results
}

/**
 * Exported as `'resolve-import/resolve-all-local-imports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { Pkg, readPkg } from './read-pkg.js'
import { resolveAllExports } from './resolve-all-exports.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveImport } from './resolve-import.js'
import { starGlob } from './star-glob.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible local import path is mapped to the file URL that it would resolve
 * to.
 *
 * Invalid and non-resolving imports are omitted.
 */
export const resolveAllLocalImports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)
  const pjURL = toFileURL(packageJsonPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllLocalImports)
  }
  const results: Record<string, URL | string> = {}

  for (const [sub, target] of getNamedImportsList(pkg, options)) {
    // if the import is local, then look it up
    // if it's another package, then look up that package
    // if it's another package with a *, then look up all exports
    // of that package, and filter by the matches.
    const parts = target.match(/^(@[^\/]+\/[^\/]+|[^\/]+)/)
    // make internal package named modules consistently `./`
    const name = pkg.name

    // non-matches already filtered out
    /* c8 ignore start */
    if (!parts) continue
    /* c8 ignore stop */

    const ssub = sub.split('*')
    const starget = target.split('*') as [string, string]
    const star = ssub.length === 2 && starget.length === 2
    if (!star) {
      // simple case, no * replacement
      // if not found, just omit it.
      // do a full resolve, because the target can be anything like
      // './foo/bar' or 'dep/blah', etc.
      try {
        results[sub] = await resolveImport(target, pjURL)
      } catch {}
      continue
    }

    // has a star, have to glob if it's localPath, or look up exports if not
    const localPath = parts[1] === '.'
    if (localPath) {
      for (const [rep, target] of await starGlob(starget, pjDir)) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
      continue
    }

    const localName = parts[1] === name
    const dep = !localPath && !localName ? parts[1] : null

    // if we can't find the package, it's not valid.
    const ppath = dep ? await findDepPackage(dep, pjDir) : pjDir
    if (!ppath) continue

    const pj = resolve(ppath, 'package.json')
    if (!(await fileExists(pj))) {
      continue
    }

    const allExports = await resolveAllExports(pj)
    for (const [k, v] of Object.entries(allExports)) {
      if (k === '.' || k === './') continue
      const i = dep + k.substring(1)
      if (i.startsWith(starget[0]) && i.endsWith(starget[1])) {
        const s =
          ssub[0] +
          i.substring(starget[0].length, i.length - starget[1].length) +
          ssub[1]
        // should be impossible to throw, because we're pulling the list
        // from the package itself, and it gets resolved at that point.
        /* c8 ignore start */
        try {
          results[s] = await resolveImport(v, pjURL)
        } catch {}
        /* c8 ignore stop */
      }
    }
  }

  return results
}

/**
 * Get the condition-resolved targets of all imports
 *
 * Stars are not expanded.
 */
const getNamedImportsList = (
  pkg: Pkg,
  options: ResolveImportOpts
): [string, string][] => {
  const results: [string, string][] = []
  const { imports } = pkg
  if (!imports || typeof imports !== 'object') return results
  for (const [k, v] of Object.entries(imports)) {
    const r = resolveConditionalValue(v, options)
    if (r && !r.startsWith('#')) results.push([k, r])
  }
  return results
}

/**
 * Exported as `'resolve-import/resolve-conditional-value'`
 * @module
 */
import { ConditionalValue, ResolveImportOpts } from './index.js'

/**
 * find the first match for string, import, node, or default
 * at this point, we know we're on the right subpath already
 */
export const resolveConditionalValue = (
  exp: ConditionalValue,
  options: ResolveImportOpts
): string | null => {
  const { conditions = ['import', 'node'] } = options
  if (exp === null || typeof exp === 'string') return exp
  if (Array.isArray(exp)) {
    for (const e of exp) {
      const r = resolveConditionalValue(e, options)
      if (r) return r
    }
    return null
  }
  for (const [k, v] of Object.entries(exp)) {
    if (conditions.includes(k) || k === 'default') {
      return resolveConditionalValue(v, options)
    }
  }
  return null
}

/**
 * Exported as `'resolve-import/resolve-import'`
 * @module
 */
import { realpath } from 'fs/promises'
import { isBuiltin } from 'module'
import { basename, dirname, isAbsolute, resolve } from 'path'
import { fileURLToPath, pathToFileURL } from 'url'
import {
  moduleNotFound,
  relativeImportWithoutParentURL,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { ResolveImportOpts } from './index.js'
import { isRelativeRequire } from './is-relative-require.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolvePackageImport } from './resolve-package-import.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

// It's pretty common to resolve against, eg, cwd + '/x', since we might not
// know the actual file that it's being loaded from, and want to resolve what
// a dep WOULD be from a given path. This allows us to realpath that directory,
// without requiring that the file exist.
const realpathParentDir = async (path: string | URL) => {
  path = toPath(path)
  return resolve(await realpath(dirname(path)), basename(path))
}

/**
 * Resolve an import URL or string as if it were coming from the
 * module at parentURL.
 *
 * Returns a string for node builtin modules, and a file:// URL
 * object for anything resolved on disk.
 *
 * If the resolution is impossible, then an error will be raised, which
 * closely matches the errors raised by Node when failing for the same
 * reason.
 */
export const resolveImport = async (
  /** the thing being imported */
  url: string | URL,
  /**
   * the place the import() would be coming from. Required for relative
   * imports.
   */
  parentURL: string | URL | undefined = undefined,
  options: ResolveImportOpts = {}
): Promise<URL | string> => {
  // already resolved, just check that it exists
  if (typeof url === 'string' && url.startsWith('file://')) {
    url = new URL(url)
  }
  if (typeof url === 'object') {
    if (!(await fileExists(url))) {
      throw moduleNotFound(String(url), String(parentURL))
    }
    const rp = await realpath(toPath(url))
    return rp !== fileURLToPath(url) ? pathToFileURL(rp) : url
  }

  const pu = parentURL
    ? toFileURL(await realpathParentDir(parentURL))
    : undefined

  if (isRelativeRequire(url)) {
    if (!pu) {
      throw relativeImportWithoutParentURL(url, parentURL)
    }
    const u = new URL(url, pu)
    if (!(await fileExists(u))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(new URL(url, pu)))
  }

  if (isAbsolute(url)) {
    if (!(await fileExists(url))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(url))
  }

  if (isBuiltin(String(url))) {
    return String(url)
  }

  // ok, we have to resolve it. some kind of bare dep import,
  // either a package name resolving to module or main, or a named export.
  const parentPath: string = toPath(
    parentURL || resolve(await realpath(process.cwd()), 'x')
  )
  const opts = {
    ...options,
    originalParent: String(options.originalParent || parentPath),
  }
  if (url) {
    return resolvePackageImport(url, parentPath, opts)
  } else {
    return resolveDependencyExports(url, parentPath, opts)
  }
}

/**
 * Exported as `'resolve-import/get-conditional-values-list'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

export type ConditionalValuesList = [
  submodulePath: string,
  conditions: Set<string>,
  resolvedValue: string | null
][]

/**
 * Given an `exports` or `imports` value from a package, return the list of all
 * possible conditional values that it might potentially resolve to, for any
 * possible set of import conditions, along with the `Set<string>` of
 * conditions, any superset of which will result in the condition.
 *
 * The list includes null results, since while these are not a valid resolution
 * per se, they do *prevent* valid resolutions that match the same conditions.
 */
export const getConditionalValuesList = (
  importsExports: Imports | Exports
): ConditionalValuesList => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: ConditionalValuesList = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getConditionalValuesListFromCond(
            importsExports as ConditionalValue
          ).map(s => ['.', ...s])
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(
        ...getConditionalValuesListFromCond(v).map(
          s => [k, ...s] as [string, Set<string>, string | null]
        )
      )
    }
    return conditions
  }
  return getConditionalValuesListFromCond(
    importsExports as ConditionalValue
  ).map(s => ['.', ...s])
}

const isSubset = (maybeSub: Set<string>, sup: Set<string>) => {
  if (maybeSub.size > sup.size) return false
  for (const c of maybeSub) {
    if (!sup.has(c)) return false
  }
  return true
}

// walk down the tree, creating a list of [Set<Condition>, value]
// if a subset of the current set is already present in the list, then omit it
const getConditionalValuesListFromCond = (
  cond?: ConditionalValue,
  path: string[] = [], // path of conditions that got here
  list: [Set<string>, string | null][] = []
): [Set<string>, string | null][] => {
  /* c8 ignore start */
  if (cond === undefined) return list
  /* c8 ignore stop */
  if (cond === null || typeof cond === 'string') {
    // reached a resolution value.
    // if we got here, we know it has not yet been seen.
    list.push([new Set(path), cond])
    return list
  }
  if (Array.isArray(cond)) {
    for (const c of cond) {
      getConditionalValuesListFromCond(c, path, list)
      // if we hit a default condition, break
      if (!c || typeof c === 'string') break
    }
    return list
  }
  // ConditionalValueObject
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    const p = k === 'default' ? path : path.concat(k)
    // if no subset seen, then recurse
    const ps = new Set(p)
    const seen = list.some(([s]) => isSubset(s, ps))
    if (!seen) {
      getConditionalValuesListFromCond(v, p, list)
    }
    if (k === 'default') break
  }
  return list
}

/**
 * Errors raised by resolve failures.
 * @module
 */

import { resolveImport } from './resolve-import.js'

// TODO: move "caller" to an options object. Then the same options object can
// be used that handles conditions, parentURL, etc., and the relevant
// top-level function can set it explicitly at the start of the process and
// just pass it along.

export const invalidImportSpecifier = (
  url: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error('invalid import() specifier: ' + url)
  Error.captureStackTrace(er, caller)
  return er
}

export const invalidPackage = (
  pj: string | URL,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error(`Not a valid package: ${pj}`)
  Error.captureStackTrace(er, caller)
  return er
}

export const relativeImportWithoutParentURL = (
  url: string,
  parentURL: any,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error('relative import without parentURL'),
    {
      url,
      parentURL,
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const subpathNotExported = (
  sub: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const p =
    sub === '.'
      ? 'No "exports" main defined'
      : `Package subpath '${sub}' is not defined by "exports"`
  const er = Object.assign(
    new Error(`${p} in ${pj} imported from ${from}`),
    {
      code: 'ERR_PACKAGE_PATH_NOT_EXPORTED',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageNotFound = (
  path: string | null,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find package '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const moduleNotFound = (
  path: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find module '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageImportNotDefined = (
  path: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(
      `Package import specifier "${path}" is not defined in package ` +
        `${pj} imported from ${from}`
    ),
    { code: 'ERR_PACKAGE_IMPORT_NOT_DEFINED' }
  )
  Error.captureStackTrace(er, caller)
  return er
}

import { subpathNotExported } from './errors.js'
import { findStarMatch } from './find-star-match.js'
import {
  ConditionalValue,
  Exports,
  ExportsSubpaths,
  ResolveImportOpts,
} from './index.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'

/**
 * Resolve an export that might be a string, subpath exports, exports value
 * object, or array of strings and exports value objects
 */
export const resolveExport = (
  sub: string,
  exp: Exports,
  pj: string,
  from: string,
  options: ResolveImportOpts
): string => {
  const s = !sub
    ? '.'
    : sub === '.' || sub.startsWith('./')
    ? sub
    : `./${sub}`

  if (typeof exp === 'string' || Array.isArray(exp)) {
    const res = s === '.' && resolveConditionalValue(exp, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  // now it must be a set of named exports or an export value object
  // first try to resolve as a value object, if that's allowed
  if (s === '.') {
    const res = resolveConditionalValue(exp, options)
    if (res) return res
  }

  // otherwise the only way to match is with subpaths
  const es = exp as ExportsSubpaths

  // if we have an exact match, use that
  const e = es[s]
  if (e !== undefined) {
    const res = resolveConditionalValue(e, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  const sm = findStarMatch(s, es)
  if (sm) {
    const [key, mid] = sm
    const res = resolveConditionalValue(
      es[key] as ConditionalValue,
      options
    )
    if (!res) throw subpathNotExported(s, pj, from)
    return res.replace(/\*/g, mid)
  }

  // did not find a match
  throw subpathNotExported(s, pj, from)
}

import { Exports, ExportsSubpaths } from './index.js'
/**
 * Get the condition-resolved targets of all exports
 *
 * Stars are not expanded.
 */
export const getNamedExportsList = (exports?: Exports): string[] => {
  if (!exports) return []
  if (!isExportSubpaths(exports)) return ['.']
  return Object.keys(exports).filter(e => e === '.' || e.startsWith('./'))
}

const isExportSubpaths = (e: Exports): e is ExportsSubpaths => {
  if (!e || typeof e !== 'object' || Array.isArray(e)) return false
  for (const p in e) {
    if (p !== '.' && !p.startsWith('./')) return false
  }
  return true
}

import { escape, glob } from 'glob'
import { resolve } from 'path'

export const starGlob = async (
  star: [string, string], // actually [string,string]
  dir: string
): Promise<[string, string][]> => {
  const pattern =
    escape(star[0]) +
    (star[0].endsWith('/') ? '' : '*/') +
    '**' +
    (star[1].startsWith('/') ? '' : '/*') +
    escape(star[1])
  const matches = await glob(pattern, {
    posix: true,
    absolute: false,
    nodir: true,
    cwd: dir,
    dotRelative: true,
  })
  return matches.map(match => {
    const rep = match.substring(
      star[0].length,
      match.length - star[1].length
    )
    return [rep, resolve(dir, match)]
  })
}

import { realpath, stat } from 'fs/promises'
import { dirname, resolve, sep } from 'path'
import { walkUp } from 'walk-up-path'

const dirExists = async (f: string): Promise<boolean> =>
  stat(f).then(
    st => st.isDirectory(),
    () => false
  )

export const findDepPackage = async (
  pkgName: string | null,
  parentPath: string
) => {
  // starting from the dirname, try to find the nearest node_modules
  for (const dir of walkUp(dirname(parentPath))) {
    const nm = resolve(dir, 'node_modules') + sep
    // if it's null, then we need the node_modules itself
    // if it's '' then we use node_modules with an extra / on it
    // thisis only relevant when generating the error message, since
    // of course node_modules// is never going to be a valid package.
    const ppath =
      pkgName === null ? nm : (!pkgName ? nm : resolve(nm, pkgName)) + sep
    if (await dirExists(ppath)) {
      try {
        return (await realpath(ppath)) + sep
        // the direxists stat will avoid almost all throws that could
        // occur here, but just in case.
        /* c8 ignore start */
      } catch {}
        /* c8 ignore stop */
    }
  }
}

import { resolve } from 'path'
import { pathToFileURL } from 'url'
import { moduleNotFound, packageNotFound } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'

/**
 * Resolve a dependency like '@dep/name/sub/module' where
 * '@dep/name' is in node_modules somewhere and exports './sub/module'
 */
export const resolveDependencyExports = async (
  url: string | null,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL> => {
  const { originalParent } = options
  const parts = url?.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/)
  const [, pkgName, sub] =
    url === null ? [, null, ''] : parts || ['', '', '']
  const ppath = await findDepPackage(pkgName, parentPath)
  if (!ppath) {
    throw packageNotFound(pkgName, originalParent)
  }

  const indexjs = resolve(ppath, 'index.js')
  const pj = resolve(ppath, 'package.json')
  const pkg = await readPkg(pj)
  const subpath = sub ? resolve(ppath, sub) : false
  // if not a package, then the sub can still be a direct path
  // if no sub, then resolves to index.js if available.
  if (!pkg) {
    if (!subpath) {
      // try index.js, otherwise fail
      if (await fileExists(indexjs)) return pathToFileURL(indexjs)
      else throw packageNotFound(ppath, originalParent)
    } else {
      if (await fileExists(subpath)) {
        return pathToFileURL(subpath)
      } else throw moduleNotFound(subpath, originalParent)
    }
  }

  // ok, have a package, look up the export if present.
  // otherwise, use main, otherwise index.js
  if (pkg.exports) {
    const subPath = resolveExport(
      sub,
      pkg.exports,
      pj,
      originalParent,
      options
    )
    const resolved = resolve(ppath, subPath)
    if (await fileExists(resolved)) return pathToFileURL(resolved)
    else throw moduleNotFound(resolved, originalParent)
  } else if (subpath) {
    if (await fileExists(subpath)) return pathToFileURL(subpath)
    else throw moduleNotFound(subpath, originalParent)
  } else if (pkg.main) {
    // fall back to index.js if main is missing
    const rmain = resolve(ppath, pkg.main)
    if (await fileExists(rmain)) return pathToFileURL(rmain)
    else if (await fileExists(indexjs)) return pathToFileURL(indexjs)
    else throw packageNotFound(ppath, originalParent)
  } else if (await fileExists(indexjs)) {
    return pathToFileURL(indexjs)
  } else {
    throw packageNotFound(ppath, originalParent)
  }
}

import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { walkUp } from 'walk-up-path'
import {
  invalidImportSpecifier,
  moduleNotFound,
  packageImportNotDefined,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { findStarMatch } from './find-star-match.js'
import { ConditionalValue, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolveExport } from './resolve-export.js'
import { resolveImport } from './resolve-import.js'

/**
 * Resolve an import like '@package/name/sub/module', where
 * './sub/module' appears in the exports of the local package.
 */
export const resolvePackageImport = async (
  url: string,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL | string> => {
  const { originalParent } = options
  const parts = url.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/) as
    | null
    | (RegExpMatchArray & [string, string, string])
  // impossible
  /* c8 ignore start */
  if (!parts) throw invalidImportSpecifier(url)
  /* c8 ignore stop */

  for (const dir of walkUp(dirname(parentPath))) {
    const pj = resolve(dir, 'package.json')
    const pkg = await readPkg(pj)
    if (!pkg) continue
    if (pkg.name && pkg.exports) {
      // can import from this package name if exports is defined
      const [, pkgName, sub] = parts
      if (pkgName === pkg.name) {
        // ok, see if sub is a valid export then
        const subPath = resolveExport(
          sub,
          pkg.exports,
          pj,
          originalParent,
          options
        )
        const resolved = resolve(dir, subPath)
        if (await fileExists(resolved)) return pathToFileURL(resolved)
        else throw moduleNotFound(resolved, originalParent)
      }
    }

    if (url.startsWith('#')) {
      if (!pkg.imports) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const exact = pkg.imports[url]
      if (exact !== undefined) {
        const res = resolveConditionalValue(exact, options)
        if (!res) {
          throw packageImportNotDefined(url, pj, originalParent)
        }
        // kind of weird behavior, but it's what node does
        if (res.startsWith('#')) {
          return resolveDependencyExports(null, parentPath, options)
        }
        return resolveImport(res, pj, options)
      }

      const sm = findStarMatch(url, pkg.imports)
      if (!sm) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const [key, mid] = sm
      const match = pkg.imports[key] as ConditionalValue
      const res = resolveConditionalValue(match, options)
      if (!res) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      if (res.startsWith('#')) {
        return resolveDependencyExports(null, parentPath, options)
      }
      const expand = res.replace(/\*/g, mid)

      // start over with the resolved import
      return resolveImport(expand, pj, options)
    }

    break
  }

  return resolveDependencyExports(url, parentPath, options)
}

/**
 * Given an object with string keys possibly containing *, and a test
 * string, return the matching key, and the section that the star should
 * expand to when matching against the test string.
 */
export const findStarMatch = (
  s: string,
  obj: Record<string, any>
): [string, string] | null => {
  // longest pattern matches take priority
  const patterns = Object.keys(obj)
    .filter(p => p.length <= s.length)
    .sort((a, b) => b.length - a.length)
    .map(p => [p, p.split('*')])
    .filter(([, p]) => (p as string[]).length === 2) as [
    string,
    [string, string]
  ][]

  for (const [key, [before, after]] of patterns) {
    if (s.startsWith(before) && s.endsWith(after)) {
      const mid = s.substring(before.length, s.length - after.length)
      return [key, mid]
    }
  }

  return null
}

import { dirname, resolve } from 'path'
export const walkUp = function* (path: string) {
  for (path = resolve(path); path;) {
    yield path
    const pp = dirname(path)
    if (pp === path) {
      break
    } else {
      path = pp
    }
  }
}

