  buffered: boolean
  #closingTestPoint: Result | null = null
  comments: string[] = []
  count: number = 0
  fail: number = 0
  failures: TapError[] = []
  skips: (Result & { skip: string | true })[] = []
  todos: (Result & { todo: string | true })[] = []
  level: number
  name: string
  ok: boolean = true
  omitVersion: boolean
  parent: Parser | null = null
  pass: number = 0
  passes: Result[] | null
  planComment: string = ''
  planEnd: number = -1
  planStart: number = -1
  pointsSeen: Map<number, Result> = new Map()
  pragmas: Pragmas
  preserveWhitespace: boolean
  results: FinalResults | null = null
  root: Parser
  skip: number = 0
  strict: boolean
  syntheticBailout: boolean = false
  syntheticPlan: boolean = false
  time: number | null = null
  todo: number = 0

  get closingTestPoint() {
    return this.#closingTestPoint
  }
  set closingTestPoint(res) {
    this.#closingTestPoint = res
    if (res) res.closingTestPoint = true
  }

  /* c8 ignore start */
  get readable(): false {
    return false
  }
  get writable(): true {
    return true
  }
  /* c8 ignore stop */

  constructor(onComplete?: (results: FinalResults) => any)
  constructor(
    options?: ParserOptions,
    onComplete?: (results: FinalResults) => any,
  )
  constructor(
    options?: ParserOptions | ((results: FinalResults) => any),
    onComplete?: (results: FinalResults) => any,
  ) {
    if (typeof options === 'function') {
      onComplete = options
      options = {} as ParserOptions
    }

    options = options || {}
    super()

    if (onComplete) this.on('complete', onComplete)

    this.name = options.name || ''
    this.parent = options.parent || null
    this.closingTestPoint =
      (this.parent && options.closingTestPoint) || null
    this.root = options.parent ? options.parent.root : this
    this.passes = options.passes ? [] : null
    this.level = options.level || 0

    this.bail = !!options.bail
    this.omitVersion = !!options.omitVersion
    this.buffered = !!options.buffered
    this.preserveWhitespace = options.preserveWhitespace || false

    this.strict = !!options.strict
    this.pragmas = { strict: this.strict }
  }

  get fullname(): string {
    const n: string[] = []
    const pn = (this.parent?.fullname ?? '').trim()
    const mn = (this.name || '').trim()
    if (pn) n.push(pn)
    if (mn) n.push(mn)
    return n.join(' > ')
  }

  tapError(
    error: Result | { tapError: string; [k: string]: any } | string,
    line: string,
  ) {
    if (line) this.emit('line', line)
    this.ok = false
    this.fail++
    if (typeof error === 'string') {
      error = {
        tapError: error,
      }
    }
    this.failures.push(error)
  }

  parseTestPoint(testPoint: RegExpMatchArray, line: string) {
    // need to hold off on this when we have a child so we can
    // associate the closing test point with the test.
    if (!this.#child) this.emitResult()

    if (this.bailedOut) return

    const resId = testPoint[2]
    const res = new Result(testPoint, this)

    if (resId && this.planStart !== -1) {
      const lessThanStart = res.id < this.planStart
      const greaterThanEnd = res.id > this.planEnd
      if (lessThanStart || greaterThanEnd) {
        if (lessThanStart) res.tapError = 'id less than plan start'
        else res.tapError = 'id greater than plan end'
        res.plan = new Plan(this.planStart, this.planEnd)
        this.tapError(res, line)
      }
    }

    if (resId && this.pointsSeen.has(res.id)) {
      res.tapError =
        'test point id ' + resId + ' appears multiple times'
      /* c8 ignore start */
      res.previous = this.pointsSeen.get(res.id) || null
      /* c8 ignore stop */
      this.tapError(res, line)
    } else if (resId) {
      this.pointsSeen.set(res.id, res)
    }

    if (this.#child) {
      if (!this.#child.closingTestPoint)
        this.#child.closingTestPoint = res
      this.emitResult()
      // can only bail out here in the case of a child with broken diags
      // anything else would have bailed out already.
      if (this.bailedOut) return
    }

    this.emit('line', line)

    if (!res.skip && !res.todo) this.ok = this.ok && res.ok

    // hold onto it, because we might get yamlish diagnostics
    this.#current = res
  }

  nonTap(data: string, didLine: boolean = false) {
    if (this.#bailingOut && /^( {4})*\}\n$/.test(data)) return

    if (this.strict) {
      const err = {
        tapError: 'Non-TAP data encountered in strict mode',
        data: data,
      }
      this.tapError(err, data)
      if (this.parent) this.parent.tapError(err, data)
    }

    // emit each line, then the extra as a whole
    if (!didLine)
      data
        .split('\n')
        .slice(0, -1)
        .forEach(line => {
          line += '\n'
          if (this.#current || this.#extraQueue.length)
            this.#extraQueue.push(['line', line])
          else this.emit('line', line)
        })

    this.emitExtra(data)
  }

  emitExtra(data: string, fromChild: boolean = false) {
    if (this.parent)
      this.parent.emitExtra(
        data.replace(/\n$/, '').replace(/^/gm, '    ') + '\n',
        true,
      )
    else if (!fromChild && (this.#current || this.#extraQueue.length))
      this.#extraQueue.push(['extra', data])
    else this.emit('extra', data)
  }

  plan(start: number, end: number, comment: string, line: string) {
    // not allowed to have more than one plan
    if (this.planStart !== -1) {
      this.nonTap(line)
      return
    }

    // can't put a plan in a child.
    if (this.#child || this.#yind) {
      this.nonTap(line)
      return
    }

    this.emitResult()
    if (this.bailedOut) return

    // 1..0 is a special case. Otherwise, end must be >= start
    if (end < start && end !== 0 && start !== 1) {
      if (this.strict)
        this.tapError(
          {
            tapError: 'plan end cannot be less than plan start',
            plan: { start, end },
          },
          line,
        )
      else this.nonTap(line)
      return
    }

    this.planStart = start
    this.planEnd = end
    const p: Plan = new Plan(start, end, comment)
    if (p.comment) this.planComment = p.comment = comment

    // This means that the plan is coming at the END of all the tests
    // Plans MUST be either at the beginning or the very end.  We treat
    // plans like '1..0' the same, since they indicate that no tests
    // will be coming.
    if (this.count !== 0 || this.planEnd === 0) {
      const seen = new Set()
      for (const [id, res] of this.pointsSeen.entries()) {
        const tapError =
          id < start ? 'id less than plan start'
          : id > end ? 'id greater than plan end'
          : null
        if (tapError) {
          seen.add(tapError)
          res.tapError = tapError
          res.plan = new Plan(start, end)
          this.tapError(res, line)
        }
      }
      this.#postPlan = true
    }

    this.emit('line', line)
    this.emit('plan', p)
  }

  resetYamlish() {
    this.#yind = ''
    this.#yamlish = ''
  }

  // that moment when you realize it's not what you thought it was
  yamlGarbage() {
    const yamlGarbage = this.#yind + '---\n' + this.#yamlish
    this.emitResult()
    if (this.bailedOut) return
    this.nonTap(yamlGarbage, true)
  }

  yamlishLine(line: string) {
    if (line === this.#yind + '...\n') {
      // end the yaml block
      this.processYamlish()
    } else {
      this.#yamlish += line
    }
  }

  processYamlish() {
    /* c8 ignore start */
    if (!this.#current) {
      throw new Error(
        'called processYamlish without a current test point',
      )
    }
    /* c8 ignore stop */
    const yamlish = this.#yamlish
    this.resetYamlish()

    let diags: any
    try {
      diags = yaml.parse(yamlish)
    } catch (er) {
      this.nonTap(
        this.#yind + '---\n' + yamlish + this.#yind + '...\n',
        true,
      )
      return
    }

    if (
      typeof diags.duration_ms === 'number' &&
      this.#current.time === null
    ) {
      this.#current.time = diags.duration_ms
      delete diags.duration_ms
    }

    this.#current.diag = diags
    // we still don't emit the result here yet, to support diags
    // that come ahead of buffered subtests.
  }

  write(
    chunk: string | Uint8Array | Buffer,
    cb?: (...x: any[]) => any,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding,
    cb?: (...x: any[]) => any,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding | ((...a: any[]) => any),
    cb?: (...x: any[]) => any,
  ): boolean {
    if (this.aborted) {
      return false
    }

    if (
      typeof encoding === 'string' &&
      encoding !== 'utf8' &&
      typeof chunk === 'string'
    ) {
      chunk = Buffer.from(chunk, encoding)
    }

    if (Buffer.isBuffer(chunk)) {
      chunk = chunk.toString('utf8')
    }

    if (typeof encoding === 'function') {
      cb = encoding
      encoding = undefined
    }

    this.buffer += chunk
    do {
      const match = this.buffer.match(/^.*\r?\n/)
      if (!match) break

      this.buffer = this.buffer.substring(match[0].length)
      this.parse(match[0])
    } while (this.buffer.length)

    if (cb) process.nextTick(cb)

    return true
  }

  end(
    chunk?: string | Buffer | Uint8Array,
    encoding?: BufferEncoding,
    cb?: (...a: any[]) => any,
  ): this
  end(
    chunk?: string | Buffer | Uint8Array,
    cb?: (...a: any[]) => any,
  ): this
  end(cb?: (...a: any[]) => any): this
  end(
    chunk?: string | Buffer | Uint8Array | ((...a: any[]) => any),
    encoding?: BufferEncoding | ((...a: any[]) => any),
    cb?: (...a: any[]) => any,
  ): this {
    if (chunk && typeof chunk !== 'function') {
      if (typeof encoding === 'function') {
        cb = encoding
        this.write(chunk)
      } else {
        this.write(chunk, encoding)
      }
    }

    if (this.buffer) {
      this.write('\n')
    }

    // if we have yamlish, means we didn't finish with a ...
    if (this.#yamlish) this.yamlGarbage()

    this.emitResult()

    if (this.syntheticBailout && this.level === 0) {
      this.syntheticBailout = false
      const reason =
        this.bailedOut === true ? '' : ' ' + this.bailedOut
      this.emit('line', 'Bail out!' + reason + '\n')
    }

    let skipAll: boolean = false

    if (this.planEnd === 0 && this.planStart === 1) {
      skipAll = true
      if (this.count === 0) {
        this.ok = true
      } else {
        this.tapError('Plan of 1..0, but test points encountered', '')
      }
    } else if (!this.bailedOut && this.planStart === -1) {
      if (this.count === 0 && !this.syntheticPlan) {
        this.syntheticPlan = true
        if (this.buffered) {
          this.planStart = 1
          this.planEnd = 0
        } else {
          if (!this.#sawVersion) {
            this.version(14, 'TAP version 14\n')
          }
          this.plan(1, 0, 'no tests found', '1..0 # no tests found\n')
        }
        skipAll = true
      } else {
        this.tapError('no plan', '')
      }
    } else if (
      this.ok &&
      this.count !== this.planEnd - this.planStart + 1
    ) {
      this.tapError('incorrect number of tests', '')
    }

    this.emitComplete(skipAll)
    if (cb) process.nextTick(cb)

    return this
  }

  emitComplete(skipAll: boolean) {
    if (!this.results) {
      const res = (this.results = new FinalResults(!!skipAll, this))

      if (!res.bailout) {
        // comment a bit at the end so we know what happened.
        // but don't repeat these comments if they're already present.
        if (res.plan.end !== res.count) {
          this.emitComment(
            'test count(' +
              res.count +
              ') != plan(' +
              res.plan.end +
              ')',
            false,
            true,
          )
        }
      }

      this.emit('complete', this.results)
      this.emit('finish')
      this.emit('close')
    }
  }

  version(version: number, line: string) {
    // If version is specified, must be at the very beginning.
    if (
      version >= 13 &&
      this.planStart === -1 &&
      this.count === 0 &&
      !this.#current
    ) {
      this.#sawVersion = true
      this.emit('line', line)
      this.emit('version', version)
    } else this.nonTap(line)
  }

  pragma(key: string, value: boolean, line: string) {
    // can't put a pragma in a child or yaml block
    if (this.#child) {
      this.nonTap(line)
      return
    }

    this.emitResult()
    if (this.bailedOut) return
    // only the 'strict' pragma is currently relevant
    if (key === 'strict') {
      this.strict = value
    }
    this.pragmas[key] = value
    this.emit('line', line)
    this.emit('pragma', key, value)
  }

  bailout(reason: string, synthetic: boolean = false) {
    this.syntheticBailout = synthetic

    if (this.#bailingOut) return

    // Guard because emitting a result can trigger a forced bailout
    // if the harness decides that failures should be bailouts.
    this.#bailingOut = reason || true

    if (!synthetic) this.emitResult()
    else this.#current = null

    this.bailedOut = this.#bailingOut
    this.ok = false
    if (!synthetic) {
      // synthetic bailouts get emitted on end
      let line = 'Bail out!'
      if (reason) line += ' ' + reason
      this.emit('line', line + '\n')
    }
    this.emit('bailout', reason)
    if (this.parent) {
      this.end()
      this.parent.bailout(reason, true)
    }
  }

  clearExtraQueue() {
    for (const [ev, data] of this.#extraQueue) {
      this.emit(ev, data)
    }
    this.#extraQueue.length = 0
  }

  endChild() {
    if (this.#child && (!this.#bailingOut || this.#child.count)) {
      this.#child.time = this.#child.closingTestPoint?.time || null
      this.#previousChild = this.#child
      this.#child.end()
      this.#child = null
    }
  }

  emitResult() {
    if (this.bailedOut) return

    this.endChild()
    this.resetYamlish()

    if (!this.#current) return this.clearExtraQueue()

    const res = this.#current
    this.#current = null

    this.count++
    const { skip, todo } = res
    if (skip) this.skips.push({ ...res, skip })
    if (todo) this.todos.push({ ...res, todo })
    if (res.ok) {
      this.pass++
      if (!skip && !todo && this.passes) this.passes.push(res)
    } else {
      this.fail++
      if (!res.todo && !res.skip) {
        this.ok = false
        this.failures.push(res)
      }
    }

    if (res.skip) this.skip++
    if (res.todo) this.todo++

    this.emitAssert(res)
    if (
      this.bail &&
      !res.ok &&
      !res.todo &&
      !res.skip &&
      !this.#bailingOut
    ) {
      this.#maybeChild = null
      const ind = new Array(this.level + 1).join('    ')
      let p: Parser
      for (p = this; p.parent; p = p.parent);
      const bailName = res.name ? ' ' + res.name : ''
      p.parse(ind + 'Bail out!' + bailName + '\n')
    }
    this.clearExtraQueue()
  }

  // TODO: We COULD say that any "relevant tap" line that's indented
  // by 4 spaces starts a child test, and just call it 'unnamed' if
  // it does not have a prefix comment.  In that case, any number of
  // 4-space indents can be plucked off to try to find a relevant
  // TAP line type, and if so, start the unnamed child.
  startChild(line: string) {
    const maybeBuffered = this.#current && this.#current.buffered
    const unindentStream = !maybeBuffered && this.#maybeChild
    const indentStream =
      !maybeBuffered &&
      !unindentStream &&
      lineTypes.subtestIndent.test(line)

    // If we have any other result waiting in the wings, we need to emit
    // that now.  A buffered test emits its test point at the *end* of
    // the child subtest block, so as to match streamed test semantics.
    if (!maybeBuffered) this.emitResult()

    if (this.bailedOut) return

    this.#child = new Parser({
      bail: this.bail,
      parent: this,
      level: this.level + 1,
      buffered: maybeBuffered || undefined,
      closingTestPoint: (maybeBuffered && this.#current) || undefined,
      preserveWhitespace: this.preserveWhitespace,
      omitVersion: true,
      strict: this.strict,
    })

    this.#child.on('complete', results => {
      if (!results.ok) this.ok = false
    })

    this.#child.on('line', l => {
      if (l.trim() || this.preserveWhitespace) l = '    ' + l
      this.emit('line', l)
    })

    // Canonicalize the parsing result of any kind of subtest
    // if it's a buffered subtest or a non-indented Subtest directive,
    // then synthetically emit the Subtest comment
    line = line.substring(4)
    let subtestComment: string
    if (indentStream) {
      subtestComment = line
      line = ''
    } else if (maybeBuffered && this.#current) {
      subtestComment = '# Subtest: ' + this.#current.name + '\n'
    } else {
      subtestComment = this.#maybeChild || '# Subtest\n'
    }

    this.#maybeChild = null
    this.#child.name = subtestComment
      .substring('# Subtest: '.length)
      .trim()

    // at some point, we may wish to move 100% to preferring
    // the Subtest comment on the parent level.  If so, uncomment
    // this line, and remove the child.emitComment below.
    // this.emit('comment', subtestComment)
    if (!this.#child.buffered) this.emit('line', subtestComment)
    this.emit('child', this.#child)
    this.#child.emitComment(subtestComment, true)
    if (line) this.#child.parse(line)
  }

  destroy(er?: Error) {
    this.abort('destroyed', er)
  }

  abort(message: string = '', extra?: any) {
    if (this.#child) {
      const b = this.#child.buffered
      this.#child.abort(message, extra)
      extra = null
      if (b) this.write('\n}\n')
    }

    let dump: string = ''
    if (extra && Object.keys(extra).length) {
      try {
        dump = yaml.stringify(extra).trimEnd()
        /* c8 ignore start */
      } catch (er) {}
      /* c8 ignore stop */
    }

    const y: string =
      dump ?
        '  ---\n  ' + dump.split('\n').join('\n  ') + '\n  ...\n'
      : '\n'

    const n = (this.count || 0) + 1 + (this.#current ? 1 : 0)

    if (this.planEnd !== -1 && this.planEnd < n && this.parent) {
      // skip it, let the parent do this.
      this.aborted = true
      return
    }

    message = message.replace(/[\n\r\s\t]/g, ' ')
    let point = '\nnot ok ' + n + ' - ' + message + '\n' + y

    if (this.planEnd === -1) point += '1..' + n + '\n'

    if (!this.#sawVersion) this.write('TAP version 14\n')
    this.write(point)
    this.aborted = true
    this.end()
  }

  emitAssert(res: Result) {
    this.emit('assert', res)

    // see if we need to surface to the top level
    const c = this.#child || this.#previousChild
    if (c) {
      this.#previousChild = null
      if (
        res.name === c.name &&
        c.results &&
        res.ok === c.results.ok &&
        c.results.count &&
        !res.todo &&
        !res.skip
      ) {
        // just procedural, ignore it
        return
      }
    }

    // surface result to the top level parser
    this.root.emit('result', res)
    if (res.skip) this.root.emit('skip', res)
    else if (res.todo) this.root.emit('todo', res)
    else if (!res.ok) this.root.emit('fail', res)
    else this.root.emit('pass', res)
  }

  emitComment(
    line: string,
    skipLine: boolean = false,
    noDuplicate: boolean = false,
  ) {
    if (line.trim().charAt(0) !== '#') line = '# ' + line

    if (line.slice(-1) !== '\n') line += '\n'

    // XXX: is this still needed?
    /* c8 ignore start */
    if (noDuplicate && this.comments.indexOf(line) !== -1) return
    /* c8 ignore stop */

    this.comments.push(line)
    const dir = parseDirective(line.replace(/^\s*#\s*/, '').trim())
    if (dir && dir[0] === 'time' && typeof dir[1] === 'number')
      this.time = dir[1]

    if (this.#current || this.#extraQueue.length) {
      // no way to get here with skipLine being true
      this.#extraQueue.push(['line', line])
      this.#extraQueue.push(['comment', line])
    } else {
      if (!skipLine) this.emit('line', line)
      this.emit('comment', line)
    }
  }

  parse(line: string) {
    // normalize line endings
    line = line.replace(/\r\n$/, '\n')

    // sometimes empty lines get trimmed, but are still part of
    // a subtest or a yaml block.  Otherwise, nothing to parse!
    if (line === '\n') {
      if (this.#child) line = '    ' + line
      else if (this.#yind) line = this.#yind + line
    }

    // If we're bailing out, then the only thing we want to see is the
    // end of a buffered child test.  Anything else should be ignored.
    // But!  if we're bailing out a nested child, and ANOTHER nested child
    // comes after that one, then we don't want the second child's } to
    // also show up, or it looks weird.
    if (this.#bailingOut) {
      if (!/^\s*}\n$/.test(line)) return
      else if (!this.braceLevel || line.length < this.braceLevel)
        this.braceLevel = line.length
      else return
    }

    // This allows omitting even parsing the version if the test is
    // an indented child test.  Several parsers get upset when they
    // see an indented version field.
    if (
      this.omitVersion &&
      lineTypes.version.test(line) &&
      !this.#yind
    ) {
      return
    }

    // check to see if the line is indented.
    // if it is, then it's either a subtest, yaml, or garbage.
    const indent = line.match(/^[ \t]*/)
    if (indent && indent[0]) {
      this.parseIndent(line, indent[0])
      return
    }

    // In any case where we're going to emitResult, that can trigger
    // a bailout, so we need to only emit the line once we know that
    // isn't happening, to prevent cases where there's a bailout, and
    // then one more line of output.  That'll also prevent the case
    // where the test point is emitted AFTER the line that follows it.

    // buffered subtests must end with a }
    if (this.#child && this.#child.buffered && line === '}\n') {
      this.endChild()
      this.emit('line', line)
      this.emitResult()
      return
    }

    // just a \n, emit only if we care about whitespace
    const validLine =
      this.preserveWhitespace || line.trim() || this.#yind
    if (line === '\n') return validLine && this.emit('line', line)

    // buffered subtest with diagnostics
    if (
      this.#current &&
      line === '{\n' &&
      this.#current.diag &&
      !this.#current.buffered &&
      !this.#child
    ) {
      this.emit('line', line)
      this.#current.buffered = true
      return
    }

    // now we know it's not indented, so if it's either valid tap
    // or garbage.  Get the type of line.
    const type = lineType(line)
    if (!type) {
      this.nonTap(line)
      return
    }

    if (type[0] === 'comment') {
      this.emitComment(line)
      return
    }

    // if we have any yamlish, it's garbage now.  We tolerate non-TAP and
    // comments in the midst of yaml (though, perhaps, that's questionable
    // behavior), but any actual TAP means that the yaml block was just
    // not valid.
    if (this.#yind) this.yamlGarbage()

    // If it's anything other than a comment or garbage, then any
    // maybeChild is just an unsatisfied promise.
    if (this.#maybeChild) {
      this.emitComment(this.#maybeChild)
      this.#maybeChild = null
    }

    // nothing but comments can come after a trailing plan
    if (this.#postPlan) {
      this.nonTap(line)
      return
    }

    // ok, now it's maybe a thing
    if (type[0] === 'bailout') {
      const msg = type[1]?.[1] || ''
      this.bailout(unesc(msg), false)
      return
    }

    if (type[0] === 'pragma') {
      const [_, posNeg, key] = type[1]
      this.pragma(String(key), posNeg === '+', line)
      return
    }

    if (type[0] === 'version') {
      const version = type[1]
      this.version(parseInt(String(version[1]), 10), line)
      return
    }

    if (type[0] === 'plan') {
      const plan = type[1]
      this.plan(
        +String(plan[1]),
        +String(plan[2]),
        unesc(plan[3] || '').trim(),
        line,
      )
      return
    }

    // streamed subtests will end when this test point is emitted
    if (type[0] === 'testPoint') {
      // note: it's weird, but possible, to have a testpoint ending in
      // { before a streamed subtest which ends with a test point
      // instead of a }.  In this case, the parser gets confused, but
      // also, even beginning to handle that means doing a much more
      // involved multi-line parse.  By that point, the subtest block
      // has already been emitted as a 'child' event, so it's too late
      // to really do the optimal thing.  The only way around would be
      // to buffer up everything and do a multi-line parse.  This is
      // rare and weird, and a multi-line parse would be a bigger
      // rewrite, so I'm allowing it as it currently is.
      this.parseTestPoint(type[1], line)
      return
    }

    // We already detected nontap up above, so the only case left
    // should be a `# Subtest:` comment.  Ignore for coverage, but
    // include the error here just for good measure.
    if (type[0] === 'subtest') {
      // this is potentially a subtest.  Not indented.
      // hold until later.
      this.#maybeChild = line
      /* c8 ignore start */
    } else {
      throw new Error('Unhandled case: ' + type[0])
    }
    /* c8 ignore stop */
  }

  parseIndent(line: string, indent: string) {
    // still belongs to the child, so pass it along.
    if (this.#child && line.substring(0, 4) === '    ') {
      line = line.substring(4)
      this.#child.write(line)
      return
    }

    // one of:
    // - continuing yaml block
    // - starting yaml block
    // - ending yaml block
    // - body of a new child subtest that was previously introduced
    // - An indented subtest directive
    // - A comment, or garbage

    // continuing/ending yaml block
    if (this.#yind) {
      if (line.indexOf(this.#yind) === 0) {
        this.emit('line', line)
        this.yamlishLine(line)
        return
      } else {
        // oops!  that was not actually yamlish, I guess.
        // this is a case where the indent is shortened mid-yamlish block
        // treat existing yaml as garbage, continue parsing this line
        this.yamlGarbage()
      }
    }

    // start a yaml block under a test point
    if (this.#current && !this.#yind && line === indent + '---\n') {
      this.#yind = indent
      this.emit('line', line)
      return
    }

    // at this point, not yamlish, and not an existing child test.
    // We may have already seen an unindented Subtest directive, or
    // a test point that ended in { indicating a buffered subtest
    // Child tests are always indented 4 spaces.
    if (line.substring(0, 4) === '    ') {
      if (
        this.#maybeChild ||
        (this.#current && this.#current.buffered) ||
        lineTypes.subtestIndent.test(line)
      ) {
        this.startChild(line)
        return
      }

      // It's _something_ indented, if the indentation is divisible by
      // 4 spaces, and the result is actual TAP of some sort, then do
      // a child subtest for it as well.
      //
      // This will lead to some ambiguity in cases where there are multiple
      // levels of non-signaled subtests, but a Subtest comment in the
      // middle of them, which may or may not be considered "indented"
      // See the subtest-no-comment-mid-comment fixture for an example
      // of this.  As it happens, the preference is towards an indented
      // Subtest comment as the interpretation, which is the only possible
      // way to resolve this, since otherwise there's no way to distinguish
      // between an anonymous subtest with a non-indented Subtest comment,
      // and an indented Subtest comment.
      const s = line.match(/( {4})+(.*\n)$/)
      if (s && s[2]?.charAt(0) !== ' ') {
        // integer number of indentations.
        const type = lineType(String(s[2]))
        if (type) {
          if (type[0] === 'comment') {
            this.emit('line', line)
            this.emitComment(line)
          } else {
            // it's relevant!  start as an "unnamed" child subtest
            this.startChild(line)
          }
          return
        }
      }
    }

    // at this point, it's either a non-subtest comment, or garbage.

    if (lineTypes.comment.test(line)) {
      this.emitComment(line)
      return
    }

    this.nonTap(line)
  }

  static parse(str: string, options: ParserOptions = {}): EventLog {
    return parse(str, options)
  }

  static stringify(msg: EventLog, options = {}): string {
    return stringify(msg, options)
  }
}

import yaml from 'tap-yaml'
import { cleanYamlObject } from './clean-yaml-object.js'
import { Extra } from './index.js'

const hasDefinedKey = (o: Extra) => {
  for (const v of Object.values(o)) {
    if (v !== undefined && v !== null) return true
  }
  return false
}

/**
 * Print the YAML diagnostics based on the {@link @tapjs/core!index.Extra}
 * object received
 */
export const diags = (obj: Extra) => {
  const clean = cleanYamlObject(obj)
  if (!clean || typeof clean !== 'object' || !hasDefinedKey(clean)) {
    return ''
  }
  return (
    '  ---\n' +
    yaml
      .stringify(clean)
      .split('\n')
      .map(l => (l.trim() ? '  ' + l : l.trim()))
      .join('\n') +
    '  ...\n'
  )
}

/**
 * get the appropriate failure message from an error object to print
 * in a `not ok` test point when unhandled throws or rejections happen.
 */
export const messageFromError = (er: unknown): string => {
  if (typeof er === 'string') return er
  if (isErrorLike(er)) {
    const { name, message, stack, error, code } = er
    if (error && typeof error == 'string') return error
    const nc = name && typeof name === 'string' ? `${name}: ` : ''
    const ncCode =
      nc && typeof code === 'string' ? `${name} [${code}]: ` : ''
    if (message && typeof message === 'string') return message
    if (typeof stack === 'string' && stack.trim()) {
      const lines = stack.trim().split('\n')
      const line = String(lines[0])
      return (
        name && line.startsWith(nc) ? line.substring(nc.length)
        : ncCode && line.startsWith(ncCode) ?
          line.substring(ncCode.length)
        : line
      )
    }
  }
  return 'unhandled error'
}

type ErrorLike =
  | (Error & { error?: any; code?: string })
  | {
      error?: any
      name?: string
      message?: string
      stack?: any
      code?: string
    }

const isErrorLike = (er: unknown): er is ErrorLike =>
  !!er &&
  typeof er === 'object' &&
  (er instanceof Error ||
    typeof (er as ErrorLike).error !== 'undefined' ||
    typeof (er as ErrorLike).name !== 'undefined' ||
    typeof (er as ErrorLike).message !== 'undefined' ||
    typeof (er as ErrorLike).stack !== 'undefined') &&
  ((er as ErrorLike).code === undefined ||
    typeof (er as ErrorLike).code === 'string')

import { builtinModules } from 'node:module'
import { resolve } from 'node:path'
import { CallSiteLike } from './call-site-like.js'
import { requireResolve } from './require-resolve.js'
export { CallSiteLike, CallSiteLikeJSON } from './call-site-like.js'
export type { GeneratedResult } from './call-site-like.js'
export type { Compiled, LineRef } from './parse.js'

const regExpEscape = (s: string) =>
  s.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&')

// platform portability accommodation
/* c8 ignore start */
let cwd =
  (
    typeof process === 'object' &&
    process &&
    typeof process.cwd === 'function'
  ) ?
    process.cwd()
  : undefined
/* c8 ignore stop */

/**
 * Set the effective cwd for shortening filenames in stack traces
 * Set to `undefined` to show full absolute paths.
 */
export const setCwd = (c: string | undefined) => (cwd = c)

/**
 * Get the effective cwd for shortening filenames in stack traces
 * If set to `undefined`, then will show full absolute paths.
 */
export const getCwd = () => cwd

let filterNodeInternals = true
/**
 * Get the current value indicating whether node internals should be
 * filtered out. (Defaults to true)
 */
export const getFilterNodeInternals = () => filterNodeInternals
/**
 * Set whether node internals should be filtered out.
 */
export const setFilterNodeInternals = (s: boolean) =>
  (filterNodeInternals = s)

// these are packages that function somewhat like node internals,
// for tap's purposes, and `@tapjs` packages themselves, when loaded
// from node_modules.
const ignoredPackages: string[] = [
  '@tapjs',
  '@isaacs/ts-node-temp-fork-for-pr-2009',
  'pirates',
  'function-loop',
  '@cspotcode/source-map-support',
  'signal-exit',
  'async-hook-domain',
]
let dirty: boolean = false

/**
 * Add a package name to the list of deps that should be excluded
 * from stack traces.
 */
export const addIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i === -1) {
    ignoredPackages.push(s)
    dirty = true
  }
}

/**
 * Remove a package name from the list of deps that should be excluded
 * from stack traces.
 */
export const removeIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i !== -1) {
    ignoredPackages.splice(i, 1)
    dirty = true
  }
}

/**
 * Get a read-only copy of the list of deps that should be excluded
 * from stack traces.
 */
export const getIgnoredPackages = () =>
  Object.freeze(ignoredPackages.slice(0))

const getTestBuiltPath = () => {
  const p = requireResolve('@tapjs/test')
  // we'll always find the test class in this project
  /* c8 ignore start */
  if (!p) return ''
  /* c8 ignore stop */
  return resolve(p, '../../../test-built')
}

const buildIgnoredPackages = () => {
  // just a safety precaution, no reason to ever do this
  /* c8 ignore start */
  if (!ignoredPackages.length) return undefined
  /* c8 ignore stop */
  const p = ignoredPackages.map(s => regExpEscape(s)).join('|')
  const nm = `[/\\\\]node_modules[/\\\\](?:${p})([/\\\\]|$)`
  // if we are ignoring @tapjs/test, then also ignore its built
  // plugged-in implementation. This is only relevant when developing
  // this project, or other cases where @tapjs/test may be linked,
  // because when it's loaded from node_modules, it'll be excluded
  // by virtue of being in that folder anyhow.
  const built =
    (
      ignoredPackages.includes('@tapjs') ||
      ignoredPackages.includes('@tapjs/test')
    ) ?
      getTestBuiltPath()
    : ''
  const re = built ? `${built}([/\\\\].*|$)|${nm}` : nm
  return new RegExp(re)
}

/**
 * exported for testing, no real purpose, but also no harm in looking
 */
export const getIgnoredPackagesRE = () =>
  !dirty ? ignoredPackagesRE : (
    (ignoredPackagesRE = buildIgnoredPackages())
  )

let ignoredPackagesRE: RegExp | undefined = buildIgnoredPackages()
let filterIgnoredPackages = true

/**
 * Set whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const setFilterIgnoredPackages = (s: boolean) =>
  (filterIgnoredPackages = s)

/**
 * Get whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const getFilterIgnoredPackages = () => filterIgnoredPackages

// detect the first line of Error.stack, 'Error: blah'
const isErrorStackHead = (c?: CallSiteLike): boolean =>
  !!c &&
  c.lineNumber === null &&
  c.columnNumber === null &&
  c.this === undefined &&
  c.evalOrigin === undefined &&
  c.function === undefined &&
  c.typeName === null &&
  c.methodName === null &&
  (typeof c.functionName === 'string' || !c.functionName) &&
  c.isEval === false &&
  c.isNative === false &&
  c.isToplevel === false &&
  c.isConstructor === false &&
  c.generated === undefined

const filter = (c: CallSiteLike): boolean => {
  const s = c.fileName
  // technically this is possible, but super unlikely
  /* c8 ignore start */
  if (!s) return true
  /* c8 ignore stop */
  if (dirty && filterIgnoredPackages) {
    ignoredPackagesRE = buildIgnoredPackages()
    dirty = false
  }
  return (
    (!filterNodeInternals ||
      !(s.startsWith('node:') || builtinModules.includes(s))) &&
    (!filterIgnoredPackages || !ignoredPackagesRE?.test(s))
  )
}

const clean = (c: CallSiteLike[]): CallSiteLike[] => {
  const filtered = c.filter(filter)
  while (isErrorStackHead(filtered[0])) filtered.shift()
  if (cwd !== undefined) {
    for (const c of filtered) {
      c.cwd = cwd
    }
  }
  return filtered
}

let capturing = false
/**
 * Get an array of {@link @tapjs/stack!call-site-like.CallSiteLike} objects for
 * the current location, from the call to the `fn` argument supplied, limited
 * to the number of frames specified by `limit`.
 *
 * If the `limit` argument is 0, then the current `Error.stackTraceLimit`
 * value will be used.
 *
 * This method is not re-entry safe, due to the fact that it relies on
 * temporarily overriding the global Error.prepareStackTrace. As a result,
 * if a capture() is triggered in any of the methods used by the
 * CallSiteLike constructor (for example, if `@tapjs/intercept` is used to
 * capture the process.cwd() method, which is used by path.resolve()),
 * then that will cause problems. To work around this, if a re-entry is
 * detected, then an empty stack of [] is returned.
 *
 * Even if it was made re-entry safe, it would be easy to accidentally
 * trigger an infinite recursion and stack overflow in such a scenario, so
 * returning an empty stack in the case of re-entry is the best workaround.
 */
export function capture(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(limit: number): CallSiteLike[]
export function capture(
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(): CallSiteLike[]
export function capture(
  limit: number | Function | ((...a: any[]) => any) = 0,
  fn: Function | ((...a: any[]) => any) = capture,
): CallSiteLike[] {
  // should be impossible to hit, but can if we have to look up
  // a sourcemap for a file we haven't seen before, and the filename
  // happens to come through as a url or relative path.
  /* c8 ignore start */
  if (capturing) return []
  /* c8 ignore stop */
  capturing = true
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const { prepareStackTrace, stackTraceLimit } = Error
  Error.prepareStackTrace = CallSiteLike.prepareStackTrace
  if (limit) {
    // we always get an extra few frames to account for internals
    // or proxy frames that might be filtered out of the top.
    Error.stackTraceLimit = limit + 10
  }
  const obj: { stack: CallSiteLike[] } = { stack: [] }
  Error.captureStackTrace(obj, fn)
  const { stack } = obj
  Object.assign(Error, { prepareStackTrace, stackTraceLimit })
  const st = clean(stack)
  capturing = false
  return limit === 0 ? st : st.slice(0, limit)
}

/**
 * Get the call site in the stack either where `at()` is called, or
 * where the supplied `fn` function is called.
 *
 * If `fn` is provided, and is not in the current call stack, then
 * `undefined` will be returned.
 */
export const at: (
  fn?: Function | ((...a: any[]) => any),
) => CallSiteLike | undefined = (fn = at) => {
  const [site] = capture(1, fn)
  return site
}

/**
 * Same as {@link @tapjs/stack!index.capture}, but return the `toString()`
 * values of the {@link @tapjs/stack!call-site-like.CallSiteLike} objects
 */
export function captureString(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): string
export function captureString(limit: number): string
export function captureString(
  fn?: Function | ((...a: any[]) => any),
): string
export function captureString(): string
export function captureString(
  limit: number | Function | ((...a: any[]) => any) = Infinity,
  fn: Function | ((...a: any[]) => any) = captureString,
): string {
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const stack = capture(limit, fn)
  return (limit === 0 ? stack : stack.slice(0, limit))
    .map(c => String(c) + '\n')
    .join('')
}

/**
 * Get an array of {@link CallSiteLike} objects corresponding to the stack
 * trace of the Error object provided.
 *
 * This does _not_ actually look at the current call site, or do anything
 * magical with the V8 engine. It's just parsing a string.
 *
 * While some effort is made to interpret stacks correctly when an Error
 * contains a `name` and `message`, remember that the `Error.stack` property in
 * JavaScript is remarkably sloppy. In some cases, if the `Error.message`
 * contains `\n` and some lines after the first look like stack trace lines,
 * incorrect data may result. It's only as good as the stack you pass to it.
 */
export const captureError = (
  e: Error | NodeJS.ErrnoException,
): CallSiteLike[] => {
  // errors almost always have these fields
  const { message = '', name = '', code } = e as NodeJS.ErrnoException
  const stack = typeof e.stack === 'string' ? e.stack : ''
  const head = name && message ? `${name}: ${message}\n` : ''
  const errnoHead =
    name && message && code ? `${name} [${code}]: ${message}` : ''
  const cleanHead = !!head && stack.startsWith(head)
  const cleanErrnoHead = !!errnoHead && stack.startsWith(errnoHead)

  const s =
    cleanHead ? stack.substring(head.length)
    : cleanErrnoHead ? stack.substring(errnoHead.length)
    : stack
  const cleaned = clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l)
      .map(line => new CallSiteLike(e, line)),
  )

  // if we didn't clean the header cleanly, then sweep the stack for
  // any weird junk it might contain
  return cleanHead ? cleaned : (
      cleaned.filter(c => !isErrorStackHead(c))
    )
}

/**
 * Get a processed string stack corresponding to the stack trace of the Error
 * object provided.
 *
 * This method has all the same caveats as {@link captureError}. If the
 * object provided has a weird looking `stack` property, then you might get
 * weird results.
 */
export const captureErrorString = (e: Error): string =>
  captureError(e)
    .map(c => String(c) + '\n')
    .join('')

/**
 * Parse a stack string and return an array of CallSiteLike objects
 *
 * We use this to get the `at` diagnostic callsite when all we have is
 * a stack, either from a native Error object, or a stringified CallSiteLike
 * stack.
 */
export const parseStack = (s: string): CallSiteLike[] =>
  clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l.trim())
      .map(line => new CallSiteLike(null, line)),
  )

export const expandStack = (s?: string | CallSiteLike[]): string => {
  if (!s) return ''
  if (typeof s === 'string') return expandStack(parseStack(s))
  return clean(s)
    .map(c => c.toString(true) + '\n')
    .join('')
}

import {
  exec,
  execFile,
  execFileSync,
  execSync,
  fork,
  spawn,
  spawnSync,
} from './child_process.js'

export * from './child_process.js'
export * from './process-info-node.js'
export { WithExternalID } from './spawn-opts.js'

import { mkdirSync, readdirSync, rmSync, Stats, writeFileSync } from 'fs'
import { mkdir, readdir, rm, stat, writeFile } from 'fs/promises'
import { basename, resolve } from 'path'
import { getExclude } from './get-exclude.js'
import { safeJSON, safeJSONSync } from './json-file.js'
import { ProcessInfoNode } from './process-info-node.js'

const p = process
const tryStat = async (f: string, stats: Map<string, Stats | null>) => {
  stats.set(f, await stat(f).catch(() => null))
}

export class ProcessInfo {
  dir: string
  exclude: RegExp
  roots: Set<ProcessInfoNode> = new Set()
  uuids: Map<string, ProcessInfoNode> = new Map()
  files: Map<string, Set<ProcessInfoNode>> = new Map()
  pendingRoot: Map<string, Set<ProcessInfoNode>> = new Map()
  pendingParent: Map<string, Set<ProcessInfoNode>> = new Map()
  externalIDs: Map<string, ProcessInfoNode> = new Map()

  static async load({
    dir = resolve(p.cwd(), '.tap/processinfo'),
    exclude = /(^|\\|\/)node_modules(\\|\/|$)/,
  }): Promise<ProcessInfo> {
    const pi = new ProcessInfo({ dir, exclude })
    await pi.load()
    return pi
  }

  static loadSync({
    dir = resolve(p.cwd(), '.tap/processinfo'),
    exclude = /(^|\\|\/)node_modules(\\|\/|$)/,
  }): ProcessInfo {
    const pi = new ProcessInfo({ dir, exclude })
    pi.loadSync()
    return pi
  }

  constructor({
    dir = resolve(p.cwd(), '.tap/processinfo'),
    exclude = /(^|\\|\/)node_modules(\\|\/|$)/,
  } = {}) {
    this.dir = dir
    this.exclude = exclude
  }

  clear() {
    this.roots.clear()
    this.files.clear()
    this.uuids.clear()
    this.externalIDs.clear()
  }

  async save() {
    await mkdir(this.dir, { recursive: true })
    const writes = []
    for (const [uuid, info] of this.uuids.entries()) {
      const f = `${this.dir}/${uuid}.json`
      writes.push(writeFile(f, JSON.stringify(info) + '\n', 'utf8'))
    }
    await Promise.all(writes)
  }

  async saveSync() {
    mkdirSync(this.dir, { recursive: true })
    for (const [uuid, info] of this.uuids.entries()) {
      const f = `${this.dir}/${uuid}.json`
      writeFileSync(f, JSON.stringify(info) + '\n', 'utf8')
    }
  }

  async erase() {
    this.clear()
    await rm(this.dir, { recursive: true })
  }

  eraseSync() {
    this.clear()
    rmSync(this.dir, { recursive: true })
  }

  async load() {
    const promises = []
    for (const entry of await readdir(this.dir).catch(() => [])) {
      const uuid = basename(entry, '.json')
      if (this.uuids.has(uuid)) {
        continue
      }
      const f = resolve(this.dir, entry)
      promises.push(
        safeJSON(f).then(data => {
          if (!data.uuid || data.uuid !== uuid) {
            return
          }
          new ProcessInfoNode(data).link(this)
        })
      )
    }
    await Promise.all(promises)

    return this
  }

  loadSync() {
    let entries: string[]
    try {
      entries = readdirSync(this.dir)
    } catch (_) {
      entries = []
    }
    for (const entry of entries) {
      const uuid = basename(entry, '.json')
      if (this.uuids.has(uuid)) {
        continue
      }
      const f = resolve(this.dir, entry)
      const data = safeJSONSync(f)
      if (!data.uuid || data.uuid !== uuid) {
        continue
      }
      new ProcessInfoNode(data).link(this)
    }

    return this
  }

  #statFiles(
    node: ProcessInfoNode,
    stats: Map<string, Stats | null>,
    exclude: RegExp
  ) {
    const promises: Promise<void>[] = []

    for (const f of node.files) {
      if (exclude.test(f)) continue
      // race
      /* c8 ignore start */
      if (stats.has(f)) continue
      /* c8 ignore stop */
      promises.push(tryStat(f, stats))
      const src = node.sources[f]
      if (src) {
        for (const s of src) {
          /* c8 ignore start */
          if (stats.has(s)) continue
          /* c8 ignore stop */
          promises.push(tryStat(s, stats))
        }
      }
    }
    for (const c of node.descendants ?? []) {
      promises.push(...this.#statFiles(c, stats, exclude))
    }
    return promises
  }

  // if any files are newer than the date, or null, then add it
  // if a file has changed, but its sources haven't, then assume
  // it's still the same content, and not "changed"
  #hasNewerFiles(
    node: ProcessInfoNode,
    stats: Map<string, Stats | null>,
    exclude: RegExp
  ) {
    const p = Date.parse(node.date)
    for (const f of node.files) {
      if (exclude.test(f)) continue
      const st = stats.get(f)
      if (!st) {
        return true
      }
      let c = Number(st.mtime) > p
      if (!c) continue
      const src = node.sources[f]
      if (!src?.length) return true
      else {
        for (const s of src) {
          const sst = stats.get(s)
          if (!sst || Number(sst.mtime) > p) {
            return true
          }
        }
      }
    }
    // no direct files are newer, check descendants
    // if one of them have a changed file, then that's a yes
    for (const c of node.descendants ?? []) {
      if (this.#hasNewerFiles(c, stats, exclude)) {
        return true
      }
    }
    return false
  }

  /**
   * Get a subset of this.externalIDs where one or more of the
   * files have changed since the date on the node.
   */
  async externalIDsChanged(
    filter: (p: string, node: ProcessInfoNode) => boolean = () => true
  ) {
    const exclude = getExclude('_TAPJS_PROCESSINFO_EXCLUDE_', false)
    const changed = new Map<string, ProcessInfoNode>()
    const promises: Promise<void>[] = []
    const stats = new Map<string, Stats | null>()
    for (const [id, node] of this.externalIDs.entries()) {
      if (!filter(id, node)) continue
      promises.push(...this.#statFiles(node, stats, exclude))
    }
    // consider limiting with promise-call-limit?
    await Promise.all(promises)

    for (const [id, node] of this.externalIDs.entries()) {
      if (filter(id, node) && this.#hasNewerFiles(node, stats, exclude)) {
        changed.set(id, node)
      }
    }
    return changed
  }

  static get Node() {
    return ProcessInfoNode
  }

  static get ProcessInfo() {
    return ProcessInfo
  }

  static get spawn() {
    return spawn
  }

  static get spawnSync() {
    return spawnSync
  }

  static get exec() {
    return exec
  }

  static get execSync() {
    return execSync
  }

  static get execFile() {
    return execFile
  }

  static get execFileSync() {
    return execFileSync
  }

  static get fork() {
    return fork
  }
}

/**
 * A handle around a Promise, so it may be resolved or
 * rejected from outside the Promise constructor.
 */
export class Deferred<T = unknown> {
  /**
   * Resolve the promise
   */
  resolve!: (v: T) => void

  /**
   * Reject the promise
   */
  reject!: (er: any) => void

  /**
   * The promise that gets resolved or rejected
   */
  promise: Promise<T> = new Promise((res, rej) => {
    this.resolve = res
    this.reject = rej
  })
}

/**
 * Sigil for implicit end() calls that should not
 * trigger an error if the user then calls t.end()
 */
export const IMPLICIT = Symbol('implicit end')

// Note: since nyc uses this module to output coverage, any lines
// that are in the direct sync flow of nyc's outputCoverage are
// ignored, since we can never get coverage for them.
// grab a reference to node's real process object right away
import { signals } from './signals.js'
export { signals }

// just a loosened process type so we can do some evil things
type ProcessRE = NodeJS.Process & {
  reallyExit: (code?: number | undefined | null) => any
  emit: (ev: string, ...a: any[]) => any
}

const processOk = (process: any): process is ProcessRE =>
  !!process &&
  typeof process === 'object' &&
  typeof process.removeListener === 'function' &&
  typeof process.emit === 'function' &&
  typeof process.reallyExit === 'function' &&
  typeof process.listeners === 'function' &&
  typeof process.kill === 'function' &&
  typeof process.pid === 'number' &&
  typeof process.on === 'function'

const kExitEmitter = Symbol.for('signal-exit emitter')
const global: typeof globalThis & { [kExitEmitter]?: Emitter } = globalThis
const ObjectDefineProperty = Object.defineProperty.bind(Object)

/**
 * A function that takes an exit code and signal as arguments
 *
 * In the case of signal exits *only*, a return value of true
 * will indicate that the signal is being handled, and we should
 * not synthetically exit with the signal we received. Regardless
 * of the handler return value, the handler is unloaded when an
 * otherwise fatal signal is received, so you get exactly 1 shot
 * at it, unless you add another onExit handler at that point.
 *
 * In the case of numeric code exits, we may already have committed
 * to exiting the process, for example via a fatal exception or
 * unhandled promise rejection, so it is impossible to stop safely.
 */
export type Handler = (
  code: number | null | undefined,
  signal: NodeJS.Signals | null
) => true | void
type ExitEvent = 'afterExit' | 'exit'
type Emitted = { [k in ExitEvent]: boolean }
type Listeners = { [k in ExitEvent]: Handler[] }

// teeny special purpose ee
class Emitter {
  emitted: Emitted = {
    afterExit: false,
    exit: false,
  }

  listeners: Listeners = {
    afterExit: [],
    exit: [],
  }

  count: number = 0
  id: number = Math.random()

  constructor() {
    if (global[kExitEmitter]) {
      return global[kExitEmitter]
    }
    ObjectDefineProperty(global, kExitEmitter, {
      value: this,
      writable: false,
      enumerable: false,
      configurable: false,
    })
  }

  on(ev: ExitEvent, fn: Handler) {
    this.listeners[ev].push(fn)
  }

  removeListener(ev: ExitEvent, fn: Handler) {
    const list = this.listeners[ev]
    const i = list.indexOf(fn)
    /* c8 ignore start */
    if (i === -1) {
      return
    }
    /* c8 ignore stop */
    if (i === 0 && list.length === 1) {
      list.length = 0
    } else {
      list.splice(i, 1)
    }
  }

  emit(
    ev: ExitEvent,
    code: number | null | undefined,
    signal: NodeJS.Signals | null
  ): boolean {
    if (this.emitted[ev]) {
      return false
    }
    this.emitted[ev] = true
    let ret: boolean = false
    for (const fn of this.listeners[ev]) {
      ret = fn(code, signal) === true || ret
    }
    if (ev === 'exit') {
      ret = this.emit('afterExit', code, signal) || ret
    }
    return ret
  }
}

abstract class SignalExitBase {
  abstract onExit(cb: Handler, opts?: { alwaysLast?: boolean }): () => void
  abstract load(): void
  abstract unload(): void
}

const signalExitWrap = <T extends SignalExitBase>(handler: T) => {
  return {
    onExit(cb: Handler, opts?: { alwaysLast?: boolean }) {
      return handler.onExit(cb, opts)
    },
    load() {
      return handler.load()
    },
    unload() {
      return handler.unload()
    },
  }
}

class SignalExitFallback extends SignalExitBase {
  onExit() {
    return () => {}
  }
  load() {}
  unload() {}
}

class SignalExit extends SignalExitBase {
  // "SIGHUP" throws an `ENOSYS` error on Windows,
  // so use a supported signal instead
  /* c8 ignore start */
  #hupSig = process.platform === 'win32' ? 'SIGINT' : 'SIGHUP'
  /* c8 ignore stop */
  #emitter = new Emitter()
  #process: ProcessRE
  #originalProcessEmit: ProcessRE['emit']
  #originalProcessReallyExit: ProcessRE['reallyExit']

  #sigListeners: { [k in NodeJS.Signals]?: () => void } = {}
  #loaded: boolean = false

  constructor(process: ProcessRE) {
    super()
    this.#process = process
    // { <signal>: <listener fn>, ... }
    this.#sigListeners = {}
    for (const sig of signals) {
      this.#sigListeners[sig] = () => {
        // If there are no other listeners, an exit is coming!
        // Simplest way: remove us and then re-send the signal.
        // We know that this will kill the process, so we can
        // safely emit now.
        const listeners = this.#process.listeners(sig)
        let { count } = this.#emitter
        // This is a workaround for the fact that signal-exit v3 and signal
        // exit v4 are not aware of each other, and each will attempt to let
        // the other handle it, so neither of them do. To correct this, we
        // detect if we're the only handler *except* for previous versions
        // of signal-exit, and increment by the count of listeners it has
        // created.
        /* c8 ignore start */
        const p = process as unknown as {
          __signal_exit_emitter__?: { count: number }
        }
        if (
          typeof p.__signal_exit_emitter__ === 'object' &&
          typeof p.__signal_exit_emitter__.count === 'number'
        ) {
          count += p.__signal_exit_emitter__.count
        }
        /* c8 ignore stop */
        if (listeners.length === count) {
          this.unload()
          const ret = this.#emitter.emit('exit', null, sig)
          /* c8 ignore start */
          const s = sig === 'SIGHUP' ? this.#hupSig : sig
          if (!ret) process.kill(process.pid, s)
          /* c8 ignore stop */
        }
      }
    }

    this.#originalProcessReallyExit = process.reallyExit
    this.#originalProcessEmit = process.emit
  }

  onExit(cb: Handler, opts?: { alwaysLast?: boolean }) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return () => {}
    }
    /* c8 ignore stop */

    if (this.#loaded === false) {
      this.load()
    }

    const ev = opts?.alwaysLast ? 'afterExit' : 'exit'
    this.#emitter.on(ev, cb)
    return () => {
      this.#emitter.removeListener(ev, cb)
      if (
        this.#emitter.listeners['exit'].length === 0 &&
        this.#emitter.listeners['afterExit'].length === 0
      ) {
        this.unload()
      }
    }
  }

  load() {
    if (this.#loaded) {
      return
    }
    this.#loaded = true

    // This is the number of onSignalExit's that are in play.
    // It's important so that we can count the correct number of
    // listeners on signals, and don't wait for the other one to
    // handle it instead of us.
    this.#emitter.count += 1

    for (const sig of signals) {
      try {
        const fn = this.#sigListeners[sig]
        if (fn) this.#process.on(sig, fn)
      } catch (_) {}
    }

    this.#process.emit = (ev: string, ...a: any[]) => {
      return this.#processEmit(ev, ...a)
    }
    this.#process.reallyExit = (code?: number | null | undefined) => {
      return this.#processReallyExit(code)
    }
  }

  unload() {
    if (!this.#loaded) {
      return
    }
    this.#loaded = false

    signals.forEach(sig => {
      const listener = this.#sigListeners[sig]
      /* c8 ignore start */
      if (!listener) {
        throw new Error('Listener not defined for signal: ' + sig)
      }
      /* c8 ignore stop */
      try {
        this.#process.removeListener(sig, listener)
        /* c8 ignore start */
      } catch (_) {}
      /* c8 ignore stop */
    })
    this.#process.emit = this.#originalProcessEmit
    this.#process.reallyExit = this.#originalProcessReallyExit
    this.#emitter.count -= 1
  }

  #processReallyExit(code?: number | null | undefined) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return 0
    }
    this.#process.exitCode = code || 0
    /* c8 ignore stop */

    this.#emitter.emit('exit', this.#process.exitCode, null)
    return this.#originalProcessReallyExit.call(
      this.#process,
      this.#process.exitCode
    )
  }

  #processEmit(ev: string, ...args: any[]): any {
    const og = this.#originalProcessEmit
    if (ev === 'exit' && processOk(this.#process)) {
      if (typeof args[0] === 'number') {
        this.#process.exitCode = args[0]
        /* c8 ignore start */
      }
      /* c8 ignore start */
      const ret = og.call(this.#process, ev, ...args)
      /* c8 ignore start */
      this.#emitter.emit('exit', this.#process.exitCode, null)
      /* c8 ignore stop */
      return ret
    } else {
      return og.call(this.#process, ev, ...args)
    }
  }
}

const process = globalThis.process
// wrap so that we call the method on the actual handler, without
// exporting it directly.
export const {
  /**
   * Called when the process is exiting, whether via signal, explicit
   * exit, or running out of stuff to do.
   *
   * If the global process object is not suitable for instrumentation,
   * then this will be a no-op.
   *
   * Returns a function that may be used to unload signal-exit.
   */
  onExit,

  /**
   * Load the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  load,

  /**
   * Unload the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  unload,
} = signalExitWrap(
  processOk(process) ? new SignalExit(process) : new SignalExitFallback()
)

/**
 * Plugin class providing {@link @tapjs/after!After#after} and
 * {@link @tapjs/after!After#teardown} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import { isPromise } from 'is-actual-promise'

/**
 * Implementation class returned by plugin function
 */
export class After {
  #t: TestBase
  #onTeardown: (() => any)[] = []
  #didOnEOF: boolean = false

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Alias for {@link @tapjs/after!After#after}
   *
   * @group Test Lifecycle Management
   */
  teardown(fn: () => any) {
    return this.after(fn)
  }

  /**
   * Runs the supplied function after the test is completely finished, and
   * before the next test starts.
   *
   * @group Test Lifecycle Management
   */
  after(fn: () => any) {
    this.#onTeardown.push(fn)

    if (!this.#didOnEOF) {
      this.#didOnEOF = true
      const onEOF = this.#t.onEOF
      this.#t.onEOF = () => {
        const ret = onEOF()
        if (isPromise(ret)) {
          return ret.then(() => this.#callTeardown())
        }
        return this.#callTeardown()
      }
    }
  }

  /**
   * call the teardown functions
   *
   * @internal
   */
  #callTeardown(): void | Promise<void> {
    let fn: (() => any) | undefined
    while ((fn = this.#onTeardown.pop())) {
      try {
        const ret = fn.call(this.#t.t)
        if (isPromise(ret)) {
          return ret.then(() => this.#callTeardown())
        }
      } catch (e) {
        this.#onTeardown.length = 0
        this.#t.threw(e)
        return
      }
    }
  }
}

/**
 * Plugin method that creates the {@link @tapjs/after!After} instance
 */
export const plugin: TapPlugin<After> = (t: TestBase) => new After(t)

/**
 * Plugin class providing
 * {@link @tapjs/before-each!BeforeEach#beforeEach} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import type { Test } from '@tapjs/test'
import { loop } from 'function-loop'
import { TestBase } from '@tapjs/core'

/**
 * Implementation class returned by plugin
 */
export class BeforeEach {
  static #refs = new Map<TestBase, BeforeEach>()
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
    BeforeEach.#refs.set(t, this)
    const pbe = t.parent && BeforeEach.#refs.get(t.parent)
    if (pbe && !t.silent) {
      this.#onBeforeEach = [...pbe.#onBeforeEach]
    } else {
      this.#onBeforeEach = []
    }
    if (this.#onBeforeEach.length) {
      const runMain = t.runMain
      // only wrap runMain if there actually is a beforeEach
      // on the parent test. we only do this once, so any beforeEach
      // added between test initiation and test run is ignored.
      t.runMain = (cb: () => void) => {
        this.#runBeforeEach(() => runMain.call(t, cb))
      }
    }
  }

  #onBeforeEach: ((t: Test) => any)[]

  /**
   * Run the supplied function before any child tests, and all of their
   * children, and so on.
   *
   * The test about to run is an argument to the function. While its test
   * method has not yet run, it is safe to call test methods on it, but note
   * that this may potentially be confusing if for example you call `t.plan()`
   * and this conflicts with the `t.plan()` called in the test method.
   *
   * @group Test Lifecycle Management
   */
  beforeEach(fn: (t: Test) => any) {
    this.#onBeforeEach.push(fn)
  }

  #runBeforeEach(cb: () => void) {
    const onerr = (er: any) => {
      this.#t.threw(er)
      cb()
    }
    loop(
      this.#onBeforeEach.map(f => () => f(this.#t.t)),
      cb,
      onerr,
    )
  }
}

/**
 * plugin method that instantiates the {@link @tapjs/before-each!BeforeEach} object
 */
export const plugin = (t: TestBase) => new BeforeEach(t)

/**
 * Plugin class providing {@link @tapjs/after-each!AfterEach#afterEach}
 * on the {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import type { Test } from '@tapjs/test'
import { loop } from 'function-loop'

/**
 * Implementation class returned by plugin function
 */
export class AfterEach {
  static #refs = new Map<TestBase, AfterEach>()
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
    AfterEach.#refs.set(t, this)
    const pae = t.parent && AfterEach.#refs.get(t.parent)
    if (
      pae &&
      !t.silent &&
      (pae.#onAfterEach.length || pae.#parentOnAfterEach.length)
    ) {
      this.#parentOnAfterEach = [
        ...pae.#onAfterEach,
        ...pae.#parentOnAfterEach,
      ]
    } else {
      this.#parentOnAfterEach = []
    }
    if (this.#parentOnAfterEach.length) {
      const runMain = t.runMain
      t.runMain = (cb: () => void) => {
        runMain.call(t, () => this.#runAfterEach(cb))
      }
    }
  }
  #onAfterEach: ((t: Test) => any)[] = []
  #parentOnAfterEach: ((t: Test) => any)[] = []

  /**
   * Run the supplied function after every *child* test, and any of those
   * child tests' children, and so on.
   *
   * The test that has just completed is passed in as an argument to the
   * function. Note that at this point, the test is fully ended, so attempting
   * to call assertion methods on it will raise an error.
   *
   * @group Test Lifecycle Management
   */
  afterEach(fn: (t: Test) => any) {
    this.#onAfterEach.push(fn)
  }

  #runAfterEach(cb: () => void) {
    const onerr = (er: any) => {
      this.#t.threw(er)
      cb()
    }
    loop(
      this.#parentOnAfterEach.map(f => () => f(this.#t.t)),
      cb,
      onerr,
    )
  }
}

/**
 * Plugin method that creates the {@link @tapjs/after-each!AfterEach} instance
 */
export const plugin: TapPlugin<AfterEach> = (t: TestBase) =>
  new AfterEach(t)

/**
 * Plugin class providing {@link @tapjs/before!Before#before} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import { isPromise } from 'is-actual-promise'

/**
 * Implementation class returned by plugin function
 */
export class Before {
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Just run the supplied function right away, but do not run any
   * child tests until it has completed.
   *
   * This is handy when some setup may require asynchronous actions, and
   * tests should wait until after it completes.
   *
   * @group Test Lifecycle Management
   */
  before(fn: () => any) {
    this.#t.currentAssert = this.before
    if (this.#t.occupied) {
      // use a silent test as a roadblock so that async subtests
      // scheduled after this will have to wait for it.
      if (this.#t.jobs > 1) {
        this.#t.t.test(
          'pause for before()',
          { silent: true, buffered: false },
          t => t.end(),
        )
      }
      this.#t.queue.push(() => this.#call(fn, true))
    } else {
      this.#call(fn)
    }
  }

  #call(fn: () => any, deferred = false) {
    this.#t.currentAssert = this.before
    // if it throws, we let it kill the test
    const ret = fn.call(this.#t.t)

    // no need to waitOn it if we're already deferred, because the
    // TestBase will already wait on functions that return promises.
    if (isPromise(ret) && !deferred) {
      this.#t.waitOn(ret, w => {
        if (w.rejected) {
          // sort of a mini bailout, just for this one test
          // drop everything from the queue, quit right away
          this.#t.queue.length = 0
          this.#t.threw(w.value)
          this.#t.end()
        }
      })
    }

    return ret
  }
}

/**
 * Plugin method that creates the {@link @tapjs/before!Before} instance
 */
export const plugin: TapPlugin<Before> = (t: TestBase) =>
  new Before(t)

import { plugin as TeardownPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import { at, CallSiteLike, captureString } from '@tapjs/stack'

const notConfig = (
  m: string,
  property: PropertyKey,
  caller: ((...a: any[]) => any) | Function,
) => {
  const s =
    typeof property === 'string' ?
      `'${property}'`
    : property.toString()
  const er = new Error(
    `Cannot ${m} ${s}, defined {configurable:false}`,
  )
  Error.captureStackTrace(er, caller)
  return er
}

/**
 * An object type containing all of the methods of the object
 * type provided
 */
export type MethodsObject<O extends object> = {
  [k in keyof O]: O[k] extends (...a: any[]) => any ? k : never
}

/**
 * The union type of all methods in an object
 */
export type Methods<O extends object> =
  MethodsObject<O>[keyof MethodsObject<O>]

/**
 * Base type of result objects generated by
 * {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultBase<F extends (...a: any[]) => any> {
  args: OverloadParams<F>
  receiver: Receiver<F>
  at?: CallSiteLike
  stack?: string
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * returned
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultReturned<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  returned: OverloadReturnType<F>
  threw: false
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * threw
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultThrew<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  threw: true
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export type CaptureResult<F extends (...a: any[]) => any> =
  | CaptureResultReturned<F>
  | CaptureResultThrew<F>
  | CaptureResultBase<F>

/**
 * like ReturnType<F>, but a union of the return types of up to 10 overloads
 *
 * @typeParam F - the (possibly overloaded) function
 */
export type OverloadReturnType<F> = TupleUnion<
  UnarrayArray<FilterTupleUnknown<ORTuple<F>>>
>

/**
 * Get member type from array/tuple type
 */
export type Unarray<A> = A extends (infer V)[] ? V : A

/**
 * Get tuple of member types from array of array types
 */
export type UnarrayArray<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [Unarray<H>, ...UnarrayArray<T>]
      : [Unarray<H>]
    : true
  : L

/**
 * Get overloaded return values as tuple of arrays
 */
export type ORTuple<F> =
  F extends (
    {
      (...a: any[]): infer A0
      (...a: any[]): infer A1
      (...a: any[]): infer A2
      (...a: any[]): infer A3
      (...a: any[]): infer A4
      (...a: any[]): infer A5
      (...a: any[]): infer A6
      (...a: any[]): infer A7
      (...a: any[]): infer A8
      (...a: any[]): infer A9
    }
  ) ?
    [A0[], A1[], A2[], A3[], A4[], A5[], A6[], A7[], A8[], A9[]]
  : never

/**
 * like Parameters<F>, but a union of parameter sets for up to 10 overloads
 */
export type OverloadParams<F> = TupleUnion<
  FilterUnknown<OverloadParamsTuple<F>>
>

/**
 * Get overloaded Parameters types as 10-tuple. When the function has less
 * than 10 overloaded signatures, the remaining param sets will be set to
 * `unknown`.
 *
 * @internal
 */
export type OverloadParamsTuple<F> =
  F extends (
    {
      (...a: infer A0): any
      (...a: infer A1): any
      (...a: infer A2): any
      (...a: infer A3): any
      (...a: infer A4): any
      (...a: infer A5): any
      (...a: infer A6): any
      (...a: infer A7): any
      (...a: infer A8): any
      (...a: infer A9): any
    }
  ) ?
    [A0, A1, A2, A3, A4, A5, A6, A7, A8, A9]
  : never

/**
 * Convert all `unknown[]` types in an array type to `never`
 */
export type NeverUnknown<T extends unknown[]> =
  unknown[] extends T ?
    (T extends {}[] ? true : false) extends true ?
      any[]
    : never
  : T

/**
 * Convert all `unknown[]` types in an array type to `never[]`
 */
export type NeverTupleUnknown<T extends unknown[]> =
  unknown[] extends T ?
    (T extends {}[] ? true : false) extends true ?
      any[]
    : never[]
  : T

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never`
 */
export type FilterUnknown<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [NeverUnknown<H>, ...FilterUnknown<T>]
      : [NeverUnknown<H>]
    : FilterUnknown<T>
  : L

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never[]`
 */
export type FilterTupleUnknown<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [NeverTupleUnknown<H>, ...FilterTupleUnknown<T>]
      : [NeverTupleUnknown<H>]
    : FilterTupleUnknown<T>
  : L

/**
 * Create a union from a tuple type
 */
export type TupleUnion<L> =
  L extends [infer H, ...infer T] ? H | TupleUnion<T> : never

/**
 * Infer the `this` target of a function
 */
export type Receiver<F> =
  F extends (this: infer T, ...a: any[]) => any ? T : never

/**
 * The method returned by {@link @tapjs/intercept!Interceptor#capture},
 * which returns the {@link @tapjs/intercept!CaptureResult} array when
 * called, and has methods to restore or get args, and exposes the list of
 * calls
 */
export type CaptureResultsMethod<
  T extends {},
  M extends Methods<T>,
  F = T[M],
> = (() => CaptureResult<
  F extends (...a: any[]) => any ? F : (...a: any[]) => any
>[]) & {
  restore: () => void
  calls: CaptureResult<
    F extends (...a: any[]) => any ? F : (...a: any[]) => any
  >[]
  args: () => OverloadParams<
    F extends { (...a: any[]): any } ? F : (...a: any[]) => any
  >[]
}

/**
 * Base class for objects created by
 * {@link @tapjs/intercept!Interceptor#intercept}
 */
export interface InterceptResultBase {
  receiver: any
  at?: CallSiteLike
  stack?: string
  value: any
  success: boolean
  threw: boolean
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for gets
 */
export interface InterceptResultGet extends InterceptResultBase {
  type: 'get'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for sets
 */
export interface InterceptResultSet extends InterceptResultBase {
  type: 'set'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResult = InterceptResultGet | InterceptResultSet

/**
 * Method returned by {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResultsMethod = (() => InterceptResult[]) & {
  restore: () => void
}

/**
 * Implementation class providing the
 * {@link @tapjs/intercept!Interceptor#intercept},
 * {@link @tapjs/intercept!Interceptor#capture}, and
 * {@link @tapjs/intercept!Interceptor#captureFn} methods.
 */
export class Interceptor {
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Intercept and track object property sets and gets.
   *
   * If a PropertyDescriptor is set, then it will be used as the replacement
   * value. Otherwise, the original descriptor will be used.
   *
   * If the `strictMode` param is set, then attempts to write to read-only
   * properties will throw an error.
   *
   * @group Spies, Mocks, and Fixtures
   */
  intercept<T extends object>(
    obj: T,
    prop: keyof T,
    desc?: PropertyDescriptor,
    strictMode: boolean = true,
  ): InterceptResultsMethod {
    const resList: InterceptResult[] = []

    // find the original property descriptor, if it exists.
    let orig = Object.getOwnPropertyDescriptor(obj, prop)
    let src = obj
    while (!orig && src) {
      if (!(orig = Object.getOwnPropertyDescriptor(src, prop))) {
        src = Object.getPrototypeOf(src)
      }
    }
    if (orig && !orig.configurable) {
      throw notConfig('intercept property', prop, this.#t.t.intercept)
    }

    let restore: () => void

    // if we have an original, then that serves as the basis for
    // the value, if desc isn't set. If we don't have a desc, and
    // don't have an orig, then it's just { value: undefined }
    if (!orig || src !== obj) {
      // either it came from the proto chain, or wasn't found.
      // either way, we're assigning here.
      restore = () => {
        delete obj[prop]
        restore = () => {}
      }
    } else {
      // have an original, on the object itself
      restore = () => {
        Object.defineProperty(obj, prop, orig as PropertyDescriptor)
        restore = () => {}
      }
    }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    if (!desc) {
      desc =
        orig ?
          { ...orig }
        : {
            value: undefined,
            configurable: true,
            writable: true,
            enumerable: true,
          }
    }

    const base = desc as PropertyDescriptor
    const interceptor: PropertyDescriptor & {
      get: () => any
      set: (v: any) => void
    } = {
      enumerable: desc.enumerable,
      configurable: true,

      get() {
        // return the current value, and track
        let threw = true
        let success = false
        let value: any
        try {
          value = base.get ? base.get.call(obj) : base.value
          threw = false
          success = true
        } finally {
          resList.push({
            receiver: this,
            type: 'get',
            at: at(interceptor.get),
            stack: captureString(interceptor.get),
            value,
            threw,
            success,
          })
          if (!threw) return value
        }
      },

      set(value: any) {
        let threw = true
        let success = false
        try {
          if (base.set) {
            base.set.call(obj, value)
            success = true
          } else if (base.get) {
            if (strictMode) {
              const er = new TypeError(
                `Cannot set property '${String(
                  prop,
                )}' of ${Object.prototype.toString.call(
                  obj,
                )} which has only a getter`,
              )
              Error.captureStackTrace(er, interceptor.set)
              throw er
            }
          } else {
            if (base.writable) {
              base.value = value
              success = true
            } else {
              if (strictMode) {
                const er = new TypeError(
                  `Cannot assign to read only property '${String(
                    prop,
                  )}' of ${Object.prototype.toString.call(obj)}`,
                )
                Error.captureStackTrace(er, interceptor.set)
                throw er
              }
            }
          }
          threw = false
        } finally {
          resList.push({
            receiver: this,
            type: 'set',
            at: at(interceptor.set),
            stack: captureString(interceptor.set),
            value,
            success,
            threw,
          })
        }
      },
    }

    Object.defineProperty(obj, prop, interceptor)
    return Object.assign(
      () => {
        const r = resList.slice()
        resList.length = 0
        return r
      },
      { restore: () => restore() },
    )
  }

  /**
   * Intercept calls to a method to track the arguments, call site,
   * and return/throw status, and replace the implementation.
   *
   * By default, the method is set to a no-op. To retain the method behavior,
   * pass the current value of the method as the third argument.  For example:
   *
   * ```
   * const results = t.capture(obj, 'foo', obj.foo)
   * ```
   *
   * Automatically restores at `t.teardown()` if the `@tapjs/after`
   * plugin is not disabled.  Otherwise, it is important to call the
   * `restore()` method on the returned function when you are done capturing.
   *
   * @group Spies, Mocks, and Fixtures
   */
  capture<T extends {}, M extends Methods<T>>(
    obj: T,
    method: M,
    impl: (this: T, ...a: any[]) => any = (..._: any[]) => {},
  ): CaptureResultsMethod<T, M> {
    const prop = Object.getOwnPropertyDescriptor(obj, method)
    if (prop && !prop.configurable) {
      throw notConfig('capture method', method, this.#t.t.capture)
    }

    // if we don't have a prop we can restore by just deleting
    // otherwise, we restore by putting it back as it was
    let restore =
      prop ?
        () => {
          Object.defineProperty(obj, method, prop)
          restore = () => {}
        }
      : () => {
          delete obj[method]
          restore = () => {}
        }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    const fn = Object.assign(this.captureFn(impl), {
      restore: () => restore(),
    })

    Object.defineProperty(obj, method, {
      enumerable: prop ? prop.enumerable : true,
      writable: true,
      value: fn,
      configurable: true,
    })

    return Object.assign(
      () => {
        const r = fn.calls.slice()
        fn.calls.length = 0
        return r
      },
      {
        restore: () => restore(),
        calls: fn.calls,
        args: () => {
          const r = fn.calls.slice()
          fn.calls.length = 0
          return r.map(({ args }) => args) as OverloadParams<
            T[M] extends { (...a: any[]): any } ? T[M]
            : (...a: any[]) => any
          >[]
        },
      },
    )
  }

  /**
   * Just wrap the function and return it.  Does not have any
   * logic to restore, since it's not actually modifying anything.
   * The results hang off the function as the 'calls' property.
   *
   * The added `fn.args()` method will return an array of the arguments
   * passed to each call since the last time it was inspected.
   *
   * @group Spies, Mocks, and Fixtures
   */
  captureFn<F extends (this: any, ...a: any[]) => any>(
    original: F,
  ): F & {
    calls: CaptureResult<F>[]
    args: () => OverloadParams<F>[]
  } {
    const calls: CaptureResult<F>[] = []
    const args = () => calls.map(({ args }) => args)
    return Object.assign(
      function wrapped(
        this: Receiver<F>,
        ...args: OverloadParams<F>
      ) {
        const res: CaptureResultBase<F> = {
          receiver: this,
          args,
          at: at(wrapped),
          stack: captureString(wrapped),
        }
        try {
          const returned = res as CaptureResultReturned<F>
          returned.returned = original.call(this, ...args)
          returned.threw = false
          calls.push(res)
          return returned.returned
        } finally {
          if ((res as CaptureResultReturned<F>).threw !== false) {
            ;(res as CaptureResultThrew<F>).threw = true
            calls.push(res)
          }
        }
      } as F,
      { calls, args },
    )
  }
}

/**
 * plugin method that instantiates an {@link @tapjs/intercept!Interceptor}
 */
export const plugin: TapPlugin<Interceptor> = t => new Interceptor(t)

import {
  Extra,
  extraFromError,
  MessageExtra,
  normalizeMessageExtra,
  TapPlugin,
  TestBase,
} from '@tapjs/core'
import * as stack from '@tapjs/stack'
import EventEmitter from 'events'
import { isPromise } from 'is-actual-promise'
import {
  CompareOptions,
  has,
  hasStrict,
  match,
  matchOnly,
  matchOnlyStrict,
  matchStrict,
  same,
  strict,
} from 'tcompare'
import { Deferred } from 'trivial-deferred'

export interface AssertOptions {
  compareOptions?: CompareOptions
}

import { normalizeThrowsArgs } from './normalize-throws-args.js'

export type ErrorMessageMatch = {
  message: string | RegExp
  [k: string]: any
}

export type ErrorNameMatch = {
  name: string | RegExp | null | undefined
  [k: string]: any
}

export type ErrorCodeMatch = {
  code: string | RegExp | number | null | undefined
  [k: string]: any
}

export type ErrorCauseMatch<C extends {}> = {
  cause: C
  [k: string]: any
}

export type ErrorErrorsMatch<C extends {}> = {
  errors: C[]
  [k: string]: any
}

export type ErrorMatch =
  | Error
  | typeof Error
  | ErrorMessageMatch
  | ErrorNameMatch
  | ErrorCodeMatch
  | ErrorCauseMatch<{}>
  | ErrorErrorsMatch<{}>
  | RegExp

export type ThrowsArgs =
  | []
  | [msg: string, extra?: Extra]
  | [wanted: ErrorMatch, ...messageExtra: MessageExtra]

export type ExpectedEmit = [
  emitted: boolean,
  emitter: EventEmitter | EventTarget,
  event: string,
  handler: (...a: any) => void,
  msg: string,
  extra: Extra,
]

// return true of every argument is an object
const objects = (...a: any[]): boolean =>
  !a.some(o => !o || typeof o !== 'object')

const hasOwn = <T extends {}>(
  obj: T,
  key: string | number | symbol,
) => Object.prototype.hasOwnProperty.call(obj, key)

export class Assertions {
  #t: TestBase
  #opts: CompareOptions
  #pendingEmits: ExpectedEmit[] = []
  #setOnBeforeEnd: boolean = false
  constructor(t: TestBase, { compareOptions }: AssertOptions) {
    this.#t = t
    // TODO: this is a pita to do in each plugin
    // Either there should be a straightforward interface for declaring
    // which fields get inherited, or maybe add some logic in TestBase
    // to inherit all but the select few that can't be.
    if (!compareOptions) {
      for (let p = t.parent; p && !compareOptions; p = p.parent) {
        compareOptions = p.options.compareOptions
      }
      if (compareOptions) t.options.compareOptions = compareOptions
    }
    this.#opts = compareOptions || {}
  }

  #onBeforeEnd() {
    for (const [emitted, emitter, event, handler, msg, extra] of this
      .#pendingEmits) {
      if (emitted) {
        this.#t.pass(msg, extra)
      } else {
        if (emitter instanceof EventEmitter) {
          emitter.removeListener(event, handler)
        } else {
          emitter.removeEventListener(event, handler)
        }
        this.#t.fail(msg, extra)
      }
    }
  }

  /**
   * Verify that the value is truthy
   *
   * @group Assertion Methods
   */
  ok(obj: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.ok
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    return obj ? this.#t.pass(...me) : this.#t.fail(...me)
  }

  /**
   * Verify that the value is not truthy
   *
   * @group Assertion Methods
   */
  notOk(obj: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notOk
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    return !obj ? this.#t.pass(...me) : this.#t.fail(...me)
  }

  /**
   * Verify that the values are equal
   *
   * @group Assertion Methods
   */
  equal<T extends unknown>(
    found: any,
    wanted: T,
    ...[msg, extra]: MessageExtra
  ): found is T {
    this.#t.currentAssert = this.#t.t.equal
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    if (found === wanted) return this.#t.pass(...me)
    if (objects(found, wanted)) {
      const { match, diff } = strict(found, wanted, this.#opts)
      if (!match) {
        Object.assign(me[1], { diff, compare: '===' })
      } else {
        Object.assign(me[1], {
          found,
          wanted,
          note: 'object identities differ',
          compare: '===',
        })
      }
    } else {
      Object.assign(me[1], { found, wanted, compare: '===' })
    }
    return this.#t.fail(...me)
  }

  /**
   * Verify that the values are not equal
   *
   * @group Assertion Methods
   */
  not(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.not
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not be equal', args)
    if (found !== doNotWant) {
      return this.#t.pass(...me)
    }
    Object.assign(me[1], {
      found,
      doNotWant,
      compare: '!==',
    })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is of the type specified
   * Type can be either a string, or a constructor.
   *
   * If a string, then it can match either the `typeof` result
   * or 'null' for `null` values, or the `name` property of the
   * object's constructor.
   *
   * @group Assertion Methods
   */
  type(
    obj: any,
    klass: string | Function,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.type

    const name =
      typeof klass === 'function' ?
        klass.name || '(anonymous constructor)'
      : klass

    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(`type is ${name}`, args)

    // simplest case, it literally is the same thing
    if (obj === klass) {
      return this.#t.pass(me[0], me[1])
    }

    const tof = typeof obj
    const type =
      !obj && tof === 'object' ? 'null'
        // treat as object, but not Object
        // t.type(() => {}, Function)
      : (
        tof === 'function' &&
        typeof klass === 'function' &&
        klass !== Object
      ) ?
        'object'
      : tof

    if (
      (type === 'number' && klass === Number) ||
      (type === 'string' && klass === String) ||
      (type === 'bigint' && klass === BigInt) ||
      (klass === 'array' && Array.isArray(obj)) ||
      (type === 'symbol' && klass === Symbol)
    ) {
      return this.#t.pass(...me)
    }

    if (type === 'object' && klass !== 'object') {
      if (typeof klass === 'function') {
        me[1].found = Object.getPrototypeOf(obj).constructor.name
        me[1].wanted = name
        return this.ok(obj instanceof klass, ...me)
      }

      // check prototype chain for name
      // at this point, we already know klass is not a function
      // if the klass specified is an obj in the proto chain, pass
      // if the name specified is the name of a ctor in the chain, pass
      for (let p = obj; p; p = Object.getPrototypeOf(p)) {
        const ctor = p.constructor && p.constructor.name
        if (p === klass || ctor === name) {
          return this.#t.pass(...me)
        }
      }
    }

    return this.equal(type, name, ...me)
  }

  /**
   * Verify that the value is loosely equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  same(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.same
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equivalent', args)
    const { match, diff } = same(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is not loosely equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  notSame(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not be equivalent', args)
    const { match } = same(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is strictly equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  strictSame<T extends unknown>(
    found: any,
    wanted: T,
    ...[msg, extra]: MessageExtra
  ): found is T {
    this.#t.currentAssert = this.#t.t.strictSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should be equivalent strictly',
      args,
    )
    const { match, diff } = strict(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is not strictly equivalent to the supplied
   * pattern object
   *
   * @group Assertion Methods
   */
  strictNotSame(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.strictNotSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should not be equivalent strictly',
      args,
    )
    const { match } = strict(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object has all of the properties and values in the
   * pattern, matching loosely.
   *
   * @group Assertion Methods
   */
  has(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.has
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should be equivalent',
      args,
    )
    const { match, diff } = has(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object does NOT have all of the properties and values
   * in the pattern, matching loosely.
   *
   * @group Assertion Methods
   */
  notHas(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notHas
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should not be equivalent',
      args,
    )
    const { match } = has(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value has all of the properties and values in the
   * pattern, matching strictly.
   *
   * @group Assertion Methods
   */
  hasStrict(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.hasStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should be equivalent strictly',
      args,
    )
    const { match, diff } = hasStrict(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does NOT contain all of the properties and
   * values in the test pattern, comparing strictly.
   *
   * Note that this will pass if the value has *some* of the listed properties,
   * or if they do not match the same type.
   *
   * @group Assertion Methods
   */
  notHasStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notHasStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should not be equivalent strictly',
      args,
    )
    const { match } = hasStrict(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided
   *
   * @group Assertion Methods
   */
  match(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.match
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = match(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does NOT match the pattern provided.
   *
   * @group Assertion Methods
   */
  notMatch(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatch
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = match(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, with no
   * extra properties.
   *
   * @group Assertion Methods
   */
  matchOnly(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.matchOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchOnly(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, with no
   * extra properties. Ie, it might either not match, or have extra props.
   *
   * @group Assertion Methods
   */
  notMatchOnly(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatchOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchOnly(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, with no
   * extra properties.
   *
   * @group Assertion Methods
   */
  matchOnlyStrict(
    found: any,
    wanted: any,
    ...[msg, extra]: MessageExtra
  ) {
    // this.#t.currentAssert = this.#t.t.matchOnlyStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchOnlyStrict(
      found,
      wanted,
      this.#opts,
    )
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, with no
   * extra properties. Ie, it might either not match, or have extra props.
   *
   * @group Assertion Methods
   */
  notMatchOnlyStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    // this.#t.currentAssert = this.#t.t.notMatchOnlyStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchOnlyStrict(
      found,
      doNotWant,
      this.#opts,
    )
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, but fail if any
   * fields *only* match via type coercion.
   *
   * For example,
   *
   * ```ts
   * t.matchStrict({ a: 1 }, { a: Number }, 'this passes')
   * t.matchStrict({ a: 1 }, { a: '1' }, 'this fails')
   * ```
   *
   * @group Assertion Methods
   */
  matchStrict(
    found: any,
    wanted: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.matchStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchStrict(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, without
   * type coercion.
   *
   * @group Assertion Methods
   */
  notMatchStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatchStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchStrict(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object has the wanted property, anywhere in its
   * prototype chain.
   *
   * @group Assertion Methods
   */
  hasProp<T extends {}>(
    found: T,
    wanted: string | number | symbol,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasProp
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'specified property should be defined',
      args,
    )
    Object.assign(me[1], { found, wanted })
    try {
      if (
        wanted in found &&
        (found as { [k: typeof wanted]: any })[wanted] !== undefined
      ) {
        return this.#t.pass(...me)
      } else {
        return this.#t.fail(...me)
      }
    } catch (er) {
      return this.#t.fail((er as Error)?.message || me[0], me[1])
    }
  }

  /**
   * Verify that the object has the wanted property, using
   * Object#hasOwnProperty
   *
   * @group Assertion Methods
   */
  hasOwnProp<T extends {}>(
    found: T,
    wanted: string | number | symbol,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnProp
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'specified property should be defined own property',
      args,
    )
    Object.assign(me[1], { found, wanted })
    try {
      if (
        hasOwn(found, wanted) &&
        (found as { [k: typeof wanted]: any })[wanted] !== undefined
      ) {
        return this.#t.pass(...me)
      } else {
        return this.#t.fail(...me)
      }
    } catch (er) {
      return this.#t.fail((er as Error)?.message || me[0], me[1])
    }
  }

  /**
   * Verify that the object has all of the properties in the `wanted`
   * list, anywhere in its prototype chain.
   *
   * @group Assertion Methods
   */
  hasProps<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasProps
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    for (const prop of wanted) {
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        Object.assign(me[1], { invalidProperty: prop })
        return this.#t.fail(
          'invalid property in hasProps assertion',
          me[1],
        )
      }
      try {
        if (
          !(
            prop in found &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the object has all of the properties listed in the
   * `wanted` list, using Object#hasOwnProperties()
   *
   * @group Assertion Methods
   */
  hasOwnProps<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnProps
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    for (const prop of wanted) {
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        Object.assign(me[1], { invalidProperty: prop })
        return this.#t.fail(
          'invalid property in hasOwnProps assertion',
          me[1],
        )
      }
      try {
        if (
          !(
            hasOwn(found, prop) &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the object has all of the properties listed in the
   * `wanted` list, using Object#hasOwnProperties(), and no others
   *
   * @group Assertion Methods
   */
  hasOwnPropsOnly<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnPropsOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    const seen = new Set<string | symbol | number>()
    for (const prop of wanted) {
      seen.add(prop)
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        return this.#t.fail(
          'invalid property in hasOwnPropsOnly assertion',
          { ...me[1], invalidProperty: prop },
        )
      }
      try {
        if (
          !(
            hasOwn(found, prop) &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    for (const prop of Object.keys(found)) {
      if (!seen.has(prop)) {
        return this.#t.fail(me[0], { ...me[1], doNotWant: prop })
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the function throws an error.
   * Thrown error is tested against the `wanted` param if provided, using
   * `t.match()`.
   *
   * Returns false on failure, or the error object thrown on success
   *
   * @group Assertion Methods
   */
  throws(
    fn: Function | (() => any),
    ...[wanted, msg, extra]: ThrowsArgs
  ): boolean | Error {
    this.#t.currentAssert = this.#t.t.throws
    const args = [wanted, msg, extra] as ThrowsArgs
    const [w, m, e] = normalizeThrowsArgs(
      fn.name || 'expected to throw',
      args,
    )
    try {
      fn()
      return this.#t.fail(m, e)
    } catch (err) {
      const er = err as Error
      if (er?.name) {
        Object.defineProperty(er, 'name', {
          value: er.name + '',
          enumerable: true,
          configurable: true,
          writable: true,
        })
      }
      return (
        (w ?
          this.match(isRegExp(wanted) ? er.message : er, wanted, m, e)
        : this.#t.pass(m, e)) && er
      )
    }
  }

  /**
   * Returns the error object if it throws and that does not fail the test
   * (by virtue of being marked skip or todo). Otherwise returns the
   * passing status, like other assertions.
   *
   * @group Assertion Methods
   */
  doesNotThrow(
    fn: Function | (() => any),
    ...[msg, extra]: MessageExtra
  ): boolean | Error {
    this.#t.currentAssert = this.#t.t.doesNotThrow
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      fn.name || 'expect to not throw',
      args,
    )

    try {
      fn()
      return this.#t.pass(...me)
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      const res =
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me)
      return (res && (er as Error)) || res
    }
  }

  /**
   * resolves to the error object rejected if it rejects as expected,
   * 'false' if it does not, or 'true' if it fails to reject but is marked
   * as skip/todo.
   *
   * @group Assertion Methods
   */
  async rejects<T extends any = any>(
    fnOrPromise: (() => Promise<T>) | Promise<T>,
    ...[wanted, msg, extra]: ThrowsArgs
  ): Promise<boolean | Error> {
    const args = [wanted, msg, extra] as ThrowsArgs
    const [w, m, e] = normalizeThrowsArgs('expected to reject', args)
    this.#t.currentAssert = this.#t.t.rejects
    e.at = e.at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.rejects',
        e,
      )
    }

    const d = new Deferred<boolean | Error>()
    this.#t.waitOn(d.promise)
    try {
      await p
      d.resolve(this.#t.fail(m, e))
    } catch (err) {
      const er = err as Error
      if (er?.name) {
        Object.defineProperty(er, 'name', {
          value: er.name + '',
          enumerable: true,
          configurable: true,
          writable: true,
        })
      }
      d.resolve(
        (w ?
          this.match(isRegExp(w) ? er.message : er, w, m, e)
        : this.#t.pass(m, e)) &&
          (er || true),
      )
    }
    return d.promise
  }

  /**
   * Resolves to 'true' if the promise resolves successfully, 'false' if
   * it rejects and fails, or the rejection error if it rejects but the
   * failure is accepted by by being marked todo or skip
   *
   * @group Assertion Methods
   */
  async resolves<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    ...[msg, extra]: MessageExtra
  ): Promise<boolean | Error> {
    this.#t.currentAssert = this.#t.t.resolves
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('expected to resolve', args)
    me[1].at = me[1].at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolves',
        me[1],
      )
    }

    const d = new Deferred<boolean | Error>()
    this.#t.waitOn(d.promise)
    try {
      await p
      d.resolve(this.#t.pass(...me))
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      const res =
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me)
      d.resolve((res && (er as Error)) || res)
    }
    return d.promise
  }

  /**
   * Test the resolved promise result with `t.match()`
   *
   * Resolves to true if it passes, false if the promise rejects or the match
   * fails, or the rejection error value if the promise rejects but the
   * assertion passes by being marked todo/skip.
   *
   * @group Assertion Methods
   */
  async resolveMatch<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    wanted: any,
    ...[msg, extra]: MessageExtra
  ): Promise<boolean> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'expected to resolve and match provided pattern',
      args,
    )
    this.#t.currentAssert = this.#t.t.resolveMatch
    me[1].at = me[1].at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolveMatch',
        me[1],
      )
    }

    const d = new Deferred<boolean>()
    this.#t.waitOn(d.promise)
    try {
      d.resolve(this.match(await p, wanted, ...me))
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      d.resolve(
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me),
      )
    }
    return d.promise
  }

  // TODO: maybe let this be guarded by an AbortSignal or timeout number?
  /**
   * Asserts that the emitter emits the specified event before the test
   * ends. Returns a promise that resolves when the event is emitted.
   * Note that waiting on the returned promise within a test can deadlock
   * the test, if the event never emits, but the returned promise can be
   * a handy way to pause a test until an event happens, if you are
   * reasonably confident that it will fire.
   *
   * @group Assertion Methods
   */
  emits(
    emitter: EventEmitter | EventTarget,
    event: string,
    ...[msg, extra]: MessageExtra
  ): Promise<void> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      `expect ${event} to be emitted`,
      args,
    )
    me[1].at = me[1].at || stack.at(this.#t.t.emits)
    me[1].stack = me[1].stack || stack.captureString(this.#t.t.emits)
    const d = new Deferred<void>()
    const handler = () => {
      pending[0] = true
      d.resolve()
    }
    const pending: ExpectedEmit = [
      false,
      emitter,
      event,
      handler,
      ...me,
    ]
    this.#pendingEmits.push(pending)
    if (emitter instanceof EventEmitter) {
      emitter.once(event, handler)
    } else {
      emitter.addEventListener(event, handler, { once: true })
    }
    if (!this.#setOnBeforeEnd) {
      this.#setOnBeforeEnd = true
      const obe = this.#t.onbeforeend
      this.#t.onbeforeend = () => {
        this.#onBeforeEnd()
        return obe.call(this.#t)
      }
    }
    return d.promise
  }

  /**
   * Assert that an error object is not provided.
   *
   * Like with {@link @tapjs/asserts!index.Assertions#doesNotThrow}, you
   * can also usually just throw the error, and tap will handle that
   * reasonably.
   *
   * This is useful in cases where you just want to assert that a callback did
   * not receive an error, without necessarily aborting the callback function
   * entirely. Both the origin of the error and the location of the failed
   * assertion will be printed in the test results.
   *
   * It is also used internally in
   * {@link @tapjs/asserts!index.Assertions#resolves},
   * {@link @tapjs/asserts!index.Assertions#doesNotThrow},
   * and {@link @tapjs/asserts!index.Assertions#resolveMatch} to show both
   * the source of a raised error as well as the callsite where the assertion
   * failed.
   *
   * @group Assertion Methods
   */
  error(er: unknown, ...[msg, extra]: MessageExtra) {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(`should not error`, args)
    this.#t.currentAssert = this.#t.t.error

    if (er === undefined || er === null) return this.#t.pass(...me)

    if (!(er instanceof Error)) {
      return this.#t.fail(
        ...normalizeMessageExtra('non-Error error encountered', [
          msg,
          { ...me[1], found: er },
        ] as MessageExtra),
      )
    }

    // ok, we got an error, that's a problem
    me[1].message = (er as Error)?.message || me[0]
    const { at, stack } = me[1]
    const ex = extraFromError(er, me[1])
    // put the error's origin on the object, but let the normal
    // diags parsing show us where the failed assertion happened,
    // or respect the at/stack added by another caller.
    // iow, the "main" error is `t.error(...)`, but the "cause"
    // callsite is whatever the error refers to.
    const { cause } = ex
    ex.cause = {
      message: er.message || msg,
      at: ex.at,
      stack: ex.stack,
    }
    if (cause !== undefined) ex.cause.cause = cause

    ex.at = at
    ex.stack = stack
    // always going to have *something* here.
    return this.#t.fail(ex.message as string, ex)
  }
}

const isIterable = (it: any) =>
  typeof it === 'string' ||
  it instanceof String ||
  (!!it &&
    typeof it === 'object' &&
    typeof it[Symbol.iterator] === 'function')

const isRegExp = (re: any): re is RegExp =>
  Object.prototype.toString.call(re) === '[object RegExp]'

export const plugin: TapPlugin<Assertions, AssertOptions> = (
  t: TestBase,
  opts: AssertOptions = {},
) => new Assertions(t, opts)

