   * Do not clean up fixtures created with `t.testdir()`
   *
   * @group Configuration
   */
  'save-fixture': {
    type: 'boolean',
    short: 'F',
    description:
      'Do not clean up fixtures created with `t.testdir()`',
  },
}

import { plugin as AfterPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import * as stack from '@tapjs/stack'
import { isBuiltin } from 'node:module'
import { mockRequire } from './mock-require.js'
import { MockService } from './mock-service.js'

/**
 * Implementation class providing the
 * {@link @tapjs/mock!index.TapMock#mockRequire},
 * {@link @tapjs/mock!index.TapMock#mockImport}, and
 * {@link @tapjs/mock!index.TapMock#createMock} methods.
 */
export class TapMock {
  #t: TestBase
  #didTeardown: boolean = false
  #mocks: MockService[] = []

  #allMock: Record<string, any>
  static #refs = new Map<TestBase, TapMock>()

  constructor(t: TestBase) {
    this.#t = t
    TapMock.#refs.set(t, this)
    // inherit #allMock
    const p = t.parent && TapMock.#refs.get(t.parent)
    this.#allMock = Object.assign(
      Object.create(null),
      p ? p.#allMock : {},
    )
  }

  /**
   * Convenience method to create a mock from an existing object by
   * overriding some (possibly deeply nested) methods or properties.
   *
   * Example:
   *
   * ```ts
   * import * as fs from 'node:fs'
   * const mockedThing = t.mockRequire('./module.js', t.createMock(
   *   { fs },
   *   { fs: { statSync: myMockedStatSync }}
   * )
   * ```
   *
   * This can also appear anywhere in the object hierarchy, which may
   * be more convenient in some cases:
   *
   * ```ts
   * import * as blah from '@long-name/blah-api'
   * const mockedThing = t.mockRequire('./module.js', {
   *   fs: t.createMock(fs, { statSync: myMockedStatSync }),
   *   child_process: t.createMock(child_process, { spawn: mockSpawn }),
   *   '@long-name/blah-api': t.createMock(blah, {
   *     some: {
   *       nested: {
   *         prop: true
   *       }
   *     }
   *   })
   * })
   * ```
   *
   * To *remove* a property, set it as undefined in the override.
   *
   * @group Spies, Mocks, and Fixtures
   */
  createMock<
    B extends { [k: PropertyKey]: any } | Array<any>,
    O extends { [k: string]: any } | Array<any>,
  >(bases: B, overrides: O): MockedObject<B, O> {
    if (Array.isArray(overrides))
      return overrides as unknown as MockedObject<B, O>
    const mockedObject = Object.fromEntries(
      Object.entries(bases)
        .map(([k, v]) => {
          if (k in overrides) {
            const bobj = !!v && typeof v === 'object'
            const oobj =
              !!overrides[k] && typeof overrides[k] === 'object'
            if (oobj && bobj) {
              return [k, this.createMock(v, overrides[k])]
            } else {
              return [k, overrides[k]]
            }
          }
          return [k, v]
        })
        .concat(
          Object.entries(overrides).filter(
            ([k]) => !Object.hasOwnProperty.call(bases, k),
          ),
        ),
    ) as MockedObject<B, O>

    if (Object.getPrototypeOf(bases) !== Object.prototype) {
      Object.setPrototypeOf(
        mockedObject,
        Object.getPrototypeOf(bases),
      )
    }

    return mockedObject
  }

  /**
   * Deprecated alias for {@link @tapjs/mock!index.TapMock#mockRequire}
   *
   * Prints a warning to stderr the first time it used, otherwise
   * identical.
   *
   * @group Spies, Mocks, and Fixtures
   *
   * @deprecated
   */
  mock<T = any>(module: string, mocks: { [k: string]: any } = {}): T {
    /* c8 ignore start */
    const at = stack.at(this.#t.t.mock)?.toJSON() || ''
    /* c8 ignore stop */
    console.error(
      't.mock() is now t.mockRequire(). Please update your tests.',
      at,
    )
    return mockRequire(module, mocks, this.#t.t.mock) as T
  }

  /**
   * Load the supplied module asynchronously using import(),
   * replacing any of the referenced modules with the mocks provided.
   *
   * Works with either ESM or CommonJS modules, but as with `import()` of
   * CommonJS modules, the `module.exports` value will be set as the
   * `default` property on the resolved object, making
   * {@link @tapjs/mock!index.TapMock#mockRequire} somewhat more intuitive in those cases.
   *
   * For type info, cast using `as typeof import(...)` or use the type
   * parameter, as TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = await t.mockImport<
   *   typeof import('../my-thing.js')
   * >('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * })
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  async mockImport<T = any>(
    module: string,
    mocks: Record<string, any> = {},
  ): Promise<T> {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockImport
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked',
      )
      return {} as T
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    if (!this.#didTeardown && this.#t.t.pluginLoaded(AfterPlugin)) {
      this.#didTeardown = true
      this.#t.t.teardown(() => this.unmock())
    }
    const service = await MockService.create(
      module,
      mocks,
      this.#t.t.mockImport,
    )
    this.#mocks.push(service)
    return Promise.resolve(service.module).then(s => import(s))
  }

  /**
   * Load the supplied module synchronously using `require()`,
   * replacing any of the referenced modules with the mocks provided.
   *
   * Only works with CommonJS modules.
   *
   * For type info, cast using `as typeof import(...)` or use the type
   * parameter, as TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = t.mockRequire<
   *   typeof import('../my-thing.js')
   * >('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * })
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  mockRequire<T = any>(
    module: string,
    mocks: Record<string, any> = {},
  ): T {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockRequire
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked',
      )
      return {} as T
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    return mockRequire(module, mocks, this.#t.t.mockRequire) as T
  }

  /**
   * Set some mocks that will be used for all subsequent
   * {@link @tapjs/mock!index.TapMock#mockImport} and
   * {@link @tapjs/mock!index.TapMock#mockRequire} calls made by this test.
   *
   * Mocks added with `mockAll` are overridden by any explicit mocks set in the
   * `t.mockRequire` or `t.mockImport` call.
   *
   * Repeated calls to `t.mockAll()` will *add* mocks to the set. If the same
   * name is used again, it will replace the previous value, not merge.
   *
   * If a key is set to `undefined` or `null`, then it will be removed from
   * the `mockAll` set.
   *
   * Reset by calling `t.mockAll(null)`
   *
   * Call with no args to return the current `mockAll` object.
   */
  mockAll(mocks?: Record<string, any> | null): Record<string, any> {
    if (mocks === null) this.#allMock = Object.create(null)
    else if (mocks !== undefined) {
      this.#allMock = Object.assign(this.#allMock, mocks)
      for (const [k, v] of Object.entries(this.#allMock)) {
        if (v === undefined || v === null) delete this.#allMock[k]
      }
    }
    return this.#allMock
  }

  /**
   * Unwind the mocks and free up the memory at the end of the test.
   *
   * Called automatically if the `@tapjs/after` plugin is not disabled.
   *
   * @group Spies, Mocks, and Fixtures
   */
  unmock() {
    for (const m of this.#mocks) {
      m.unmock()
    }
  }
}

/**
 * Utility type, overrides the properties in B with the properties
 * in O, deeply nested.
 */
export type MockedObject<B, O> =
  O extends Array<any> ? O
  : B extends { [k: PropertyKey]: any } ?
    O extends Function ? O
    : O extends { [k: string]: any } ?
      {
        [k in keyof B]: k extends keyof O ? MockedObject<B[k], O[k]>
        : B[k]
      }
    : O
  : O

/**
 * Loader that supports {@link @tapjs/mock!index.TapMock#mockImport}
 */
export const loader = '@tapjs/mock/loader'

/**
 * Importer for use with node --import
 */
export const importLoader = '@tapjs/mock/import'

/**
 * plugin method that instantiates {@link @tapjs/mock!index.TapMock}
 */
export const plugin: TapPlugin<TapMock> = (t: TestBase) =>
  new TapMock(t)

import type { TapPlugin, TAP } from '@tapjs/core'
import { env } from '@tapjs/core'
import { serialize } from './serialize.js'

let serialized = false
export const plugin: TapPlugin<{}> = t => {
  if (
    t.name === 'TAP' &&
    !t.parent &&
    env.NODE_TEST_CONTEXT === 'child-v8' &&
    !serialized
  ) {
    // make sure we don't serialize anything else in this process
    serialized = true
    env.NODE_TEST_CONTEXT = 'child'
    serialize(t as TAP)
  }
  return {}
}

import {
  BaseOpts,
  env,
  Extra,
  parseTestArgs,
  PromiseWithSubtest,
  TapPlugin,
  TestArgs,
  TestBase,
  TestBaseOpts,
} from '@tapjs/core'
import type { Test, TestOpts } from '@tapjs/test'

const reRegExpLiteral = /^\/(.*)\/([a-z]*)$/

/**
 * Options added by this plugin
 *
 * @group Test Options
 */
export interface FilterOptions extends TestBaseOpts {
  /**
   * Set to true to run in contexts where `runOnly` is set
   */
  only?: boolean

  /**
   * Only run subtests marked with { only: true }
   */
  runOnly?: boolean

  /**
   * only run subtests whose name matches the supplied pattern.
   * Provide an array of strings/regexps to match down the test
   * heirarchy.
   */
  grep?: string | RegExp | (string | RegExp)[]

  /**
   * only run tests that DON'T match the pattern provided in `grep`
   */
  grepInvert?: boolean
}

/**
 * Class that provides the {@link @tapjs/filter!Filter#only} method, and
 * extends the {@link @tapjs/core!test-base.TestBase#shouldSkipChild} method to
 * respect the grep option
 */
export class Filter {
  #t: TestBase
  #grep: (string | RegExp)[] = []
  #grepInvert: boolean
  #runOnly: boolean
  #filterQuietly: boolean

  get runOnly() {
    return this.#runOnly
  }
  set runOnly(o: boolean) {
    this.#runOnly = o
  }

  constructor(t: TestBase, opts: FilterOptions) {
    this.#t = t
    const eq = env?.TAP_FILTER_QUIETLY
    this.#filterQuietly =
      eq === '1' || (!!t.options.failSkip && eq !== '0')

    // don't filter test files when we're the cli test runner
    const { grep, grepInvert, runOnly } =
      opts.context === Symbol.for('tap.isRunner') ?
        ({
          grep: [],
          grepInvert: false,
          runOnly: false,
        } as FilterOptions)
      : opts

    if (grep !== undefined) {
      this.#grep = !Array.isArray(grep) ? [grep] : grep
    } else if (env.TAP_GREP !== undefined) {
      this.#grep = env.TAP_GREP.split('\n').map(g => {
        const p = g.match(reRegExpLiteral)
        g = p && p[1] ? p[1] : g
        const flags = p ? p[2] : ''
        return new RegExp(g, flags)
      })
    }
    if (grepInvert !== undefined) {
      this.#grepInvert = !!grepInvert
    } else {
      this.#grepInvert = env.TAP_INVERT === '1'
    }
    if (runOnly !== undefined) {
      this.#runOnly = !!runOnly
    } else {
      this.#runOnly = env.TAP_ONLY === '1'
    }
    if (this.#grep?.length) t.options.grep = this.#grep

    const { shouldSkipChild } = t
    t.shouldSkipChild = extra =>
      this.#shouldSkipChild(extra, shouldSkipChild)
  }

  #shouldSkipChild(
    extra: TestBaseOpts | BaseOpts | TestOpts,
    shouldSkipChild: (
      extra: TestBaseOpts | BaseOpts | TestOpts,
    ) => boolean,
  ) {
    const opts = extra as FilterOptions & Extra & TestBaseOpts
    const [pattern, ...rest] = this.#grep
    if (pattern !== undefined) {
      /* c8 ignore start */
      const name = opts.name || ''
      /* c8 ignore stop */
      const m =
        typeof pattern === 'string' ?
          name.includes(pattern)
        : pattern.test(name)
      const match = this.#grepInvert ? !m : m
      if (!match) {
        if (!this.#filterQuietly) {
          opts.skip = `filter${
            this.#grepInvert ? ' out' : ''
          }: ${pattern}`
        }
        shouldSkipChild(opts)
        return true
      } else {
        opts.grep = rest
      }
    } else if (this.#grep) {
      // just set to an empty array so it doesn't try to load from the
      // env once we've exhausted the pattern parts.
      opts.grep = []
    }
    if (this.#runOnly && !opts.only) {
      if (!this.#filterQuietly) {
        opts.skip = 'filter: only'
      }
      shouldSkipChild(opts)
      return true
    }
    if (opts.only && !this.#runOnly) {
      this.#t.comment(
        '%j has `only` set but all tests run',
        extra.name,
      )
    }
    if (typeof opts.runOnly === 'undefined') {
      opts.runOnly = this.#runOnly
    }
    return shouldSkipChild(opts)
  }

  /**
   * Run a child test that will run when the `--only` config is set,
   * or `{ runOnly: true }` is set in the parent test options.
   *
   * @group Subtest Methods
   */
  only(
    name: string,
    extra: TestOpts,
    cb: (t: Test) => any,
  ): PromiseWithSubtest<Test>
  only(name: string, cb: (t: Test) => any): PromiseWithSubtest<Test>
  only(
    extra: TestOpts,
    cb: (t: Test) => any,
  ): PromiseWithSubtest<Test>
  only(cb: (t: Test) => any): PromiseWithSubtest<Test>
  only(...args: TestArgs<Test>): PromiseWithSubtest<Test> {
    const extra = parseTestArgs(...args)
    /* c8 ignore start */
    const name = extra.name || ''
    /* c8 ignore stop */
    return this.#t.t.test(name, { ...extra, only: true }, extra.cb)
  }
}

/**
 * plugin method that instantiates a {@link @tapjs/filter!Filter} object
 */
export const plugin: TapPlugin<Filter, FilterOptions> = (t, opts) =>
  new Filter(t, opts)

/**
 * Configuration options added by this plugin
 *
 * @group Configuration
 */
export const config = {
  /**
   * flag
   *
   * Only run tests with `{only: true}` option, or created with the
