export const parseArgs = pa as typeof import('util')['parseArgs']

/**
 * Collection of the various types of lines encountered in a TAP stream
 */
export const lineTypes = {
  testPoint: /^(not )?ok(?: ([0-9]+))?(?:(?: -)?( .*?))?(\{?)\n$/,
  pragma: /^pragma ([+-])([a-zA-Z0-9_-]+)\n$/,
  bailout: /^bail out!(.*)\n$/i,
  version: /^TAP version ([0-9]+)\n$/i,
  childVersion: /^(    )+TAP version ([0-9]+)\n$/i,
  plan: /^([0-9]+)\.\.([0-9]+)(?:\s+(?:#\s*(.*)))?\n$/,
  subtest: /^# Subtest(?:: (.*))?\n$/,
  subtestIndent: /^    # Subtest(?:: (.*))?\n$/,
  comment: /^\s*#.*\n$/,
} as const

/**
 * The type of a line, and its constituent parsed pieces
 */
export type ParsedLine = [string, RegExpMatchArray]

/**
 * Determine the type of line, and parse it into a {@link ParsedLine}
 */
export const lineType = (line: string): ParsedLine | null => {
  for (const [t, pattern] of Object.entries(lineTypes)) {
    const match = line.match(pattern)
    if (match) return [t, match]
  }
  return null
}

import { OPEN_BRACE_EOL } from './brace-patterns.js'
import { Parser } from './index.js'
import { Directive, parseDirective } from './parse-directive.js'
import { Plan } from './plan.js'

/**
 * An indication that a violation of the TAP specification has occurred
 *
 * This can indicate a test point that exceeds the plan, a test point
 * encountered after a trailing plan, or in the case of `pragma +strict`,
 * any non-TAP data.
 */
export type TapError = Result | { tapError: string; [k: string]: any }

/**
 * A representation of a TestPoint result, with diagnostics if present.
 */
export class Result {
  public ok: boolean
  public name: string = ''
  public id: number = 0
  public buffered: boolean = false
  public tapError: string | null = null
  public skip: boolean | string = false
  public todo: boolean | string = false
  public previous: Result | null = null
  public plan: Plan | null = null
  public diag: any | null = null
  public time: number | null = null
  public fullname: string = ''
  public closingTestPoint: boolean = false

  constructor(parsed: string[], parser: Parser) {
    const ok = !parsed[1]
    const id = +(parsed[2] || 0)
    let buffered = parsed[4]
    this.ok = ok
    if (parsed[2]) this.id = id

    let rest = parsed[3] || ''
    let name: string
    // We know at this point the parsed result cannot contain \n,
    // so we can leverage that as a placeholder.
    // first, replace any PAIR of \ chars with \n
    // then, split on any # that is not preceeded by \
    // the first of these is definitely the description
    // the rest is the directive, if recognized, otherwise
    // we just lump it onto the description, but escaped.
    // then any \n chars in either are turned into \ (just one)

    // escape \ with \
    // swap out escaped \ with \n, then swap back
    rest = rest.replace(/(\\\\)/g, '\n')
    const [h, ...r] = rest.split(/(?<=\s|^)(?<!\\)#/g)
    name = (h || '').replace(/\\#/g, '#').replace(/\n/g, '\\')
    rest = r.join('#').replace(/\\#/g, '#').replace(/\n/g, '\\')

    // now, let's see if there's a directive in there.
    const dir = parseDirective(rest.trim())
    if (!dir) name += (rest ? '#' + rest : '') + buffered
    else {
      // handle buffered subtests with todo/skip on them, like
      // ok 1 - bar # todo foo {\n
      const dirKey: Directive = dir[0]
      const dirValue = dir[1]
      if (dirKey === 'todo' || dirKey === 'skip') {
        this[dirKey] = dirValue
      } else {
        if (dirKey === 'time') {
          this.time = parseFloat(dirValue)
          /* c8 ignore start */
        }
        /* c8 ignore stop */
      }
    }

    if (OPEN_BRACE_EOL.test(name)) {
      name = name.replace(OPEN_BRACE_EOL, '')
      buffered = '{'
    }

    if (buffered === '{') this.buffered = true

    if (name) this.name = name.trim()
    const n: string[] = []
    if (parser.fullname) n.push(parser.fullname)
    if (this.name) n.push(this.name)
    this.fullname = n.join(' > ').trim()
  }
}

import { OPEN_BRACE_EOL } from './brace-patterns.js'

/**
 * Directive types supported by this library.
 *
 * `'time'` is not officially in the specification, but node-tap has
 * supported it for a long time, and uses it to report how long
 * subtests take to complete.
 */
export type Directive = 'todo' | 'skip' | 'time'

/**
 * Parse a "directive", the bit that follows the `#` character
 * on a TestPoint line.
 */
export const parseDirective = (
  line: string,
): [Directive, any] | false => {
  if (!line.trim()) return false

  line = line
    .replace(OPEN_BRACE_EOL, '')
    .trim()
    .replace(/^duration_ms ([0-9.]+)$/, 'time=$1ms')
  const time = line.match(
    /^time=((?:[1-9][0-9]*|0)(?:\.[0-9]+)?)(ms|s)$/i,
  )
  const t = time?.[1]
  const s = time?.[2]
  if (typeof t === 'string') {
    let n = +t
    if (s === 's') {
      // JS does weird things with floats.  Round it off a bit.
      n *= 1000000
      n = Math.round(n)
      n /= 1000
    }
    return ['time', n]
  }

  const ts = line.match(/^(todo|skip)(?:\S*)\b(.*)$/i)
  const type = ts?.[1]?.toLowerCase()
  const msg = ts?.[2]?.trim()
  if (!type) return false

  // we know at this point it must be either 'todo' or 'skip',
  // in unknown upper/lower case
  return [type as Directive, msg || true]
}

/**
 * A class representing the TAP plan line
 */
export class Plan {
  public start: number
  public end: number
  public comment: string
  constructor(start: number, end: number, comment: string = '') {
    this.start = start
    this.end = end
    this.comment = comment
  }
}

/**
 * Static `parse()` and `stringify()` methods
 */
import etoa from 'events-to-array'
type EventLog = ReturnType<typeof etoa>
import { Parser, ParserOptions } from './index.js'
import { esc } from './escape.js'
import yaml from 'tap-yaml'
import { SPACE_OPEN_BRACE_EOL } from './brace-patterns.js'

// used in flattening mode
const getId = (): { (): number; current: number } => {
  const id = () => id.current++
  id.current = 1
  return id
}

/**
 * Parse a TAP text stream into a log of all the events encountered
 */
export const parse = (
  str: string,
  options: ParserOptions,
): EventLog => {
  const { flat = false } = options
  const ignore = ['line', 'pass', 'fail', 'todo', 'skip', 'result']
  if (flat) ignore.push('assert', 'child', 'plan', 'complete')
  const parser = new Parser(options)
  const events = etoa(parser, ignore)
  if (flat) {
    const id = getId()
    parser.on('result', res => {
      res.name = res.fullname
      res.id = id()
      events.push(['assert', res])
    })
    parser.on('complete', res => {
      if (!res.bailout)
        events.push(['plan', { end: id.current - 1, start: 1 }])
      events.push(['complete', res])
    })
  }

  parser.end(str)
  return events
}

/**
 * Turn an EventLog from {@link tap-parser!statics.parse} into a TAP string
 */
export const stringify = (
  msg: EventLog,
  { flat = false, indent = '', id = getId() },
): string => {
  const ind = flat ? '' : indent
  return (
    ind +
    msg
      .map(item => {
        switch (item[0]) {
          case 'child':
            const comment = item[1][0]
            const child = item[1].slice(1)
            return (
              Parser.stringify([comment], {
                flat,
                indent: '',
                id,
              }) +
              Parser.stringify(child, {
                flat,
                indent: '    ',
                id,
              })
            )

          case 'version':
            return 'TAP version ' + item[1] + '\n'

          case 'plan':
            if (flat) {
              if (indent !== '') return ''
              item[1].start = 1
              item[1].end = id.current - 1
            }
            return (
              item[1].start +
              '..' +
              item[1].end +
              (item[1].comment ? ' # ' + esc(item[1].comment) : '') +
              '\n'
            )

          case 'pragma':
            return 'pragma ' + (item[2] ? '+' : '-') + item[1] + '\n'

          case 'bailout':
            return (
              'Bail out!' + (item[1] ? ' ' + esc(item[1]) : '') + '\n'
            )

          case 'assert':
            const res = item[1]
            if (flat) {
              res.id = id()
              res.name = res.fullname
            }
            return (
              (res.ok ? '' : 'not ') +
              'ok' +
              (res.id ? ' ' + res.id : '') +
              (res.name ?
                ' - ' +
                esc(res.name).replace(SPACE_OPEN_BRACE_EOL, '')
              : '') +
              (res.skip ?
                ' # SKIP' +
                (res.skip === true ? '' : ' ' + esc(res.skip))
              : '') +
              (res.todo ?
                ' # TODO' +
                (res.todo === true ? '' : ' ' + esc(res.todo))
              : '') +
              (res.time ? ' # time=' + res.time + 'ms' : '') +
              '\n' +
              (res.diag ?
                '  ---\n  ' +
                yaml
                  .stringify(res.diag)
                  .split('\n')
                  .join('\n  ')
                  .trim() +
                '\n  ...\n'
              : '')
            )

          case 'extra':
          case 'comment':
            return item[1]
        }
      })
      .join('')
      .split('\n')
      .join('\n' + ind)
      .trim() +
    '\n'
  )
}

import * as stack from '@tapjs/stack'
import { createTwoFilesPatch } from 'diff'
import { readFileSync } from 'node:fs'
import { format, strict } from 'tcompare'
import { extraFromError } from './extra-from-error.js'
import { Extra } from './index.js'

const tryReadFile = (path: string) => {
  try {
    return readFileSync(path, 'utf8')
  } catch (_) {
    return
  }
}

const hasOwn = (obj: { [k: string]: any }, key: string) =>
  Object.prototype.hasOwnProperty.call(obj, key)

/**
 * Prepare an object for printing to YAML diagnostics.
 *
 * Looks up source, calculates diffs of actual/expected values, and so on.
 */
export const cleanYamlObject = (
  obj: { [k: string]: any },
  seen: Set<any> = new Set(),
) => {
  seen.add(obj)
  const res = { ...obj }
  seen.add(res)
  if (hasOwn(res, 'stack') && !hasOwn(res, 'at')) {
    const st =
      Array.isArray(res.stack) ?
        res.stack.map(s => String(s).trimEnd() + '\n').join('')
      : String(res.stack)
    const p = stack.parseStack(st)
    res.at = p[0]
    res.stack = p.map(c => String(c) + '\n').join('')
  }
  if (
    typeof res.stack === 'string' &&
    res.stack &&
    !res.stack.endsWith('\n')
  ) {
    res.stack = res.stack.trimEnd() + '\n'
  }

  /* c8 ignore start - old pre-Error.cause custom decorator */
  if (res.errorOrigin && typeof res.errorOrigin === 'object') {
    res.errorOrigin = cleanYamlObject(res.errorOrigin, seen)
  }
  /* c8 ignore stop */

  if (
    res.cause &&
    typeof res.cause === 'object' &&
    !seen.has(res.cause)
  ) {
    seen.add(res.cause)
    const { message } = res.cause
    const ex = extraFromError(res.cause)
    const clean = cleanYamlObject(ex, seen)
    if (message !== undefined) clean.message = message
    res.cause = clean
  }
  if (Array.isArray(res.errors)) {
    const cleaned: Extra[] = []
    for (const e of res.errors) {
      if (!!e && typeof e === 'object') {
        seen.add(e)
        const { message } = e
        const ex = extraFromError(e)
        const clean = cleanYamlObject(ex, seen)
        if (message !== undefined) clean.message = message
        cleaned.push(clean)
      } else {
        cleaned.push(e)
      }
    }
    res.errors = cleaned
  }

  if (
    res.at &&
    res.at instanceof stack.CallSiteLike &&
    res.at.fileName &&
    res.at.absoluteFileName &&
    res.at.lineNumber &&
    !res.source
  ) {
    const file = res.at.absoluteFileName
    const content = tryReadFile(file)
    if (content) {
      const lines = content.split('\n')
      if (res.at.lineNumber <= lines.length) {
        const startLine = Math.max(res.at.lineNumber - 3, 0)
        const endLine = Math.min(res.at.lineNumber + 2, lines.length)
        const line = lines[res.at.lineNumber - 1]
        const caret =
          (
            res.at.columnNumber &&
            line &&
            res.at.columnNumber <= line.length
          ) ?
            ['-'.repeat(res.at.columnNumber - 1) + '^']
          : []
        const context = lines
          .slice(startLine, res.at.lineNumber)
          .concat(caret)
          .concat(lines.slice(res.at.lineNumber, endLine))
        const csplit = context.join('\n').trimEnd()
        if (csplit) res.source = csplit + '\n'
      }
    }
  }
  if (res.at && res.at instanceof stack.CallSiteLike) {
    res.at = res.at.toJSON()
  }

  // show a line by line string diff
  // diff the yaml, to make it more humane, especially
  // when strings or buffers are very large or multi-line
  // the shipped compare methods will generally supply
  // their own diff, which is much nicer.
  if (
    'found' in res &&
    'wanted' in res &&
    res.found !== res.wanted &&
    !res.diff
  ) {
    const f = res.found
    const w = res.wanted
    if (typeof f === 'string' && typeof w === 'string')
      res.diff = createTwoFilesPatch(
        'expected',
        'actual',
        w + '\n',
        f + '\n',
      ).replace(/^=+\n/, '')
    else if (
      f &&
      w &&
      typeof f === 'object' &&
      typeof w === 'object'
    ) {
      const s = strict(f, w)
      if (!s.match) {
        res.diff = s.diff
      } else {
        res.note = 'object identities differ'
      }
    } else {
      // some mixed stringly bits
      const ff = format(f)
      const fw = format(w)
      const fs = (typeof f === 'string' ? f : ff) + '\n'
      const ws = (typeof w === 'string' ? w : fw) + '\n'
      if (fw !== ff) {
        res.diff = createTwoFilesPatch(
          'expected',
          'actual',
          ws,
          fs,
        ).replace(/^=+\n/, '')
      }
    }
    if (res.diff === '--- expected\n+++ actual\n') {
      delete res.diff
    }
    if (res.diff) {
      delete res.found
      delete res.wanted
    }
  }

  for (const [key, value] of Object.entries(res)) {
    if (shouldDeleteKey(key, value)) {
      delete res[key]
    }
  }

  // if the 'message' is a string, then we print it on the
  // test point, so no need to repeat in the diags
  if (typeof res.message === 'string') delete res.message

  // worker: remove inline code
  if (
    res.eval === true &&
    typeof res.filename === 'string' &&
    res.filename.includes('\n')
  ) {
    res.filename = '<inline code>'
  }
  // always put cause at the end.
  if (res.cause) {
    const { cause, ...props } = res
    return { ...props, cause }
  }

  return res
}

/**
 * Properties that are *always* removed from the diagnostics, either because
 * they are internal (eg, `time`), overly noisy (eg, `parent`), or captured
 * elsewhere in the TAP output (eg, `skip`).
 */
export const deleteAlways = new Set([
  'todo',
  'time',
  'childId',
  'cb',
  'name',
  'indent',
  'skip',
  'bail',
  'diagnostic',
  'buffered',
  'parent',
  // only relevant if activated, a failedTodo or failedSkip will be added
  'failSkip',
  'failTodo',
  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'grep',
  'grepInvert',
  'only',
  'saveFixture',
  'env',
])

/**
 * Fields on this list are removed from YAML diagnostics if they are empty
 * (ie, falsey, empty array, or object with no keys)
 */
export const deleteIfEmpty = new Set([
  'at',
  'stack',
  'context',
  'debug',

  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'runOnly',
  'compareOptions',
])

/**
 * Fields are removed from YAML diagnostics if they match any of these
 * patterns.
 */
export const deleteIfMatch = [
  /^_?tapChild/,
  /^tapStream/,
  // TODO: create a @tapjs/mocha plugin
  /^tapMochaTest/,
]

const shouldDeleteKey = (key: string, value: any) =>
  deleteAlways.has(key) ||
  (deleteIfEmpty.has(key) && isEmpty(value)) ||
  deleteIfMatch.some(r => r.test(key))

// return true if object is empty, including inherited properties
const isEmpty = (obj: any): obj is {} => {
  if (!obj) {
    return true
  }
  if (typeof obj !== 'object') {
    return false
  }
  if (Array.isArray(obj)) {
    return obj.length === 0
  }
  for (const _ in obj) {
    return false
  }
  return true
}

import { findSourceMap, SourceMap } from 'module'
import { isAbsolute, relative, resolve } from 'path'
import { fileURLToPath } from 'url'
import {
  Compiled,
  isCompiledCallSiteLine,
  parseCallSiteLine,
} from './parse.js'

const methodRe = /^(.*?) \[as (.*?)\]$/

export interface CallSiteLikeJSON {
  fileName?: string
  lineNumber?: number
  columnNumber?: number
  evalOrigin?: CallSiteLikeJSON
  typeName?: string
  methodName?: string
  functionName?: string
  isEval?: true
  isNative?: true
  isToplevel?: true
  isConstructor?: true
  generated?: {
    fileName?: string
    lineNumber?: number
    columnNumber?: number
  }
}

export interface GeneratedResult {
  fileName?: string | null
  lineNumber?: number | null
  columnNumber?: number | null
}

const isCallSite = (c: any): c is NodeJS.CallSite =>
  !!c && typeof c === 'object' && c.constructor?.name === 'CallSite'

export class CallSiteLike {
  static prepareStackTrace(e: Error, c: NodeJS.CallSite[]) {
    return c.map(c => new CallSiteLike(e, c))
  }

  #fileName?: string | null
  #cwd?: string
  lineNumber: ReturnType<NodeJS.CallSite['getLineNumber']>
  columnNumber: ReturnType<NodeJS.CallSite['getColumnNumber']>
  this: ReturnType<NodeJS.CallSite['getThis']>
  evalOrigin?: CallSiteLike
  function: ReturnType<NodeJS.CallSite['getFunction']>
  typeName: ReturnType<NodeJS.CallSite['getTypeName']>
  methodName: ReturnType<NodeJS.CallSite['getMethodName']>
  functionName: ReturnType<NodeJS.CallSite['getFunctionName']>
  isEval: ReturnType<NodeJS.CallSite['isEval']>
  isNative: ReturnType<NodeJS.CallSite['isNative']>
  isToplevel: ReturnType<NodeJS.CallSite['isToplevel']>
  isConstructor: ReturnType<NodeJS.CallSite['isConstructor']>
  generated?: GeneratedResult
  #sourceMap?: SourceMap

  // normalize and relativize filename if cwd is set
  get fileName() {
    return this.#relativize(this.#fileName)
  }

  get absoluteFileName() {
    if (!this.#fileName) return this.#fileName
    else if (this.#fileName.startsWith('file://')) {
      return fileURLToPath(this.#fileName)
    } else {
      return this.#fileName
    }
  }

  get cwd(): string | undefined {
    return this.#cwd
  }

  set cwd(cwd: string | undefined) {
    if (cwd === undefined) {
      if (this.generated) {
        this.generated.fileName = this.#derelativize(
          this.generated?.fileName,
        )
      }
    }
    this.#cwd = cwd?.replace(/\\/g, '/')
    if (cwd !== undefined) {
      if (this.generated) {
        this.generated.fileName = this.#relativize(
          this.generated?.fileName,
        )
      }
    }
    if (this.evalOrigin) this.evalOrigin.cwd = cwd
  }

  constructor(
    e: Error | null,
    c: NodeJS.CallSite | string | Compiled,
  ) {
    if (typeof c === 'string') {
      c = parseCallSiteLine(c)
    }

    if (isCallSite(c)) {
      const fileName = c.getFileName()
      this.#fileName = typeof fileName === 'string' ? fileName : null
      this.lineNumber = c.getLineNumber()
      this.columnNumber = c.getColumnNumber()
      this.this = c.getThis()
      const evalOrigin = c.getEvalOrigin()
      if (evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, evalOrigin)
      }
      this.function = c.getFunction()
      this.typeName = c.getTypeName()
      this.methodName = c.getMethodName()
      this.functionName = c.getFunctionName()
      this.isEval = c.isEval()
      this.isNative = c.isNative()
      this.isToplevel = c.isToplevel()
      this.isConstructor = c.isConstructor()
    } else if (isCompiledCallSiteLine(c)) {
      // compiled object from stack line
      this.isEval = !!c.isEval
      this.isToplevel = false
      if (c.evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, c.evalOrigin)
      }

      this.lineNumber =
        c.lineNumber === undefined ? null : c.lineNumber
      this.columnNumber =
        c.columnNumber === undefined ? null : c.columnNumber
      const fileName = c.fileName
      this.#fileName = typeof fileName === 'string' ? fileName : null
      const { generated } = c
      if (generated) {
        this.generated = generated
        this.generated.fileName = this.#relativize(generated.fileName)
      }
      let fname = c.fname?.trim()
      let method: null | string = null
      this.isNative = !!c.isNative

      if (fname) {
        if (fname.startsWith('new ')) {
          this.isConstructor = true
          fname = fname.substring('new '.length).trim()
        } else {
          this.isConstructor = false
        }
        this.methodName = null
        const methodMatch = fname.match(methodRe) as [
          string,
          string,
          string,
        ]
        if (methodMatch) {
          fname = methodMatch[1]
          method = methodMatch[2]
        }
        const dots = fname.split('.')
        const m = dots.pop()
        if (m !== undefined) {
          this.typeName = dots.join('.').trim() || null
          this.methodName = method || m
          if (this.methodName.match(/^get |set /)) {
            this.methodName = this.methodName.substring(4)
          }
          this.functionName = m
          // we know it's not undefined, but TS is afraid of pop()
          /* c8 ignore start */
        } else {
          this.functionName = null
          this.typeName = null
        }
        /* c8 ignore stop */
      } else {
        this.isConstructor = false
        this.typeName = null
        this.functionName = null
        this.methodName = null
      }
    } else {
      throw new Error('invalid call site information provided')
    }

    // This is a slight deviation from the CallSite API, but it's very useful
    // to have a field that's the actual function with type and method name.
    if (
      this.typeName &&
      this.functionName &&
      !this.functionName.startsWith(this.typeName)
    ) {
      this.functionName = `${this.typeName}.${this.functionName}`
    } else if (this.functionName === this.methodName) {
      this.methodName = null
    }

    // We only do the sourcemap lookup if we're parsing from a CallSite
    // If we get it from an Error stack line, then Node has already done
    // the mapping for us, and when ts-node (and other in-place
    // transpilers) create JavaScript, they use the same filename, leading
    // to an incorrect double offset,.
    if (
      this.#fileName &&
      isCallSite(c) &&
      !this.#sourceMap &&
      !this.#fileName.startsWith('node:')
    ) {
      // Passing an object that isn't an actual Error object to
      // findSourceMap causes problems in node 16
      /* c8 ignore start */
      const sme = e && e instanceof Error ? e : undefined
      /* c8 ignore stop */
      this.#sourceMap = findSourceMap(this.#fileName, sme)
      if (this.#sourceMap && typeof this.lineNumber === 'number') {
        // SourceMap.findEntry doesn't actually return the line/column
        // number, despite the property names, but rather the zero-indexed
        // line/column start of a mapping range, and must be looked up
        // using the zero-indexed line and column.
        // To find the mapping, we look it up with the zero-indexed
        // line/col, then figure out how far our line/col is from the
        // mapping, and apply that same offset to the start of the origin
        // in the mapping.
        const payload = this.#sourceMap.findEntry(
          // safety bounds around numbers here, impossible to hit
          // if we're looking up actual call sites though.
          /* c8 ignore start */
          Math.max(0, this.lineNumber - 1),
          Math.max(0, (this.columnNumber || 0) - 1),
          /* c8 ignore stop */
        )
        if (payload) {
          const offset: [number, number] = [
            this.lineNumber - payload.generatedLine,
            /* c8 ignore start */
            (this.columnNumber || 1) - payload.generatedColumn,
            /* c8 ignore stop */
          ]
          const originalLine = payload.originalLine + offset[0]
          const originalColumn = payload.originalColumn + offset[1]
          const genFilename = this.#relativize(this.#fileName)
          this.generated = {
            /* c8 ignore start */
            fileName: this.#relativize(genFilename || null),
            /* c8 ignore stop */
            lineNumber: this.lineNumber,
            columnNumber: this.columnNumber,
          }

          this.#fileName = payload.originalSource
          this.lineNumber = originalLine
          this.columnNumber = originalColumn
        }
      }
    }
  }

  #relativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    else f = f.replace(/\\/g, '/')
    try {
      const rel = relative(this.#cwd, f)
      return rel.length < f.length ? rel : f
    } catch {
      return f
    }
  }
  #derelativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    try {
      return resolve(this.#cwd, f)
    } catch {
      return f
    }
  }

  toString(jsStyle = false): string {
    // in js style mode, use the origin source file if it is within
    // our cwd. Otherwise, use the generated source location.
    const useGen =
      jsStyle &&
      this.generated?.fileName &&
      this.fileName &&
      this.generated.fileName !== this.fileName &&
      (isAbsolute(this.fileName) || this.fileName.startsWith('..'))
    const { fileName, lineNumber, columnNumber, generated } =
      useGen && this.generated ?
        {
          fileName: this.#derelativize(this.generated.fileName),
          lineNumber: this.generated.lineNumber,
          columnNumber: this.generated.columnNumber,
          generated: undefined,
        }
      : jsStyle ?
        {
          fileName: this.#derelativize(this.fileName),
          lineNumber: this.lineNumber,
          columnNumber: this.columnNumber,
          generated: undefined,
        }
      : this
    const loc = { fileName, lineNumber, columnNumber, generated }
    for (const l of [loc, loc.generated]) {
      if (l?.fileName) {
        l.fileName =
          jsStyle ?
            this.#derelativize(l.fileName)
          : this.#relativize(l.fileName)
      }
    }

    let fname = this.functionName || ''
    let tn = ''
    let tnGet = ''
    let tnSet = ''
    if (this.typeName) {
      const mn = this.methodName || '<anonymous>'
      tn = this.typeName + '.' + mn
      tnGet = this.typeName + '.get ' + mn
      tnSet = this.typeName + '.set ' + mn
    }
    if (!fname && tn) {
      fname = tn
    }
    const mn = this.methodName
    if (
      fname &&
      tn &&
      mn &&
      fname !== tn &&
      fname !== tnGet &&
      fname !== tnSet
    ) {
      fname += ` [as ${mn}]`
    }
    if (this.isConstructor && fname) {
      fname = `new ${fname}`
    }
    let ev = ''
    const nat = this.isNative ? 'native' : ''
    let file = loc.fileName || ''
    const hasLC = loc.lineNumber && loc.columnNumber
    if (this.evalOrigin) {
      ev = `eval at ${this.evalOrigin.toString(jsStyle)}`
      if (hasLC) {
        const f = loc.fileName || '<anonymous>'
        let lr = `${f}:${loc.lineNumber}:${loc.columnNumber}`
        if (loc.generated && loc.generated.fileName) {
          const f = loc.generated.fileName
          const { lineNumber: l, columnNumber: c } = loc.generated
          lr = `${f}:${l}:${c} (${lr})`
        }
        ev += `, ${lr}`
      }
      // should always have an fname at this point
      /* c8 ignore start */
      ev = fname ? ` (${ev})` : ev
      /* c8 ignore stop */
      return `${fname}${ev}`
    }

    if (file || hasLC) {
      if (hasLC) {
        /* c8 ignore start */
        file = file || '<anonymous>'
        /* c8 ignore stop */
        file += `:${loc.lineNumber}:${loc.columnNumber}`
      }
    } else if (nat) {
      file = 'native'
      // impossible in normal cases
      /* c8 ignore start */
    } else {
      file = ''
    }
    /* c8 ignore stop */
    let g = ''
    if (loc.generated && loc.generated.fileName) {
      const { fileName, lineNumber, columnNumber } = loc.generated
      g = fileName
      /* c8 ignore start */
      if (!g) g = ''
      /* c8 ignore stop */
      if (g === loc.fileName) g = ''
      if (g) {
        if (
          typeof lineNumber === 'number' &&
          typeof columnNumber === 'number'
        ) {
          g += `:${lineNumber}:${columnNumber}`
        }
        if (ev || fname) {
          g = ` (${g})`
        }
      }
    }
    if (file && (ev || fname || g)) {
      file = ` (${file})`
    }
    const pre = jsStyle ? '    at ' : ''
    return `${pre}${fname}${ev}${g}${file}`
  }

  toJSON(): CallSiteLikeJSON {
    const {
      fileName,
      lineNumber,
      columnNumber,
      evalOrigin,
      typeName,
      methodName,
      functionName,
      isEval,
      isNative,
      isToplevel,
      isConstructor,
      generated,
    } = this
    const json: CallSiteLikeJSON = {}
    if (fileName !== null) json.fileName = fileName
    if (lineNumber || lineNumber === 0) json.lineNumber = lineNumber
    if (columnNumber || columnNumber === 0)
      json.columnNumber = columnNumber
    if (evalOrigin) json.evalOrigin = evalOrigin.toJSON()

    if (typeName !== null) {
      json.typeName = typeName
    }

    if (methodName !== null) json.methodName = methodName
    if (functionName !== null) json.functionName = functionName
    if (isEval) json.isEval = isEval
    if (isNative) json.isNative = isNative
    if (isToplevel) json.isToplevel = isToplevel
    if (isConstructor) json.isConstructor = isConstructor
    if (generated && generated.fileName) {
      const f = this.#relativize(generated.fileName)
      if (
        f &&
        typeof f === 'string' &&
        (f !== json.fileName || f === '<anonymous>')
      ) {
        const gen: Record<string, string | number> = {}
        gen.fileName = f
        if (generated.lineNumber)
          gen.lineNumber = generated.lineNumber
        if (generated.columnNumber)
          gen.columnNumber = generated.columnNumber
        if (Object.keys(gen).length > 0) {
          json.generated = gen
        }
      }
    }
    return json
  }
}

