const version = process.env.__TESTING_RIMRAF_NODE_VERSION__ || process.version
const versArr = version.replace(/^v/, '').split('.')

/* c8 ignore start */
const [major = 0, minor = 0] = versArr.map(v => parseInt(v, 10))
/* c8 ignore stop */
const hasNative = major > 14 || (major === 14 && minor >= 14)

// we do NOT use native by default on Windows, because Node's native
// rm implementation is less advanced.  Change this code if that changes.
export const useNative: (opt?: RimrafAsyncOptions) => boolean =
  !hasNative || platform === 'win32' ?
    () => false
  : opt => !opt?.signal && !opt?.filter
export const useNativeSync: (opt?: RimrafOptions) => boolean =
  !hasNative || platform === 'win32' ?
    () => false
  : opt => !opt?.signal && !opt?.filter

/**
 * utility used in creating the source of a mocked import module
 * @module
 */

const exportIndex = new Map<string, number>()

/**
 * Create the `export` lines in the generated module source
 */
export const exportLine = (k: string, mockSrc: string): string => {
  if (k === 'default') {
    return `const defExp = ${mockSrc}.default
export default defExp
`
  }
  const kSrc = JSON.stringify(k)

  const i = exportIndex.get(mockSrc) || 0
  exportIndex.set(mockSrc, i + 1)
  return `const exp${i} = ${mockSrc}[${kSrc}]
export { exp${i} as ${kSrc} }
`
}

import { isBuiltin } from 'module'
import { isAbsolute, resolve } from 'path'
import { isRelativeRequire } from 'resolve-import/is-relative-require'
import { pathToFileURL } from 'url'

// turn all the mocks we get into either the exports provided,
// if it's an object, or { default: <value> } otherwise.

export const mungeMocks = (
  mocksInput: Record<string, any>,
  dir: string,
): { [k: string]: { [j: string]: any } } => {
  const mocks = Object.create(null)
  for (const [k, v] of Object.entries(mocksInput)) {
    const m =
      v && typeof v === 'object' && !Array.isArray(v) ?
        v
      : { default: v }
    if (isBuiltin(k)) {
      mocks[k] = m
      /* c8 ignore start */
      if (k.startsWith('node:')) {
        /* c8 ignore stop */
        const bare = k.substring('node:'.length)
        if (!(bare in mocksInput)) {
          mocks[bare] = m
        }
      } else {
        const prefixed = `node:${k}`
        if (!(prefixed in mocksInput)) {
          mocks[prefixed] = m
        }
      }
    } else if (/^(file:\/\/\/|https?:\/\/)/.test(k)) {
      mocks[k] = m
    } else if (isRelativeRequire(k) || isAbsolute(k)) {
      mocks[String(pathToFileURL(resolve(dir, k)))] = m
    } else {
      // absolute package name
      mocks[k] = m
    }
  }
  return mocks
}

//@ts-ignore
import serviceKeyCJS from '../commonjs/service-key.js'
export const { serviceKey } = serviceKeyCJS as { serviceKey: string }

import { resolveImport } from 'resolve-import'

export const resolveMockEntryPoint = async (
  url: URL,
  module: string,
  serviceKey: string,
  key: string,
  caller: Function | ((...a: any[]) => any),
): Promise<string> => {
  let mockedModuleURL: URL
  if (module.startsWith('./') || module.startsWith('../')) {
    mockedModuleURL = new URL(module, url)
  } else {
    const res = (await resolveImport(module, url)) as URL
    // caught at the exposed API, defense in depth only
    // but the experience if it throws here is unhelpful.
    /* c8 ignore start */
    if (typeof res === 'string') {
      const er = new TypeError(
        'node builtins cannot be mock imported',
      )
      Error.captureStackTrace(er, caller)
      throw er
    }
    /* c8 ignore stop */
    mockedModuleURL = res
  }
  mockedModuleURL.searchParams.set('tapmock', `${serviceKey}.${key}`)
  return String(mockedModuleURL)
}

import { Base, TAP } from '@tapjs/core'
import {
  FailData,
  PassData,
  TestStreamSerialize,
} from '@tapjs/error-serdes'
import { TestMap } from './test-map.js'
import { testMessageData } from './test-message-data.js'
import { testNestedLocation } from './test-nested-location.js'
import { testPointMessageData } from './test-point-message-data.js'
import { testPointResults } from './test-point-results.js'
import { testResults } from './test-results.js'

const isPass = (pf?: PassData | FailData): pf is PassData =>
  !!pf?.details && !pf.details.error
const isFail = (pf?: PassData | FailData): pf is FailData =>
  !!pf?.details.error

export const printMessagesFn = (
  tap: TAP,
  stream: TestStreamSerialize,
  subsMap: TestMap<Base[]>,
  diagsMap: TestMap<DiagnosticData[]>,
) => {
  const printMessages = (t: Base, testNumber: number = 0) => {
    // enqueue all my subs, then dequeue each one and print them
    const subs = subsMap.get(t)
    if (t.parent) {
      stream.dequeue(testMessageData(t))
      stream.start(testMessageData(t))
    }
    let count = 0

    // print all the test points emitted directly on this test
    const results = t.results
    /* c8 ignore start */
    if (!results)
      throw new Error('printing results before test completion')
    /* c8 ignore stop */
    for (const type of [
      'passes',
      'skips',
      'todos',
      'failures',
    ] as const) {
      for (const res of results[type] ?? []) {
        if (res.closingTestPoint) continue
        stream.enqueue(testPointMessageData(res, t))
        stream.dequeue(testPointMessageData(res, t))
        stream.start(testPointMessageData(res, t))
        const passFail = testPointResults(res, t, ++count)
        if (isPass(passFail)) {
          stream.pass(passFail)
        } else {
          stream.fail(passFail)
        }
      }
    }

    // recurse to all subtests
    if (subs) {
      // determine whether a suite or assertion based on subs.length
      for (const t of subs) {
        // console.error(t.parser)
        stream.enqueue(testMessageData(t))
      }
      for (const t of subs) {
        printMessages(t, ++count)
      }
    }

    if (count && t !== tap) {
      stream.plan({ ...testNestedLocation(t), count })
    }

    const passFail = testResults(t, count, testNumber)
    if (isPass(passFail)) {
      stream.pass(passFail)
    } else if (isFail(passFail)) {
      stream.fail(passFail)
    }

    /* c8 ignore start */
    const diags = diagsMap.get(t) || []
    /* c8 ignore stop */
    for (const d of diags) {
      stream.diagnostic(d)
    }
  }
  return printMessages
}

import { Base } from '@tapjs/core'
import { TestStreamSerialize } from '@tapjs/error-serdes'
import { format } from 'util'
import { locFromAt } from './loc-from-at.js'
import { TestMap } from './test-map.js'

export const commentMethod = (
  stream: TestStreamSerialize,
  diagsMap: TestMap<DiagnosticData[]>,
) => {
  return function comment(this: Base, ...args: any[]) {
    const message = format(...args)
    const diags = diagsMap.get(this)
    const m = {
      ...locFromAt(comment),
      nesting: this.nestingLevel,
      message,
    }
    if (diags) diags.push(m)
    else stream.diagnostic(m)
  }
}

import { Base, TAP } from '@tapjs/core'
import { DiagnosticData } from '@tapjs/error-serdes'
import { TestMap } from './test-map.js'
import { testNestedLocation } from './test-nested-location.js'

export const onAddFn = (
  comment: (this: Base, ...args: any[]) => void,
  diagsMap: TestMap<DiagnosticData[]>,
  subsMap: TestMap<Base[]>,
) => {
  const onAdd = (t: Base) => {
    const tt = t as TAP
    const subs: Base[] = []
    const diags: DiagnosticData[] = diagsMap.get(t) || []
    subsMap.set(t, subs)
    diagsMap.set(t, diags)

    if (typeof tt.comment === 'function') {
      tt.comment = comment
    } else if (!tt.comment) {
      t.parser.on('comment', message => {
        message = message.replace(/^#/, '').trim()
        diags.push({ ...testNestedLocation(t), message })
      })
    }
    tt.on('subtestAdd', onAdd)
    if (t.parent) {
      subsMap.get(t.parent)?.push(t)
    }
  }
  return onAdd
}

// A map of test objects, but make sure that we don't get confused
// if we get the Test proxy or the underlying TestBase.

import { Base } from '@tapjs/core'

const kSerializationKey = Symbol.for('@tapjs/node-serialize.key')
const getKey = (
  t: Base & { [kSerializationKey]?: string },
): string => {
  const k = t[kSerializationKey]
  if (k) return k
  const n = String(Math.random())
  Object.defineProperty(t, kSerializationKey, {
    value: n,
    writable: false,
    configurable: true,
    enumerable: false,
  })
  return n
}

export class TestMap<T extends {}> extends Map<Base, T> {
  constructor(items?: [Base, T][]) {
    super()
    if (items) {
      for (const [t, v] of items) {
        this.set(t, v)
      }
    }
  }
  get(t: Base): T | undefined {
    return super.get(getKey(t) as unknown as Base)
  }
  has(t: Base) {
    return super.has(getKey(t) as unknown as Base)
  }
  set(t: Base, v: T) {
    return super.set(getKey(t) as unknown as Base, v)
  }
}

import { createRequire } from 'node:module'
//@ts-ignore
export const req = createRequire(import.meta.url)

import { TestStreamSerialize } from '@tapjs/error-serdes'
import { locFromAt } from './loc-from-at.js'

export const serializeStdio = (
  stream: TestStreamSerialize,
  process: NodeJS.Process,
  which: 'stdout' | 'stderr',
) => {
  const write = (m: Buffer | string) => {
    const message = String(m)
    stream[which]({ ...locFromAt(write), message })
  }
  const orig = process[which]
  Object.defineProperty(process, which, {
    configurable: true,
    enumerable: true,
    writable: true,
    // create a new object that inherits from the original, but has
    // a write() method that captures a test:stderr/test:stdout message
    value: Object.defineProperties(Object.create(orig), {
      write: { value: write },
    }),
  })
}

import { argvToNodeOptions } from './argv-to-node-options.js'
import {
  importLoader,
  importMatch,
  legacyLoader,
  legacyMatch,
} from './loader-paths.js'
import { nodeOptionsToArgv } from './node-options-to-argv.js'

import Module from 'node:module'

const getKeyValue = (
  args: readonly string[],
  i: number
): [boolean, string, string | undefined] => {
  const arg = args[i]
  /* c8 ignore start */
  if (typeof arg !== 'string') throw new Error('invalid arg')
  /* c8 ignore stop */
  if (arg.includes('=')) {
    const [k, ...rest] = arg.split('=') as [string, ...string[]]
    return [true, k, rest.join('=')]
  } else if (i < args.length - 1) {
    return [false, arg, args[i + 1]]
  } else {
    return [false, arg, undefined]
  }
}

const useImport = !!(Module as { register?: (...a: any[]) => any })
  .register

// JUST test if we need to do anything at all with the env.
// if the loader is set already in the args, even incorrectly, return true
const hasLoader = (args: readonly string[]): boolean => {
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || arg === '--') break
    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      continue
    }
    if (!eq) i++
    if (
      (k === '--experimental-loader' || k === '--loader') &&
      legacyMatch(v)
    ) {
      return true
    }
    if (k === '--import' && importMatch(v)) return true
  }
  return false
}

const rmLoader = (args: string[]) => {
  const doNotWantKeys = ['--experimental-loader', '--loader', '--import']

  const result: string[] = []

  let doubledash = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, remove it
      continue
    }
    // not ours, leave it
    result.push(arg)
    const next = args[i]
    if (!eq && typeof next === 'string') result.push(next)
    continue
  }
  return result
}

const addLoader = (args: string[]) => {
  const addKey = useImport ? '--import' : '--loader'
  const addValue = useImport ? importLoader : legacyLoader
  const doNotWantKeys = [
    '--experimental-loader',
    useImport ? '--loader' : '--import',
  ]
  const test = useImport ? importMatch : legacyMatch

  const result: string[] = []

  let doubledash = false
  let found = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, but not how we want it, omit
      continue
    }

    if (k === addKey && test(v)) {
      // already present, don't let it be set multiple times.
      if (found) continue
      found = true
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
    } else {
      // not ours
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
      continue
    }
  }
  if (!found) result.push(`${addKey}=${addValue}`)
  return !useImport ? addIgnoreLoadersWarning(result) : result
}

const addIgnoreLoadersWarning = (args: readonly string[]) =>
  args.includes('--no-warnings') ||
  args.includes('--no-warnings=ExperimentalLoader')
    ? args
    : args.concat('--no-warnings')

export const nodeOptionsEnv = (
  env: NodeJS.ProcessEnv,
  args: readonly string[]
) => {
  // if we already have the loader in args, don't add to NODE_OPTIONS
  const no = nodeOptionsToArgv(env.NODE_OPTIONS)
  return argvToNodeOptions(hasLoader(args) ? rmLoader(no) : addLoader(no))
}

// See: https://github.com/algolia/react-element-to-jsx-string/pull/819
import { createRequire } from 'module'
//@ts-ignore
const require = createRequire(import.meta.url)
const r =
  require('react-element-to-jsx-string') as typeof import('react-element-to-jsx-string')

import type { Options as ReactElementToJSXStringOptions } from 'react-element-to-jsx-string'

export type { ReactElementToJSXStringOptions }

export default r.default

// The default temporary folder location for use in the windows algorithm.
// It's TEMPting to use dirname(path), since that's guaranteed to be on the
// same device.  However, this means that:
// rimraf(path).then(() => rimraf(dirname(path)))
// will often fail with EBUSY, because the parent dir contains
// marked-for-deletion directory entries (which do not show up in readdir).
// The approach here is to use os.tmpdir() if it's on the same drive letter,
// or resolve(path, '\\temp') if it exists, or the root of the drive if not.
// On Posix (not that you'd be likely to use the windows algorithm there),
// it uses os.tmpdir() always.
import { tmpdir } from 'os'
import { parse, resolve } from 'path'
import { promises, statSync } from './fs.js'
import platform from './platform.js'
const { stat } = promises

const isDirSync = (path: string) => {
  try {
    return statSync(path).isDirectory()
  } catch (er) {
    return false
  }
}

const isDir = (path: string) =>
  stat(path).then(
    st => st.isDirectory(),
    () => false,
  )

const win32DefaultTmp = async (path: string) => {
  const { root } = parse(path)
  const tmp = tmpdir()
  const { root: tmpRoot } = parse(tmp)
  if (root.toLowerCase() === tmpRoot.toLowerCase()) {
    return tmp
  }

  const driveTmp = resolve(root, '/temp')
  if (await isDir(driveTmp)) {
    return driveTmp
  }

  return root
}

const win32DefaultTmpSync = (path: string) => {
  const { root } = parse(path)
  const tmp = tmpdir()
  const { root: tmpRoot } = parse(tmp)
  if (root.toLowerCase() === tmpRoot.toLowerCase()) {
    return tmp
  }

  const driveTmp = resolve(root, '/temp')
  if (isDirSync(driveTmp)) {
    return driveTmp
  }

  return root
}

const posixDefaultTmp = async () => tmpdir()
const posixDefaultTmpSync = () => tmpdir()

export const defaultTmp =
  platform === 'win32' ? win32DefaultTmp : posixDefaultTmp
export const defaultTmpSync =
  platform === 'win32' ? win32DefaultTmpSync : posixDefaultTmpSync

// returns an array of entries if readdir() works,
// or the error that readdir() raised if not.
import { promises, readdirSync } from './fs.js'
const { readdir } = promises
export const readdirOrError = (path: string) =>
  readdir(path).catch(er => er as NodeJS.ErrnoException)
export const readdirOrErrorSync = (path: string) => {
  try {
    return readdirSync(path)
  } catch (er) {
    return er as NodeJS.ErrnoException
  }
}

export const ignoreENOENT = async (p: Promise<any>) =>
  p.catch(er => {
    if (er.code !== 'ENOENT') {
      throw er
    }
  })

export const ignoreENOENTSync = (fn: () => any) => {
  try {
    return fn()
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code !== 'ENOENT') {
      throw er
    }
  }
}

import {
  MakeDirectoryOptions,
  mkdir,
  mkdirSync,
  stat,
  Stats,
  statSync,
} from 'fs'

export interface FsProvider {
  stat?: (
    path: string,
    callback: (err: NodeJS.ErrnoException | null, stats: Stats) => any
  ) => any
  mkdir?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean },
    callback: (err: NodeJS.ErrnoException | null, made?: string) => any
  ) => any
  statSync?: (path: string) => Stats
  mkdirSync?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => string | undefined
}

interface Options extends FsProvider {
  mode?: number | string
  fs?: FsProvider
  mkdirAsync?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => Promise<string | undefined>
  statAsync?: (path: string) => Promise<Stats>
}

export type MkdirpOptions = Options | number | string

export interface MkdirpOptionsResolved {
  mode: number
  fs: FsProvider
  mkdirAsync: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => Promise<string | undefined>
  statAsync: (path: string) => Promise<Stats>
  stat: (
    path: string,
    callback: (err: NodeJS.ErrnoException | null, stats: Stats) => any
  ) => any
  mkdir: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean },
    callback: (err: NodeJS.ErrnoException | null, made?: string) => any
  ) => any
  statSync: (path: string) => Stats
  mkdirSync: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => string | undefined
  recursive?: boolean
}

export const optsArg = (opts?: MkdirpOptions): MkdirpOptionsResolved => {
  if (!opts) {
    opts = { mode: 0o777 }
  } else if (typeof opts === 'object') {
    opts = { mode: 0o777, ...opts }
  } else if (typeof opts === 'number') {
    opts = { mode: opts }
  } else if (typeof opts === 'string') {
    opts = { mode: parseInt(opts, 8) }
  } else {
    throw new TypeError('invalid options argument')
  }

  const resolved = opts as MkdirpOptionsResolved
  const optsFs = opts.fs || {}

  opts.mkdir = opts.mkdir || optsFs.mkdir || mkdir

  opts.mkdirAsync = opts.mkdirAsync
    ? opts.mkdirAsync
    : async (
        path: string,
        options: MakeDirectoryOptions & { recursive?: boolean }
      ): Promise<string | undefined> => {
        return new Promise<string | undefined>((res, rej) =>
          resolved.mkdir(path, options, (er, made) =>
            er ? rej(er) : res(made)
          )
        )
      }

  opts.stat = opts.stat || optsFs.stat || stat
  opts.statAsync = opts.statAsync
    ? opts.statAsync
    : async (path: string) =>
        new Promise((res, rej) =>
          resolved.stat(path, (err, stats) => (err ? rej(err) : res(stats)))
        )

  opts.statSync = opts.statSync || optsFs.statSync || statSync
  opts.mkdirSync = opts.mkdirSync || optsFs.mkdirSync || mkdirSync

  return resolved
}

// note: max backoff is the maximum that any *single* backoff will do

import { RimrafAsyncOptions, RimrafOptions } from './index.js'

export const MAXBACKOFF = 200
export const RATE = 1.2
export const MAXRETRIES = 10
export const codes = new Set(['EMFILE', 'ENFILE', 'EBUSY'])

export const retryBusy = (fn: (path: string) => Promise<any>) => {
  const method = async (
    path: string,
    opt: RimrafAsyncOptions,
    backoff = 1,
    total = 0,
  ) => {
    const mbo = opt.maxBackoff || MAXBACKOFF
    const rate = opt.backoff || RATE
    const max = opt.maxRetries || MAXRETRIES
    let retries = 0
    while (true) {
      try {
        return await fn(path)
      } catch (er) {
        const fer = er as NodeJS.ErrnoException
        if (fer?.path === path && fer?.code && codes.has(fer.code)) {
          backoff = Math.ceil(backoff * rate)
          total = backoff + total
          if (total < mbo) {
            return new Promise((res, rej) => {
              setTimeout(() => {
                method(path, opt, backoff, total).then(res, rej)
              }, backoff)
            })
          }
          if (retries < max) {
            retries++
            continue
          }
        }
        throw er
      }
    }
  }

  return method
}

// just retries, no async so no backoff
export const retryBusySync = (fn: (path: string) => any) => {
  const method = (path: string, opt: RimrafOptions) => {
    const max = opt.maxRetries || MAXRETRIES
    let retries = 0
    while (true) {
      try {
        return fn(path)
      } catch (er) {
        const fer = er as NodeJS.ErrnoException
        if (
          fer?.path === path &&
          fer?.code &&
          codes.has(fer.code) &&
          retries < max
        ) {
          retries++
          continue
        }
        throw er
      }
    }
  }
  return method
}

import { dirname } from 'path'
import { MkdirpOptions, optsArg } from './opts-arg.js'

export const mkdirpManualSync = (
  path: string,
  options?: MkdirpOptions,
  made?: string | undefined | void
): string | undefined | void => {
  const parent = dirname(path)
  const opts = { ...optsArg(options), recursive: false }

  if (parent === path) {
    try {
      return opts.mkdirSync(path, opts)
    } catch (er) {
      // swallowed by recursive implementation on posix systems
      // any other error is a failure
      const fer = er as NodeJS.ErrnoException
      if (fer && fer.code !== 'EISDIR') {
        throw er
      }
      return
    }
  }

  try {
    opts.mkdirSync(path, opts)
    return made || path
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer && fer.code === 'ENOENT') {
      return mkdirpManualSync(path, opts, mkdirpManualSync(parent, opts, made))
    }
    if (fer && fer.code !== 'EEXIST' && fer && fer.code !== 'EROFS') {
      throw er
    }
    try {
      if (!opts.statSync(path).isDirectory()) throw er
    } catch (_) {
      throw er
    }
  }
}

export const mkdirpManual = Object.assign(
  async (
    path: string,
    options?: MkdirpOptions,
    made?: string | undefined | void
  ): Promise<string | undefined | void> => {
    const opts = optsArg(options)
    opts.recursive = false
    const parent = dirname(path)
    if (parent === path) {
      return opts.mkdirAsync(path, opts).catch(er => {
        // swallowed by recursive implementation on posix systems
        // any other error is a failure
        const fer = er as NodeJS.ErrnoException
        if (fer && fer.code !== 'EISDIR') {
          throw er
        }
      })
    }

    return opts.mkdirAsync(path, opts).then(
      () => made || path,
      async er => {
        const fer = er as NodeJS.ErrnoException
        if (fer && fer.code === 'ENOENT') {
          return mkdirpManual(parent, opts).then(
            (made?: string | undefined | void) => mkdirpManual(path, opts, made)
          )
        }
        if (fer && fer.code !== 'EEXIST' && fer.code !== 'EROFS') {
          throw er
        }
        return opts.statAsync(path).then(
          st => {
            if (st.isDirectory()) {
              return made
            } else {
              throw er
            }
          },
          () => {
            throw er
          }
        )
      }
    )
  },
  { sync: mkdirpManualSync }
)

import { chmodSync, promises } from './fs.js'
const { chmod } = promises

export const fixEPERM =
  (fn: (path: string) => Promise<any>) => async (path: string) => {
    try {
      return await fn(path)
    } catch (er) {
      const fer = er as NodeJS.ErrnoException
      if (fer?.code === 'ENOENT') {
        return
      }
      if (fer?.code === 'EPERM') {
        try {
          await chmod(path, 0o666)
        } catch (er2) {
          const fer2 = er2 as NodeJS.ErrnoException
          if (fer2?.code === 'ENOENT') {
            return
          }
          throw er
        }
        return await fn(path)
      }
      throw er
    }
  }

export const fixEPERMSync = (fn: (path: string) => any) => (path: string) => {
  try {
    return fn(path)
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer?.code === 'ENOENT') {
      return
    }
    if (fer?.code === 'EPERM') {
      try {
        chmodSync(path, 0o666)
      } catch (er2) {
        const fer2 = er2 as NodeJS.ErrnoException
        if (fer2?.code === 'ENOENT') {
          return
        }
        throw er
      }
      return fn(path)
    }
    throw er
  }
}

const platform = process.env.__TESTING_MKDIRP_PLATFORM__ || process.platform
import { parse, resolve } from 'path'
export const pathArg = (path: string) => {
  if (/\0/.test(path)) {
    // simulate same failure that node raises
    throw Object.assign(
      new TypeError('path must be a string without null bytes'),
      {
        path,
        code: 'ERR_INVALID_ARG_VALUE',
      }
    )
  }

  path = resolve(path)
  if (platform === 'win32') {
    const badWinChars = /[*|"<>?:]/
    const { root } = parse(path)
    if (badWinChars.test(path.substring(root.length))) {
      throw Object.assign(new Error('Illegal characters in path.'), {
        path,
        code: 'EINVAL',
      })
    }
  }

  return path
}

import { dirname } from 'path'
import { findMade, findMadeSync } from './find-made.js'
import { mkdirpManual, mkdirpManualSync } from './mkdirp-manual.js'
import { MkdirpOptions, optsArg } from './opts-arg.js'

export const mkdirpNativeSync = (
  path: string,
  options?: MkdirpOptions
): string | void | undefined => {
  const opts = optsArg(options)
  opts.recursive = true
  const parent = dirname(path)
  if (parent === path) {
    return opts.mkdirSync(path, opts)
  }

  const made = findMadeSync(opts, path)
  try {
    opts.mkdirSync(path, opts)
    return made
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer && fer.code === 'ENOENT') {
      return mkdirpManualSync(path, opts)
    } else {
      throw er
    }
  }
}

export const mkdirpNative = Object.assign(
  async (
    path: string,
    options?: MkdirpOptions
  ): Promise<string | void | undefined> => {
    const opts = { ...optsArg(options), recursive: true }
    const parent = dirname(path)
    if (parent === path) {
      return await opts.mkdirAsync(path, opts)
    }

    return findMade(opts, path).then((made?: string | undefined) =>
      opts
        .mkdirAsync(path, opts)
        .then(m => made || m)
        .catch(er => {
          const fer = er as NodeJS.ErrnoException
          if (fer && fer.code === 'ENOENT') {
            return mkdirpManual(path, opts)
          } else {
            throw er
          }
        })
    )
  },
  { sync: mkdirpNativeSync }
)

import { mkdir, mkdirSync } from 'fs'
import { MkdirpOptions, optsArg } from './opts-arg.js'

const version = process.env.__TESTING_MKDIRP_NODE_VERSION__ || process.version
const versArr = version.replace(/^v/, '').split('.')
const hasNative = +versArr[0] > 10 || (+versArr[0] === 10 && +versArr[1] >= 12)

export const useNativeSync = !hasNative
  ? () => false
  : (opts?: MkdirpOptions) => optsArg(opts).mkdirSync === mkdirSync

export const useNative = Object.assign(
  !hasNative
    ? () => false
    : (opts?: MkdirpOptions) => optsArg(opts).mkdir === mkdir,
  {
    sync: useNativeSync,
  }
)

// This module is more or less a direct port of the serialization logic
// from node's lib/internal/error_serdes module, with primordials removed.
import {
  errorCtorNames,
  kCustomInspect,
  kCustomInspectedObject,
  kInspectedError,
  kInspectedSymbol,
  kSerializedError,
  kSerializedObject,
} from './constants.js'

import { inspect } from 'node:util'
import { serialize } from 'v8'

const getAllProps = (obj: any, target = obj) => {
  const all = Object.create(null)
  if (obj === null) return all
  Object.assign(all, getAllProps(Object.getPrototypeOf(obj), target))
  const keys = Object.getOwnPropertyNames(obj)
  for (const key of keys) {
    const desc = Object.getOwnPropertyDescriptor(obj, key)
    /* c8 ignore start */
    if (!desc) continue
    /* c8 ignore stop */
    const getter = desc.get
    if (getter) {
      try {
        delete desc.get
        delete desc.set
        desc.value = getter.call(target)
      } catch {}
    }
    if (key === 'cause') {
      desc.value = serializeError(desc.value)
      all.cause = desc
    } else if (
      'value' in desc &&
      typeof desc.value !== 'function' &&
      typeof desc.value !== 'symbol'
    ) {
      all[key] = desc
    }
  }
  return all
}

const isError = (obj: any): obj is Error =>
  typeof obj === 'object' &&
  Object.prototype.toString.call(obj) === '[object Error]'

const getCtors = (obj: any) => {
  const ctors: Function[] = []
  for (
    let cur = obj;
    cur !== null;
    cur = Object.getPrototypeOf(cur)
  ) {
    const desc = Object.getOwnPropertyDescriptor(cur, 'constructor')
    const value = desc?.value
    if (typeof value === 'function') {
      ctors.push(value)
    }
  }
  return ctors
}

const getName = (f: Function): string | undefined => {
  const n = Object.getOwnPropertyDescriptor(f, 'name')?.value
  if (typeof n === 'string') return n
}

export const serializeError = (er: unknown) => {
  if (typeof er === 'symbol') {
    return Buffer.from(
      String.fromCharCode(kInspectedSymbol) + String(er),
      'utf8',
    )
  }
  try {
    if (isError(er)) {
      const ctors = getCtors(er)
      for (const c of ctors) {
        const name = getName(c)
        if (name && errorCtorNames.has(name)) {
          const serialized = serialize({
            constructor: name,
            properties: getAllProps(er),
          })
          return Buffer.concat([
            Buffer.from([kSerializedError]),
            serialized,
          ])
        }
      }
      /* c8 ignore start */
    }
  } catch {}
  /* c8 ignore stop */
  try {
    if (
      er !== null &&
      er !== undefined &&
      kCustomInspect in (er as {})
    ) {
      return Buffer.from(
        String.fromCharCode(kCustomInspectedObject) + inspect(er),
        'utf8',
      )
    }
  } catch {}
  try {
    const serialized = serialize(er)
    return Buffer.concat([
      Buffer.from([kSerializedObject]),
      serialized,
    ])
  } catch {}
  return Buffer.from(
    String.fromCharCode(kInspectedError) + inspect(er),
    'utf8',
  )
}

// This module is more or less a direct port of the deserialization logic
// from node's lib/internal/error_serdes module, with primordials removed.
import { deserialize } from 'v8'
import {
  errors,
  kCustomInspectedObject,
  kInspectedError,
  kInspectedSymbol,
  kSerializedError,
  kSerializedObject,
} from './constants.js'

export const deserializeError = (error: Buffer) => {
  switch (error[0]) {
    case kSerializedError: {
      // serialized Error
      const { properties, constructor } = deserialize(
        error.subarray(1),
      )
      const ctor = errors[constructor as keyof typeof errors]
      if ('cause' in properties && 'value' in properties.cause) {
        properties.cause.value = deserializeError(
          properties.cause.value,
        )
      }
      return Object.create(ctor.prototype, properties)
    }
    case kSerializedObject: {
      return deserialize(error.subarray(1))
    }
    case kInspectedError: {
      return error.subarray(1).toString('utf8')
    }
    case kInspectedSymbol: {
      return Symbol.for(
        error
          .subarray(1 + 'Symbol('.length, error.length - 1)
          .toString(),
      )
    }
    case kCustomInspectedObject: {
      const s = error.subarray(1).toString()
      return Object.assign(Object.create(null), {
        [Symbol.for('nodejs.util.inspect.custom')]: () => s,
      })
    }
  }
}

import { Minipass } from 'minipass'
import { DefaultSerializer } from 'v8'
import {
  kSerializedSizeHeader,
  kV8HeaderLength,
} from './constants.js'
import {
  FailData,
  isMessageFail,
  Message,
  MessageFail,
  NamedNestedLocation,
  PassData,
  PlanData,
  StringData,
} from './messages.js'
import { serializeError } from './serialize.js'
export * from './messages.js'

export class TestStreamSerialize extends Minipass<Buffer> {
  #serializer: DefaultSerializer = new DefaultSerializer()

  constructor() {
    super()
    this.writable = false
  }

  diagnostic(data: DiagnosticData) {
    this.#write({ type: 'test:diagnostic', data })
  }
  dequeue(data: NamedNestedLocation) {
    this.#write({ type: 'test:dequeue', data })
  }
  enqueue(data: NamedNestedLocation) {
    this.#write({ type: 'test:enqueue', data })
  }
  fail(data: FailData) {
    this.#write({ type: 'test:fail', data })
  }
  pass(data: PassData) {
    this.#write({ type: 'test:pass', data })
  }
  plan(data: PlanData) {
    this.#write({ type: 'test:plan', data })
  }
  start(data: NamedNestedLocation) {
    this.#write({ type: 'test:start', data })
  }
  stderr(data: StringData) {
    this.#write({ type: 'test:stderr', data })
  }
  stdout(data: StringData) {
    this.#write({ type: 'test:stdout', data })
  }

  #write(item: Message) {
    let originalError: undefined | (Error & { cause?: any })
    if (
      isMessageFail(item) &&
      item.data.details.error !== undefined
    ) {
      originalError = item.data.details.error
      Object.assign(item.data.details, {
        error: serializeError(originalError),
      })
    }

    this.#serializer.writeHeader()
    // Add 4 bytes, to later populate with message length
    this.#serializer.writeRawBytes(Buffer.allocUnsafe(4))
    this.#serializer.writeHeader()
    this.#serializer.writeValue(item)

    if (originalError !== undefined) {
      Object.assign((item as MessageFail).data.details, {
        error: originalError,
      })
    }
    const serializedMessage = this.#serializer.releaseBuffer()
    const serializedMessageLength =
      serializedMessage.length - kSerializedSizeHeader

    serializedMessage.set(
      [
        (serializedMessageLength >> 24) & 0xff,
        (serializedMessageLength >> 16) & 0xff,
        (serializedMessageLength >> 8) & 0xff,
        serializedMessageLength & 0xff,
      ],
      kV8HeaderLength,
    )
    super.write(serializedMessage)
  }

  //@ts-ignore
  write() {
    throw new Error('not directly writable, use message methods')
  }
}

// These will all be undefined if the t.options.at is null, or not a file
export type Location = {
  file?: string
  line?: number
  column?: number
}

export interface StringData extends Location {
  message: string
}

export interface NestedLocation extends Location {
  nesting: number
}

export type DiagnosticData = StringData & NestedLocation

export interface NamedNestedLocation extends NestedLocation {
  name: string
}

export interface PlanData extends NestedLocation {
  count: number
}

export interface ResultsDataDetails {
  duration_ms: number
  type?: 'suite'
  error?: Buffer | (Error & { cause?: any })
}
export type PassDataDetails = ResultsDataDetails & {
  error?: undefined
}

export interface FailDataDetails extends ResultsDataDetails {
  error: Error & { cause?: any }
}
export interface FailDataDetailsSerialized
  extends ResultsDataDetails {
  error: Buffer
}

export interface TestData extends NamedNestedLocation {
  details: PassDataDetails | FailDataDetails
  testNumber: number
  todo?: string | boolean
  skip?: string | boolean
}
export type PassData = TestData & { details: PassDataDetails }
export type FailData = TestData & {
  details: FailDataDetails
}

export type MessageDequeue = {
  type: 'test:dequeue'
  data: NamedNestedLocation
}

export type MessageDiagnostic = {
  type: 'test:diagnostic'
  data: DiagnosticData
}

export type MessageEnqueue = {
  type: 'test:enqueue'
  data: NamedNestedLocation
}

export type MessageFail = {
  type: 'test:fail'
  data: FailData
}

export type MessagePass = {
  type: 'test:pass'
  data: PassData
}

export type MessagePlan = {
  type: 'test:plan'
  data: PlanData
}

export type MessageStart = {
  type: 'test:start'
  data: NamedNestedLocation
}

export type MessageStderr = {
  type: 'test:stderr'
  data: StringData
}

export type MessageStdout = {
  type: 'test:stdout'
  data: StringData
}

export type Message =
  | MessageDiagnostic
  | MessageEnqueue
  | MessageDequeue
  | MessageStart
  | MessageFail
  | MessagePass
  | MessagePlan
  | MessageStderr
  | MessageStdout

const hasDetails = (m: Message): m is MessagePass | MessageFail =>
  !!(m as MessagePass | MessageFail).data.details
export const isMessageFail = (m: Message): m is MessageFail => {
  return (
    m.type === 'test:fail' &&
    hasDetails(m) &&
    'error' in m.data.details
  )
}

import { DefaultSerializer } from 'node:v8'

export const kSerializedError = 0
export const kSerializedObject = 1
export const kInspectedError = 2
export const kInspectedSymbol = 3
export const kCustomInspectedObject = 4
export const kCustomInspect = Symbol.for('nodejs.util.inspect.custom')
export const errors = {
  Error,
  TypeError,
  RangeError,
  URIError,
  SyntaxError,
  ReferenceError,
  EvalError,
}
export const errorCtorNames = new Set(Object.keys(errors))

// get the header length that v8 uses
const serializer = new DefaultSerializer()
serializer.writeHeader()
const v8Header = serializer.releaseBuffer()
export const kV8HeaderLength = v8Header.length
// add space for the Uint32 message size
export const kSerializedSizeHeader = 4 + kV8HeaderLength

import { Base } from '@tapjs/core'
import { NamedNestedLocation } from '@tapjs/error-serdes'
import { testNestedLocation } from './test-nested-location.js'

export const testMessageData = (t: Base): NamedNestedLocation => {
  const { name } = t
  return { name, ...testNestedLocation(t) }
}

import { Minipass } from 'minipass'
import { DefaultDeserializer } from 'v8'
import {
  kSerializedSizeHeader,
  kV8HeaderLength,
} from './constants.js'
import { deserializeError } from './deserialize.js'
import { Message } from './messages.js'
export * from './messages.js'

export class TestStreamDeserialize extends Minipass<
  Message,
  Minipass.ContiguousData
> {
  #buffer?: Buffer

  constructor() {
    super({ objectMode: true })
  }

  write(chunk: Minipass.ContiguousData, cb?: () => void): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding,
    cb?: () => void,
  ): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void,
  ): boolean {
    // stream.write types boilerplate
    /* c8 ignore start */
    if (typeof encoding === 'function') {
      cb = encoding
      encoding = undefined
    }
    if (encoding === 'buffer') encoding = undefined
    if (typeof chunk === 'string') {
      chunk = Buffer.from(chunk, encoding ?? 'utf8')
    } else if (!Buffer.isBuffer(chunk)) {
      chunk = Buffer.from(chunk as ArrayBuffer)
    }
    /* c8 ignore stop */

    let buf =
      this.#buffer?.length ?
        Buffer.concat([this.#buffer, chunk as Buffer])
      : (chunk as Buffer)
    this.#buffer = undefined

    while (buf.length >= kSerializedSizeHeader) {
      const size = readSize(buf, kV8HeaderLength)
      if (buf.length < size + kSerializedSizeHeader) {
        break
      }
      const des = new DefaultDeserializer(
        buf.subarray(
          kSerializedSizeHeader,
          size + kSerializedSizeHeader,
        ),
      )
      buf = buf.subarray(size + kSerializedSizeHeader)
      des.readHeader()
      const item = des.readValue()
      if (item?.data?.details?.error) {
        item.data.details.error = deserializeError(
          item.data.details.error,
        )
      }

      //@ts-ignore
      super.write(item as Message)
    }

    if (buf.length) this.#buffer = buf

    /* c8 ignore start */
    if (cb) {
      if (this.flowing) cb()
      else this.once('drain', cb)
    }
    /* c8 ignore stop */
    return this.flowing
  }

  end(cb?: () => void): this
  end(chunk: Minipass.ContiguousData, cb?: () => void): this
  end(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding,
    cb?: () => void,
  ): this
  end(
    chunk?: Minipass.ContiguousData | (() => void),
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void,
  ) {
    // just affordance for TS's persnicketiness
    /* c8 ignore start */
    const ret =
      chunk === undefined || typeof chunk === 'function' ?
        super.end(chunk)
      : typeof encoding === 'function' ? super.end(chunk, encoding)
      : super.end(chunk, encoding, cb)
    /* c8 ignore stop */
    if (this.#buffer) {
      this.emit('error', new Error('deserialize ended mid-message'))
    }
    return ret
  }
}

const readSize = (buf: Buffer, offset: number) => {
  const a = buf[offset]
  const b = buf[offset + 1]
  const c = buf[offset + 2]
  const d = buf[offset + 3]

  // not possible, we check the length
  /* c8 ignore start */
  if (
    a === undefined ||
    b === undefined ||
    c === undefined ||
    d === undefined
  ) {
    throw new Error('Invalid buffer, not long enough to readSize')
  }
  /* c8 ignore stop */

  return (a << 24) | (b << 16) | (c << 8) | d
}

// a bunch of lines are ignored for coverage in this file, because
// the situations they cover ought to be impossible, but are covered
// for type safety, and in case there's a bug in tapjs/core that allows
// that incorrect behavior.
import { Base } from '@tapjs/core'
import { FailData, PassData } from '@tapjs/error-serdes'
import { expandStack } from '@tapjs/stack'
import { resultToError } from './result-to-error.js'
import { testMessageData } from './test-message-data.js'

export const testResults = (
  t: Base,
  count: number,
  testNumber: number,
): PassData | FailData | undefined => {
  if (!t.parent) return

  const results = t.results
  /* c8 ignore start */
  if (!results) {
    throw new Error('printing messages before results available')
  }
  /* c8 ignore stop */
  const suite = count > 0
  const p: PassData = {
    ...testMessageData(t),
    details: {
      duration_ms: t.time,
    },
    testNumber,
  }

  const skip =
    results.plan.skipAll ?
      results.plan.skipReason || true
    : t.options.skip
  if (skip) p.skip = skip
  const todo = t.options.todo
  if (todo) p.todo = todo
  if (suite) p.details.type = 'suite'

  if (results.ok) return p

  if (suite) {
    const nf = results.failures.length
    const m = `${nf} subtest${nf === 1 ? '' : 's'} failed`
    return {
      ...p,
      details: {
        ...p.details,
        error: Object.assign(new Error(m), {
          code: 'ERR_TEST_FAILURE',
          failureType: 'subtestsFailed',
          cause: m,
          /* c8 ignore start */
          failures: results.failures.map(f => f.name || f.tapError),
          stack:
            t.options.stack ?
              expandStack(t.options.stack)
            : undefined,
          /* c8 ignore stop */
        }),
      },
    }
  }

  // a failure here indicates a strange test that had no failing test points,
  // but then was marked as not ok.
  const fe = resultToError(
    t.lists.fail[0] ??
      results.failures[0] ?? {
        ok: false,
        name: 'Unknown test failure',
        diag: {},
      },
    t,
  )

  return {
    ...p,
    details: {
      ...p.details,
      error: Object.assign(new Error(fe.message), {
        cause: fe,
        code: 'ERR_TEST_FAILURE',
        failureType: 'testCodeFailure',
        /* c8 ignore start */
        failures: results.failures.map(f => f.name || f.tapError),
        /* c8 ignore stop */
        stack: expandStack(t.options.stack).trimEnd(),
      }),
    },
  }
}

import { Base } from '@tapjs/core'
import { FailData, PassData } from '@tapjs/error-serdes'
import { expandStack } from '@tapjs/stack'
import { Result, TapError } from 'tap-parser'
import { locFromCallSite } from './loc-from-callsite.js'
import { resultToError } from './result-to-error.js'

export const testPointResults = (
  res: Result | TapError,
  t: Base,
  testNumber: number,
): PassData | FailData => {
  const p: PassData = {
    name: res.name,
    testNumber,
    ...(res.diag?.at ?
      locFromCallSite(res.diag.at)
    : locFromCallSite(t.options.at)),
    nesting: t.nestingLevel + 1,
    details: {
      duration_ms: res.time ?? 0,
    },
  }
  if (res.skip) p.skip = res.skip
  if (res.todo) p.todo = res.todo
  if (res.ok || res.skip || res.todo) return p

  const fe = resultToError(res)
  // console.error(res, fe)
  return {
    ...p,
    details: {
      ...p.details,
      error: Object.assign(new Error(fe.message), {
        cause: fe,
        code: 'ERR_TEST_FAILURE',
        failureType: 'testCodeFailure',
        stack: expandStack(
          res.diag?.stack ?? t.options.stack,
        ).trimEnd(),
      }),
    },
  }
}

