import { dirname } from 'path'
import { MkdirpOptionsResolved } from './opts-arg.js'

export const findMade = async (
  opts: MkdirpOptionsResolved,
  parent: string,
  path?: string
): Promise<undefined | string> => {
  // we never want the 'made' return value to be a root directory
  if (path === parent) {
    return
  }

  return opts.statAsync(parent).then(
    st => (st.isDirectory() ? path : undefined), // will fail later
    er => {
      const fer = er as NodeJS.ErrnoException
      return fer && fer.code === 'ENOENT'
        ? findMade(opts, dirname(parent), parent)
        : undefined
    }
  )
}

export const findMadeSync = (
  opts: MkdirpOptionsResolved,
  parent: string,
  path?: string
): undefined | string => {
  if (path === parent) {
    return undefined
  }

  try {
    return opts.statSync(parent).isDirectory() ? path : undefined
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    return fer && fer.code === 'ENOENT'
      ? findMadeSync(opts, dirname(parent), parent)
      : undefined
  }
}

import { CallSiteLike, CallSiteLikeJSON } from '@tapjs/stack'
import { resolve } from 'path'

export const locFromCallSite = (
  loc?: CallSiteLike | CallSiteLikeJSON | null,
) => {
  const f = loc?.fileName ?? undefined
  const file = f !== undefined ? resolve(f) : f
  const line = loc?.lineNumber ?? undefined
  const column = loc?.columnNumber ?? undefined
  return { file, line, column }
}

// turn a tap-parser Result object into an Error
// Put some of the diagnostic info into the error message,
// because that is the string the node --test reporter shows.

import { Base } from '@tapjs/core'
import {
  CallSiteLike,
  CallSiteLikeJSON,
  expandStack,
} from '@tapjs/stack'
import { Result, TapError } from 'tap-parser'
import { locFromCallSite } from './loc-from-callsite.js'
import { prettyDiff } from './pretty-diff.js'

export const resultToError = (
  result:
    | Result
    | TapError
    | {
        ok?: boolean
        tapError?: string
        name?: string
        diag?: Result['diag']
      },
  test?: Base,
): Error => {
  const {
    stack: diagStack,
    diff: rawDiff,
    source,
    passes: _,
    ...diag
  } = result.diag || {}
  const at: CallSiteLike | CallSiteLikeJSON =
    diag.at ?? test?.options.at
  const lfa = !!at ? locFromCallSite(at) : undefined
  const stack =
    diagStack ||
    (lfa?.file ? `${lfa.file}:${lfa.line}:${lfa.column}` : undefined)
  delete diag.at

  const diff = prettyDiff(rawDiff)
  if (diff) {
    delete diag.actual
    delete diag.expected
    delete diag.found
    delete diag.wanted
  } else if (result.diag) {
    if ('found' in diag) {
      diag.actual = diag.found
    }
    if ('wanted' in diag) {
      diag.expected = diag.wanted
    }
    delete diag.found
    delete diag.wanted
  }

  const name =
    (result.name || result.tapError || 'unnamed test') +
    (diff ? '\n' + diff : '') +
    (source ? '\n| ' + source.trimEnd().split('\n').join('\n| ') : '')

  return Object.assign(new Error(name), {
    ...diag,
    stack: `Error: ${name}\n` + expandStack(stack).trimEnd(),
  })
}

export const nodeOptionsToArgv = (no: string | undefined): string[] => {
  if (!no) return []
  const argv = []
  let escaping = false
  let inquote = false
  let tokStart = 0
  let tok = ''
  for (let i = 0; i < no.length; i++) {
    const c = no.charAt(i)
    switch (c) {
      case '"':
        if (escaping) {
          tok += no.slice(tokStart, i - 1) + '"'
          tokStart = i + 1
          escaping = false
        } else if (inquote) {
          tok += no.slice(tokStart, i)
          tokStart = i + 1
          inquote = false
        } else {
          inquote = true
          tok += no.slice(tokStart, i)
          tokStart = i + 1
        }
        continue

      case '\\':
        if (inquote) {
          escaping = true
        }
        continue

      case ' ':
        if (inquote) continue
        tok += no.slice(tokStart, i)
        tokStart = i + 1
        argv.push(tok)
        tok = ''
        continue

      default:
        escaping = false
        continue
    }
  }
  if (inquote) {
    return []
  }
  tok += no.slice(tokStart)
  argv.push(tok)
  return argv
}

// This is a WAY less advanced version of the diff highlighting
// that the reporter does.
//
// We use this when serializing the diagnostics for node --test,
// because it all has to be concatenated into one big string.

const theme = {
  end: '\x1B[39m\x1B[49m\x1b[m',
  green: '\x1B[48;2;58;117;0m\x1B[38;2;242;255;229m',
  red: '\x1B[48;2;172;62;163m\x1B[38;2;255;229;241m',
  ctx: '\x1B[48;2;34;34;34m\x1B[38;2;117;158;239m',
  white: '\x1B[48;2;51;51;51m\x1B[38;2;204;204;204m',
} as const

import { proc } from '@tapjs/core'

const cols = Math.max(25, Math.min(proc?.stdout.columns || 75, 75))

export const prettyDiff = (diff: any) => {
  if (!diff || typeof diff !== 'string') return ''
  return diff
    .trimEnd()
    .replace(/^.*$/gm, s => s.trimEnd().padEnd(cols))
    .replace(/^\-.+$/gm, s => theme.red + s + theme.end)
    .replace(/^\+.+$/gm, s => theme.green + s + theme.end)
    .replace(/^\@.+$/gm, s => theme.ctx + s + theme.end)
    .replace(/^ .+$/gm, s => theme.white + s + theme.end)
}

