// run the provided tests
import { plugin as AfterPlugin } from '@tapjs/after'
import { plugin as BeforePlugin } from '@tapjs/before'
import { LoadedConfig } from '@tapjs/config'
import { TapFile, TapFileOpts, type TAP } from '@tapjs/core'
import { plugin as SpawnPlugin } from '@tapjs/spawn'
import { plugin as StdinPlugin } from '@tapjs/stdin'
import { glob } from 'glob'
import { stat } from 'node:fs/promises'
import { relative, resolve } from 'node:path'
import { rimraf } from 'rimraf'
import { runAfter } from './after.js'
import { runBefore } from './before.js'
import { build } from './build.js'
import { getCoverageMap } from './coverage-map.js'
import { executeTestSuite } from './execute-test-suite.js'
import { values } from './main-config.js'
import { outputDir } from './output-dir.js'
import { readSave } from './save-list.js'
import { testArgv } from './test-argv.js'
import { testIsSerial } from './test-is-serial.js'

const regExpEscape = (s: string) =>
  s.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&')

const node = process.execPath

export const run = async (args: string[], config: LoadedConfig) => {
  // timeout in ms for test subprocesses, has a default
  // so it'll always be set to a number.
  /* c8 ignore start */
  const timeout = (config.get('timeout') ?? 30) * 1000
  /* c8 ignore stop */

  // these all default to an empty array
  /* c8 ignore start */
  const testArgs: string[] = values['test-arg'] || []
  const testEnv: string[] = values['test-env'] || []
  /* c8 ignore stop */
  process.env._TAPJS_PROCESSINFO_CWD_ = config.projectRoot
  process.env._TAPJS_PROCESSINFO_EXCLUDE_ = String(
    // don't consider snapshots and fixtures, or else we'll
    // always think that all tests are new after generating!
    // TODO: these come from plugins, they should be able to export
    // this, like with testFileExtensions.
    /(^(node|tapmock):|[\\\/](\.tap|tap-snapshots)[\\\/])/,
  )

  const argv = testArgv(config)

  let env: NodeJS.ProcessEnv | undefined = undefined

  const saveList = await readSave(config)
  const map = await getCoverageMap(config)

  const covExcludeFiles: (string | undefined)[] = []
  let _TAPJS_PROCESSINFO_COV_EXCLUDE_FILES_: string | undefined =
    undefined

  // always exclude the root node_modules from coverage. we don't report
  // on it, and it just takes up space in the .tap/coverage data.
  const _TAPJS_PROCESSINFO_COV_EXCLUDE_ = String(
    new RegExp(
      '^' + regExpEscape(resolve(config.projectRoot, 'node_modules')),
    ),
  )

  return executeTestSuite(
    args,
    config,
    (
      t,
    ): TAP &
      ReturnType<typeof AfterPlugin> &
      ReturnType<typeof SpawnPlugin> &
      ReturnType<typeof BeforePlugin> &
      ReturnType<typeof StdinPlugin> => {
      // generate the env here, in case any plugins updated it
      env = { ...process.env }
      for (const e of testEnv) {
        if (!e.includes('=')) {
          delete env[e]
        } else {
          const split = e.split('=')
          const k = split[0] as string
          const v = split.slice(1).join('=')
          env[k] = v
        }
      }

      // always set in dev, and the applyPlugin behavior is well tested
      /* c8 ignore start */
      const a =
        (t as TAP).pluginLoaded(SpawnPlugin) ?
          t
        : t.applyPlugin(SpawnPlugin)
      const b =
        (a as TAP).pluginLoaded(BeforePlugin) ?
          a
        : a.applyPlugin(BeforePlugin)
      const c =
        (b as TAP).pluginLoaded(StdinPlugin) ?
          b
        : b.applyPlugin(StdinPlugin)
      /* c8 ignore stop */
      return c as TAP &
        ReturnType<typeof AfterPlugin> &
        ReturnType<typeof SpawnPlugin> &
        ReturnType<typeof BeforePlugin> &
        ReturnType<typeof StdinPlugin>
    },

    async t => {
      // if the configured plugins aren't what tap is built with, rebuild it
      // this won't affect the tap loaded here in the runner, but will in the
      // the test files we're about to run.
      const needBuild = t.pluginSignature !== config.pluginSignature
      if (needBuild) await build([], config)

      // have to register before doing before/after because otherwise
      // that will trigger a pipe to stdout.
      t.register()
      covExcludeFiles.push(runBefore(t, argv, config))
      covExcludeFiles.push(runAfter(t, argv, config))

      // don't delete old coverage if only running subset of suites
      if (!config.get('changed') && !saveList.length) {
        await rimraf(resolve(config.projectRoot, '.tap/processinfo'))
        await rimraf(resolve(config.projectRoot, '.tap/coverage'))
      }

      outputDir(t, config)
    },

    async (t, file, files, hasReporter) => {
      if (!_TAPJS_PROCESSINFO_COV_EXCLUDE_FILES_) {
        _TAPJS_PROCESSINFO_COV_EXCLUDE_FILES_ = [
          ...covExcludeFiles.filter(f => typeof f === 'string'),
          ...files.map(f => resolve(config.projectRoot, f)),
        ].join('\n')
      }

      /* c8 ignore start */
      const stdio = hasReporter ? 'pipe' : 'inherit'
      /* c8 ignore end */

      if (file === '/dev/stdin') {
        if (files.length === 1) return t.stdinOnly()
        else return t.stdin()
      }
      const name = relative(config.projectRoot, file).replace(
        /\\/g,
        '/',
      )
      const mapped = map(name)
      let coveredFiles: null | string[] =
        mapped === null ? null : (
          await glob(mapped, {
            cwd: config.projectRoot,
            absolute: true,
          })
        )
      if (mapped?.length && !coveredFiles?.length) {
        // provided a glob expression that didn't match anything.
        // cover nothing.
        coveredFiles = null
      }
      const _TAPJS_PROCESSINFO_COVERAGE_ =
        coveredFiles === null ? '0' : '1'
      const _TAPJS_PROCESSINFO_COV_FILES_ = (coveredFiles || []).join(
        '\n',
      )
      const buffered = !testIsSerial(file) && t.jobs > 1
      const args = [...argv, file, ...testArgs]
      // support stuff like `tap <(...)` or raw .tap files.
      const st = await stat(file)
      const raw =
        st.isCharacterDevice() ||
        st.isBlockDevice() ||
        st.isFIFO() ||
        file.toLowerCase().endsWith('.tap')
      return raw ?
          t.sub<TapFile, TapFileOpts>(TapFile, {
            at: null,
            cwd: config.projectRoot,
            buffered,
            filename: file,
          })
        : t.spawn(node, args, {
            at: null,
            stack: '',
            buffered,
            timeout,
            stdio,
            env: {
              ...env,
              _TAPJS_PROCESSINFO_COVERAGE_,
              _TAPJS_PROCESSINFO_COV_FILES_,
              _TAPJS_PROCESSINFO_COV_EXCLUDE_FILES_,
              _TAPJS_PROCESSINFO_COV_EXCLUDE_,
            },
            name,
            cwd: config.projectRoot,
            externalID: name,
          })
    },
  )
}

import { LoadedConfig } from '@tapjs/config'
import { TAP } from '@tapjs/core'
import { foregroundChild } from 'foreground-child'
import { resolve } from 'path'
const node = process.execPath

export const runAfter = (
  t: TAP,
  argv: string[],
  config: LoadedConfig,
) => {
  const after = config.get('after')
  if (after) {
    t.after(async () => {
      await new Promise<void>(res => {
        foregroundChild(
          node,
          [...argv, resolve(after)],
          (code, signal) => {
            res()
            if (code || signal) return
            return false
          },
        )
      })
    })
    return resolve(after)
  }
}

import { LoadedConfig } from '@tapjs/config'
import { TAP } from '@tapjs/core'
import { foregroundChild } from 'foreground-child'
import { resolve } from 'path'

const node = process.execPath

export const runBefore = (
  t: TAP,
  argv: string[],
  config: LoadedConfig,
) => {
  const before = config.get('before')
  if (before) {
    t.before(
      async () =>
        new Promise<void>(res => {
          foregroundChild(
            node,
            [...argv, resolve(before)],
            (code, signal) => {
              res()
              if (code || signal) return
              return false
            },
          )
        }),
    )
    return resolve(before)
  }
}

/**
 * This provides the functionality that is shared between `tap run`
 * and `tap replay`, to figure out what tests need to be run, create
 * the various subtests for each, and pipe the whole thing through an
 * appropriate reporter.
 *
 * When called by `run`, the execution method is `t.spawn`. When called
 * by `replay`, the file is mapped to its appropriate saved results file in
 * `.tap/test-results`, and `t.tapFile()` is called.
 *
 * @module
 */
// run the provided tests
import { plugin as AfterPlugin } from '@tapjs/after'
import { LoadedConfig } from '@tapjs/config'
import type { TAP } from '@tapjs/core'
import { tap } from '@tapjs/core'
import { resolve } from 'path'
import { FinalResults } from 'tap-parser'
import { handleReporter } from './handle-reporter.js'
import { list } from './list.js'
import { values } from './main-config.js'
import { outputFile } from './output-file.js'
import { proxyFatalSignals } from './proxy-fatal-signals.js'
import { report } from './report.js'
import { readSave, writeSave } from './save-list.js'

export const executeTestSuite = async <
  T extends TAP & ReturnType<typeof AfterPlugin>,
  S extends (t: TAP & ReturnType<typeof AfterPlugin>) => T,
>(
  args: string[],
  config: LoadedConfig,
  /**
   * called with the tap object, so any additional plugins can be applied or
   * other modifications if needed.
   */
  applyPlugins: S,

  /**
   * called before running the test files, but only if we have files to run
   */
  setup: (t: T) => void | Promise<void>,

  /**
   * called with the mutated tap, and each test file path, relative to
   * config.projectRoot
   */
  execute: (
    t: T,
    testFile: string,
    files: string[],
    hasReporter: boolean,
  ) => Promise<any>,
) => {
  let base = tap({
    // special runner context so plugins can behave differently.
    // currently, this is only used by @tapjs/filter
    context: Symbol.for('tap.isRunner'),
  })
  if (!base.pluginLoaded(AfterPlugin)) {
    base = (base as TAP).applyPlugin(AfterPlugin) as TAP &
      ReturnType<typeof AfterPlugin>
  }
  const t = applyPlugins(base)

  // we don't want to time out the runner, just the subtests
  t.setTimeout(0)

  // proxy all fatal signals to child processes, so that we can report
  // their status properly.
  proxyFatalSignals(t)

  const files = await list(args, config)
  if (!files.length) {
    // see if we WOULD have gotten files, if not for the fact that they
    // are all unchanged.
    const unpruned =
      config.get('changed') ? await list(args, config, true) : []
    if (args.length && !unpruned.length) {
      console.error(
        'No valid test files found matching ' +
          args.map(a => JSON.stringify(a)).join(' '),
      )
      if (
        args.length === 2 &&
        args[0] === 'edit' &&
        args[1] === 'config'
      ) {
        console.error(`(Did you mean 'tap config edit'?)`)
      }
      t.debug('runner exit=1 no files found, args')
      process.exitCode = 1
    } else if (unpruned.length) {
      console.log('No new tests to run')
    } else {
      t.debug('runner exit=1 no files found, no args')
      console.error('No test files found')
      process.exitCode = 1
    }
    return
  }

  // jobs has a default of os.availableParallelism or 1
  /* c8 ignore start */
  if (typeof values.jobs !== 'number') {
    throw new Error('no jobs option provided')
  }
  /* c8 ignore stop */

  await setup(t)

  const hasReporter = await handleReporter(t, config)

  t.buffered = false

  const readSaveList = await readSave(config)
  const saveList =
    config.get('save') && !readSaveList.length ? files : readSaveList

  // after() can run in the same tick that the test promise resolves,
  // so we have to wait to write the save file until they're all done.
  // only an issue with more than one subtests that all run in parallel,
  // and end very quickly.
  const testPromises: Promise<any>[] = []
  if (config.get('save')) {
    t.after(async () => {
      await Promise.all(testPromises)
      await writeSave(config, saveList)
    })
  }

  t.jobs = Math.max(1, Math.min(values.jobs, files.length))
  const stdinOnly =
    files.length === 1 &&
    (files[0] === '-' || files[0] === '/dev/stdin')

  if (!stdinOnly) {
    t.teardown(() => report([], config))
    t.plan(files.length)
  }

  outputFile(t, config, hasReporter)

  const sorted = files.sort((a, b) => a.localeCompare(b, 'en'))
  for (const f of sorted) {
    const file =
      f === '-' || f === '/dev/stdin' ?
        '/dev/stdin'
      : resolve(config.projectRoot, f)
    const p = execute(t, file, sorted, hasReporter)

    testPromises.push(
      !saveList.length ? p : (
        p.then((results: FinalResults | null) => {
          if (results?.ok && saveList.includes(f))
            saveList.splice(saveList.indexOf(f), 1)
        })
      ),
    )
  }

  return t.promise()
}

import type { LoadedConfig } from '@tapjs/config'
import { foregroundChild } from 'foreground-child'
import { fileURLToPath } from 'node:url'
import { resolveImport } from 'resolve-import'
import { mainCommand } from './main-config.js'
const tmbin = fileURLToPath(
  await resolveImport(
    '@tapjs/test/generate-tap-test-class',
    import.meta.url,
  ),
)
const node = process.execPath

export const build = async (args: string[], config: LoadedConfig) => {
  if (args.length !== 0) {
    throw new TypeError(
      'build command does not take positional arguments',
    )
  }

  const argv = [tmbin, ...config.pluginList]
  return new Promise<void>((res, rej) => {
    foregroundChild(node, argv, {}, (code, signal) => {
      // if this is the main command, just terminate in the same way
      // otherwise, let the promise communicate the build status
      if (mainCommand === 'build') {
        res()
        return
      }
      if (code || signal) {
        rej(
          Object.assign(new Error('build failed'), {
            code,
            signal,
          }),
        )
      } else {
        res()
      }
      return false
    })
  })
}

import { LoadedConfig } from '@tapjs/config'
import { resolve } from 'path'
import { pathToFileURL } from 'url'

const isStringArray = (a: any): a is string[] =>
  Array.isArray(a) && !a.some(s => typeof s !== 'string')

type CoverageMapFn = (file: string) => null | string | string[]

export const getCoverageMap = async (config: LoadedConfig) => {
  if (config.get('disable-coverage')) return () => null
  const coverageMap = config.get('coverage-map')
  if (!coverageMap) return () => []
  const mapModule = (await import(
    String(pathToFileURL(resolve(config.projectRoot, coverageMap)))
  ).catch(er => {
    throw new Error(
      `Coverage map ${coverageMap} is not a valid module. ${er.message}`,
    )
  })) as CoverageMapFn | { default: CoverageMapFn }
  /* c8 ignore start */
  const map =
    typeof mapModule === 'object' ? mapModule.default : mapModule
  /* c8 ignore stop */

  if (typeof map !== 'function') {
    throw new Error(
      `Coverage map ${coverageMap} did not default export a function`,
    )
  }
  return (f: string) => {
    const mapped: null | string | string[] = map(f)
    if (
      mapped !== null &&
      typeof mapped !== 'string' &&
      !isStringArray(mapped)
    ) {
      throw new Error(
        `Coverage map ${coverageMap} must return string, string[], or null`,
      )
    }
    return mapped
  }
}

import { LoadedConfig } from '@tapjs/config'
import { Spawn, TAP } from '@tapjs/core'
import { createWriteStream } from 'fs'
import { mkdirpSync } from 'mkdirp'
import { dirname, resolve } from 'path'

// Always write to .tap/test-results, plus the config if set
export const outputDir = (t: TAP, config: LoadedConfig) => {
  const outputDir = config.get('output-dir')
  const resultsDir = resolve(config.projectRoot, '.tap/test-results')
  t.on('spawn', subtest => pipes(subtest, resultsDir, outputDir))
}

const pipes = (
  subtest: Spawn,
  resultsDir: string,
  outputDir?: string,
) => {
  if (outputDir && outputDir !== resultsDir) {
    pipeToDir(subtest, outputDir)
  }
  pipeToDir(subtest, resultsDir)
}

const pipeToDir = (subtest: Spawn, dir: string) => {
  const out = resolve(dir, subtest.name + '.tap')
  mkdirpSync(dirname(out))
  subtest.on('process', proc =>
    proc.stdout.pipe(createWriteStream(out)),
  )
}

// the arguments when running test files, abstracted from run.ts for testing
import type { LoadedConfig } from '@tapjs/config'
import {
  execArgv,
  importLoaders,
  loaderFallbacks,
  loaders,
} from '@tapjs/test'
import module from 'node:module'
import { resolveImport } from 'resolve-import'

// if we have Module.register(), then use --import wherever possible
const useImport = !!(module as { register?: (...a: any) => any })
  .register

const testModule = String(
  await resolveImport('@tapjs/test', import.meta.url),
)

const resolveLoaders = (loaders: string[]) =>
  Promise.all(
    loaders.map(async loader =>
      String(await resolveImport(loader, testModule)),
    ),
  )
const importScripts = await resolveLoaders(
  useImport ? importLoaders : [],
)
const loaderScripts = await resolveLoaders(
  useImport ? loaders : loaderFallbacks,
)

const pi =
  useImport ?
    `--import=${await resolveImport(
      '@tapjs/processinfo/import',
      import.meta.url,
    )}`
  : `--loader=${await resolveImport(
      '@tapjs/processinfo/loader',
      import.meta.url,
    )}`

const always = [
  ...importScripts.map(l => `--import=${l}`),
  ...loaderScripts.map(l => `--loader=${l}`),
  ...(useImport && !loaderScripts.length ? [] : ['--no-warnings']),
  '--enable-source-maps',
  // ensure this always comes last in the list
  pi,
]

export const testArgv = (config: LoadedConfig) => [
  ...always,
  ...execArgv(config.values),
  ...(config.get('node-arg') || []),
]

import { LoadedConfig } from '@tapjs/config'
import { readFile, unlink, writeFile } from 'fs/promises'
import { resolve } from 'path'

// return the list of entries in the save config, or []
let list: string[]
let file: string

export const readSave = async (config: LoadedConfig) => {
  if (list) return list
  const save = config.get('save')
  if (!save) return (list = [])
  if (!file) file = resolve(config.projectRoot, save)
  const d = (await readFile(file, 'utf8').catch(() => '')).trim()
  return (list = d ? d.split('\n') : [])
}
export const writeSave = async (
  config: LoadedConfig,
  list: string[],
) => {
  const save = config.get('save')
  if (!save) return
  if (!file) file = resolve(config.projectRoot, save)
  if (!list.length) await unlink(file).catch(() => {})
  else await writeFile(file, list.join('\n') + '\n', 'utf8')
}

import { resolve, sep } from 'path'
import { values } from './main-config.js'
let serial: string[] | undefined = undefined
export const testIsSerial = (file: string) => {
  if (!serial) {
    serial = (values.serial || []).map(
      s => resolve(s).toLowerCase() + sep,
    )
  }
  // tack the sep onto the end so that if the config specifies an
  // individual file, rather than a dir, it still matches.
  return serial.some(s => (file.toLowerCase() + sep).startsWith(s))
}

import type { LoadedConfig } from '@tapjs/config'
import type { TAP } from '@tapjs/core'
import {
  report as testReport,
  types as reportTypes,
} from '@tapjs/reporter'
import { spawn } from 'node:child_process'
import { createWriteStream } from 'node:fs'
import { WriteStream } from 'node:tty'
import { resolve } from 'node:path'
import type { Writable } from 'node:stream'
import { pathToFileURL } from 'node:url'
import { resolveImport } from 'resolve-import'
import which from 'which'

const rawTap = (t: TAP, out: Writable) => pipe(t, out)
const pipe = (t: TAP, dest: Writable): false => {
  t.pipe(dest)
  return false
}

/**
 * Return type indicates whether or not stderr is also being handled.
 * If a stream or raw TAP is used, then `false` is returned, indicating
 * that child test process stderr is *not* handled, and stdio should be
 * inherited from main runner process.
 * If a React component is used, then it's responsible for handling the
 * stderr of any spawned child processes, and this function returns true
 * to indicate that stdio should be piped.
 */
export const handleReporter = async (
  t: TAP,
  config: LoadedConfig,
) => {
  // figure out if we MUST use the 'tap' reporter
  const rf = config.get('reporter-file')
  const reporter =
    rf === '/dev/null' ? 'silent' : (config.get('reporter') as string)
  const isRawTap = process.env.TAP === '1'
  const out =
    (
      !rf ||
      rf === '-' ||
      rf === '/dev/stdout' ||
      isRawTap ||
      reporter === 'silent'
    ) ?
      process.stdout
    : createWriteStream(rf)

  if (reporter === 'tap' || isRawTap) {
    return rawTap(t, out)
  }

  if (reporter === 'silent') {
    t.register()
    t.resume()
    return true
  }

  // if it's one of the keys we know, then use that
  if (reportTypes[reporter]) {
    const Type = reporter as keyof typeof reportTypes
    testReport(Type, t, config, out)
    return true
  }

  // Check to see if it's an executable program. If so, run it
  // and pipe the TAP data into its stdin.
  try {
    const stdio: ['pipe', 'inherit' | 'pipe', 'inherit'] = [
      'pipe',
      out === process.stdout ? 'inherit' : 'pipe',
      'inherit',
    ]
    const exe = await which(reporter)
    const rargs = config.get('reporter-arg') || []
    const proc = spawn(exe, rargs, {
      shell: true,
      stdio,
    })
    if (proc.stdout) proc.stdout.pipe(out)
    return pipe(t, proc.stdin as WriteStream)
  } catch {}

  // ok, not one of those, check to see if we can import it
  // load it relative to the cwd, so relative paths work.
  const from = pathToFileURL(resolve('x'))
  const mod = String(
    await resolveImport(reporter, from).catch(() => ''),
  )

  const imported =
    mod && (await import(mod).catch(() => null))?.default
  if (imported) {
    if (typeof imported === 'function') {
      // either react or stream
      if (
        typeof imported.prototype === 'object' &&
        !!imported.prototype &&
        typeof imported.prototype.write === 'function' &&
        typeof imported.prototype.end === 'function'
      ) {
        const Cls = imported as typeof Writable
        const dest = new Cls()
        dest.pipe(out)
        return pipe(t, dest)
      } else {
        // React function component
        testReport(imported, t, config, out)
        return true
      }
    }
  }

  console.error(
    `Could not load ${JSON.stringify(
      reporter,
    )} reporter. Displaying raw TAP.`,
  )
  return rawTap(t, out)
}

// this has to do some wicked things with types, because c8's
// declarations are somewhat lacking.
import { LoadedConfig } from '@tapjs/config'
import { tap } from '@tapjs/core'
import { Report } from 'c8'
import { readdir } from 'fs/promises'
import opener from 'opener'
import { resolve } from 'path'

import { mainCommand } from './main-config.js'

const reporterFiles = {
  clover: 'clover.xml',
  cobertura: 'cobertura-coverage.xml',
  json: 'coverage-final.json',
  'json-summary': 'coverage-summary.json',
  lcov: 'lcov.info',
  lcovonly: 'lcov.info',
}

export const report = async (
  args: string[],
  config: LoadedConfig,
) => {
  const rconf = config.get('coverage-report')
  // if there are args passed in, use that. The config is used if
  // calling this at the end of `tap run`
  const reporter =
    args.length ? args
    : rconf && rconf.length ? rconf
    : ['text']

  // verify that we actually have coverage, otherwise don't even try
  const tempDirectory = resolve(config.projectRoot, '.tap/coverage')
  const ok = await readdir(tempDirectory).then(
    entries => !!entries.length,
    () => false,
  )
  if (!ok) {
    tap().comment('No coverage generated')
    tap().debug('run/report exit=1 no coverage generated')
    if (!config.get('allow-empty-coverage')) {
      process.exitCode = 1
    }
    return
  }

  // C8 report only works when run from the cwd where the files are
  const cwd = process.cwd()
  /* c8 ignore start */
  try {
    process.chdir(config.projectRoot)
  } catch {}
  /* c8 ignore stop */

  let showFullCoverage = config.get('show-full-coverage')
  let r = new Report({
    skipFull: !showFullCoverage,
    // no need to include/exclude, we already did that when we captured
    reporter,
    reportsDirectory: resolve(config.projectRoot, '.tap/report'),
    tempDirectory: resolve(config.projectRoot, '.tap/coverage'),
    excludeNodeModules: true,
  })

  // See if we need to generate a text report, or if we should skip it
  // because --show-full-coverage is false and we have full coverage,
  // or alternatively, there's NO coverage, and we'll print the warning
  // anyway.
  if (reporter.includes('text') && !showFullCoverage) {
    const summary = await getSummary(r)
    if (isFullCoverage(summary) || isEmptyCoverage(summary)) {
      if (mainCommand === 'report' && showFullCoverage !== false) {
        r = new Report({
          skipFull: false,
          reporter,
          reportsDirectory: resolve(
            config.projectRoot,
            '.tap/report',
          ),
          tempDirectory: resolve(config.projectRoot, '.tap/coverage'),
          excludeNodeModules: true,
        })
      } else {
        // don't show the text report, it's just noise.
        // but make `tap report html --no-show-full-coverage` still
        // output a summary, at least, so it doesn't seem broken
        const txt = reporter.indexOf('text')
        if (txt !== -1) {
          const none = args.includes('text') ? 'text-summary' : 'none'
          reporter.splice(txt, 1)
          if (reporter.length === 0) {
            reporter.push(none)
          }
        }
      }
    }
  }

  // XXX: istanbul-reports just dumps to process.stdout, which collides
  // with our ink-based reporters. Hijack it and write with console.log
  // so that patch-console can put it in the intended place (or ignore it).
  // TODO: make istanbul-reports more configurable.
  const { write } = process.stdout
  const stdout: string[] = []
  process.stdout.write = c => {
    stdout.push(String(c))
    return true
  }
  await r.run()
  process.stdout.write = write
  const s = stdout.join('').trimEnd()
  if (s) console.log(s)
  for (const [style, filename] of Object.entries(reporterFiles)) {
    if (reporter.includes(style)) {
      const f = resolve(config.projectRoot, '.tap/report', filename)
      console.log(`${style} report: ${f}`)
    }
  }
  if (reporter.includes('html')) {
    opener(resolve(config.projectRoot, '.tap/report/index.html'))
  } else if (reporter.includes('lcov')) {
    opener(
      resolve(
        config.projectRoot,
        '.tap/report/lcov-report/index.html',
      ),
    )
  }

  await checkCoverage(r, config)
  /* c8 ignore start */
  try {
    process.chdir(cwd)
  } catch {}
  /* c8 ignore stop */
}

interface Summary {
  lines: { pct: number }
  functions: { pct: number }
  statements: { pct: number }
  branches: { pct: number }
}

const summaries = new Map<Report, Summary>()
const getSummary = async (report: Report) => {
  const c = summaries.get(report)
  if (c) return c
  const r = report as Report & {
    getCoverageMapFromAllCoverageFiles(): Promise<{
      getCoverageSummary: () => Summary
    }>
  }
  const map = await r.getCoverageMapFromAllCoverageFiles()
  const summary = map.getCoverageSummary()
  summaries.set(report, summary)
  return summary
}

const thresholds: (keyof Summary)[] = [
  'statements',
  'branches',
  'functions',
  'lines',
]

const isEmptyCoverage = (summary: Summary) =>
  Math.max(...thresholds.map(th => Number(summary[th].pct) || 0)) ===
  0

const isFullCoverage = (summary: Summary) =>
  Math.min(...thresholds.map(th => Number(summary[th].pct) || 0)) ===
  100

const checkCoverage = async (
  report: Report,
  config: LoadedConfig,
) => {
  const cr = config.get('coverage-report')
  const comment = cr && !cr.includes('text')
  const summary = await getSummary(report)
  let success = true

  const t = tap()

  // if we didn't get anything, that means that even though it wrote
  // some coverage files, it didn't actually cover anything, which can
  // happen, for example if the test crashes before actually loading.
  if (isEmptyCoverage(summary)) {
    t.comment('No coverage generated')
    t.debug('run/report exit=1 coverage is empty', summary)
    if (!config.get('allow-empty-coverage')) {
      process.exitCode = 1
    }
    return
  }

  // TODO: make levels configurable, just *default* to 100
  // only comment it if not using the text reporter, because that makes
  // it pretty obvious where the shortcomings are already.
  for (const th of thresholds) {
    const coverage = Number(summary[th].pct) || 0
    if (coverage < 100) {
      if (comment) {
        t.comment(`ERROR: incomplete ${th} coverage (${coverage}%)`)
      }
      success = false
    }
  }
  if (success === false) {
    t.debug('run/report exit=1 coverage not 100', summary)
    if (!config.get('allow-incomplete-coverage')) {
      process.exitCode = 1
    }
  }
}

import { LoadedConfig } from '@tapjs/config'
import { ProcessInfo } from '@tapjs/processinfo'
import { glob, Glob, IgnoreLike } from 'glob'
import { resolve } from 'node:path'
import type { Path, PathScurry } from 'path-scurry'
import { mainCommand, values } from './main-config.js'
import { readSave } from './save-list.js'

const alwaysExcludeNames = [
  '.tap',
  '.nyc_output',
  '.git',
  '.hg',
  'node_modules',
  'tap-snapshots',
]

const alwaysExcludePattern = `**/@(${alwaysExcludeNames.join(
  '|',
)})/**`

const defaultInclude =
  '**/{' +
  '@(test?(s)|__test?(s)__)/**/*,' +
  '*.@(test?(s)|spec),' +
  'test?(s)' +
  '}.__EXTENSIONS__'
const dirInclude = '**/*.__EXTENSIONS__'

// --save=<file>
//    only run the files in the list, write failures back to it.
//    If the file doesn't exist, run everything normally.
//    Otherwise, don't delete coverage history.
//    If they all pass, delete the file.
// --changed
//    Figure out which files in the suite have changed since last run,
//    and only run those. Do not delete coverage history ever.

export const list = async (
  args: string[],
  config: LoadedConfig,
  noPruneUnchanged: boolean = false,
) => {
  const saveList: Set<string> = new Set(await readSave(config))

  if (args.length === 0) args = config.get('files') || []

  const ignore = [alwaysExcludePattern]
  if (values.exclude) ignore.push(...values.exclude)

  const g = new Glob(
    values.include || config.expandInclude(defaultInclude),
    {
      ignore,
      cwd: config.projectRoot,
      withFileTypes: true,
    },
  )

  const { scurry } = g
  // resolve non-existent paths as globs in the actual cwd
  // turn everything into a Path in our main scurry, except
  // stdin designators, which are left as strings
  // This way the expected behavior of "the thing glob says"
  // will work the same way across platforms, *and* the tests
  // are all resolved to absolute paths that can be run from
  // the cwd context of the project root.
  const entries = new Set<Path | '-' | '/dev/stdin'>(
    (
      await Promise.all(
        (
          await (!args.length ?
            g.walk()
          : Promise.all(
              args.map(async a => {
                if (a === '-' || a === '/dev/stdin') return a
                const st = await scurry.cwd
                  .resolve(resolve(a))
                  .lstat()
                if (st) return st
                // from process cwd, because it's a cli positional
                return glob(a, { absolute: true })
              }),
            ))
        ).reduce(
          (
            set: (
              | string
              | Path
              | Promise<Path | undefined>
              | undefined
            )[],
            entry: string | Path | string[],
          ) => {
            // stat the glob results a second time, even though we know
            // that they exist, because we need their stat info later.
            if (Array.isArray(entry)) {
              set.push(
                ...entry.map(e => scurry.cwd.resolve(e).lstat()),
              )
            } else if (entry === '-' || entry === '/dev/stdin') {
              set.push(entry)
            } else {
              set.push(entry)
            }
            return set
          },
          [],
        ),
      )
    ).filter(p => {
      // enoents should already be filtered out by glob, but just in case
      /* c8 ignore start */
      if (!p) return false
      /* c8 ignore stop */
      return true
    }) as (Path | '-' | '/dev/stdin')[],
  )
  await expandDirectories(config, scurry, entries, g.ignore)
  const before = config.get('before')
  const after = config.get('after')
  if (before) {
    entries.delete(scurry.cwd.resolve(before))
  }
  if (after) {
    entries.delete(scurry.cwd.resolve(after))
  }

  if (saveList.size) {
    for (const p of entries) {
      if (typeof p === 'string') continue
      if (!saveList.has(p.relativePosix())) entries.delete(p)
    }
  }

  const foundEntries = entries.size !== 0
  if (config.get('changed') && foundEntries && !noPruneUnchanged) {
    await pruneUnchanged(scurry, entries)
  }

  const files = [...entries].map(p =>
    typeof p === 'string' ? p : p.relativePosix(),
  )
  if (mainCommand === 'list') {
    if (foundEntries) {
      // don't report an error if we found something but it's just not new
      if (files.length) console.log(files.join('\n').trimEnd())
    } else {
      console.error('No files found.')
      process.exitCode = 1
      if (args.length === 1) {
        const maybe = args[0]?.match(/^(plugin|config)s?$/)
        if (maybe) {
          console.error(`(Did you mean 'tap ${maybe[1]} list'?)`)
        }
      }
    }
  }
  return files
}

const expandDirectories = async (
  config: LoadedConfig,
  scurry: PathScurry,
  entries: Set<Path | '-' | '/dev/stdin'>,
  ignore: string | string[] | IgnoreLike | undefined,
) => {
  // for each one that's a directory, expand it with the files it contains
  // then go back to the original dir when we're done expanding.
  const originalCwd = scurry.cwd
  for (const entry of entries) {
    if (typeof entry === 'string') continue
    if (entry.isDirectory()) {
      entries.delete(entry)
      scurry.chdir(entry)
      // if we match a dir, then pull in any runnable files from within it
      for (const s of await glob(config.expandInclude(dirInclude), {
        scurry,
        ignore,
        withFileTypes: true,
        cwd: scurry.cwd.fullpath(),
      })) {
        entries.add(s)
      }
    }
  }
  scurry.chdir(originalCwd)
}

// delete all the entries in the set that do not reference files
// that have changed since the last run.
const pruneUnchanged = async (
  scurry: PathScurry,
  entries: Set<Path | '-' | '/dev/stdin'>,
) => {
  const dir = scurry.resolve('.tap/processinfo')
  const db = new ProcessInfo({ dir })
  await db.load()
  // for each entry in the list, find it in the processinfo db's list
  // of externalIDs with changed files. but, we only need to check the
  // ones for which there is SOME externalID node in the processinfo,
  // because otherwise that means it's never been run.
  const ids = new Map<string, Path>()
  for (const e of entries) {
    if (typeof e === 'string') continue
    const rp = e.relativePosix()
    const pi = db.externalIDs.get(rp)
    if (!pi) {
      // if there's no processinfo entry for it, then it must be new
      // so don't even check it.
      continue
    }
    ids.set(rp, e)
  }
  const changed = await db.externalIDsChanged(id => ids.has(id))
  for (const [id, e] of ids.entries()) {
    if (!changed.has(id)) {
      entries.delete(e)
    }
  }
}

import { Base, proc, Spawn, TestBase, Worker } from '@tapjs/core'
import { ChildProcess } from 'child_process'
import { Worker as NodeWorker } from 'node:worker_threads'
import { signals as fatalSignals } from 'signal-exit'

let exitSignal: NodeJS.Signals | undefined = undefined

const didTerminalSignalProxy = new Set<TestBase>()
const proxySignal = (t: TestBase, signal: NodeJS.Signals) => {
  const { pool, subtests } = t

  const onProc = (proc: ChildProcess | NodeWorker) => {
    const c = proc as ChildProcess
    const w = proc as NodeWorker
    c.kill?.(signal)
    w.terminate?.()
  }

  const onST = (t: Base) => {
    t.options.signal = signal
    // if it's already processed and printed, then this a no-op.
    // but otherwise, we treat any as-yet unprocessed test as a failure.
    if (t.results) {
      t.results.ok = false
      t.parser.ok = false
    } else {
      t.on('complete', results => {
        t.parser.ok = false
        results.ok = false
      })
    }

    const s = t as Spawn
    const w = t as Worker
    s.proc?.kill?.(signal)
    w.worker?.terminate?.()
    if (!s.proc && !w.worker) {
      // don't double up
      s.removeListener('process', onProc)
      s.on('process', onProc)
    }
  }

  for (const st of [...pool, ...subtests]) {
    if (
      st.constructor.name === 'Worker' ||
      st.constructor.name === 'Spawn'
    ) {
      onST(st)
    }
  }

  t.on('spawn', onST)
  t.on('worker', onST)
}

export const proxyFatalSignals = (t: TestBase) => {
  if (didTerminalSignalProxy.has(t)) return
  didTerminalSignalProxy.add(t)

  const p = proc
  /* c8 ignore start */
  if (!p) return
  /* c8 ignore stop */

  const sigListeners: { [k in NodeJS.Signals]?: () => void } = {}
  for (const sig of fatalSignals) {
    const onFatalSignal = () => {
      exitSignal ??= sig
      proxySignal(t, sig)
      // remove after this tick, so signal-exit doesn't
      // get an itchy trigger finger and kill the process
      // before we have a chance to proxy the signal.
      setTimeout(() => p.removeListener(sig, onFatalSignal))
    }
    try {
      p.on(sig, onFatalSignal)
      sigListeners[sig] = onFatalSignal
      /* c8 ignore start */
    } catch {}
    /* c8 ignore stop */
  }

  // stop proxying when tap is complete, and exit with the signal
  t.on('complete', () => {
    for (const [sig, fn] of Object.entries(sigListeners)) {
      p.removeListener(sig, fn)
    }
    if (exitSignal) {
      p.kill(p.pid, exitSignal)
      setTimeout(() => {}, 200)
    }
  })
}

import { LoadedConfig } from '@tapjs/config'
import { TAP } from '@tapjs/core'
import { Minipass } from 'minipass'
import { mkdirpSync } from 'mkdirp'
import { createWriteStream } from 'node:fs'
import { dirname, resolve } from 'node:path'

export const outputFile = (
  t: TAP,
  config: LoadedConfig,
  hasReporter: boolean,
) => {
  const outputFile = config.get('output-file')
  if (outputFile) {
    mkdirpSync(dirname(resolve(outputFile)))
    const m = new Minipass<string, string>({ encoding: 'utf8' })
    if (!hasReporter) {
      m.pipe(process.stdout)
      t.register()
    }
    m.pipe(createWriteStream(resolve(outputFile)))
    t.pipe(m)
  }
}

import {
  ChildProcess,
  SendHandle,
  Serializable,
  spawn as nodeSpawn,
  SpawnOptions,
} from 'child_process'
import crossSpawn from 'cross-spawn'
import { onExit } from 'signal-exit'
import { allSignals } from './all-signals.js'
import { watchdog } from './watchdog.js'

/* c8 ignore start */
const spawn = process?.platform === 'win32' ? crossSpawn : nodeSpawn
/* c8 ignore stop */

/**
 * The signature for the cleanup method.
 *
 * Arguments indicate the exit status of the child process.
 *
 * If a Promise is returned, then the process is not terminated
 * until it resolves, and the resolution value is treated as the
 * exit status (if a number) or signal exit (if a signal string).
 *
 * If `undefined` is returned, then no change is made, and the parent
 * exits in the same way that the child exited.
 *
 * If boolean `false` is returned, then the parent's exit is canceled.
 *
 * If a number is returned, then the parent process exits with the number
 * as its exitCode.
 *
 * If a signal string is returned, then the parent process is killed with
 * the same signal that caused the child to exit.
 */
export type Cleanup = (
  code: number | null,
  signal: null | NodeJS.Signals
) =>
  | void
  | undefined
  | number
  | NodeJS.Signals
  | false
  | Promise<void | undefined | number | NodeJS.Signals | false>

export type FgArgs =
  | [program: string | string[], cleanup?: Cleanup]
  | [program: string[], opts?: SpawnOptions, cleanup?: Cleanup]
  | [program: string, cleanup?: Cleanup]
  | [program: string, opts?: SpawnOptions, cleanup?: Cleanup]
  | [program: string, args?: string[], cleanup?: Cleanup]
  | [
      program: string,
      args?: string[],
      opts?: SpawnOptions,
      cleanup?: Cleanup
    ]

/**
 * Normalizes the arguments passed to `foregroundChild`.
 *
 * Exposed for testing.
 *
 * @internal
 */
export const normalizeFgArgs = (
  fgArgs: FgArgs
): [
  program: string,
  args: string[],
  spawnOpts: SpawnOptions,
  cleanup: Cleanup
] => {
  let [program, args = [], spawnOpts = {}, cleanup = () => {}] = fgArgs
  if (typeof args === 'function') {
    cleanup = args
    spawnOpts = {}
    args = []
  } else if (!!args && typeof args === 'object' && !Array.isArray(args)) {
    if (typeof spawnOpts === 'function') cleanup = spawnOpts
    spawnOpts = args
    args = []
  } else if (typeof spawnOpts === 'function') {
    cleanup = spawnOpts
    spawnOpts = {}
  }
  if (Array.isArray(program)) {
    const [pp, ...pa] = program
    program = pp
    args = pa
  }
  return [program, args, { ...spawnOpts }, cleanup]
}

/**
 * Spawn the specified program as a "foreground" process, or at least as
 * close as is possible given node's lack of exec-without-fork.
 *
 * Cleanup method may be used to modify or ignore the result of the child's
 * exit code or signal. If cleanup returns undefined (or a Promise that
 * resolves to undefined), then the parent will exit in the same way that
 * the child did.
 *
 * Return boolean `false` to prevent the parent's exit entirely.
 */
export function foregroundChild(
  cmd: string | string[],
  cleanup?: Cleanup
): ChildProcess
export function foregroundChild(
  program: string,
  args?: string[],
  cleanup?: Cleanup
): ChildProcess
export function foregroundChild(
  program: string,
  spawnOpts?: SpawnOptions,
  cleanup?: Cleanup
): ChildProcess
export function foregroundChild(
  program: string,
  args?: string[],
  spawnOpts?: SpawnOptions,
  cleanup?: Cleanup
): ChildProcess
export function foregroundChild(...fgArgs: FgArgs): ChildProcess {
  const [program, args, spawnOpts, cleanup] = normalizeFgArgs(fgArgs)

  spawnOpts.stdio = [0, 1, 2]
  if (process.send) {
    spawnOpts.stdio.push('ipc')
  }

  const child = spawn(program, args, spawnOpts)

  const unproxySignals = proxySignals(child)
  const childHangup = () => {
    try {
      child.kill('SIGHUP')

      /* c8 ignore start */
    } catch (_) {
      // SIGHUP is weird on windows
      child.kill('SIGTERM')
    }
    /* c8 ignore stop */
  }
  const removeOnExit = onExit(childHangup)

  const dog = watchdog(child)

  let done = false
  child.on('close', async (code, signal) => {
    dog.kill('SIGKILL')
    /* c8 ignore start */
    if (done) {
      return
    }
    /* c8 ignore stop */
    done = true
    const result = cleanup(code, signal)
    const res = isPromise(result) ? await result : result
    removeOnExit()
    unproxySignals()

    if (res === false) return
    else if (typeof res === 'string') {
      signal = res
      code = null
    } else if (typeof res === 'number') {
      code = res
      signal = null
    }

    if (signal) {
      // If there is nothing else keeping the event loop alive,
      // then there's a race between a graceful exit and getting
      // the signal to this process.  Put this timeout here to
      // make sure we're still alive to get the signal, and thus
      // exit with the intended signal code.
      /* istanbul ignore next */
      setTimeout(() => {}, 2000)
      try {
        process.kill(process.pid, signal)
        /* c8 ignore start */
      } catch (_) {
        process.kill(process.pid, 'SIGTERM')
      }
      /* c8 ignore stop */
    } else {
      process.exit(code || 0)
    }
  })

  if (process.send) {
    process.removeAllListeners('message')

    child.on('message', (message, sendHandle) => {
      process.send?.(message, sendHandle)
    })

    process.on('message', (message, sendHandle) => {
      child.send(
        message as Serializable,
        sendHandle as SendHandle | undefined
      )
    })
  }

  return child
}

/**
 * Starts forwarding signals to `child` through `parent`.
 */
const proxySignals = (child: ChildProcess) => {
  const listeners = new Map()

  for (const sig of allSignals) {
    const listener = () => {
      // some signals can only be received, not sent
      try {
        child.kill(sig)
        /* c8 ignore start */
      } catch (_) {}
      /* c8 ignore stop */
    }
    try {
      // if it's a signal this system doesn't recognize, skip it
      process.on(sig, listener)
      listeners.set(sig, listener)
      /* c8 ignore start */
    } catch (_) {}
    /* c8 ignore stop */
  }

  return () => {
    for (const [sig, listener] of listeners) {
      process.removeListener(sig, listener)
    }
  }
}

const isPromise = (o: any): o is Promise<any> =>
  !!o && typeof o === 'object' && typeof o.then === 'function'

import { LoadedConfig } from '@tapjs/config'
import { TAP, TestBase } from '@tapjs/core'
import { render } from 'ink'
import { isStream, Minipass } from 'minipass'
import React, { FC } from 'react'
