}

class SignalExitFallback extends SignalExitBase {
  onExit() {
    return () => {}
  }
  load() {}
  unload() {}
}

class SignalExit extends SignalExitBase {
  // "SIGHUP" throws an `ENOSYS` error on Windows,
  // so use a supported signal instead
  /* c8 ignore start */
  #hupSig = process.platform === 'win32' ? 'SIGINT' : 'SIGHUP'
  /* c8 ignore stop */
  #emitter = new Emitter()
  #process: ProcessRE
  #originalProcessEmit: ProcessRE['emit']
  #originalProcessReallyExit: ProcessRE['reallyExit']

  #sigListeners: { [k in NodeJS.Signals]?: () => void } = {}
  #loaded: boolean = false

  constructor(process: ProcessRE) {
    super()
    this.#process = process
    // { <signal>: <listener fn>, ... }
    this.#sigListeners = {}
    for (const sig of signals) {
      this.#sigListeners[sig] = () => {
        // If there are no other listeners, an exit is coming!
        // Simplest way: remove us and then re-send the signal.
        // We know that this will kill the process, so we can
        // safely emit now.
        const listeners = this.#process.listeners(sig)
        let { count } = this.#emitter
        // This is a workaround for the fact that signal-exit v3 and signal
        // exit v4 are not aware of each other, and each will attempt to let
        // the other handle it, so neither of them do. To correct this, we
        // detect if we're the only handler *except* for previous versions
        // of signal-exit, and increment by the count of listeners it has
        // created.
        /* c8 ignore start */
        const p = process as unknown as {
          __signal_exit_emitter__?: { count: number }
        }
        if (
          typeof p.__signal_exit_emitter__ === 'object' &&
          typeof p.__signal_exit_emitter__.count === 'number'
        ) {
          count += p.__signal_exit_emitter__.count
        }
        /* c8 ignore stop */
        if (listeners.length === count) {
          this.unload()
          const ret = this.#emitter.emit('exit', null, sig)
          /* c8 ignore start */
          const s = sig === 'SIGHUP' ? this.#hupSig : sig
          if (!ret) process.kill(process.pid, s)
          /* c8 ignore stop */
        }
      }
    }

    this.#originalProcessReallyExit = process.reallyExit
    this.#originalProcessEmit = process.emit
  }

  onExit(cb: Handler, opts?: { alwaysLast?: boolean }) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return () => {}
    }
    /* c8 ignore stop */

    if (this.#loaded === false) {
      this.load()
    }

    const ev = opts?.alwaysLast ? 'afterExit' : 'exit'
    this.#emitter.on(ev, cb)
    return () => {
      this.#emitter.removeListener(ev, cb)
      if (
        this.#emitter.listeners['exit'].length === 0 &&
        this.#emitter.listeners['afterExit'].length === 0
      ) {
        this.unload()
      }
    }
  }

  load() {
    if (this.#loaded) {
      return
    }
    this.#loaded = true

    // This is the number of onSignalExit's that are in play.
    // It's important so that we can count the correct number of
    // listeners on signals, and don't wait for the other one to
    // handle it instead of us.
    this.#emitter.count += 1

    for (const sig of signals) {
      try {
        const fn = this.#sigListeners[sig]
        if (fn) this.#process.on(sig, fn)
      } catch (_) {}
    }

    this.#process.emit = (ev: string, ...a: any[]) => {
      return this.#processEmit(ev, ...a)
    }
    this.#process.reallyExit = (code?: number | null | undefined) => {
      return this.#processReallyExit(code)
    }
  }

  unload() {
    if (!this.#loaded) {
      return
    }
    this.#loaded = false

    signals.forEach(sig => {
      const listener = this.#sigListeners[sig]
      /* c8 ignore start */
      if (!listener) {
        throw new Error('Listener not defined for signal: ' + sig)
      }
      /* c8 ignore stop */
      try {
        this.#process.removeListener(sig, listener)
        /* c8 ignore start */
      } catch (_) {}
      /* c8 ignore stop */
    })
    this.#process.emit = this.#originalProcessEmit
    this.#process.reallyExit = this.#originalProcessReallyExit
    this.#emitter.count -= 1
  }

  #processReallyExit(code?: number | null | undefined) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return 0
    }
    this.#process.exitCode = code || 0
    /* c8 ignore stop */

    this.#emitter.emit('exit', this.#process.exitCode, null)
    return this.#originalProcessReallyExit.call(
      this.#process,
      this.#process.exitCode
    )
  }

  #processEmit(ev: string, ...args: any[]): any {
    const og = this.#originalProcessEmit
    if (ev === 'exit' && processOk(this.#process)) {
      if (typeof args[0] === 'number') {
        this.#process.exitCode = args[0]
        /* c8 ignore start */
      }
      /* c8 ignore start */
      const ret = og.call(this.#process, ev, ...args)
      /* c8 ignore start */
      this.#emitter.emit('exit', this.#process.exitCode, null)
      /* c8 ignore stop */
      return ret
    } else {
      return og.call(this.#process, ev, ...args)
    }
  }
}

const process = globalThis.process
// wrap so that we call the method on the actual handler, without
// exporting it directly.
export const {
  /**
   * Called when the process is exiting, whether via signal, explicit
   * exit, or running out of stuff to do.
   *
   * If the global process object is not suitable for instrumentation,
   * then this will be a no-op.
   *
   * Returns a function that may be used to unload signal-exit.
   */
  onExit,

  /**
   * Load the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  load,

  /**
   * Unload the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  unload,
} = signalExitWrap(
  processOk(process) ? new SignalExit(process) : new SignalExitFallback()
)

// this spawns a child process that listens for SIGHUP when the
// parent process exits, and after 200ms, sends a SIGKILL to the
// child, in case it did not terminate.

import { ChildProcess, spawn } from 'child_process'

const watchdogCode = String.raw`
const pid = parseInt(process.argv[1], 10)
process.title = 'node (foreground-child watchdog pid=' + pid + ')'
if (!isNaN(pid)) {
  let barked = false
  // keepalive
  const interval = setInterval(() => {}, 60000)
  const bark = () => {
    clearInterval(interval)
    if (barked) return
    barked = true
    process.removeListener('SIGHUP', bark)
    setTimeout(() => {
      try {
        process.kill(pid, 'SIGKILL')
        setTimeout(() => process.exit(), 200)
      } catch (_) {}
    }, 500)
  })
  process.on('SIGHUP', bark)
}
`

export const watchdog = (child: ChildProcess) => {
  let dogExited = false
  const dog = spawn(
    process.execPath,
    ['-e', watchdogCode, String(child.pid)],
    {
      stdio: 'ignore',
    }
  )
  dog.on('exit', () => (dogExited = true))
  child.on('exit', () => {
    if (!dogExited) dog.kill('SIGTERM')
  })
  return dog
}

import { Box, Text } from 'ink'
import React, { FC, useLayoutEffect, useState } from 'react'
import { TestBase } from '@tapjs/core'
import { Result } from 'tap-parser'
import { Reporter } from './index.js'
import { listenCleanup } from './listen-cleanup.js'
import { ResultDetailList } from './result-detail-list.js'

type Color = 'red' | 'green' | 'cyan' | 'magenta'
const getColor = (r: Result) =>
  r.skip ? 'cyan'
  : r.todo ? 'magenta'
  : !r.ok ? 'red'
  : 'green'
const DOTS: Color[] = []
const Dots: FC<{ test: TestBase }> = ({ test }) => {
  const [dots, updateDots] = useState<Color[]>([])
  useLayoutEffect(
    () =>
      listenCleanup(test, 'assert', r => {
        DOTS.push(getColor(r))
        updateDots([...DOTS])
      }),
    [dots],
  )
  const width = Math.max(15, columns)
  return (
    <Box width={width} flexWrap="wrap">
      {dots.map((c, i) => (
        <Text key={i} color={c}>
          .
        </Text>
      ))}
    </Box>
  )
}

const { columns = 70 } = process.stdout

export const Dot: Reporter = ({ test }) => (
  <Box flexDirection="column">
    <Dots test={test} />
    <ResultDetailList
      test={test}
      filter={({ parser, counts }) =>
        !parser.ok || !!counts.todo || !!counts.fail
      }
    />
  </Box>
)

// junit representation of the test run
// https://github.com/testmoapp/junitxml

import { Minipass } from 'minipass'
import { FinalResults, Parser, Result } from 'tap-parser'
import { stringify } from 'tap-yaml'

const xmlEscape = (s: string) =>
  s
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')

const cdata = (s: string) =>
  s
    .split(']]>')
    .map(s => `<![CDATA[\n${s.trimEnd()}\n]]>`)
    .join(xmlEscape(']]>'))

class Properties {
  data?: Record<string, any>
  constructor(data?: Record<string, any>) {
    this.data = data
  }
  toString() {
    /* c8 ignore start */
    if (!this.data) return ''
    /* c8 ignore stop */
    const body = Object.entries(this.data)
      .map(([k, v]) => {
        if (
          v === undefined ||
          v === null ||
          v === '' ||
          v === false
        ) {
          return ''
        } else if (typeof v === 'string') {
          return v.includes('\n') ?
              `<property name="${xmlEscape(k)}">${cdata(
                v,
              )}</property>`
            : `<property name="${xmlEscape(k)}" value="${xmlEscape(
                v,
              )}" />`
        } else if (typeof v === 'number' || typeof v === 'boolean') {
          return `<property name="${xmlEscape(k)}" value="${xmlEscape(
            String(v),
          )}" />`
        } else {
          return `<property name="${xmlEscape(k)}">${cdata(
            stringify(v),
          )}</property>`
        }
      })
      .filter(v => !!v)
    if (!body.length) return ''
    return `<properties>
${body.join('\n')}
</properties>`
  }
}

class Failure {
  result: Result
  constructor(result: Result) {
    this.result = result
  }
  toString() {
    const msg = stringify({
      ...(this.result.diag || { name: this.result.name }),
    })
    return `<failure>${cdata(msg).trimEnd()}</failure>`
  }
}

class Case {
  result: Result
  classname: string
  constructor(result: Result) {
    this.result = result
    const fn = result.fullname
    /* c8 ignore start */
    this.classname =
      fn.endsWith(result.name) ?
        fn
          .substring(0, fn.length - result.name.length)
          .replace(/>? $/, '')
          .trimEnd()
      : fn
    /* c8 ignore stop */
  }
  get failures() {
    return this.result.ok ? 0 : 1
  }
  get skipped() {
    return this.result.skip || this.result.todo ? 1 : 0
  }
  get tests() {
    return 1
  }
  toString() {
    /* c8 ignore start */
    const file =
      this.result.diag?.at?.fileName ||
      this.result.diag?.at?.file ||
      this.result.diag?.file
    const line =
      this.result.diag?.at?.lineNumber ||
      this.result.diag?.at?.line ||
      this.result.diag?.line
    const column =
      this.result.diag?.at?.columnNumber ||
      this.result.diag?.at?.column ||
      this.result.diag?.column
    /* c8 ignore stop */
    const { ok, name, skip, todo, tapError, time, diag } = this.result
    const props = String(
      new Properties({
        ...(skip ? { skip } : {}),
        ...(todo ? { todo } : {}),
        /* c8 ignore start */
        ...(tapError ? { tapError } : {}),
        /* c8 ignore stop */
        ...(diag || {}),
      }),
    ).trimEnd()
    return `<testcase id="${this.result.id}" name="${xmlEscape(
      name,
    )}" classname="${xmlEscape(this.classname)}"${
      time ? ` time="${seconds(time)}"` : ''
    }${file ? ` file="${xmlEscape(String(file))}"` : ''}${
      file && line ? ` line="${xmlEscape(String(line))}"` : ''
    }${
      file && line && column ?
        ` column="${xmlEscape(String(column))}"`
      : ''
    }${
      ok && !props ? ' />' : (
        `>
${!ok ? new Failure(this.result) : props}
</testcase>
`
      )
    }`
  }
}

class Suite {
  parser: Parser
  suites: Suite[] = []
  cases: Case[] = []
  name: string
  results?: FinalResults
  constructor(parser: Parser) {
    this.parser = parser
    this.name = parser.name
    this.parser.on('complete', res => (this.results = res))
    this.parser.on('child', p => this.suites.push(new Suite(p)))
    this.parser.on('assert', a => this.onAssert(a))
  }
  onAssert(a: Result) {
    this.cases.push(new Case(a))
  }
  get tests(): number {
    return (
      [...this.suites, ...this.cases]
        .map(s => s.tests)
        /* c8 ignore start */
        .reduce((a, b) => a + b, this.results ? 1 : 0)
    )
    /* c8 ignore stop */
  }
  get failures(): number {
    return [...this.suites, ...this.cases]
      .map(s => s.failures)
      .reduce((a, b) => a + b, this.results?.ok === false ? 1 : 0)
  }
  get assertions(): number {
    return this.suites
      .map(s => s.assertions)
      .reduce((a, b) => a + b, this.cases.length)
  }
  get skipped(): number {
    return [...this.suites, ...this.cases]
      .map(s => s.skipped)
      .reduce(
        (a, b) => a + b,
        (
          this.results?.plan.skipAll ||
            this.results?.skip ||
            this.results?.todo
        ) ?
          1
        : 0,
      )
  }

  toString() {
    const props = new Properties({
      ok: this.results?.ok,
      plan: this.results?.plan,
      bailout: this.results?.bailout,
    })

    return `<testsuite name="${xmlEscape(this.name)}" tests="${
      this.tests
    }" failures="${this.failures}" assertions="${
      this.assertions
    }" skipped="${this.skipped}"${
      this.results?.time ?
        ` time="${seconds(this.results.time)}"`
      : ''
    }>
${props}
${this.suites
  .map(s => String(s))
  .join('\n')
  .trimEnd()}
${this.cases
  .map(c => String(c))
  .join('\n')
  .trimEnd()}
</testsuite>
`
  }
}

class Suites extends Suite {
  onAssert() {}
  toString() {
    const props = String(
      new Properties({
        ok: this.results?.ok,
        plan: this.results?.plan,
        bailout: this.results?.bailout,
      }),
    )
    const id = new Date()
      .toISOString()
      .replace(/[^0-9T]/g, '')
      .replace('T', '_')
    return `<testsuites id="${xmlEscape(id)}" name="${
      xmlEscape(this.name) || 'TAP Tests'
    }" tests="${this.tests}" failures="${
      this.failures
    }" assertions="${this.assertions}" skipped="${this.skipped}" ${
      /* c8 ignore start */
      this.results?.time ? `time="${seconds(this.results.time)}"` : ''
    }>${props ? '\n' + props : ''}
${this.suites
  /* c8 ignore stop */
  .map(s => String(s))
  .join('\n')
  .trimEnd()}
</testsuites>
`
  }
}

const seconds = (m: number) => Math.floor(m * 1000) / 1000000

export class JUnit extends Minipass<string> {
  parser: Parser = new Parser()
  constructor() {
    super({ encoding: 'utf8' })
    super.write('<?xml version="1.0" encoding="UTF-8" ?>\n')
    const suites = new Suites(this.parser)
    this.parser.on('complete', () => {
      super.write(String(suites))
      super.end()
    })
  }

  write(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): boolean
  write(chunk: any, encoding?: any, cb?: any): boolean {
    return this.parser.write(chunk, encoding, cb)
  }

  end(cb?: (() => void) | undefined): this
  end(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): this
  end(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): this
  end(chunk?: any, encoding?: any, cb?: any): this {
    this.parser.end(chunk, encoding, cb)
    return this
  }
}

// json representation of the test run

import EventEmitter from 'events'
import { Minipass } from 'minipass'
import { Parser, Result } from 'tap-parser'

const trimResult = (
  o: Record<string, any>,
): Record<string, any> | undefined => {
  const entries = Object.entries(o)
    .map(([k, v]) => [
      k,
      !!v && typeof v === 'object' && !Array.isArray(v) ?
        trimResult(v)
      : v,
    ])
    .filter(
      ([k, v]) =>
        k === 'ok' ||
        k === 'id' ||
        (v !== false &&
          v !== null &&
          v !== undefined &&
          v !== '' &&
          !(Array.isArray(v) && !v.length)),
    )
  return Object.fromEntries(entries)
}

export class Case {
  result: Result
  constructor(result: Result) {
    this.result = result
  }
  get failures() {
    return this.result.ok && !this.result.skip && !this.result.todo ?
        0
      : 1
  }
  get skipped() {
    return this.result.skip || this.result.todo ? 1 : 0
  }
  get tests() {
    return 1
  }
  toJSON() {
    return trimResult(this.result)
  }
}

export class Suite extends EventEmitter {
  parser: Parser
  suites: Suite[] = []
  cases: Case[] = []
  name: string
  results?: Record<string, any>
  summary?: Record<string, any>
  constructor(parser: Parser) {
    super()
    this.parser = parser
    this.name = parser.name
    this.parser.on('complete', res => (this.results = res))
    this.parser.on('child', p => {
      const s = new Suite(p)
      this.suites.push(s)
      this.emit('suite', s)
      s.on('suite', s => this.emit('suite', s))
      s.on('suiteEnd', s => this.emit('suiteEnd', s))
      s.on('case', c => this.emit('case', c))
    })
    this.parser.on('assert', a => this.onAssert(a))
  }
  onAssert(a: Result) {
    // check to see if it's the summary test point for a suite
    const s = this.suites[this.suites.length - 1]
    if (s && !s.summary && a.name === s.name) {
      s.summary = trimResult(a)
      this.emit('suiteEnd', s)
    } else {
      const c = new Case(a)
      this.cases.push(c)
      this.emit('case', c)
    }
  }
  toJSON() {
    const { tests, failures, assertions, skipped } = this
    return trimResult({
      name: this.name,
      level: this.parser.level,
      ...(this.summary || {}),
      plan: this.results?.plan,
      bailout: this.results?.bailout,
      tests,
      failures,
      assertions,
      skipped,
      suites: this.suites,
      cases: this.cases,
    })
  }
  get tests(): number {
    return [...this.suites, ...this.cases]
      .map(s => s.tests)
      .reduce((a, b) => a + b, this.suites.length)
  }
  get failures(): number {
    return [...this.suites, ...this.cases]
      .map(s => s.failures)
      .reduce((a, b) => a + b, 0)
  }
  get assertions(): number {
    return this.suites
      .map(s => s.assertions)
      .reduce((a, b) => a + b, this.cases.length)
  }
  get skipped(): number {
    return [...this.suites, ...this.cases]
      .map(s => s.skipped)
      .reduce(
        (a, b) => a + b,
        (
          this.results?.plan.skipAll ||
            this.summary?.skip ||
            this.summary?.todo
        ) ?
          1
        : 0,
      )
  }
}

export class Suites extends Suite {
  constructor(p: Parser) {
    super(p)
    this.name ||= 'TAP Tests'
  }
}

