export class JSONStream extends Minipass<string> {
  parser: Parser = new Parser()
  constructor() {
    super({ encoding: 'utf8' })
    const suites = new Suites(this.parser)
    suites.on('suite', s => this.suiteStart(s))
    suites.on('case', c => this.onCase(c))
    suites.on('suiteEnd', s => this.suiteEnd(s))
    this.suiteStart(suites)
    this.parser.on('complete', () => {
      this.suiteEnd(suites)
      super.end()
    })
  }

  onCase(c: Case) {
    super.write(
      JSON.stringify([
        c.result.skip ? 'skip'
        : c.result.todo ? 'todo'
        : c.result.ok ? 'pass'
        : 'fail',
        c,
      ]) + '\n',
    )
  }

  suiteStart(s: Suite) {
    super.write(
      JSON.stringify([
        'start',
        { name: s.name, level: s.parser.level },
      ]) + '\n',
    )
  }

  suiteEnd(s: Suite) {
    super.write(
      JSON.stringify([
        'end',
        {
          ...s.toJSON(),
          suites: undefined,
          cases: undefined,
        },
      ]) + '\n',
    )
  }

  write(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): boolean
  write(chunk: any, encoding?: any, cb?: any): boolean {
    return this.parser.write(chunk, encoding, cb)
  }

  end(cb?: (() => void) | undefined): this
  end(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): this
  end(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): this
  end(chunk?: any, encoding?: any, cb?: any): this {
    this.parser.end(chunk, encoding, cb)
    return this
  }
}

// markdown representation of the test run

import { Minipass } from 'minipass'
import { parse } from 'path'
import { Parser } from 'tap-parser'
import { stringify } from 'tap-yaml'
import { Case, Suite, Suites } from './json.js'

export class MarkdownStream extends Minipass<string> {
  parser: Parser = new Parser()
  constructor() {
    super({ encoding: 'utf8' })
    const suites = new Suites(this.parser)
    suites.on('suite', s => this.suiteStart(s))
    suites.on('case', c => this.onCase(c))
    suites.on('suiteEnd', s => this.suiteEnd(s))
    this.suiteStart(suites)
    this.parser.on('complete', () => {
      this.suiteEnd(suites)
      super.end()
    })
  }

  onCase(c: Case) {
    const { name, id, diag, ok, skip, todo } = c.result
    const t =
      skip !== false ? 'skip'
      : todo !== false ? 'todo'
      : ok ? 'pass'
      : 'fail'
    const color =
      t === 'skip' ? 'cyan'
      : t === 'todo' ? 'magenta'
      : t === 'pass' ? 'green'
      : 'red'

    const msg =
      typeof skip === 'string' ? `<span color="cyan">${skip}</span>`
      : typeof todo === 'string' ?
        `<span color="magenta">${todo}</span>`
      : ''

    const { source, diff, ...extra } = diag || {}
    /* c8 ignore start */
    const file = diag?.at?.fileName || diag?.at?.file || diag?.file
    /* c8 ignore stop */
    const srcLang = file ? parse(file).ext.substring(1) : ''
    const srcPre =
      source ?
        `\n\n\`\`\`${srcLang}\n${source.trimEnd()}\n\`\`\``
      : ''
    const ymlExtra = stringify(extra).trimEnd()
    const preExtra =
      ymlExtra === '{}' ? '' : `\n\n\`\`\`yaml\n${ymlExtra}\n\`\`\``
    const preDiff =
      diff ? `\n\n\`\`\`diff\n${diff.trimEnd()}\n\`\`\`` : ''
    const b = (preDiff + srcPre + preExtra)
      .replace(/^/gm, '    ')
      .replace(/\n    \n/g, '\n\n')
    const body = b.trim() ? '\n\n    ' + b.trimStart() : '\n'
    super.write(
      `${id}. <b style="color:${color}">${t}</b> ${name}${msg}${body}\n`,
    )
  }

  suiteStart(s: Suite) {
    const h = Math.min(6, s.parser.level + 1)
    super.write(`${'#'.repeat(h)} ${s.name.replace(/\n/g, ' ')}\n\n`)
  }

  suiteEnd(s: Suite) {
    const summary = {
      ...s.toJSON(),
      suites: undefined,
      cases: undefined,
    } as Record<string, any>
    const { source, ...diag } = summary.diag || {}
    if (summary.diag) delete summary.diag.source
    /* c8 ignore start */
    const file = diag.at?.fileName || diag.at?.file || diag.file
    /* c8 ignore stop */
    const srcLang = file ? parse(file).ext.substring(1) : ''
    const srcPre =
      source ? `\n\n\`\`\`${srcLang}\n${source}\n\`\`\`` : ''
    const fn = summary.fullname || summary.name
    delete summary.fullname
    delete summary.name
    super.write(
      `\n----\n\n**${fn}**${srcPre}\n\n\`\`\`yaml\n${stringify(
        summary,
      ).trimEnd()}\n\`\`\`\n\n`,
    )
  }

  write(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): boolean
  write(chunk: any, encoding?: any, cb?: any): boolean {
    return this.parser.write(chunk, encoding, cb)
  }

  end(cb?: (() => void) | undefined): this
  end(
    chunk: Minipass.ContiguousData,
    cb?: (() => void) | undefined,
  ): this
  end(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | undefined,
    cb?: (() => void) | undefined,
  ): this
  end(chunk?: any, encoding?: any, cb?: any): this {
    this.parser.end(chunk, encoding, cb)
    return this
  }
}

import { Base, TapBaseEvents, TestBase } from '@tapjs/core'
import { Box } from 'ink'
import React, { FC } from 'react'
import { useIsDone } from './hooks/use-is-done.js'
import { useSubtests } from './hooks/use-subtests.js'

import { TestSummary } from './test-summary.js'

export const ResultDetailList: FC<{
  test: TestBase
  Banner?: FC<{}>
  filter?: (t: Base<TapBaseEvents>) => boolean
}> = ({
  test,
  Banner,
  filter = ({ counts, parser }) =>
    !!counts.fail || !!counts.skip || !!counts.todo || !parser.ok,
}) => {
  const tests = useSubtests(test, 'finished')
  const t = tests.filter(filter)
  const done = useIsDone(test)

  return !done ?
      <></>
    : <Box flexDirection="column">
        {Banner ?
          <Banner />
        : <></>}
        {t.map((test, key) => (
          <Box key={key} flexDirection="column" marginTop={1}>
            <TestSummary test={test} details />
          </Box>
        ))}
      </Box>
}

import { TestBase } from '@tapjs/core'
import { Box } from 'ink'
import React, { FC } from 'react'
import { useSubtests } from './hooks/use-subtests.js'
import { TestSummary } from './test-summary.js'

// show the tests currently running
export const Runs: FC<{ test: TestBase }> = ({ test }) => {
  const t = useSubtests(test, 'active')
  return !t.length ?
      <></>
    : <Box flexDirection="column" marginTop={1}>
        {t.map(test => (
          <Box key={test.childId}>
            <TestSummary test={test} />
          </Box>
        ))}
      </Box>
}

// The <Static> section that appends results as the tests run
// Each log entry can be one of:
// - A finished test
// - A failed Result object (on the root test)
// - a stderr or stdout message

import { Box, Static, Text } from 'ink'
import React, { FC } from 'react'
import {
  ConsoleLog,
  isConsoleLog,
  isStdioLog,
  isTestLog,
  LogEntry,
  StdioLog,
  TestLog,
  useLog,
} from './hooks/use-log.js'
import { TapReportOpts } from './index.js'
import { TestSummary } from './test-summary.js'

export const TestLogLine: FC<TestLog> = ({ test, previous }) => (
  <Box paddingTop={!!previous && !isTestLog(previous) ? 1 : 0}>
    <TestSummary test={test} />
  </Box>
)

export const ConsoleLogLine: FC<ConsoleLog> = ({
  text,
  previous,
}) => (
  <Box paddingTop={!!previous && !isConsoleLog(previous) ? 1 : 0}>
    <Text>{text.trimEnd()}</Text>
  </Box>
)

export const StdioLogLine: FC<StdioLog> = ({
  name,
  fd,
  text,
  previous: p,
}) => {
  const prefix =
    isStdioLog(p) && p.fd === fd && p.name === name ?
      <></>
    : <Box gap={1} paddingTop={1}>
        {fd === 1 ?
          <Text color="cyan" bold dimColor>{`1>`}</Text>
        : <Text color="red" bold dimColor>{`2>`}</Text>}
        <Text dimColor>{name}</Text>
      </Box>

  return (
    <Box flexDirection="column">
      {prefix}
      <Box>
        <Text>{String(text).trimEnd()}</Text>
      </Box>
    </Box>
  )
}

export interface LogOpts extends TapReportOpts {
  includeTests?: boolean
}

export const Log: FC<LogOpts> = ({ test, config, includeTests }) => {
  if (test.results) return <></>

  const logs = useLog(test, config, includeTests)
  return (
    <Static items={logs}>
      {(log, key) => <LogLine {...log} key={key} />}
    </Static>
  )
}

const LogLine: FC<LogEntry> = log =>
  isTestLog(log) ? <TestLogLine {...log} />
  : isStdioLog(log) ? <StdioLogLine {...log} />
  : <ConsoleLogLine {...log} />

import EventEmitter from 'node:events'
export const listenCleanup = (
  e: EventEmitter | null | undefined,
  ev: string,
  fn: (...a: any[]) => any,
) => {
  if (!e) return () => {}
  e.on(ev, fn)
  return () => {
    e.removeListener(ev, fn)
  }
}

import { Counts, TestBase } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'

export const useAssertTotals = (test: TestBase) => {
  const [asserts, updateAsserts] = useState<Counts>(new Counts())
  useLayoutEffect(() =>
    listenCleanup(test, 'assert', () =>
      updateAsserts(new Counts(test.counts)),
    ),
  )
  return asserts
}

import { Base } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'

export const useBailedOut = (t: Base) => {
  const [bailedOut, setBailedOut] = useState<string | boolean>(
    t.bailedOut,
  )
  useLayoutEffect(
    () =>
      listenCleanup(t, 'bailout', (reason?: string) =>
        setBailedOut(reason || true),
      ),
    [t.bailedOut],
  )
  return bailedOut
}

import { useLayoutEffect } from 'react'

export type CleanupEffect = (
  cleanup: (() => any)[],
  doCleanup: () => void,
) => void | undefined | (() => void)

// push cleanup functions onto the array, and they'll be cleaned up
export const useCleanup = (effect: CleanupEffect, deps: any[]) =>
  useLayoutEffect(() => {
    const cleanup: (() => any)[] = []
    let c: (() => void) | void | undefined = undefined
    const doCleanup = () => {
      if (c) c()
      for (const c of cleanup) c()
      cleanup.length = 0
    }
    c = effect(cleanup, doCleanup)
    return doCleanup
  }, deps)

import { Base } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'
/**
 * Return true if the test is done, otherwise false
 */
export const useIsDone = (test: Base) => {
  let [done, setDone] = useState<boolean>(false)
  useLayoutEffect(
    () => listenCleanup(test, 'complete', () => setDone(true)),
    [test],
  )
  return done
}

import { Base } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'

// We can sometimes get multiple comments all synchronously in the
// same tick, which would mean multiple state updates from the same
// starting state, and what we want is a consistent append-only list,
// so stash it outside of the react component.

const log = new Map<Base, string[]>()

export const useComments = (test: Base) => {
  const comments_ = log.get(test) || []
  const [comments, updateComments] = useState<string[]>([
    ...comments_,
  ])
  if (!log.has(test)) log.set(test, comments_)

  useLayoutEffect(
    () =>
      listenCleanup(test.parser, 'comment', (c: string) => {
        if (c.trim()) {
          comments_.push(c.trimEnd())
          updateComments([...comments_])
        }
      }),
    [comments, test],
  )

  return [...comments_]
}

import { LoadedConfig } from '@tapjs/config'
import { Base, Spawn, TestBase } from '@tapjs/core'
import patchConsole from 'patch-console'
import { useState } from 'react'
import { Parser } from 'tap-parser'
import { listenCleanup } from '../listen-cleanup.js'
import { useCleanup } from './use-cleanup.js'
import { useSubtests } from './use-subtests.js'

const isBase = (o: any): o is Base =>
  !!o &&
  typeof o === 'object' &&
  // only relevant when mocking the import in our own tests
  /* c8 ignore start */
  (o instanceof Base ||
    (typeof o.name === 'string' &&
      !!o.parser &&
      typeof o.parser === 'object'))
/* c8 ignore stop */

const proceduralComment = /^# Subtest(?:\n?$|: )/

export type LogEntry = StdioLog | TestLog | ConsoleLog

export interface TestLog {
  test: Base
  previous?: LogEntry
}

export const isTestLog = (p?: LogEntry): p is TestLog =>
  !!p && isBase((p as TestLog).test)

export interface ConsoleLog {
  text: string
  previous?: LogEntry
}

export const isConsoleLog = (p?: LogEntry): p is ConsoleLog =>
  !!p &&
  typeof (p as ConsoleLog).text === 'string' &&
  !isBase((p as TestLog).test)

export interface StdioLog extends ConsoleLog {
  name: string
  fd: 0 | 1 | 2
}

export const isStdioLog = (p?: LogEntry): p is StdioLog =>
  isConsoleLog(p) &&
  typeof (p as StdioLog).name === 'string' &&
  typeof (p as StdioLog).fd === 'number'

// prevent same-tick state updates from clobbering each other
// by keeping a persistent copy of the logs for any given test.
const LOGS = new Map<TestBase, LogEntry[]>()

export const useLog = (
  test: TestBase,
  config: LoadedConfig,
  includeTests: boolean = false,
) => {
  const fromCache = LOGS.get(test) || []
  LOGS.set(test, fromCache)

  const [logs, updateLogs] = useState<LogEntry[]>(fromCache)
  const appendLog = (l: LogEntry) => {
    const logs = LOGS.get(test) as LogEntry[]
    const previous = logs[logs.length - 1]
    l.previous = previous
    const newLogs = logs.concat(l)
    LOGS.set(test, newLogs)
    updateLogs(newLogs)
  }
  const tests = useSubtests(test, 'all')

  useCleanup(
    cleanup => {
      cleanup.push(
        patchConsole((_stream, text) => {
          appendLog({ text })
        }),
      )

      for (const test of tests) {
        // stdout that isn't tap is "extra"
        cleanup.push(
          listenCleanup(test.parser, 'extra', (c: string) => {
            appendLog({
              name: test.name,
              fd: 1,
              text: c,
            })
          }),
        )

        // the terse report does not show log lines for tests
        // completing, just the other stuff.
        if (includeTests) {
          cleanup.push(
            listenCleanup(test, 'complete', () =>
              appendLog({ test }),
            ),
          )
        }

        const { proc } = test as Spawn
        if (proc) {
          cleanup.push(
            listenCleanup(proc.stderr, 'data', c => {
              appendLog({
                name: test.name,
                fd: 2,
                text: String(c),
              })
            }),
          )
        }

        // treat comments a little like a stdio log
        if (config.get('comments')) {
          const onChild = (p: Parser) => {
            cleanup.push(listenCleanup(p, 'child', onChild))
            cleanup.push(
              listenCleanup(p, 'comment', c => {
                // just a precaution, we don't actually listen in time
                // to get these, because we're not hooking onto the parser
                // until it's already been emitted.
                /* c8 ignore start */
                if (proceduralComment.test(c)) return
                /* c8 ignore stop */
                appendLog({
                  name: p.fullname,
                  fd: 0,
                  text: c,
                })
              }),
            )
          }
          onChild(test.parser)
        }
      }
    },
    [logs, tests],
  )

  return logs
}

import { Base, TestBase } from '@tapjs/core'
import { useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'
import { useCleanup } from './use-cleanup.js'

const ALLSUBS = new Map<Base, Base[]>()
const FINSUBS = new Map<Base, Base[]>()

export const useSubtests = (
  test: TestBase,
  which: 'active' | 'finished' | 'all' = 'all',
) => {
  const [tests, updateTests] = useState<Base[]>([])
  const cache = which === 'finished' ? FINSUBS : ALLSUBS
  const addSub = (t: Base) => {
    const tests = (cache.get(test) || []).concat(t)
    cache.set(test, tests)
    updateTests(tests)
  }

  const updateActive = () => updateTests([...test.activeSubtests])

  useCleanup(
    cleanup => {
      if (which === 'active') {
        cleanup.push(
          listenCleanup(test, 'subtestStart', updateActive),
        )
        cleanup.push(listenCleanup(test, 'subtestEnd', updateActive))
      } else {
        cleanup.push(
          listenCleanup(test, 'complete', () => cache.delete(test)),
        )
        cleanup.push(
          listenCleanup(
            test,
            which === 'all' ? 'subtestStart' : 'subtestEnd',
            addSub,
          ),
        )
      }
    },
    [test, tests, which],
  )
  return tests
}

import { Base, Counts } from '@tapjs/core'
import { useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'
import { useCleanup } from './use-cleanup.js'

const SUITES = new Map<Base, Counts>()

export const useSuiteTotals = (test: Base) => {
  // multiple subtests can end in the same tick, so we need to track
  // this in a local var as well as the component render state so that
  // they don't clobber each other's totals.
  const fromCache = SUITES.get(test) || new Counts()
  SUITES.set(test, fromCache)

  const [suites, updateSuites] = useState<Counts>(fromCache)

  const addSuite = () => {
    const suites = SUITES.get(test) as Counts
    suites.total++
    SUITES.set(test, new Counts(suites))
    updateSuites(suites)
  }

  const finishSuite = (t: Base) => {
    const suites = SUITES.get(test) as Counts
    const { results } = t
    // will always have results when the subtest ends
    /* c8 ignore start */
    if (!results) return
    /* c8 ignore stop */
    let { total, fail, pass, skip, complete } = suites
    complete++
    const isFail =
      !results.ok || (results.plan.skipAll && test.options.failSkip)
    if (isFail) fail++
    else if (results.plan.skipAll) skip++
    else pass++

    const ns = new Counts({
      total,
      fail,
      pass,
      skip,
      complete,
    })

    SUITES.set(test, ns)
    updateSuites(ns)
  }

  useCleanup(
    cleanup => {
      cleanup.push(listenCleanup(test, 'subtestAdd', addSuite))
      cleanup.push(listenCleanup(test, 'subtestEnd', finishSuite))
    },
    [test, suites],
  )
  return suites
}

import { Base, MILLISECONDS } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'

export const useTestTime = (
  test: Base,
  interval: MILLISECONDS = 247,
): number => {
  const [time, updateTime] = useState<number>(test.time)
  const [start] = useState<number>(Date.now())
  useLayoutEffect(() => {
    const i =
      test.time ? null : (
        setInterval(() => updateTime(Date.now() - start), interval)
      )
    const maybeCT = () => (i === null ? null : clearTimeout(i))
    const cleanup = listenCleanup(test, 'complete', maybeCT)
    return () => {
      maybeCT()
      cleanup()
    }
  }, [test.time])
  return time
}

import { Base, Extra } from '@tapjs/core'
import { useLayoutEffect, useState } from 'react'
import { listenCleanup } from '../listen-cleanup.js'

export const useTimedOut = (t: Base) => {
  const [timedout, setTimedout] = useState<Extra | undefined>(
    undefined,
  )
  useLayoutEffect(
    () => listenCleanup(t, 'timeout', extra => setTimedout(extra)),
    [t.timedOut],
  )
  return timedout
}

// turn a diff string into pretty colors
import { Box, Text } from 'ink'
import React, { FC } from 'react'

import stringLength from 'string-length'

// Color scheme to work with protanopia, deuteranopia, and tritanopia,
// since diffs use color as a meaningful indicator.
// should this be configurable?
const theme = {
  green: {
    bg: '#3A7500',
    fg: '#F2FFE5',
  },
  red: {
    bg: '#AC3EA3',
    fg: '#FFE5F1',
  },
  ctx: {
    bg: '#222',
    fg: '#759EEF',
    extra: '#E599FF',
  },
  white: {
    fg: '#ccc',
    bg: '#333',
  },
}

const columns = process.stdout.columns || 80

const Line: FC<{ line: string }> = ({ line }) =>
  line.charAt(0) === '+' ? <Green line={line} />
  : line.charAt(0) === '-' ? <Red line={line} />
  : line.charAt(0) === '@' ? <Ctx line={line} />
  : <White line={line} />

const White: FC<{ line: string }> = ({ line }) => (
  <Text backgroundColor={theme.white.bg} color={theme.white.fg}>
    {line}
  </Text>
)

const Ctx: FC<{ line: string }> = ({ line }) => {
  const f = line.match(/^(\@\@.*?\@\@)( .*)$/)
  return f ?
      <Box>
        <Text
          bold
          backgroundColor={theme.ctx.bg}
          color={theme.ctx.fg}>
          {f[1]}
        </Text>
        <Text
          bold
          backgroundColor={theme.ctx.bg}
          color={theme.ctx.extra}>
          {f[2]}
        </Text>
      </Box>
    : <Text bold backgroundColor={theme.ctx.bg} color={theme.ctx.fg}>
        {line}
      </Text>
}

const Green: FC<{ line: string }> = ({ line }) => (
  <Text color={theme.green.fg} backgroundColor={theme.green.bg}>
    {line}
  </Text>
)
const Red: FC<{ line: string }> = ({ line }) => (
  <Text color={theme.red.fg} backgroundColor={theme.red.bg}>
    {line}
  </Text>
)

export const Diff: FC<{ diff: string }> = ({ diff = '' }) => {
  const sd: string = diff.trimEnd()
  if (!sd) return <></>

  let width = 0
  const maxLen = Math.max(columns - 5, 0)
  const lines = sd
    .replace(/\x1b/g, '\\' + 'x1b')
    .split('\n')
    .filter(line => {
      if (line.length > width) {
        width = Math.min(maxLen, line.length)
      }
      return (
        line !== '\\ No newline at end of file' && !/^\=+$/.test(line)
      )
    })
  return (
    <Box flexDirection="column">
      {lines
        .map(line =>
          stringLength(line) <= width ?
            line + ' '.repeat(width - stringLength(line))
          : line,
        )
        .map((line, key) => (
          <Box key={key}>
            <Line line={line} />
          </Box>
        ))}
    </Box>
  )
}

import chalk from 'chalk'
import { Box, Text } from 'ink'
import React, { FC } from 'react'
import { Result } from 'tap-parser'
import { stringify } from 'tap-yaml'
import { same } from 'tcompare'
import { Diff } from './diff.js'
import { Source } from './source.js'
import { Stack } from './stack.js'

export const ResultDetails: FC<{
  result: Result
  seen?: unknown[]
  heading?: string
  flush?: boolean
}> = ({ result, seen = [], heading, flush }) => {
  if (!result.diag || typeof result.diag !== 'object') return <></>
  const {
    test,
    stack,
    at,
    source,
    location,
    cause,
    error,
    errors,
    code,
    errorOrigin,
    message,
    ...otherDiags
  } = result.diag

  if (
    'expected' in otherDiags &&
    'actual' in otherDiags &&
    !('found' in otherDiags) &&
    !('wanted' in otherDiags)
  ) {
    otherDiags.found = otherDiags.actual
    otherDiags.wanted = otherDiags.expected
    delete otherDiags.actual
    delete otherDiags.expected
  }

  if (
    'found' in otherDiags &&
    'wanted' in otherDiags &&
    !('diff' in otherDiags)
  ) {
    const { diff } = same(otherDiags.found, otherDiags.wanted)
    otherDiags.diff = diff
  }

  // if we have a diff, don't show found/wanted, as it is frequently huge.
  if (otherDiags.diff?.trimEnd()) {
    delete otherDiags.found
    delete otherDiags.wanted
  }
  const { diff } = otherDiags
  delete otherDiags.diff

  return (
    <Box paddingLeft={!!heading ? 0 : 4} flexDirection="column">
      {heading ?
        <Box flexDirection="column" paddingTop={flush ? 0 : 1}>
          <Text color="white" bold>
            {heading}
          </Text>
        </Box>
      : <></>}
      <Source
        stack={stack}
        location={location}
        at={at}
        source={source}
        errorOrigin={errorOrigin}
      />
      <Diff diff={diff} />
      {error ?
        <Text>
          {stringify({ error })
            .trimEnd()
            .replace(/^error:([^\n]*\n)?/, chalk.dim('error:$1'))}
        </Text>
      : <></>}
      {code ?
        <Text>
          {stringify({ code })
            .trimEnd()
            .replace(/^code:([^\n]*\n)?/, chalk.dim('code:$1'))}
        </Text>
      : <></>}
      {!!Object.keys(otherDiags).length && (
        <Text dimColor>{stringify(otherDiags).trimEnd()}</Text>
      )}
      <Stack stack={stack} />
      {cause !== undefined &&
        (!!cause && typeof cause === 'object' ?
          /* c8 ignore start - exceedingly unlikely */
          seen.includes(cause) ?
            '(circular)'
          : /* c8 ignore stop */
            <ResultDetails
              heading={`cause: ${
                typeof cause.message === 'string' ?
                  `${typeof cause.type === 'string' ? cause.type : 'Error'}: ${cause.message}`
                : ''
              }`}
              flush={false}
              result={{
                ...result,
                diag: cause,
              }}
              seen={seen.concat(cause)}
            />
        : <Box paddingTop={1}>
            <Text>
              {chalk.white.bold('cause: ')}
              {chalk.dim(stringify(cause).trimEnd())}
            </Text>
          </Box>)}
      {(
        errors !== undefined && Array.isArray(errors) && errors.length
      ) ?
        [
          <Box flexDirection="column" key={-1} paddingTop={1}>
            <Text color="white" bold>
              errors: [
            </Text>
          </Box>,
          ...errors.map((e, i) => (
            <Box
              key={i}
              flexDirection="row"
              paddingLeft={2}
              paddingTop={+!!i}>
              <Text color="white" bold>
                {`- `}
              </Text>
              {!!e && typeof e === 'object' ?
                <ResultDetails
                  flush
                  result={{
                    ...result,
                    diag: e,
                  }}
                  heading={`${
                    typeof e.message === 'string' ?
                      `${typeof e.type === 'string' ? e.type : 'Error'}: ${e.message}`
                    : ''
                  }`}
                  seen={seen.concat(e)}
                />
              : <Text dimColor>{stringify(e).trimEnd()}</Text>}
            </Box>
          )),
          <Box flexDirection="column" key={errors.length}>
            <Text color="white" bold>
              ]
            </Text>
          </Box>,
        ]
      : <></>}
    </Box>
  )
}

import { Base } from '@tapjs/core'
import { Box, Text } from 'ink'
import React, { FC } from 'react'
import { Result } from 'tap-parser'
import { assertName } from './assert-name.js'
import { ResultDetails } from './result-details.js'

export interface ResultOpts {
  result: Result
  details?: boolean
  test: Base
}

export const ResultTag: FC<ResultOpts> = ({
  result,
  details = false,
  test,
}) => {
  const showCallsite = !details
  const c =
    result.skip ? '~'
    : result.todo ? ''
    : !result.ok ? ''
    : ''
  const textc =
    result.skip ? 'cyan'
    : result.todo ? 'magenta'
    : !result.ok ? 'red'
    : 'green'
  const pref = (
    <Text bold color={textc}>
      {c}
    </Text>
  )
  let st = result.skip || result.todo || result.tapError
  const suff =
    typeof st === 'string' ? <Text color={textc}>{st}</Text> : <></>
  const at = result.diag?.at
  const fileName = at?.fileName
  const callsite =
    showCallsite && at && fileName ?
      <Text dimColor>
        {fileName}
        {at.lineNumber && at.columnNumber ?
          `:${at.lineNumber}:${at.columnNumber}`
        : ''}
      </Text>
    : <></>
  const name = assertName(result, test)

  return (
    <Box flexDirection="column">
      <Box gap={1} paddingLeft={1}>
        {pref}
        <Text>{name}</Text>
        {suff}
        {callsite}
      </Box>
      {!!details && <ResultDetails result={result} />}
    </Box>
  )
}

// Syntax-highlighted source code tag

import { CallSiteLike, parseStack } from '@tapjs/stack'
import { highlightFileSync } from 'prismjs-terminal'

import chalk from 'chalk'
import { Box, Text } from 'ink'
import React, { FC } from 'react'
import stringLength from 'string-length'

export interface SourceOpts {
  source?: string
  stack?: string
  location?: string
  at?:
    | CallSiteLike
    | {
        lineNumber?: number
        fileName?: string
        columnNumber?: number
      }
  errorOrigin?: {
    source?: string
    at?:
      | CallSiteLike
      | {
          lineNumber?: number
          fileName?: string
          columnNumber?: number
        }
  }
  isErrorOrigin?: boolean
}

/**
 * Pass in a test result.diag that has a source and callsite,
 * and it'll return a prettied up source line with the callsite
 * highlighted
 */
export const Source: FC<SourceOpts> = ({
  source,
  at,
  errorOrigin,
  stack,
  location,
  isErrorOrigin = false,
}) => {
  if (errorOrigin && typeof errorOrigin === 'object') {
    return (
      <>
        <Source source={source} at={at} />
        <Source {...errorOrigin} isErrorOrigin={true} />
      </>
    )
  }
  if (location && !stack) {
    return (
      <Source
        {...{
          source,
          at,
          errorOrigin,
          stack: location,
          isErrorOrigin,
        }}
      />
    )
  }
  if (stack && !at) {
    if (location) {
      return (
        <>
          <Source stack={location} />
          <Source stack={stack} isErrorOrigin={true} />
        </>
      )
    }
    const parsed = parseStack(stack)
    for (const p of parsed) {
      if (p.fileName) {
        at = p
        break
      }
    }
  }
  if (at && at.lineNumber && at.columnNumber && at.fileName) {
    try {
      const lines: string[] = highlightFileSync(at.fileName, {
        lineNumbers: true,
        theme: 'moria',
        padding: 0,
        /* c8 ignore start */
        maxWidth:
          process.stdout.columns && process.stdout.columns - 5,
        /* c8 ignore stop */
      }).split('\n')
      const lastLine = lines[lines.length - 1]
      if (lastLine && stringLength(lastLine) === 0) lines.pop()
      const ctx = 4
      const startLine = Math.max(at.lineNumber - ctx, 0)
      const endLine = Math.min(at.lineNumber + ctx, lines.length)
      const numLen = at.lineNumber.toString().length
      const maxNumLen = lines.length.toString().length
      const excess = maxNumLen - numLen
      const atLine = lines[at.lineNumber - 1] as string
      const before = lines.slice(startLine, at.lineNumber - 1)
      const after = lines.slice(at.lineNumber, endLine)
      const len = Math.min(...before.map(l => stringLength(l)))
      const msg =
        (isErrorOrigin ? 'error origin: ' : '') + at.fileName
      const title = chalk.bgAnsi256(234).dim(msg.padEnd(len))
      const caret =
        (
          at.columnNumber &&
          at.columnNumber < stringLength(atLine) &&
          at.columnNumber > 0
        ) ?
          chalk.ansi256(252).bgAnsi256(234)(
            chalk.red(
              ' '.repeat(excess) +
                ''.repeat(numLen + at.columnNumber) +
                chalk.bold('') +
                ' '.repeat(len - (numLen + at.columnNumber) - 1 - excess),
            ),
          )
        : ''
      const context = [title]
      if (!caret) {
        context.push(
          ...before.map(b => ' ' + b),
          chalk.bold.red('') + atLine,
          ...after.map(l => ' ' + l),
        )
      } else {
        context.push(...before, atLine, caret, ...after)
      }
      return (
        <Box flexDirection="column" paddingTop={isErrorOrigin ? 1 : 0}>
          {context.map((l, key) => (
            <Text key={key}>{l}</Text>
          ))}
        </Box>
      )
    } catch {}
  }
  return source ?
      <Box>
        <Text>{source}</Text>
      </Box>
    : <></>
}

// @tapjs/stack makes stack traces a bit nicer, but we can do even better
// with some colors and highlighting.

import { CallSiteLike, parseStack } from '@tapjs/stack'
import chalk from 'chalk'
import { Box, Text } from 'ink'
import { isAbsolute } from 'path'
import React, { FC } from 'react'
import { stringify } from 'tap-yaml'
import { HangingIndent } from './hanging-indent.js'

// only show generated callsite info if it's not ours
// it's useful to know where to start throwing console.logs, but
// if it's our own code, it's just noise.
// Treat ./node_modules as "absolute" for this purpose, since deps
// aren't "local" in the same sense, even though they live in cwd.
const relativeOrMissing = (p?: string | null) =>
  !p || !(isAbsolute(p) || p.startsWith('node_modules'))
const removeRelativeGenerated = (c?: CallSiteLike | null) => {
  if (c && relativeOrMissing(c.fileName)) c.generated = undefined
}

// Only highlight *our* filenames, not those from deps or outside paths.
// We use chalk.dim() directly here, because neighboring Text nodes get
// squashed together.
const highlightFilename = (s: string, f?: string | null) => {
  if (
    !f ||
    f === 'native' ||
    f === '<anonymous>' ||
    isAbsolute(f) ||
    f.startsWith('..') ||
    !s.includes(f)
  ) {
    return <Text>{chalk.dim(s)}</Text>
  }
  const split = s.split(f)
  const last = split[split.length - 1]
  split.pop()
  return (
    <Text>
      {split
        .map(s => `${chalk.dim(s)}${chalk.yellowBright(f)}`)
        .join('') + chalk.dim(last)}
    </Text>
  )
}

const isUseful = (c: CallSiteLike) =>
  !!c.lineNumber || !!c.columnNumber || !!c.fileName

export const Stack: FC<{ stack?: string }> = ({ stack }) => {
  if (!stack?.trim()) return <></>

  const p = parseStack(stack)
  if (!p.length) return <></>

  const st = p
    .map(c => String(c))
    .join('\n')
    .replace(/\n+$/, '')
    .split('\n')
    .map(l => {
      const c = new CallSiteLike(null, l)
      removeRelativeGenerated(c)
      removeRelativeGenerated(c.evalOrigin)
      if (!isUseful(c)) return undefined
      return c
    })
    .filter(c => !!c)
    .map(c =>
      highlightFilename(
        String(c),
        c?.evalOrigin ? c?.evalOrigin.fileName : c?.fileName,
      ),
    )
  // if nothing useful was found, just show the string
  const showRaw = !st.length && { stack }

  return (
    <Box flexDirection="column">
      {st.length ?
        st.map((line, key) => (
          <HangingIndent key={key}>{line}</HangingIndent>
        ))
      : showRaw ?
        <Text dimColor>{stringify(showRaw).trimEnd()}</Text>
      : /* c8 ignore next - impossible */ <></>}
    </Box>
  )
}

import { Base } from '@tapjs/core'
import { Text } from 'ink'
import React, { FC } from 'react'

export interface TestBadgeOpts {
  test: Base
}

export const TestBadge: FC<TestBadgeOpts> = ({ test }) => {
  const { results, counts, options, bailedOut } = test
  const { exitCode, signal, failSkip, failTodo } = options
  if (!results) {
    return (
      <Text backgroundColor="yellow" color="#000" bold>
        {' RUNS '}
      </Text>
    )
  }
  const { ok, skip: resultsSkip, plan } = results
  // we don't get these things on a bailout
  const { skipAll } = plan || {}
  const { fail, todo, skip } = counts || {}
  const isSkip = !!skip || skipAll || !!resultsSkip
  const isFail =
    !ok ||
    !!fail ||
    signal ||
    bailedOut ||
    exitCode ||
    (!!todo && failTodo) ||
    (isSkip && failSkip)
  return (
    isFail ?
      <Text backgroundColor="red" color="#fff" bold>
        {' FAIL '}
      </Text>
    : !!todo ?
      <Text backgroundColor="#808" color="#fff" bold>
        {' TODO '}
      </Text>
    : isSkip ?
      <Text backgroundColor="blue" color="#fff" bold>
        {' SKIP '}
      </Text>
    : <Text backgroundColor="#0a0" color="#000" bold>
        {' PASS '}
      </Text>
  )
}

import { Base } from '@tapjs/core'
import { Box, Text } from 'ink'
import React, { FC, ReactElement } from 'react'
import { Result } from 'tap-parser'
import { ResultTag } from './result-tag.js'

export interface TestResultsListOpts {
  test: Base
  details?: boolean
}

const isResult = (r: any): r is Result =>
  !!r && typeof r === 'object' && typeof r.ok === 'boolean'

export const TestResultsList: FC<TestResultsListOpts> = ({
  test,
  details = false,
}) => {
  const { results, options, bailedOut } = test
  const { exitCode, signal } = options
  if (!results) return <></>
  const lists = test.lists

  const resultsList: (ReactElement | Result)[] = []
  if (results.plan.skipAll && !bailedOut && !signal && !exitCode) {
    resultsList.push(
      <Box gap={1} paddingLeft={1}>
        <Text color="cyan" bold>
          ~
        </Text>
        <Text>{results.plan.skipReason || 'no tests found'}</Text>
      </Box>,
    )
  }
  resultsList.push(
    ...lists.pass,
    ...lists.skip,
    ...lists.todo,
    ...lists.fail,
  )

  if (typeof test.options.signal === 'string') {
    resultsList.push(
      <Box gap={1} paddingLeft={1}>
        <Text color="red" bold>

        </Text>
        <Text>{test.options.signal}</Text>
      </Box>,
    )
  }
  if (bailedOut && !details) {
    resultsList.push(
      <Box gap={1} paddingLeft={1}>
        <Text color="red" bold>
          Bailout!
        </Text>
        <Text>{bailedOut}</Text>
      </Box>,
    )
  }

  // group them so the short ones clump together into a single section
  // with a 1 block space between longer entries
  type RLG = (ReactElement[] | ReactElement)[]
  const resultsListGrouped: RLG = resultsList.reduce(
    (s: RLG, f: ReactElement | Result) => {
      const el =
        isResult(f) ?
          <ResultTag result={f} details={details} test={test} />
        : f
      if (!details || !(f instanceof Result) || !f.diag) {
        let l = s[s.length - 1]
        if (!Array.isArray(l)) {
          l = []
          s.push(l)
        }
        l.push(el)
      } else {
        s.push(el)
      }
      return s
    },
    [],
  )

  return (
    <Box gap={1} flexDirection="column">
      {resultsListGrouped.map((f, key) => (
        <Box flexDirection="column" key={key}>
          {Array.isArray(f) ?
            f.map((f, key) => (
              <Box flexDirection="column" key={key}>
                {f}
              </Box>
            ))
          : <Box key={key}>{f}</Box>}
        </Box>
      ))}
    </Box>
  )
}

import { Base } from '@tapjs/core'
import { Box, Text } from 'ink'
import React, { FC } from 'react'
import { useCountsLists } from './hooks/use-counts-lists.js'
import { useTestTime } from './hooks/use-test-time.js'
import { ms } from './ms.js'
import { TestBadge } from './test-badge.js'
import { TestResultsList } from './test-results-list.js'

export interface TestSummaryOpts {
  /**
   * The test being summarized
   */
  test: Base
  /**
   * Set to show assertion details.
   *
   * This is left unset in the log component, then set to true when
   * summarizing at the end of the run.
   */
  details?: boolean
}

export const TestSummary: FC<TestSummaryOpts> = ({
  test,
  details = false,
}) => {
  const [counts] = useCountsLists(test)
  const time = useTestTime(test)

  const { total, todo, skip, fail } = counts
  const { exitCode, signal } = test.options
  const ok = !todo && !skip && !fail && !exitCode && !signal

  return (
    <Box flexDirection="column">
      <Box gap={1}>
        <TestBadge test={test} />
        <Text>{test.name}</Text>
        {!!fail && <Text color="red">{fail} failed</Text>}
        {!!todo && <Text color="magenta">{todo} todo</Text>}
        {!!skip && <Text color="cyan">{skip} skip</Text>}
        {!!(fail || todo || skip) && <Text>of</Text>}
        <Text bold>{total}</Text>
        {ok && <Text color="green">OK</Text>}
        {time !== 0 && (
          <Text bold dimColor>
            {ms(time)}
          </Text>
        )}
      </Box>
      <TestResultsList test={test} details={details} />
    </Box>
  )
}

// TODO: start stack with [`language-${lang}`]

/**
 * A function that applies styles, typically one of the chalk builder functions
 */
export type StyleFn = (s: string) => string

/**
 * Either one {@link StyleFn} or several
 */
export type Styles = StyleFn | StyleFn[]

/**
 * Theme objects can be either a Map or object where the keys are the selectors
 * and the values are either a styling function or an array of styling
 * functions to be applied in order.
 *
 * The `_` style rule applies to the block as a whole, and is used
 * as the default style. This is where you'd usually port a PrismJS
 * theme's `code[class*="language-"]` css rule.
 *
 * The `lineNumber` style rule will apply to line numbers, if they
 * are used.
 *
 * The semantics are similar to CSS, where a nested property will be
 * applied to nodes within that nesting stack with a higher
 * priority the more tags that match, and later rules taking
 * precedence over earlier ones. It's _not_ a full CSS selector
 * syntax though, so things like `.token.italic.bold` aren't
 * supported. Just individual token class names, possibly nested.
 * Also, chalk is not CSS, and a terminal is not a browser, so
 * there are some differences and limitations of course.
 *
 * Aliases are also not supported, styles have to be applied to the
 * actual parsed class names PrismJS provides.
 */
export type Theme = Record<string, Styles> | Map<string, Styles>

type CompiledRule = [stack: string[], styles: StyleFn[]][]

/**
 * A theme that has been compiled for use in highlighting functions
 * Optimized for faster lookup of tokens, with rules sorted based on
 * priority.
 */
type CompiledTheme = Map<string, CompiledRule>

import chalk from 'chalk'
import { readFileSync } from 'fs'
import { readFile } from 'fs/promises'
import { parse } from 'path'
import Prism from 'prismjs'
import stringLength from 'string-length'

// primary use is to highlight ts/js programs, call
// loadLanguages() to support others.
import loadLanguages from 'prismjs/components/index.js'
loadLanguages(['tsx', 'typescript', 'javascript', 'jsx'])

const parseSelector = (s: string): string[][] => {
  const parsed: string[][] = []
  const selectors = s.split(',').map(s => s.trim())
  for (const s of selectors) {
    parsed.push(s.split(/\s+/))
  }
  return parsed
}

const arraysEq = (a: string[], b: string[]) =>
  a.length === b.length && !a.some((aa, i) => aa !== b[i])

const stackMatch = (
  // the stack defined in the rule
  ruleStack: string[],
  // the actual stack
  stack: string[]
) => {
  if (!ruleStack.length) return true
  let j = 0
  for (const t of ruleStack) {
    if (t === stack[j]) {
      j++
      if (j === ruleStack.length) return true
    }
  }
  return false
}

const filterRule = (stack: string[], rule: CompiledRule): CompiledRule =>
  rule.filter(([ruleStack]) => stackMatch(ruleStack, stack))

// return style functions sorted in *ascending* order of priority
const getStyles = (stack: string[], rule: CompiledRule): StyleFn[] => {
  const f = filterRule(stack, rule)
    .sort(([a], [b]) => a.length - b.length)
    .map(([_, r]) => r)
  return f.reduce((s: StyleFn[], r: StyleFn[]) => {
    s.push(...r)
    return s
  }, [])
}

const applyStyles = (
  content: string,
  tag: string,
  stack: string[],
  t: CompiledTheme
) => {
  const rule = t.get(tag)
  if (!rule) return content
  const styles = getStyles(stack, rule)
  for (let i = styles.length - 1; i > -1; i--) {
    content = styles[i](content)
  }
  return content
}

type Tokens = string | Prism.Token | (string | Prism.Token)[]

/**
 * Options for the highlighting functions
 */
export interface PrismJSTerminalOpts {
  /**
   * The theme to use. Either a {@link Theme} object, or a
   * string identifying one of the built-in themes.
   *
   * @default 'moria'
   */
  theme?: keyof typeof themes | Theme

  /**
   * The language of the supplied code to highlight. Defaults to `tsx` if no
   * filename is provided, or else tries to infer the language from the
   * filename. You must have previously called `loadLanguages([...])` from
   * `PrismJS` in order to highlight a given language, if you want something
   * that is not automatically included when `tsx` and `typescript` are
   * included.
   *
   * @default 'tsx'
   */
  language?: string

  /**
   * The minimum width to make the block on the screen.
   *
   * @default 0
   */
  minWidth?: number

  /**
   * The maximum width to make the block on the screen.
   *
   * @default `process.stdout.columns` or `80`.
   */
  maxWidth?: number

  /**
   * How many spaces to horizontally pad the code block.
   *
   * @default 1
   */
  padding?: number

  /**
   * Whether or not to prepend a line number to each line.
   *
   * @default false
   */
  lineNumbers?: boolean
}

const trimTrailingCR = (c: string) =>
  c.endsWith('\n') ? c.substring(0, c.length - 1) : c

const blockStyle = (
  code: string,
  c: CompiledTheme,
  {
    minWidth = 0,
    maxWidth = process.stdout.columns || 80,
    padding = 1,
    lineNumbers = false,
  }: PrismJSTerminalOpts
): string => {
  const lines = trimTrailingCR(code).split('\n')
  const lens: number[] = []
  let max = minWidth
  const npad = lineNumbers ? String(lines.length).length : 0
  const tpad = npad + padding * 2
  for (const l of lines) {
    const len = stringLength(l)
    lens.push(len)
    if (len < maxWidth - tpad && len > max) max = len
  }
  for (let i = 0; i < lens.length; i++) {
    const len = lens[i]
    const pad = max - len + padding
    const r = pad > 0 ? ' '.repeat(pad) : ''
    const l =
      ' '.repeat(padding) +
      (lineNumbers
        ? applyStyles(
            String(i + 1).padStart(npad) + ' ',
            'lineNumber',
            [],
            c
          )
        : '')
    lines[i] = l + lines[i] + r
  }
  code = lines.join('\n') + '\n'
  return applyStyles(code, '_', [], c)
}

/**
 * Highlight the string of code provided, returning the string of highlighted
 * code.
 */
export const highlight = (
  code: string,
  {
    language = 'tsx',
    theme = 'moria',
    minWidth,
    maxWidth,
    padding,
    lineNumbers,
  }: PrismJSTerminalOpts = {}
): string => {
  const t = typeof theme === 'string' ? themes[theme] : theme
  if (!t) {
    throw new Error('invalid theme: ' + theme)
  }
  const c = compileTheme(t)
  return blockStyle(
    stringify(Prism.tokenize(code, Prism.languages[language]), c),
    c,
    { minWidth, maxWidth, padding, lineNumbers }
  )
}

const detectLanguage = (filename: string): string => {
  const { ext } = parse(filename)
  switch (ext) {
    case '.ts':
    case '.mts':
    case '.cts':
      return 'typescript'
    case '.js':
    case '.cjs':
    case '.mjs':
      return 'javascript'
    case '.htm':
      return 'html'
    case '':
    case '.':
      throw new Error('could not detect language for file: ' + filename)
    default:
      // cross our fingers, I guess
      return ext.substring(1)
  }
}

/**
 * Read the filename provided, and highlight its code. If a language is not
 * provided in the opts, it will attempt to infer from the filename.
 */
export const highlightFile = async (
  filename: string,
  opts: PrismJSTerminalOpts = {}
): Promise<string> => {
  if (!opts.language) opts.language = detectLanguage(filename)
  return highlight(await readFile(filename, 'utf8'), opts)
}

/**
 * Read the filename provided, and highlight its code. If a language is not
 * provided in the opts, it will attempt to infer from the filename.
 *
 * Synchronous {@link highlightFile}
 */
export const highlightFileSync = (
  filename: string,
  opts: PrismJSTerminalOpts = {}
): string => {
  if (!opts.language) opts.language = detectLanguage(filename)
  return highlight(readFileSync(filename, 'utf8'), opts)
}

const stringify = (
  tok: Tokens,
  theme: CompiledTheme,
  stack: string[] = []
): string => {
  if (typeof tok === 'string') return tok
  if (Array.isArray(tok)) {
    return tok.map(t => stringify(t, theme, stack)).join('')
  } else {
    return applyStyles(
      stringify(tok.content, theme, [...stack, tok.type]),
      tok.type,
      stack,
      theme
    )
  }
}

const compiledThemes = new Map<Theme, CompiledTheme>()
const compileTheme = (t: Theme): CompiledTheme => {
  const pre = compiledThemes.get(t)
  if (pre) return pre
  if (!(t instanceof Map)) {
    const c = compileTheme(new Map(Object.entries(t)))
    compiledThemes.set(t, c)
    return c
  }
  const c: CompiledTheme = new Map()
  for (const [s, tr] of t.entries()) {
    const selectors = parseSelector(s)
    for (const sel of selectors) {
      // sel is a stack, so `x y z` becomes `['x', 'y', 'z']`
      // add the stack with the rule to the last item,
      // so we add [['x', 'y'], tr] to 'z'
      const last = sel[sel.length - 1]
      sel.pop()
      const cr = c.get(last) || []
      let pushed = false
      for (const [stack, rules] of cr) {
        if (arraysEq(sel, stack)) {
          rules.push(...(Array.isArray(tr) ? tr : [tr]))
          pushed = true
          break
        }
      }
      if (!pushed) cr.push([sel, Array.isArray(tr) ? tr : [tr]])
      if (!c.has(last)) c.set(last, cr)
    }
  }

  const def = c.get('_')
  if (!def) c.set('_', [[[], [chalk.reset]]])

  compiledThemes.set(t, c)
  return c
}

