  // Passed path must be normalized!
  function classifyModuleNonCached(path: string): ModuleTypeClassification {
    const matched = matchPatterns(patternTypePairs, (_) => _.pattern, path);
    if (matched) return classifications[matched.type];
    return auto;
  }

  const classifyModule = cachedLookup(classifyModuleNonCached);

  function classifyModuleAuto(path: String) {
    return auto;
  }

  return {
    classifyModuleByModuleTypeOverrides: patternTypePairs.length ? classifyModule : classifyModuleAuto,
  };
}

function parsePattern(basePath: string, patternString: string): RegExp {
  const pattern = getPatternFromSpec(patternString, basePath);
  return pattern !== undefined ? new RegExp(pattern) : /(?:)/;
}

function matchPatterns<T>(objects: T[], getPattern: (t: T) => RegExp, candidate: string): T | undefined {
  for (let i = objects.length - 1; i >= 0; i--) {
    const object = objects[i];
    const pattern = getPattern(object);

    if (pattern?.test(candidate)) {
      return object;
    }
  }
}

import { resolve } from 'path';
import type { CreateOptions } from '.';
import type { Extensions } from './file-extensions';
import type { TSCommon, TSInternal } from './ts-compiler-types';
import type { ProjectLocalResolveHelper } from './util';

/**
 * @internal
 * In a factory because these are shared across both CompilerHost and LanguageService codepaths
 */
export function createResolverFunctions(kwargs: {
  ts: TSCommon;
  host: TSCommon.ModuleResolutionHost;
  cwd: string;
  getCanonicalFileName: (filename: string) => string;
  config: TSCommon.ParsedCommandLine;
  projectLocalResolveHelper: ProjectLocalResolveHelper;
  options: CreateOptions;
  extensions: Extensions;
}) {
  const { host, ts, config, cwd, getCanonicalFileName, projectLocalResolveHelper, options, extensions } = kwargs;
  const moduleResolutionCache = ts.createModuleResolutionCache(cwd, getCanonicalFileName, config.options);
  const knownInternalFilenames = new Set<string>();
  /** "Buckets" (module directories) whose contents should be marked "internal" */
  const internalBuckets = new Set<string>();

  // Get bucket for a source filename.  Bucket is the containing `./node_modules/*/` directory
  // For '/project/node_modules/foo/node_modules/bar/lib/index.js' bucket is '/project/node_modules/foo/node_modules/bar/'
  // For '/project/node_modules/foo/node_modules/@scope/bar/lib/index.js' bucket is '/project/node_modules/foo/node_modules/@scope/bar/'
  const moduleBucketRe = /.*\/node_modules\/(?:@[^\/]+\/)?[^\/]+\//;
  function getModuleBucket(filename: string) {
    const find = moduleBucketRe.exec(filename);
    if (find) return find[0];
    return '';
  }

  // Mark that this file and all siblings in its bucket should be "internal"
  function markBucketOfFilenameInternal(filename: string) {
    internalBuckets.add(getModuleBucket(filename));
  }

  function isFileInInternalBucket(filename: string) {
    return internalBuckets.has(getModuleBucket(filename));
  }

  function isFileKnownToBeInternal(filename: string) {
    return knownInternalFilenames.has(filename);
  }

  /**
   * If we need to emit JS for a file, force TS to consider it non-external
   */
  const fixupResolvedModule = (resolvedModule: TSCommon.ResolvedModule | TSCommon.ResolvedTypeReferenceDirective) => {
    const { resolvedFileName } = resolvedModule;
    if (resolvedFileName === undefined) return;
    // [MUST_UPDATE_FOR_NEW_FILE_EXTENSIONS]
    // .ts,.mts,.cts is always switched to internal
    // .js is switched on-demand
    if (
      resolvedModule.isExternalLibraryImport &&
      ((resolvedFileName.endsWith('.ts') && !resolvedFileName.endsWith('.d.ts')) ||
        (resolvedFileName.endsWith('.cts') && !resolvedFileName.endsWith('.d.cts')) ||
        (resolvedFileName.endsWith('.mts') && !resolvedFileName.endsWith('.d.mts')) ||
        isFileKnownToBeInternal(resolvedFileName) ||
        isFileInInternalBucket(resolvedFileName))
    ) {
      resolvedModule.isExternalLibraryImport = false;
    }
    if (!resolvedModule.isExternalLibraryImport) {
      knownInternalFilenames.add(resolvedFileName);
    }
  };
  /*
   * NOTE:
   * Older ts versions do not pass `redirectedReference` nor `options`.
   * We must pass `redirectedReference` to newer ts versions, but cannot rely on `options`, hence the weird argument name
   */
  const resolveModuleNames: TSCommon.LanguageServiceHost['resolveModuleNames'] = (
    moduleNames: string[],
    containingFile: string,
    reusedNames: string[] | undefined,
    redirectedReference: TSCommon.ResolvedProjectReference | undefined,
    optionsOnlyWithNewerTsVersions: TSCommon.CompilerOptions,
    containingSourceFile?: TSCommon.SourceFile
  ): (TSCommon.ResolvedModule | undefined)[] => {
    return moduleNames.map((moduleName, i) => {
      const mode = containingSourceFile
        ? (ts as any as TSInternal).getModeForResolutionAtIndex?.(containingSourceFile, i)
        : undefined;
      let { resolvedModule } = ts.resolveModuleName(
        moduleName,
        containingFile,
        config.options,
        host,
        moduleResolutionCache,
        redirectedReference,
        mode
      );
      if (!resolvedModule && options.experimentalTsImportSpecifiers) {
        const lastDotIndex = moduleName.lastIndexOf('.');
        const ext = lastDotIndex >= 0 ? moduleName.slice(lastDotIndex) : '';
        if (ext) {
          const replacements = extensions.tsResolverEquivalents.get(ext);
          for (const replacementExt of replacements ?? []) {
            ({ resolvedModule } = ts.resolveModuleName(
              moduleName.slice(0, -ext.length) + replacementExt,
              containingFile,
              config.options,
              host,
              moduleResolutionCache,
              redirectedReference,
              mode
            ));
            if (resolvedModule) break;
          }
        }
      }
      if (resolvedModule) {
        fixupResolvedModule(resolvedModule);
      }
      return resolvedModule;
    });
  };

  // language service never calls this, but TS docs recommend that we implement it
  const getResolvedModuleWithFailedLookupLocationsFromCache: TSCommon.LanguageServiceHost['getResolvedModuleWithFailedLookupLocationsFromCache'] =
    (
      moduleName,
      containingFile,
      resolutionMode?: TSCommon.ModuleKind.CommonJS | TSCommon.ModuleKind.ESNext
    ): TSCommon.ResolvedModuleWithFailedLookupLocations | undefined => {
      const ret = ts.resolveModuleNameFromCache(moduleName, containingFile, moduleResolutionCache, resolutionMode);
      if (ret && ret.resolvedModule) {
        fixupResolvedModule(ret.resolvedModule);
      }
      return ret;
    };

  const resolveTypeReferenceDirectives: TSCommon.LanguageServiceHost['resolveTypeReferenceDirectives'] = (
    typeDirectiveNames: string[] | readonly TSCommon.FileReference[],
    containingFile: string,
    redirectedReference: TSCommon.ResolvedProjectReference | undefined,
    options: TSCommon.CompilerOptions,
    containingFileMode?: TSCommon.SourceFile['impliedNodeFormat'] | undefined // new impliedNodeFormat is accepted by compilerHost
  ): (TSCommon.ResolvedTypeReferenceDirective | undefined)[] => {
    // Note: seems to be called with empty typeDirectiveNames array for all files.
    // TODO consider using `ts.loadWithTypeDirectiveCache`
    return typeDirectiveNames.map((typeDirectiveName) => {
      // Copy-pasted from TS source:
      const nameIsString = typeof typeDirectiveName === 'string';
      const mode = nameIsString
        ? undefined
        : (ts as any as TSInternal).getModeForFileReference!(typeDirectiveName, containingFileMode);
      const strName = nameIsString ? typeDirectiveName : typeDirectiveName.fileName.toLowerCase();
      let { resolvedTypeReferenceDirective } = ts.resolveTypeReferenceDirective(
        strName,
        containingFile,
        config.options,
        host,
        redirectedReference,
        undefined,
        mode
      );
      if (typeDirectiveName === 'node' && !resolvedTypeReferenceDirective) {
        // Resolve @types/node relative to project first, then __dirname (copy logic from elsewhere / refactor into reusable function)
        let typesNodePackageJsonPath: string | undefined;
        try {
          typesNodePackageJsonPath = projectLocalResolveHelper('@types/node/package.json', true);
        } catch {} // gracefully do nothing when @types/node is not installed for any reason
        if (typesNodePackageJsonPath) {
          const typeRoots = [resolve(typesNodePackageJsonPath, '../..')];
          ({ resolvedTypeReferenceDirective } = ts.resolveTypeReferenceDirective(
            typeDirectiveName,
            containingFile,
            {
              ...config.options,
              typeRoots,
            },
            host,
            redirectedReference
          ));
        }
      }
      if (resolvedTypeReferenceDirective) {
        fixupResolvedModule(resolvedTypeReferenceDirective);
      }
      return resolvedTypeReferenceDirective;
    });
  };

  return {
    resolveModuleNames,
    getResolvedModuleWithFailedLookupLocationsFromCache,
    resolveTypeReferenceDirectives,
    isFileKnownToBeInternal,
    markBucketOfFilenameInternal,
  };
}

import type Module = require('module');
import type { Service } from '.';

/** @internal */
export type ModuleConstructorWithInternals = typeof Module & {
  _resolveFilename(
    request: string,
    parent?: Module,
    isMain?: boolean,
    options?: ModuleResolveFilenameOptions,
    ...rest: any[]
  ): string;
  _preloadModules(requests?: string[]): void;
  _findPath(request: string, paths: string[], isMain: boolean): string;
};

interface ModuleResolveFilenameOptions {
  paths?: Array<string>;
}

/**
 * @internal
 */
export function installCommonjsResolveHooksIfNecessary(tsNodeService: Service) {
  const Module = require('module') as ModuleConstructorWithInternals;
  const originalResolveFilename = Module._resolveFilename;
  const originalFindPath = Module._findPath;
  const shouldInstallHook = tsNodeService.options.experimentalResolver;
  if (shouldInstallHook) {
    const { Module_findPath, Module_resolveFilename } = tsNodeService.getNodeCjsLoader();
    Module._resolveFilename = _resolveFilename;
    Module._findPath = _findPath;
    function _resolveFilename(
      this: any,
      request: string,
      parent?: Module,
      isMain?: boolean,
      options?: ModuleResolveFilenameOptions,
      ...rest: []
    ): string {
      if (!tsNodeService.enabled())
        return originalResolveFilename.call(this, request, parent, isMain, options, ...rest);

      return Module_resolveFilename.call(this, request, parent, isMain, options, ...rest);
    }
    function _findPath(this: any): string {
      if (!tsNodeService.enabled()) return originalFindPath.apply(this, arguments as any);
      return Module_findPath.apply(this, arguments as any);
    }
  }
}

import { readPackageScope } from '../dist-raw/node-internal-modules-cjs-loader';

/**
 * Determine how to emit a module based on tsconfig "module" and package.json "type"
 *
 * Supports module=nodenext/node16 with transpileOnly, where we cannot ask the
 * TS typechecker to tell us if a file is CJS or ESM.
 *
 * Return values indicate:
 * - cjs
 * - esm
 * - nodecjs == node-flavored cjs where dynamic imports are *not* transformed into `require()`
 * - undefined == emit according to tsconfig `module` config, whatever that is
 * @internal
 */
export function classifyModule(
  nativeFilename: string,
  isNodeModuleType: boolean
): 'nodecjs' | 'cjs' | 'esm' | 'nodeesm' | undefined {
  // [MUST_UPDATE_FOR_NEW_FILE_EXTENSIONS]
  const lastDotIndex = nativeFilename.lastIndexOf('.');
  const ext = lastDotIndex >= 0 ? nativeFilename.slice(lastDotIndex) : '';
  switch (ext) {
    case '.cjs':
    case '.cts':
      return isNodeModuleType ? 'nodecjs' : 'cjs';
    case '.mjs':
    case '.mts':
      return isNodeModuleType ? 'nodeesm' : 'esm';
  }
  if (isNodeModuleType) {
    const packageScope = readPackageScope(nativeFilename);
    if (packageScope && packageScope.data.type === 'module') return 'nodeesm';
    return 'nodecjs';
  }
  return undefined;
}

import type * as _ts from 'typescript';
import type { RegisterOptions } from '.';
import { versionGteLt } from './util';

/**
 * Centralized specification of how we deal with file extensions based on
 * project options:
 * which ones we do/don't support, in what situations, etc.  These rules drive
 * logic elsewhere.
 * @internal
 * */
export type Extensions = ReturnType<typeof getExtensions>;

const nodeEquivalents = new Map<string, string>([
  ['.ts', '.js'],
  ['.tsx', '.js'],
  ['.jsx', '.js'],
  ['.mts', '.mjs'],
  ['.cts', '.cjs'],
]);

const tsResolverEquivalents = new Map<string, readonly string[]>([
  ['.ts', ['.js']],
  ['.tsx', ['.js', '.jsx']],
  ['.mts', ['.mjs']],
  ['.cts', ['.cjs']],
]);

// All extensions understood by vanilla node
const vanillaNodeExtensions: readonly string[] = ['.js', '.json', '.node', '.mjs', '.cjs'];

// Extensions added by vanilla node's require() if you omit them:
// js, json, node
// Extensions added by vanilla node if you omit them with --experimental-specifier-resolution=node
// js, json, node, mjs
// Extensions added by ESM codepath's legacy package.json "main" resolver
// js, json, node (not mjs!)

const nodeDoesNotUnderstand: readonly string[] = ['.ts', '.tsx', '.jsx', '.cts', '.mts'];

export function tsSupportsMtsCtsExts(tsVersion: string) {
  return versionGteLt(tsVersion, '4.5.0');
}

/**
 * [MUST_UPDATE_FOR_NEW_FILE_EXTENSIONS]
 * @internal
 */
export function getExtensions(config: _ts.ParsedCommandLine, options: RegisterOptions, tsVersion: string) {
  // TS 4.5 is first version to understand .cts, .mts, .cjs, and .mjs extensions
  const supportMtsCtsExts = tsSupportsMtsCtsExts(tsVersion);

  const requiresHigherTypescriptVersion: string[] = [];
  if (!tsSupportsMtsCtsExts) requiresHigherTypescriptVersion.push('.cts', '.cjs', '.mts', '.mjs');

  const allPossibleExtensionsSortedByPreference = Array.from(
    new Set([
      ...(options.preferTsExts ? nodeDoesNotUnderstand : []),
      ...vanillaNodeExtensions,
      ...nodeDoesNotUnderstand,
    ])
  );

  const compiledJsUnsorted: string[] = ['.ts'];
  const compiledJsxUnsorted: string[] = [];

  if (config.options.jsx) compiledJsxUnsorted.push('.tsx');
  if (supportMtsCtsExts) compiledJsUnsorted.push('.mts', '.cts');
  if (config.options.allowJs) {
    compiledJsUnsorted.push('.js');
    if (config.options.jsx) compiledJsxUnsorted.push('.jsx');
    if (supportMtsCtsExts) compiledJsUnsorted.push('.mjs', '.cjs');
  }

  const compiledUnsorted = [...compiledJsUnsorted, ...compiledJsxUnsorted];
  const compiled = allPossibleExtensionsSortedByPreference.filter((ext) => compiledUnsorted.includes(ext));

  const compiledNodeDoesNotUnderstand = nodeDoesNotUnderstand.filter((ext) => compiled.includes(ext));

  /**
   * TS's resolver can resolve foo.js to foo.ts, by replacing .js extension with several source extensions.
   * IMPORTANT: Must preserve ordering according to preferTsExts!
   *            Must include the .js/.mjs/.cjs extension in the array!
   *            This affects resolution behavior!
   * [MUST_UPDATE_FOR_NEW_FILE_EXTENSIONS]
