import { fileURLToPath } from 'url'

const kLLC = Symbol.for('@tapjs/processinfo lineLength cache')
const g = global as {
  [kLLC]?: Map<string, number[]>
}
const cache = g[kLLC] || new Map<string, number[]>()
g[kLLC] = cache

const sourceMapComment = '//# sourceMappingURL='
export const saveLineLengths = (
  filename: string,
  content?: string | Buffer
) => {
  if (content === undefined) return
  if (typeof content === 'object') content = String(content)
  if (filename.startsWith('file://')) filename = fileURLToPath(filename)
  // no need if it's not sourcemapped
  // don't cache an empty array, though, because ts-node files will show
  // up first as source, and then as their built content.
  const last = content.trimEnd().split('\n').pop()
  if (cache.has(filename) || !last?.startsWith(sourceMapComment)) return
  const ll = content.split(/\n|\u2028|\u2029/).map(l => l.length)

  cache.set(filename, ll)
}

export const getLineLengths = (filename: string) => {
  if (filename.startsWith('file://')) filename = fileURLToPath(filename)
  return cache.get(filename)
}

// For some reason that is very mysterious as of the time of writing this,
// node sporadically will in rare cases hang and fail to gracefully exit if
// a sufficiently large number of findSourceMap calls are made during the
// process exit event.
//
// However, we cannot look up source maps until *after* the module load
// event is completely finished and the module is about to be executed,
// because that is when the source map is added to node's cache.
//
// To work around this, every time a module is loaded, we attempt to
// determine whether it likely has the magic sourceMappingURL comment.
// If so, then we put it in a list, and at each message, attempt to load
// the sources for all modules in the list. Then, on process exit, if
// there's anything still pending that likely has a source map, we only
// have to look up at most one module (ie, if the last module loaded had a
// source map), which seems to not trigger the hang.

import { findSourceMapSafe } from './find-source-map-safe.js'

// the list of modules that likely have source maps
const maybeSM = new Set<string>()
export const sourcesCache = new Map<string, string[]>()

export const loadPendingSourceMaps = () => {
  for (const url of maybeSM) {
    const sm = findSourceMapSafe(url)
    // only possible on node 19+
    /* c8 ignore start */
    if (sm === false) {
      // can only happen if node found the SM comment, and tried to load it,
      // but got an error creating the sourcemap, because it's invalid or
      // the file is not present. No need to keep trying.
      maybeSM.delete(url)
    } else {
      /* c8 ignore stop */
      const sources = sm?.payload?.sources
      if (sources) {
        sourcesCache.set(url, sources)
        maybeSM.delete(url)
      }
    }
  }
}

export const lookupSources = (url: string, processEnd?: boolean) =>
  getSources(processEnd).get(url)

let didFinalLookupAttempt = false
export const getSources = (processEnd: boolean = false) => {
  if (maybeSM.size && (!processEnd || !didFinalLookupAttempt)) {
    if (processEnd) didFinalLookupAttempt = true
    loadPendingSourceMaps()
  }
  return sourcesCache
}

export const likelyHasSourceMap = (url: string) => {
  if (!sourcesCache.has(url)) maybeSM.add(url)
  loadPendingSourceMaps()
}

// This is the machinery that supports the load() and resolve()
// methods used by the loader, returning the appropriate urls
// and source.
// It runs on the main thread, not the loader thread.
//
// The globalPreload/initialize inits the port and grabs a reference to it
// in the loader thread.
// Then the loader asks the service over that port for the mocked url and
// source code, which is sent back.
//
// The mock-import module creates a MockService instance when t.mockImport
// is called, giving it a key and setting up its mocks, and calls
// import(startURL) as the return value.
//
// There should be at most 1 MockService instance for a given tap test,
// with its own unique key that's used for loading.
//
// There are some weird lines ignored for coverage in this file, owing to the
// fact that it is loaded as part of tap's initial startup, prior to
// instrumenting coverage, and loaded very differently in node 20 vs prior
// versions that ran loaders on the main thread..

import type { CallSiteLike, CallSiteLikeJSON } from '@tapjs/stack'
import * as stack from '@tapjs/stack'
import { randomBytes } from 'crypto'
import { dirname, isAbsolute } from 'path'
import { resolveImport } from 'resolve-import'
import { isRelativeRequire } from 'resolve-import/is-relative-require'
import { pathToFileURL } from 'url'
import { MessagePort } from 'worker_threads'
import { exportLine } from './export-line.js'
import { mungeMocks } from './munge-mocks.js'
import { resolveMockEntryPoint } from './resolve-mock-entry-point.js'
import { serviceKey } from './service-key.js'

const { hasOwnProperty } = Object.prototype
const hasOwn = (o: any, k: PropertyKey) => hasOwnProperty.call(o, k)
const getKey = () => randomBytes(8).toString('hex')

/**
 * Build the source code for a mocked module.
 */
const buildSrc = (m: MockService, url: string) => {
  const mock = m?.mocks?.[url]
  /* c8 ignore start */
  if (!mock) return
  /* c8 ignore stop */

  const { key } = m

  const keySrc = `__tapmock${serviceKey}$${key}`
  const mockSrc = `global[${keySrc}].mocks[${JSON.stringify(url)}]`
  const src = Object.keys(mock).map(k => exportLine(k, mockSrc))
  if (!Object.keys(mock).includes('default')) {
    src.push(`const defExp = ${mockSrc}
export default defExp\n`)
  }
  const symFor = JSON.stringify(keySrc)
  return `const ${keySrc} = Symbol.for(${symFor})\n${src.join('\n')}`
}

export type MockServiceLoadRequest = {
  action: 'load'
  url: string
  id: string
}
export type MockServiceResolveRequest = {
  action: 'resolve'
  url: string
  parentURL: string
  id: string
}

export type MockServiceRequest =
  | MockServiceLoadRequest
  | MockServiceResolveRequest

export type MockServiceResponse = MockServiceRequest & {
  // a false response means "not a mocked module"
  response: string | undefined
}

export const isMockServiceResponse = (
  m: any,
): m is MockServiceResponse =>
  isMockServiceRequest(m) &&
  Object.keys(m).includes('response') &&
  (typeof (m as MockServiceResponse).response === 'string' ||
    typeof (m as MockServiceResponse).response === 'undefined')

export const isMockServiceRequest = (
  m: any,
): m is MockServiceRequest =>
  !!m &&
  typeof m === 'object' &&
  ((m.action === 'resolve' && typeof m.parentURL === 'string') ||
    (m.action === 'load' && m.parentURL === undefined)) &&
  typeof m.url === 'string' &&
  typeof m.id === 'string'

// keys and instances across dialects, so we get the right one when
// it's loaded from the commonjs globalPreload, or just a cjs test
const kInstances = Symbol.for(`${serviceKey}.instances`)
const g = globalThis as typeof globalThis & {
  [kInstances]?: Map<string, MockService>
}

// this gets called at startup before coverage is initiated,
// so it never covers the second case where it isn't already set.
/* c8 ignore start */
const instances = g[kInstances] || new Map<string, MockService>()
/* c8 ignore stop */
g[kInstances] = instances

const mockServiceCtorSymbol = Symbol('private')
export class MockService {
  static #port?: MessagePort
  key: string = getKey()
  module?: string | Promise<string>
  mocks?: Record<string, Record<string, any>>
  caller?: {
    path: string
    dir: string
    url: URL
    at: CallSiteLike | CallSiteLikeJSON
    stack?: string
  }

  constructor(priv: typeof mockServiceCtorSymbol) {
    if (priv !== mockServiceCtorSymbol) {
      throw new Error('create MockService with MockService.get()')
    }
    instances.set(this.key, this)
  }

  // pass in the main-thread end of the loader port set, and respond
  // appropriately to messages we can handle
  /* c8 ignore start */
  static async listen(port: MessagePort) {
    this.#port = port
    port.on('message', async msg => {
      /* c8 ignore stop */
      /* c8 ignore start */
      if (!isMockServiceRequest(msg)) return
      /* c8 ignore stop */
      const r = await this.handle(msg).catch(e => console.error(e))
      // typescript handles 'void' weirdly
      const response = r === undefined ? undefined : r
      const msr: MockServiceResponse = { ...msg, response }
      port.postMessage(msr)
      /* c8 ignore start */
    })
    port.unref()
  }
  /* c8 ignore stop */

  static async handle(msg: any) {
    if (!isMockServiceRequest(msg)) return
    return msg.action === 'resolve' ?
        this.resolve(msg)
      : this.load(msg)
  }

  static async resolve(req: MockServiceResolveRequest) {
    const { parentURL } = req
    const p = new URL(parentURL)
    const [sk, k] = (p.searchParams.get('tapmock') || '').split('.')
    if (sk !== serviceKey || !k) return
    return this.get(k).resolve(req)
  }
  async resolve({ url, parentURL }: MockServiceResolveRequest) {
    const resolvedURL =
      hasOwn(this.mocks, url) ? url
      : isRelativeRequire(url) ? String(new URL(url, parentURL))
      : isAbsolute(url) ? String(pathToFileURL(url))
      : url

    if (!hasOwn(this.mocks, resolvedURL)) {
      // parent is mocked, but this module isn't, so the things IT loads
      // should be loaded from the mock, even though it isn't. Need to
      // resolveImport() here so that it doesn't get confused when
      // loading deps out of node_modules.
      //
      // If a node builtin is mocked, and another builtin references it, then
      // the builtin will get the original builtin, not the mock. This is a
      // shortcoming owing to the fact that there's no way here to tack a
      // search param on the "url" for an internal module. If it causes
      // problems, then the solution could be to swap out internal modules with
      // a known url type like tapmockBuiltin://node:fs or something, and use
      // that as the indicator that its builtin deps might need to be mocked.
      // For now, it's just a known design limitation, because that's a bit
      // tricky to get right.
      let mocker = await resolveImport(url, parentURL).catch(() => {})
      // we can't resolve it, but maybe someone else can.
      // if RI gives us a string, then it's a builtin, do nothing
      if (!mocker || typeof mocker !== 'object') return
      mocker.searchParams.set('tapmock', `${serviceKey}.${this.key}`)
      return String(mocker)
    }

    const mockRes = new URL(`tapmock://${serviceKey}.${this.key}/`)
    mockRes.searchParams.set('url', resolvedURL)
    return String(mockRes)
  }

  static async load(req: MockServiceLoadRequest) {
    const { url } = req
    if (!url.startsWith(`tapmock://${serviceKey}.`)) return
    /* c8 ignore start */
    const u = new URL(url)
    /* c8 ignore stop */
    const [_, key] = u.host.split('.')
    /* c8 ignore start */
    if (!key) return
    /* c8 ignore stop */
    return this.get(key).load(req)
  }
  async load({ url }: MockServiceLoadRequest) {
    if (!url.startsWith('tapmock://')) return
    const u = new URL(url)
    const key = u.host
    const mockURL = u.searchParams.get('url')
    if (key !== `${serviceKey}.${this.key}` || !mockURL) return
    return buildSrc(this, mockURL)
  }

  static async create(
    module: string,
    mocks: Record<string, any> = {},
    caller: Function | ((...a: any[]) => any) = MockService.create,
  ): Promise<MockService & { module: string | Promise<string> }> {
    const ms = new MockService(mockServiceCtorSymbol)

    /* c8 ignore start */
    const needIgnoreTap = !stack
      .getIgnoredPackages()
      .includes('@tapjs')
    if (needIgnoreTap) stack.addIgnoredPackage('@tapjs')
    const at = stack.at(caller)
    if (needIgnoreTap) stack.removeIgnoredPackage('@tapjs')

    const path = at?.absoluteFileName
    if (!path) {
      throw new Error('could not get current call site')
    }
    /* c8 ignore stop */

    // tell the loader hooks thread that it's ok to start using it now.
    this.#port?.postMessage({ start: true })

    const dir = dirname(path)
    const url = pathToFileURL(path)

    const resolved = resolveMockEntryPoint(
      url,
      module,
      serviceKey,
      ms.key,
      caller,
    )
    resolved.then(s => (ms.module = s))

    ms.mocks = mungeMocks(mocks, dir)
    ms.caller = {
      path,
      dir,
      url,
      at,
      stack: stack.captureString(caller),
    }
    const sym = Symbol.for(`__tapmock${serviceKey}$${ms.key}`)
    const g = globalThis as typeof globalThis & {
      [sym]?: MockService
    }
    return (g[sym] = Object.assign(ms, { module: resolved }))
  }

  unmock() {
    const sym = Symbol.for(`__tapmock${serviceKey}$${this.key}`)
    const g = globalThis as typeof globalThis & {
      [sym]?: MockService
    }
    delete g[sym]
    instances.delete(this.key)
    this.mocks = undefined
  }

  static get(key: string) {
    const i = instances.get(key)
    if (!i) {
      throw new Error('mock service instance key not found')
    }
    return i
  }
}

// we always want this
const p = process as NodeJS.Process & {
  setSourceMapsEnabled(v: boolean): void
  _eval?: string
}
p.setSourceMapsEnabled(true)

import { v4 as uuid } from 'uuid'
import { getMain } from './get-main.js'
import { ProcessInfoNodeData } from './process-info-node.js'

const envKey = (k: string) => `_TAPJS_PROCESSINFO_${k.toUpperCase()}_`
const getEnv = (k: string) => p.env[envKey(k)]
const setEnv = (k: string, v: string) => (p.env[envKey(k)] = v)
const delEnv = (k: string) => delete p.env[envKey(k)]

import { register as registerCoverage } from './register-coverage.js'
import { register as registerEnv } from './register-env.js'
import { register as registerProcessEnd } from './register-process-end.js'
import { register as registerRequire } from './register-require.js'

// this module is hybridized.  In node v20.0 - v20.6, it's the *commonjs* one
// that gets loaded, because the esm loader context can't modify the main
// thread except via communication over the port to the globalPreload env. So,
// we have to store our singleton on the global.
//
// If it later loads the esm form of this module, that's fine, because it'll
// see the global processInfo object, and not re-register anything.
//
// CJS registration can be removed once node < 20.6 is no longer supported.

const kProcessInfo = Symbol.for('@tapjs/processinfo.ProcessInfoNodeData')
const g = global as typeof globalThis & {
  [kProcessInfo]?: ProcessInfoNodeData
}

// only used for tests so we can simulate multiple processes
export const reset = () => {
  g[kProcessInfo] = undefined
  return { getProcessInfo }
}

export const getProcessInfo = (): ProcessInfoNodeData => {
  if (g[kProcessInfo]) return g[kProcessInfo]

  g[kProcessInfo] = {
    hrstart: p.hrtime(),
    date: new Date().toISOString(),
    argv: p.argv,
    execArgv: p.execArgv,
    NODE_OPTIONS: p.env.NODE_OPTIONS,
    cwd: p.cwd(),
    pid: p.pid,
    ppid: p.ppid,
    parent: getEnv('parent') || null,
    uuid: uuid(),
    files: [getMain()],
    sources: Object.create(null),
  }

  if (!g[kProcessInfo].parent) {
    g[kProcessInfo].root = g[kProcessInfo].uuid
    setEnv('root', g[kProcessInfo].uuid)
  } else {
    g[kProcessInfo].root = getEnv('root')
  }
  // this is the parent of any further child processes
  setEnv('parent', g[kProcessInfo].uuid)
  const externalID = getEnv('external_id')
  if (externalID) {
    g[kProcessInfo].externalID = externalID
    // externalID only applies to ONE process, not all its children.
    delEnv('external_id')
  }

  // switch to turn off registration for some tests.
  // excluded from coverage because that's the reason why it's here
  // in the first place, it confuses c8.
  /* c8 ignore start */
  if (
    process.env.__TAPJS_PROCESSINFO_TESTING_NO_REGISTER__ !==
    String(process.pid)
  ) {
    registerRequire()
    registerEnv()
    registerCoverage()
    registerProcessEnd()
  }
  /* c8 ignore stop */

  return g[kProcessInfo]
}

import { findSourceMap, SourceMap } from 'module'
import { pathToURL } from './path-to-url.js'

const sourceMaps = new Map<string, SourceMap>()

export const findSourceMapSafe = (
  s: string | URL
): false | undefined | SourceMap => {
  // Have to look up by URL, because the ?tapmock param will be in
  // the internal key used by node, as those are "different" modules.
  const mod = pathToURL(s)
  const c = sourceMaps.get(mod)
  if (c) return c

  // this can throw in some cases, eg if the sourcemap file is missing
  try {
    const sm = findSourceMap(mod)
    if (sm) sourceMaps.set(mod, sm)
    return sm
    // only throws on node 20
    /* c8 ignore start */
  } catch {
    return false
  }
  /* c8 ignore stop */
}

import { builtinModules } from 'node:module'
import { resolve } from 'node:path'
import { CallSiteLike } from './call-site-like.js'
import { requireResolve } from './require-resolve.js'
export { CallSiteLike, CallSiteLikeJSON } from './call-site-like.js'
export type { GeneratedResult } from './call-site-like.js'
export type { Compiled, LineRef } from './parse.js'

const regExpEscape = (s: string) =>
  s.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&')

// platform portability accommodation
/* c8 ignore start */
let cwd =
  (
    typeof process === 'object' &&
    process &&
    typeof process.cwd === 'function'
  ) ?
    process.cwd()
  : undefined
/* c8 ignore stop */

/**
 * Set the effective cwd for shortening filenames in stack traces
 * Set to `undefined` to show full absolute paths.
 */
export const setCwd = (c: string | undefined) => (cwd = c)

/**
 * Get the effective cwd for shortening filenames in stack traces
 * If set to `undefined`, then will show full absolute paths.
 */
export const getCwd = () => cwd

let filterNodeInternals = true
/**
 * Get the current value indicating whether node internals should be
 * filtered out. (Defaults to true)
 */
export const getFilterNodeInternals = () => filterNodeInternals
/**
 * Set whether node internals should be filtered out.
 */
export const setFilterNodeInternals = (s: boolean) =>
  (filterNodeInternals = s)

// these are packages that function somewhat like node internals,
// for tap's purposes, and `@tapjs` packages themselves, when loaded
// from node_modules.
const ignoredPackages: string[] = [
  '@tapjs',
  '@isaacs/ts-node-temp-fork-for-pr-2009',
  'pirates',
  'function-loop',
  '@cspotcode/source-map-support',
  'signal-exit',
  'async-hook-domain',
]
let dirty: boolean = false

/**
 * Add a package name to the list of deps that should be excluded
 * from stack traces.
 */
export const addIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i === -1) {
    ignoredPackages.push(s)
    dirty = true
  }
}

/**
 * Remove a package name from the list of deps that should be excluded
 * from stack traces.
 */
export const removeIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i !== -1) {
    ignoredPackages.splice(i, 1)
    dirty = true
  }
}

/**
 * Get a read-only copy of the list of deps that should be excluded
 * from stack traces.
 */
export const getIgnoredPackages = () =>
  Object.freeze(ignoredPackages.slice(0))

const getTestBuiltPath = () => {
  const p = requireResolve('@tapjs/test')
  // we'll always find the test class in this project
  /* c8 ignore start */
  if (!p) return ''
  /* c8 ignore stop */
  return resolve(p, '../../../test-built')
}

const buildIgnoredPackages = () => {
  // just a safety precaution, no reason to ever do this
  /* c8 ignore start */
  if (!ignoredPackages.length) return undefined
  /* c8 ignore stop */
  const p = ignoredPackages.map(s => regExpEscape(s)).join('|')
  const nm = `[/\\\\]node_modules[/\\\\](?:${p})([/\\\\]|$)`
  // if we are ignoring @tapjs/test, then also ignore its built
  // plugged-in implementation. This is only relevant when developing
  // this project, or other cases where @tapjs/test may be linked,
  // because when it's loaded from node_modules, it'll be excluded
  // by virtue of being in that folder anyhow.
  const built =
    (
      ignoredPackages.includes('@tapjs') ||
      ignoredPackages.includes('@tapjs/test')
    ) ?
      getTestBuiltPath()
    : ''
  const re = built ? `${built}([/\\\\].*|$)|${nm}` : nm
  return new RegExp(re)
}

/**
 * exported for testing, no real purpose, but also no harm in looking
 */
export const getIgnoredPackagesRE = () =>
  !dirty ? ignoredPackagesRE : (
    (ignoredPackagesRE = buildIgnoredPackages())
  )

let ignoredPackagesRE: RegExp | undefined = buildIgnoredPackages()
let filterIgnoredPackages = true

/**
 * Set whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const setFilterIgnoredPackages = (s: boolean) =>
  (filterIgnoredPackages = s)

/**
 * Get whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const getFilterIgnoredPackages = () => filterIgnoredPackages

// detect the first line of Error.stack, 'Error: blah'
const isErrorStackHead = (c?: CallSiteLike): boolean =>
  !!c &&
  c.lineNumber === null &&
  c.columnNumber === null &&
  c.this === undefined &&
  c.evalOrigin === undefined &&
  c.function === undefined &&
  c.typeName === null &&
  c.methodName === null &&
  (typeof c.functionName === 'string' || !c.functionName) &&
  c.isEval === false &&
  c.isNative === false &&
  c.isToplevel === false &&
  c.isConstructor === false &&
  c.generated === undefined

const filter = (c: CallSiteLike): boolean => {
  const s = c.fileName
  // technically this is possible, but super unlikely
  /* c8 ignore start */
  if (!s) return true
  /* c8 ignore stop */
  if (dirty && filterIgnoredPackages) {
    ignoredPackagesRE = buildIgnoredPackages()
    dirty = false
  }
  return (
    (!filterNodeInternals ||
      !(s.startsWith('node:') || builtinModules.includes(s))) &&
    (!filterIgnoredPackages || !ignoredPackagesRE?.test(s))
  )
}

const clean = (c: CallSiteLike[]): CallSiteLike[] => {
  const filtered = c.filter(filter)
  while (isErrorStackHead(filtered[0])) filtered.shift()
  if (cwd !== undefined) {
    for (const c of filtered) {
      c.cwd = cwd
    }
  }
  return filtered
}

let capturing = false
/**
 * Get an array of {@link @tapjs/stack!call-site-like.CallSiteLike} objects for
 * the current location, from the call to the `fn` argument supplied, limited
 * to the number of frames specified by `limit`.
 *
 * If the `limit` argument is 0, then the current `Error.stackTraceLimit`
 * value will be used.
 *
 * This method is not re-entry safe, due to the fact that it relies on
 * temporarily overriding the global Error.prepareStackTrace. As a result,
 * if a capture() is triggered in any of the methods used by the
 * CallSiteLike constructor (for example, if `@tapjs/intercept` is used to
 * capture the process.cwd() method, which is used by path.resolve()),
 * then that will cause problems. To work around this, if a re-entry is
 * detected, then an empty stack of [] is returned.
 *
 * Even if it was made re-entry safe, it would be easy to accidentally
 * trigger an infinite recursion and stack overflow in such a scenario, so
 * returning an empty stack in the case of re-entry is the best workaround.
 */
export function capture(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(limit: number): CallSiteLike[]
export function capture(
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(): CallSiteLike[]
export function capture(
  limit: number | Function | ((...a: any[]) => any) = 0,
  fn: Function | ((...a: any[]) => any) = capture,
): CallSiteLike[] {
  // should be impossible to hit, but can if we have to look up
  // a sourcemap for a file we haven't seen before, and the filename
  // happens to come through as a url or relative path.
  /* c8 ignore start */
  if (capturing) return []
  /* c8 ignore stop */
  capturing = true
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const { prepareStackTrace, stackTraceLimit } = Error
  Error.prepareStackTrace = CallSiteLike.prepareStackTrace
  if (limit) {
    // we always get an extra few frames to account for internals
    // or proxy frames that might be filtered out of the top.
    Error.stackTraceLimit = limit + 10
  }
  const obj: { stack: CallSiteLike[] } = { stack: [] }
  Error.captureStackTrace(obj, fn)
  const { stack } = obj
  Object.assign(Error, { prepareStackTrace, stackTraceLimit })
  const st = clean(stack)
  capturing = false
  return limit === 0 ? st : st.slice(0, limit)
}

/**
 * Get the call site in the stack either where `at()` is called, or
 * where the supplied `fn` function is called.
 *
 * If `fn` is provided, and is not in the current call stack, then
 * `undefined` will be returned.
 */
export const at: (
  fn?: Function | ((...a: any[]) => any),
) => CallSiteLike | undefined = (fn = at) => {
  const [site] = capture(1, fn)
  return site
}

/**
 * Same as {@link @tapjs/stack!index.capture}, but return the `toString()`
 * values of the {@link @tapjs/stack!call-site-like.CallSiteLike} objects
 */
export function captureString(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): string
export function captureString(limit: number): string
export function captureString(
  fn?: Function | ((...a: any[]) => any),
): string
export function captureString(): string
export function captureString(
  limit: number | Function | ((...a: any[]) => any) = Infinity,
  fn: Function | ((...a: any[]) => any) = captureString,
): string {
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const stack = capture(limit, fn)
  return (limit === 0 ? stack : stack.slice(0, limit))
    .map(c => String(c) + '\n')
    .join('')
}

/**
 * Get an array of {@link CallSiteLike} objects corresponding to the stack
 * trace of the Error object provided.
 *
 * This does _not_ actually look at the current call site, or do anything
 * magical with the V8 engine. It's just parsing a string.
 *
 * While some effort is made to interpret stacks correctly when an Error
 * contains a `name` and `message`, remember that the `Error.stack` property in
 * JavaScript is remarkably sloppy. In some cases, if the `Error.message`
 * contains `\n` and some lines after the first look like stack trace lines,
 * incorrect data may result. It's only as good as the stack you pass to it.
 */
export const captureError = (
  e: Error | NodeJS.ErrnoException,
): CallSiteLike[] => {
  // errors almost always have these fields
  const { message = '', name = '', code } = e as NodeJS.ErrnoException
  const stack = typeof e.stack === 'string' ? e.stack : ''
  const head = name && message ? `${name}: ${message}\n` : ''
  const errnoHead =
    name && message && code ? `${name} [${code}]: ${message}` : ''
  const cleanHead = !!head && stack.startsWith(head)
  const cleanErrnoHead = !!errnoHead && stack.startsWith(errnoHead)

  const s =
    cleanHead ? stack.substring(head.length)
    : cleanErrnoHead ? stack.substring(errnoHead.length)
    : stack
  const cleaned = clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l)
      .map(line => new CallSiteLike(e, line)),
  )

  // if we didn't clean the header cleanly, then sweep the stack for
  // any weird junk it might contain
  return cleanHead ? cleaned : (
      cleaned.filter(c => !isErrorStackHead(c))
    )
}

/**
 * Get a processed string stack corresponding to the stack trace of the Error
 * object provided.
 *
 * This method has all the same caveats as {@link captureError}. If the
 * object provided has a weird looking `stack` property, then you might get
 * weird results.
 */
export const captureErrorString = (e: Error): string =>
  captureError(e)
    .map(c => String(c) + '\n')
    .join('')

/**
 * Parse a stack string and return an array of CallSiteLike objects
 *
 * We use this to get the `at` diagnostic callsite when all we have is
 * a stack, either from a native Error object, or a stringified CallSiteLike
 * stack.
 */
export const parseStack = (s: string): CallSiteLike[] =>
  clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l.trim())
      .map(line => new CallSiteLike(null, line)),
  )

export const expandStack = (s?: string | CallSiteLike[]): string => {
  if (!s) return ''
  if (typeof s === 'string') return expandStack(parseStack(s))
  return clean(s)
    .map(c => c.toString(true) + '\n')
    .join('')
}

/**
 * utility used in creating the source of a mocked import module
 * @module
 */

const exportIndex = new Map<string, number>()

/**
 * Create the `export` lines in the generated module source
 */
export const exportLine = (k: string, mockSrc: string): string => {
  if (k === 'default') {
    return `const defExp = ${mockSrc}.default
export default defExp
`
  }
  const kSrc = JSON.stringify(k)

  const i = exportIndex.get(mockSrc) || 0
  exportIndex.set(mockSrc, i + 1)
  return `const exp${i} = ${mockSrc}[${kSrc}]
export { exp${i} as ${kSrc} }
`
}

import { isBuiltin } from 'module'
import { isAbsolute, resolve } from 'path'
import { isRelativeRequire } from 'resolve-import/is-relative-require'
import { pathToFileURL } from 'url'

// turn all the mocks we get into either the exports provided,
// if it's an object, or { default: <value> } otherwise.

export const mungeMocks = (
  mocksInput: Record<string, any>,
  dir: string,
): { [k: string]: { [j: string]: any } } => {
  const mocks = Object.create(null)
  for (const [k, v] of Object.entries(mocksInput)) {
    const m =
      v && typeof v === 'object' && !Array.isArray(v) ?
        v
      : { default: v }
    if (isBuiltin(k)) {
      mocks[k] = m
      /* c8 ignore start */
      if (k.startsWith('node:')) {
        /* c8 ignore stop */
        const bare = k.substring('node:'.length)
        if (!(bare in mocksInput)) {
          mocks[bare] = m
        }
      } else {
        const prefixed = `node:${k}`
        if (!(prefixed in mocksInput)) {
          mocks[prefixed] = m
        }
      }
    } else if (/^(file:\/\/\/|https?:\/\/)/.test(k)) {
      mocks[k] = m
    } else if (isRelativeRequire(k) || isAbsolute(k)) {
      mocks[String(pathToFileURL(resolve(dir, k)))] = m
    } else {
      // absolute package name
      mocks[k] = m
    }
  }
  return mocks
}

import { resolveImport } from 'resolve-import'

export const resolveMockEntryPoint = async (
  url: URL,
  module: string,
  serviceKey: string,
  key: string,
  caller: Function | ((...a: any[]) => any),
): Promise<string> => {
  let mockedModuleURL: URL
  if (module.startsWith('./') || module.startsWith('../')) {
    mockedModuleURL = new URL(module, url)
  } else {
    const res = (await resolveImport(module, url)) as URL
    // caught at the exposed API, defense in depth only
    // but the experience if it throws here is unhelpful.
    /* c8 ignore start */
    if (typeof res === 'string') {
      const er = new TypeError(
        'node builtins cannot be mock imported',
      )
      Error.captureStackTrace(er, caller)
      throw er
    }
    /* c8 ignore stop */
    mockedModuleURL = res
  }
  mockedModuleURL.searchParams.set('tapmock', `${serviceKey}.${key}`)
  return String(mockedModuleURL)
}

//@ts-ignore
import serviceKeyCJS from '../commonjs/service-key.js'
export const { serviceKey } = serviceKeyCJS as { serviceKey: string }

// commonjs style loader hook, modifying require.extensions
import { addHook } from 'pirates'
import { getExclude } from './get-exclude.js'
import { getProcessInfo } from './get-process-info.js'
import { saveLineLengths } from './line-lengths.js'

const kRegisterRequire = Symbol.for('@tapjs/processinfo.registerRequire')
const g = global as typeof globalThis & {
  [kRegisterRequire]?: boolean
}

export const register = () => {
  if (g[kRegisterRequire]) return
  g[kRegisterRequire] = true
  // by default we include everything in processInfo.files
  const exclude = getExclude('_TAPJS_PROCESSINFO_EXCLUDE_', false)
  addHook(
    (code, filename) => {
      getProcessInfo().files.push(filename)
      saveLineLengths(filename, code)
      return code
    },
    {
      exts: ['.js', '.cjs', '.ts', '.cts', '.jsx', '.tsx'],
      matcher: filename => !exclude.test(filename),
    }
  )
}

import processOnSpawn from 'process-on-spawn'
import { nodeOptionsEnv } from './node-options-env.js'

const p = process
const envRE = /^_TAPJS_PROCESSINFO_/
const { hasOwnProperty } = Object.prototype

const getEnvs = (env?: NodeJS.ProcessEnv, args: readonly string[] = []) => {
  // load it here so that it isn't cached before the loader attaches
  // in self-test scenario.
  // copy all of OUR envs, if not specifically set on the env object
  return Object.fromEntries(
    Object.entries(p.env)
      .filter(
        ([k]) => !(env && hasOwnProperty.call(env, k)) && envRE.test(k)
      )
      .concat([
        ['NODE_OPTIONS', nodeOptionsEnv(env?.NODE_OPTIONS ? env : p.env, args)],
      ])
  )
}

export const register = () => {
  processOnSpawn.addListener(obj => {
    obj.env = {
      ...(obj.env || p.env),
      ...getEnvs(obj.env, obj.args),
    }
    return obj
  })
}

// start tracking coverage, unless disabled explicltly
// export so that we know to collect at the end of the process
const p = process
const enabled = p.env._TAPJS_PROCESSINFO_COVERAGE_ !== '0'
import { mkdirSync, writeFileSync } from 'node:fs'
import { Session } from 'node:inspector'
import { SourceMapPayload } from 'node:module'
import { resolve } from 'node:path'
import { fileURLToPath } from 'node:url'
import { canonicalSource } from './canonical-source.js'
import { findSourceMapSafe } from './find-source-map-safe.js'
import { getExclude } from './get-exclude.js'
import { getLineLengths } from './line-lengths.js'
import { lookupSources } from './lookup-sources.js'
import { ProcessInfoNodeData } from './process-info-node.js'

export let SESSION: Session | undefined = undefined

// This is a \n delimited list of files to show coverage for
// If not set, or empty, then coverage is included for all files
// that pass the exclusion RegExp filter. If included in this list,
// then coverage will be recorded, even if it matches exclude.
const cfEnv = p.env._TAPJS_PROCESSINFO_COV_FILES_ || ''
const coveredFiles: string[] = cfEnv
  .trim()
  .split('\n')
  .filter(f => !!f)

// NB: coverage exclusion is in addition to processinfo
// exclusion.  Only show coverage for a file we care
// about at least somewhat, but coverage is a subset.
const cxEnv = p.env._TAPJS_PROCESSINFO_COV_EXCLUDE_FILES_ || ''
const uncoveredFiles: string[] = cxEnv
  .trim()
  .split('\n')
  .filter(f => !!f)

const exclude = p.env._TAPJS_PROCESSINFO_COV_EXCLUDE_
  ? getExclude('_TAPJS_PROCESSINFO_COV_EXCLUDE_', false)
  : /[\\\/]node_modules[\\\/]/
const fileEx = getExclude('_TAPJS_PROCESSINFO_EXCLUDE_', false)

const fileCovered = (
  f: string,
  sources: string[] = [],
  files: string[] = []
) => {
  const testFiles = [f]
  for (const src of sources || []) {
    testFiles.push(
      resolve(src.startsWith('file://') ? fileURLToPath(src) : src)
    )
  }

  // never include coverage if the file is fully ignored.
  if (!testFiles.some(f => files.includes(f))) {
    // just in case it was missed somehow, make sure it *should* be excluded
    for (const f of testFiles) {
      if (fileEx.test(f)) {
        return false
      }
    }
    // otherwise, it was missed by the loader recording somehow
    // this can happen with commonjs transpilations in some cases
    files.push(f)
  }

  // if at least one of them are explicitly covered, then include it,
  // otherwise omit if we explicitly listed
  if (coveredFiles.length) {
    return testFiles.some(f => coveredFiles.includes(f))
  }

  // if any of the filenames are explicitly excluded, no coverage
  // otherwise, it is covered
  return !testFiles.some(
    f => uncoveredFiles.includes(f) || exclude?.test(f)
  )
}

// C8 can't see that this function runs, best theory is that it
// collides with what it's doing with the coverage it's collecting
// This ignore can possibly be removed once this is being tested
// with a version of tap that uses this library, but it might just
// be an unresolveable bootstrap problem.
// The test does verify that it ran, because otherwise, there would
// be no coverage, and it verifies that it gets the expected coverage.
/* c8 ignore start */
export const register = () => {
  if (!enabled) return
  p.env._TAPJS_PROCESSINFO_COVERAGE_ = '1'

  SESSION = new Session()
  SESSION.connect()
  SESSION.post('Profiler.enable')
  SESSION.post('Runtime.enable')
  SESSION.post('Profiler.startPreciseCoverage', {
    callCount: true,
    detailed: true,
  })
}
/* c8 ignore stop */

export const coverageOnProcessEnd = (
  cwd: string,
  processInfo: ProcessInfoNodeData
) => {
  // Similar to the coverage tracking bootstrap problem above, c8
  // doesn't see that this function runs, even though it DOES see
  // that the function defined below runs, which is weird.
  /* c8 ignore start */
  if (!SESSION) return
  const session = SESSION

  const f = `${cwd}/.tap/coverage/${processInfo.uuid}.json`
  mkdirSync(`${cwd}/.tap/coverage`, { recursive: true })

  session.post('Profiler.takePreciseCoverage', (er, cov) => {
    session.post('Profiler.stopPreciseCoverage')
    /* c8 ignore stop */

    // something very strange and bad happened
    /* c8 ignore start */
    if (er) {
      throw er
    }
    /* c8 ignore stop */

    // Create a source-map-cache that c8 uses in report generation
    const sourceMapCache: {
      [k: string]: {
        lineLengths: number[]
        data: SourceMapPayload
      }
    } = {}
    Object.assign(cov, {
      'source-map-cache': sourceMapCache,
    })

    cov.result = cov.result.filter(obj => {
      if (!/^file:/.test(obj.url)) {
        return false
      }
      obj.url = canonicalSource(obj.url)
      const f = fileURLToPath(obj.url)
      // see if it has a source map
      // need to look up via the url, not the file path, because mocks
      // attach a tapmock search param, which is in node's internal key.
      const sources = lookupSources(obj.url, true)
      if (!fileCovered(f, sources, processInfo.files)) {
        return false
      }
      // Most of the time this will be cached at the time of recording, but
      // if it's the last module loaded, or transpiled in-place by ts-node,
      // the sourcemap won't be pre-loaded and will have to be looked up.
      const s = findSourceMapSafe(obj.url)
      const { payload } = s || { payload: null }
      if (payload) {
        sourceMapCache[obj.url] = Object.assign(Object.create(null), {
          /* c8 ignore start */
          // node's SourceMap objects provide this as of 20.5.0
          //@ts-ignore
          lineLengths: s?.lineLengths || getLineLengths(f),
          /* c8 ignore stop */
          data: {
            ...payload,
            sources: payload.sources?.map(s => canonicalSource(s)),
          },
        })
      }
      return true
    })

    writeFileSync(f, JSON.stringify(cov, null, 2) + '\n', 'utf8')
    /* c8 ignore start */
  })
}
/* c8 ignore stop */

import { onExit } from 'signal-exit'
import { getProcessInfo } from './get-process-info.js'

import { mkdirSync, writeFileSync } from 'fs'
import { coverageOnProcessEnd } from './register-coverage.js'
import { setSources } from './set-sources.js'

const proc = process

const cwd = proc.env._TAPJS_PROCESSINFO_CWD_ || proc.cwd()
proc.env._TAPJS_PROCESSINFO_CWD_ = cwd
const globals = new Set(Object.keys(global))

export const register = () => {
  onExit(
    (code, signal) => {
      const processInfo = getProcessInfo()
      processInfo.code = code
      processInfo.signal = signal
      const runtime = proc.hrtime(processInfo.hrstart)
      delete processInfo.hrstart
      processInfo.files = [...new Set(processInfo.files)]
      // try to find the actual sources of the files we loaded
      // This can't be done up front, because the sourcemap isn't
      // present during the load phase, since it's in the contents.
      setSources(processInfo)
      processInfo.runtime = runtime[0] * 1e3 + runtime[1] / 1e6
      const globalsAdded = Object.keys(global).filter(k => !globals.has(k))
      if (globalsAdded.length) {
        processInfo.globalsAdded = globalsAdded
      }

      const f = `${cwd}/.tap/processinfo/${processInfo.uuid}.json`
      mkdirSync(`${cwd}/.tap/processinfo`, { recursive: true })
      writeFileSync(f, JSON.stringify(processInfo, null, 2) + '\n', 'utf8')
      coverageOnProcessEnd(cwd, processInfo)
    },
    { alwaysLast: true }
  )
}

