const isCallSite = (c: any): c is NodeJS.CallSite =>
  !!c && typeof c === 'object' && c.constructor?.name === 'CallSite'

export class CallSiteLike {
  static prepareStackTrace(e: Error, c: NodeJS.CallSite[]) {
    return c.map(c => new CallSiteLike(e, c))
  }

  #fileName?: string | null
  #cwd?: string
  lineNumber: ReturnType<NodeJS.CallSite['getLineNumber']>
  columnNumber: ReturnType<NodeJS.CallSite['getColumnNumber']>
  this: ReturnType<NodeJS.CallSite['getThis']>
  evalOrigin?: CallSiteLike
  function: ReturnType<NodeJS.CallSite['getFunction']>
  typeName: ReturnType<NodeJS.CallSite['getTypeName']>
  methodName: ReturnType<NodeJS.CallSite['getMethodName']>
  functionName: ReturnType<NodeJS.CallSite['getFunctionName']>
  isEval: ReturnType<NodeJS.CallSite['isEval']>
  isNative: ReturnType<NodeJS.CallSite['isNative']>
  isToplevel: ReturnType<NodeJS.CallSite['isToplevel']>
  isConstructor: ReturnType<NodeJS.CallSite['isConstructor']>
  generated?: GeneratedResult
  #sourceMap?: SourceMap

  // normalize and relativize filename if cwd is set
  get fileName() {
    return this.#relativize(this.#fileName)
  }

  get absoluteFileName() {
    if (!this.#fileName) return this.#fileName
    else if (this.#fileName.startsWith('file://')) {
      return fileURLToPath(this.#fileName)
    } else {
      return this.#fileName
    }
  }

  get cwd(): string | undefined {
    return this.#cwd
  }

  set cwd(cwd: string | undefined) {
    if (cwd === undefined) {
      if (this.generated) {
        this.generated.fileName = this.#derelativize(
          this.generated?.fileName,
        )
      }
    }
    this.#cwd = cwd?.replace(/\\/g, '/')
    if (cwd !== undefined) {
      if (this.generated) {
        this.generated.fileName = this.#relativize(
          this.generated?.fileName,
        )
      }
    }
    if (this.evalOrigin) this.evalOrigin.cwd = cwd
  }

  constructor(
    e: Error | null,
    c: NodeJS.CallSite | string | Compiled,
  ) {
    if (typeof c === 'string') {
      c = parseCallSiteLine(c)
    }

    if (isCallSite(c)) {
      const fileName = c.getFileName()
      this.#fileName = typeof fileName === 'string' ? fileName : null
      this.lineNumber = c.getLineNumber()
      this.columnNumber = c.getColumnNumber()
      this.this = c.getThis()
      const evalOrigin = c.getEvalOrigin()
      if (evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, evalOrigin)
      }
      this.function = c.getFunction()
      this.typeName = c.getTypeName()
      this.methodName = c.getMethodName()
      this.functionName = c.getFunctionName()
      this.isEval = c.isEval()
      this.isNative = c.isNative()
      this.isToplevel = c.isToplevel()
      this.isConstructor = c.isConstructor()
    } else if (isCompiledCallSiteLine(c)) {
      // compiled object from stack line
      this.isEval = !!c.isEval
      this.isToplevel = false
      if (c.evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, c.evalOrigin)
      }

      this.lineNumber =
        c.lineNumber === undefined ? null : c.lineNumber
      this.columnNumber =
        c.columnNumber === undefined ? null : c.columnNumber
      const fileName = c.fileName
      this.#fileName = typeof fileName === 'string' ? fileName : null
      const { generated } = c
      if (generated) {
        this.generated = generated
        this.generated.fileName = this.#relativize(generated.fileName)
      }
      let fname = c.fname?.trim()
      let method: null | string = null
      this.isNative = !!c.isNative

      if (fname) {
        if (fname.startsWith('new ')) {
          this.isConstructor = true
          fname = fname.substring('new '.length).trim()
        } else {
          this.isConstructor = false
        }
        this.methodName = null
        const methodMatch = fname.match(methodRe) as [
          string,
          string,
          string,
        ]
        if (methodMatch) {
          fname = methodMatch[1]
          method = methodMatch[2]
        }
        const dots = fname.split('.')
        const m = dots.pop()
        if (m !== undefined) {
          this.typeName = dots.join('.').trim() || null
          this.methodName = method || m
          if (this.methodName.match(/^get |set /)) {
            this.methodName = this.methodName.substring(4)
          }
          this.functionName = m
          // we know it's not undefined, but TS is afraid of pop()
          /* c8 ignore start */
        } else {
          this.functionName = null
          this.typeName = null
        }
        /* c8 ignore stop */
      } else {
        this.isConstructor = false
        this.typeName = null
        this.functionName = null
        this.methodName = null
      }
    } else {
      throw new Error('invalid call site information provided')
    }

    // This is a slight deviation from the CallSite API, but it's very useful
    // to have a field that's the actual function with type and method name.
    if (
      this.typeName &&
      this.functionName &&
      !this.functionName.startsWith(this.typeName)
    ) {
      this.functionName = `${this.typeName}.${this.functionName}`
    } else if (this.functionName === this.methodName) {
      this.methodName = null
    }

    // We only do the sourcemap lookup if we're parsing from a CallSite
    // If we get it from an Error stack line, then Node has already done
    // the mapping for us, and when ts-node (and other in-place
    // transpilers) create JavaScript, they use the same filename, leading
    // to an incorrect double offset,.
    if (
      this.#fileName &&
      isCallSite(c) &&
      !this.#sourceMap &&
      !this.#fileName.startsWith('node:')
    ) {
      // Passing an object that isn't an actual Error object to
      // findSourceMap causes problems in node 16
      /* c8 ignore start */
      const sme = e && e instanceof Error ? e : undefined
      /* c8 ignore stop */
      this.#sourceMap = findSourceMap(this.#fileName, sme)
      if (this.#sourceMap && typeof this.lineNumber === 'number') {
        // SourceMap.findEntry doesn't actually return the line/column
        // number, despite the property names, but rather the zero-indexed
        // line/column start of a mapping range, and must be looked up
        // using the zero-indexed line and column.
        // To find the mapping, we look it up with the zero-indexed
        // line/col, then figure out how far our line/col is from the
        // mapping, and apply that same offset to the start of the origin
        // in the mapping.
        const payload = this.#sourceMap.findEntry(
          // safety bounds around numbers here, impossible to hit
          // if we're looking up actual call sites though.
          /* c8 ignore start */
          Math.max(0, this.lineNumber - 1),
          Math.max(0, (this.columnNumber || 0) - 1),
          /* c8 ignore stop */
        )
        if (payload) {
          const offset: [number, number] = [
            this.lineNumber - payload.generatedLine,
            /* c8 ignore start */
            (this.columnNumber || 1) - payload.generatedColumn,
            /* c8 ignore stop */
          ]
          const originalLine = payload.originalLine + offset[0]
          const originalColumn = payload.originalColumn + offset[1]
          const genFilename = this.#relativize(this.#fileName)
          this.generated = {
            /* c8 ignore start */
            fileName: this.#relativize(genFilename || null),
            /* c8 ignore stop */
            lineNumber: this.lineNumber,
            columnNumber: this.columnNumber,
          }

          this.#fileName = payload.originalSource
          this.lineNumber = originalLine
          this.columnNumber = originalColumn
        }
      }
    }
  }

  #relativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    else f = f.replace(/\\/g, '/')
    try {
      const rel = relative(this.#cwd, f)
      return rel.length < f.length ? rel : f
    } catch {
      return f
    }
  }
  #derelativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    try {
      return resolve(this.#cwd, f)
    } catch {
      return f
    }
  }

  toString(jsStyle = false): string {
    // in js style mode, use the origin source file if it is within
    // our cwd. Otherwise, use the generated source location.
    const useGen =
      jsStyle &&
      this.generated?.fileName &&
      this.fileName &&
      this.generated.fileName !== this.fileName &&
      (isAbsolute(this.fileName) || this.fileName.startsWith('..'))
    const { fileName, lineNumber, columnNumber, generated } =
      useGen && this.generated ?
        {
          fileName: this.#derelativize(this.generated.fileName),
          lineNumber: this.generated.lineNumber,
          columnNumber: this.generated.columnNumber,
          generated: undefined,
        }
      : jsStyle ?
        {
          fileName: this.#derelativize(this.fileName),
          lineNumber: this.lineNumber,
          columnNumber: this.columnNumber,
          generated: undefined,
        }
      : this
    const loc = { fileName, lineNumber, columnNumber, generated }
    for (const l of [loc, loc.generated]) {
      if (l?.fileName) {
        l.fileName =
          jsStyle ?
            this.#derelativize(l.fileName)
          : this.#relativize(l.fileName)
      }
    }

    let fname = this.functionName || ''
    let tn = ''
    let tnGet = ''
    let tnSet = ''
    if (this.typeName) {
      const mn = this.methodName || '<anonymous>'
      tn = this.typeName + '.' + mn
      tnGet = this.typeName + '.get ' + mn
      tnSet = this.typeName + '.set ' + mn
    }
    if (!fname && tn) {
      fname = tn
    }
    const mn = this.methodName
    if (
      fname &&
      tn &&
      mn &&
      fname !== tn &&
      fname !== tnGet &&
      fname !== tnSet
    ) {
      fname += ` [as ${mn}]`
    }
    if (this.isConstructor && fname) {
      fname = `new ${fname}`
    }
    let ev = ''
    const nat = this.isNative ? 'native' : ''
    let file = loc.fileName || ''
    const hasLC = loc.lineNumber && loc.columnNumber
    if (this.evalOrigin) {
      ev = `eval at ${this.evalOrigin.toString(jsStyle)}`
      if (hasLC) {
        const f = loc.fileName || '<anonymous>'
        let lr = `${f}:${loc.lineNumber}:${loc.columnNumber}`
        if (loc.generated && loc.generated.fileName) {
          const f = loc.generated.fileName
          const { lineNumber: l, columnNumber: c } = loc.generated
          lr = `${f}:${l}:${c} (${lr})`
        }
        ev += `, ${lr}`
      }
      // should always have an fname at this point
      /* c8 ignore start */
      ev = fname ? ` (${ev})` : ev
      /* c8 ignore stop */
      return `${fname}${ev}`
    }

    if (file || hasLC) {
      if (hasLC) {
        /* c8 ignore start */
        file = file || '<anonymous>'
        /* c8 ignore stop */
        file += `:${loc.lineNumber}:${loc.columnNumber}`
      }
    } else if (nat) {
      file = 'native'
      // impossible in normal cases
      /* c8 ignore start */
    } else {
      file = ''
    }
    /* c8 ignore stop */
    let g = ''
    if (loc.generated && loc.generated.fileName) {
      const { fileName, lineNumber, columnNumber } = loc.generated
      g = fileName
      /* c8 ignore start */
      if (!g) g = ''
      /* c8 ignore stop */
      if (g === loc.fileName) g = ''
      if (g) {
        if (
          typeof lineNumber === 'number' &&
          typeof columnNumber === 'number'
        ) {
          g += `:${lineNumber}:${columnNumber}`
        }
        if (ev || fname) {
          g = ` (${g})`
        }
      }
    }
    if (file && (ev || fname || g)) {
      file = ` (${file})`
    }
    const pre = jsStyle ? '    at ' : ''
    return `${pre}${fname}${ev}${g}${file}`
  }

  toJSON(): CallSiteLikeJSON {
    const {
      fileName,
      lineNumber,
      columnNumber,
      evalOrigin,
      typeName,
      methodName,
      functionName,
      isEval,
      isNative,
      isToplevel,
      isConstructor,
      generated,
    } = this
    const json: CallSiteLikeJSON = {}
    if (fileName !== null) json.fileName = fileName
    if (lineNumber || lineNumber === 0) json.lineNumber = lineNumber
    if (columnNumber || columnNumber === 0)
      json.columnNumber = columnNumber
    if (evalOrigin) json.evalOrigin = evalOrigin.toJSON()

    if (typeName !== null) {
      json.typeName = typeName
    }

    if (methodName !== null) json.methodName = methodName
    if (functionName !== null) json.functionName = functionName
    if (isEval) json.isEval = isEval
    if (isNative) json.isNative = isNative
    if (isToplevel) json.isToplevel = isToplevel
    if (isConstructor) json.isConstructor = isConstructor
    if (generated && generated.fileName) {
      const f = this.#relativize(generated.fileName)
      if (
        f &&
        typeof f === 'string' &&
        (f !== json.fileName || f === '<anonymous>')
      ) {
        const gen: Record<string, string | number> = {}
        gen.fileName = f
        if (generated.lineNumber)
          gen.lineNumber = generated.lineNumber
        if (generated.columnNumber)
          gen.columnNumber = generated.columnNumber
        if (Object.keys(gen).length > 0) {
          json.generated = gen
        }
      }
    }
    return json
  }
}

/**
 * Exported as `'resolve-import/get-all-conditions'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

/**
 * Given an `exports` or `imports` value from a package, return the list of
 * conditions that it is sensitive to.
 *
 * `default` is not included in the returned list, since that's always
 * effectively relevant.
 *
 * Note that a condition being returned by this method does not mean
 * that the export/import object actually has a *target* for that condition,
 * since it may map to `null`, be nested under another condition, etc. But it
 * does potentially have some kind of conditional behavior for all the
 * conditions returned.
 *
 * Ordering of returned conditions is arbitrary, and does not imply precedence
 * or object shape.
 */
export const getAllConditions = (
  importsExports: Imports | Exports
): string[] => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: string[] = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getAllConditionsFromCond(
            importsExports as ConditionalValue
          )
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(...getAllConditionsFromCond(v))
    }
    return [...new Set(conditions)]
  }

  return getAllConditionsFromCond(importsExports as ConditionalValue)
}

const getAllConditionsFromCond = (cond?: ConditionalValue): string[] => {
  if (!cond || typeof cond === 'string') return []
  if (Array.isArray(cond)) {
    const conditions: string[] = []
    for (const e of cond) {
      if (!e || typeof e === 'string') break
      conditions.push(...getAllConditionsFromCond(e))
    }
    return [...new Set(conditions)]
  }
  const conditions: string[] = []
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    // anything after 'default' isn't relevant
    conditions.push(...getAllConditionsFromCond(v))
    if (k === 'default') break
    else conditions.push(k)
  }
  return [...new Set(conditions)]
}

// esm style, create a require and use that
import { createRequire } from 'module'
//@ts-ignore
const { resolve } = createRequire(import.meta.url)
export const requireResolve = (s: string) => {
  try {
    return resolve(s)
  } catch {
    return null
  }
}

import { getConditionalValuesList } from './get-conditional-values-list.js'
import { Exports, Imports } from './index.js'

/**
 * Get the minimal set of conditions that can potentially produce different
 * resolution values for a given imports or exports object from a package
 * manifest.
 *
 * For example:
 *
 * ```json
 * {
 *   ".": [{"import":[{"types":"x.d.ts"},"x.mjs"], "require":"y.js"}]
 *   "./a": {"browser":{"require":"./a.js"}},
 *   "./b": {"browser":"./b.js"},
 *   "./c": {"require":{"browser":"./c.js"}}
 * }
 * ```
 *
 * would return:
 * ```js
 * [
 *   ['import','types'],
 *   ['import'],
 *   ['require'],
 *   ['browser'],
 *   ['browser', 'require'],
 * ]
 * ```
 *
 * With the `['require', 'browser']` condition set omitted, as it is already
 * covered by `['browser', 'require']`.
 *
 * Condition ordering is arbitrary and not guaranteed to be consistent.
 */
export const getUniqueConditionSets = (
  importsExports: Imports | Exports
): string[][] => {
  const list = getConditionalValuesList(importsExports)
  let results: string[][] = []
  for (const [_, conditions] of list) {
    if (!results.some(arr => arrayIsEquivalent(arr, conditions))) {
      results.push([...conditions])
    }
  }
  return results
}

const arrayIsEquivalent = (arr: string[], sup: Set<string>) => {
  if (arr.length !== sup.size) return false
  for (const c of arr) {
    if (!sup.has(c)) return false
  }
  return true
}

/**
 * Exported as `'resolve-import/resolve-all-exports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { getNamedExportsList } from './get-named-exports-list.js'
import { Exports, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'
import { starGlob } from './star-glob.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible export path is mapped to the file URL that it would resolve to.
 *
 * Invalid exports are omitted. No errors are raised as long as the file is a
 * valid `package.json`.
 *
 * Note: in cases like `"./x/*": "./file.js"`, where the list of possible
 * import paths is unbounded, the returned object will contain `"./x/*"` as the
 * key, since there's no way to expand that to every possible match.
 */
export const resolveAllExports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllExports)
  }

  const results: Record<string, string | URL> = {}

  const { exports } = pkg
  for (const sub of getNamedExportsList(exports)) {
    let res

    // this can't shouldn't be able to actually throw, because we're
    // pulling the list from the set itself.
    /* c8 ignore start */
    try {
      res = resolveExport(sub, exports as Exports, pjPath, pjPath, options)
    } catch {}
    if (!res) continue
    /* c8 ignore stop */

    // if it contains a *, then we have to glob,
    // in package.json exports * is actually **, but only
    // relevant if there is exactly ONE star
    const sres = res.split('*')
    const ssub = sub.split('*')
    if (sres.length === 2 && ssub.length === 2) {
      for (const [rep, target] of await starGlob(
        sres as [string, string],
        pjDir
      )) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
    } else {
      results[sub] = pathToFileURL(resolve(pjDir, res))
    }
  }

  return results
}

import { argvToNodeOptions } from './argv-to-node-options.js'
import {
  importLoader,
  importMatch,
  legacyLoader,
  legacyMatch,
} from './loader-paths.js'
import { nodeOptionsToArgv } from './node-options-to-argv.js'

import Module from 'node:module'

const getKeyValue = (
  args: readonly string[],
  i: number
): [boolean, string, string | undefined] => {
  const arg = args[i]
  /* c8 ignore start */
  if (typeof arg !== 'string') throw new Error('invalid arg')
  /* c8 ignore stop */
  if (arg.includes('=')) {
    const [k, ...rest] = arg.split('=') as [string, ...string[]]
    return [true, k, rest.join('=')]
  } else if (i < args.length - 1) {
    return [false, arg, args[i + 1]]
  } else {
    return [false, arg, undefined]
  }
}

const useImport = !!(Module as { register?: (...a: any[]) => any })
  .register

// JUST test if we need to do anything at all with the env.
// if the loader is set already in the args, even incorrectly, return true
const hasLoader = (args: readonly string[]): boolean => {
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || arg === '--') break
    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      continue
    }
    if (!eq) i++
    if (
      (k === '--experimental-loader' || k === '--loader') &&
      legacyMatch(v)
    ) {
      return true
    }
    if (k === '--import' && importMatch(v)) return true
  }
  return false
}

const rmLoader = (args: string[]) => {
  const doNotWantKeys = ['--experimental-loader', '--loader', '--import']

  const result: string[] = []

  let doubledash = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, remove it
      continue
    }
    // not ours, leave it
    result.push(arg)
    const next = args[i]
    if (!eq && typeof next === 'string') result.push(next)
    continue
  }
  return result
}

const addLoader = (args: string[]) => {
  const addKey = useImport ? '--import' : '--loader'
  const addValue = useImport ? importLoader : legacyLoader
  const doNotWantKeys = [
    '--experimental-loader',
    useImport ? '--loader' : '--import',
  ]
  const test = useImport ? importMatch : legacyMatch

  const result: string[] = []

  let doubledash = false
  let found = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, but not how we want it, omit
      continue
    }

    if (k === addKey && test(v)) {
      // already present, don't let it be set multiple times.
      if (found) continue
      found = true
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
    } else {
      // not ours
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
      continue
    }
  }
  if (!found) result.push(`${addKey}=${addValue}`)
  return !useImport ? addIgnoreLoadersWarning(result) : result
}

const addIgnoreLoadersWarning = (args: readonly string[]) =>
  args.includes('--no-warnings') ||
  args.includes('--no-warnings=ExperimentalLoader')
    ? args
    : args.concat('--no-warnings')

export const nodeOptionsEnv = (
  env: NodeJS.ProcessEnv,
  args: readonly string[]
) => {
  // if we already have the loader in args, don't add to NODE_OPTIONS
  const no = nodeOptionsToArgv(env.NODE_OPTIONS)
  return argvToNodeOptions(hasLoader(args) ? rmLoader(no) : addLoader(no))
}

/**
 * Exported as `'resolve-import/resolve-all-local-imports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { Pkg, readPkg } from './read-pkg.js'
import { resolveAllExports } from './resolve-all-exports.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveImport } from './resolve-import.js'
import { starGlob } from './star-glob.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible local import path is mapped to the file URL that it would resolve
 * to.
 *
 * Invalid and non-resolving imports are omitted.
 */
export const resolveAllLocalImports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)
  const pjURL = toFileURL(packageJsonPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllLocalImports)
  }
  const results: Record<string, URL | string> = {}

  for (const [sub, target] of getNamedImportsList(pkg, options)) {
    // if the import is local, then look it up
    // if it's another package, then look up that package
    // if it's another package with a *, then look up all exports
    // of that package, and filter by the matches.
    const parts = target.match(/^(@[^\/]+\/[^\/]+|[^\/]+)/)
    // make internal package named modules consistently `./`
    const name = pkg.name

    // non-matches already filtered out
    /* c8 ignore start */
    if (!parts) continue
    /* c8 ignore stop */

    const ssub = sub.split('*')
    const starget = target.split('*') as [string, string]
    const star = ssub.length === 2 && starget.length === 2
    if (!star) {
      // simple case, no * replacement
      // if not found, just omit it.
      // do a full resolve, because the target can be anything like
      // './foo/bar' or 'dep/blah', etc.
      try {
        results[sub] = await resolveImport(target, pjURL)
      } catch {}
      continue
    }

    // has a star, have to glob if it's localPath, or look up exports if not
    const localPath = parts[1] === '.'
    if (localPath) {
      for (const [rep, target] of await starGlob(starget, pjDir)) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
      continue
    }

    const localName = parts[1] === name
    const dep = !localPath && !localName ? parts[1] : null

    // if we can't find the package, it's not valid.
    const ppath = dep ? await findDepPackage(dep, pjDir) : pjDir
    if (!ppath) continue

    const pj = resolve(ppath, 'package.json')
    if (!(await fileExists(pj))) {
      continue
    }

    const allExports = await resolveAllExports(pj)
    for (const [k, v] of Object.entries(allExports)) {
      if (k === '.' || k === './') continue
      const i = dep + k.substring(1)
      if (i.startsWith(starget[0]) && i.endsWith(starget[1])) {
        const s =
          ssub[0] +
          i.substring(starget[0].length, i.length - starget[1].length) +
          ssub[1]
        // should be impossible to throw, because we're pulling the list
        // from the package itself, and it gets resolved at that point.
        /* c8 ignore start */
        try {
          results[s] = await resolveImport(v, pjURL)
        } catch {}
        /* c8 ignore stop */
      }
    }
  }

  return results
}

/**
 * Get the condition-resolved targets of all imports
 *
 * Stars are not expanded.
 */
const getNamedImportsList = (
  pkg: Pkg,
  options: ResolveImportOpts
): [string, string][] => {
  const results: [string, string][] = []
  const { imports } = pkg
  if (!imports || typeof imports !== 'object') return results
  for (const [k, v] of Object.entries(imports)) {
    const r = resolveConditionalValue(v, options)
    if (r && !r.startsWith('#')) results.push([k, r])
  }
  return results
}

/**
 * Exported as `'resolve-import/resolve-conditional-value'`
 * @module
 */
import { ConditionalValue, ResolveImportOpts } from './index.js'

/**
 * find the first match for string, import, node, or default
 * at this point, we know we're on the right subpath already
 */
export const resolveConditionalValue = (
  exp: ConditionalValue,
  options: ResolveImportOpts
): string | null => {
  const { conditions = ['import', 'node'] } = options
  if (exp === null || typeof exp === 'string') return exp
  if (Array.isArray(exp)) {
    for (const e of exp) {
      const r = resolveConditionalValue(e, options)
      if (r) return r
    }
    return null
  }
  for (const [k, v] of Object.entries(exp)) {
    if (conditions.includes(k) || k === 'default') {
      return resolveConditionalValue(v, options)
    }
  }
  return null
}

/**
 * Exported as `'resolve-import/resolve-import'`
 * @module
 */
import { realpath } from 'fs/promises'
import { isBuiltin } from 'module'
import { basename, dirname, isAbsolute, resolve } from 'path'
import { fileURLToPath, pathToFileURL } from 'url'
import {
  moduleNotFound,
  relativeImportWithoutParentURL,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { ResolveImportOpts } from './index.js'
import { isRelativeRequire } from './is-relative-require.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolvePackageImport } from './resolve-package-import.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

// It's pretty common to resolve against, eg, cwd + '/x', since we might not
// know the actual file that it's being loaded from, and want to resolve what
// a dep WOULD be from a given path. This allows us to realpath that directory,
// without requiring that the file exist.
const realpathParentDir = async (path: string | URL) => {
  path = toPath(path)
  return resolve(await realpath(dirname(path)), basename(path))
}

/**
 * Resolve an import URL or string as if it were coming from the
 * module at parentURL.
 *
 * Returns a string for node builtin modules, and a file:// URL
 * object for anything resolved on disk.
 *
 * If the resolution is impossible, then an error will be raised, which
 * closely matches the errors raised by Node when failing for the same
 * reason.
 */
export const resolveImport = async (
  /** the thing being imported */
  url: string | URL,
  /**
   * the place the import() would be coming from. Required for relative
   * imports.
   */
  parentURL: string | URL | undefined = undefined,
  options: ResolveImportOpts = {}
): Promise<URL | string> => {
  // already resolved, just check that it exists
  if (typeof url === 'string' && url.startsWith('file://')) {
    url = new URL(url)
  }
  if (typeof url === 'object') {
    if (!(await fileExists(url))) {
      throw moduleNotFound(String(url), String(parentURL))
    }
    const rp = await realpath(toPath(url))
    return rp !== fileURLToPath(url) ? pathToFileURL(rp) : url
  }

  const pu = parentURL
    ? toFileURL(await realpathParentDir(parentURL))
    : undefined

  if (isRelativeRequire(url)) {
    if (!pu) {
      throw relativeImportWithoutParentURL(url, parentURL)
    }
    const u = new URL(url, pu)
    if (!(await fileExists(u))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(new URL(url, pu)))
  }

  if (isAbsolute(url)) {
    if (!(await fileExists(url))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(url))
  }

  if (isBuiltin(String(url))) {
    return String(url)
  }

  // ok, we have to resolve it. some kind of bare dep import,
  // either a package name resolving to module or main, or a named export.
  const parentPath: string = toPath(
    parentURL || resolve(await realpath(process.cwd()), 'x')
  )
  const opts = {
    ...options,
    originalParent: String(options.originalParent || parentPath),
  }
  if (url) {
    return resolvePackageImport(url, parentPath, opts)
  } else {
    return resolveDependencyExports(url, parentPath, opts)
  }
}

export const defaultExclude =
  /(^|[\\\/])(node_modules|\.tap|tap-testdir-.*?|tap-snapshots|tests?|[^\\\/]+\.test\.([cm]?[jt]s|[jt]sx?)|__tests?__)([\\\/]|$)/i

const parseExclude = (src: string | undefined): RegExp | undefined => {
  if (!src) return
  const parsed = src.match(/^\/(.*)\/([a-z]*)$/) as
    | null
    | [string, string, string]
  if (parsed) {
    try {
      return new RegExp(parsed[1], parsed[2])
    } catch (e) {}
  }
}

const proc = process

export const getExclude = (k: string, useDefault = true) => {
  const src = proc.env[k]
  const exclude = parseExclude(src) || (useDefault ? defaultExclude : /$./)
  proc.env[k] = String(exclude)
  return exclude
}

// The sources reported in sourcemaps can be fickle,
// and istanbul's reporting doesn't handle it so well.
// So, we canonicalize all entries in sources to a file://
// url with consistent capitalization.

import { relative, resolve } from 'path'
import { fileURLToPath, pathToFileURL } from 'url'
const cwd = process.cwd()

export const canonicalSource = (s: string): string => {
  // convert to a path
  if (s.startsWith('file://')) {
    const u = new URL(s)
    const hash = u.hash
    const search = u.search
    return String(pathToFileURL(canonicalPath(fileURLToPath(s)))) +
      hash + search
  }
  // re-resolve it relative to the cwd
  // back to a file: url
  return String(pathToFileURL(canonicalPath(s)))
}

export const canonicalPath = (s: string): string => {
  const rel = relative(cwd, s)
  s = resolve(cwd, rel)
  // capitalize drive letters
  if (/^[a-z]:/.test(s)) s = s.charAt(0).toUpperCase() + s.substring(1)
  // capitalize UNC paths
  if (s.startsWith('\\\\')) {
    s = s.replace(/\\\\[^\\]+\\[^\\]+\\/, _0 => _0.toUpperCase())
  }
  return s
}

// Note: since nyc uses this module to output coverage, any lines
// that are in the direct sync flow of nyc's outputCoverage are
// ignored, since we can never get coverage for them.
// grab a reference to node's real process object right away
import { signals } from './signals.js'
export { signals }

// just a loosened process type so we can do some evil things
type ProcessRE = NodeJS.Process & {
  reallyExit: (code?: number | undefined | null) => any
  emit: (ev: string, ...a: any[]) => any
}

const processOk = (process: any): process is ProcessRE =>
  !!process &&
  typeof process === 'object' &&
  typeof process.removeListener === 'function' &&
  typeof process.emit === 'function' &&
  typeof process.reallyExit === 'function' &&
  typeof process.listeners === 'function' &&
  typeof process.kill === 'function' &&
  typeof process.pid === 'number' &&
  typeof process.on === 'function'

const kExitEmitter = Symbol.for('signal-exit emitter')
const global: typeof globalThis & { [kExitEmitter]?: Emitter } = globalThis
const ObjectDefineProperty = Object.defineProperty.bind(Object)

/**
 * A function that takes an exit code and signal as arguments
 *
 * In the case of signal exits *only*, a return value of true
 * will indicate that the signal is being handled, and we should
 * not synthetically exit with the signal we received. Regardless
 * of the handler return value, the handler is unloaded when an
 * otherwise fatal signal is received, so you get exactly 1 shot
 * at it, unless you add another onExit handler at that point.
 *
 * In the case of numeric code exits, we may already have committed
 * to exiting the process, for example via a fatal exception or
 * unhandled promise rejection, so it is impossible to stop safely.
 */
export type Handler = (
  code: number | null | undefined,
  signal: NodeJS.Signals | null
) => true | void
type ExitEvent = 'afterExit' | 'exit'
type Emitted = { [k in ExitEvent]: boolean }
type Listeners = { [k in ExitEvent]: Handler[] }

// teeny special purpose ee
class Emitter {
  emitted: Emitted = {
    afterExit: false,
    exit: false,
  }

  listeners: Listeners = {
    afterExit: [],
    exit: [],
  }

  count: number = 0
  id: number = Math.random()

  constructor() {
    if (global[kExitEmitter]) {
      return global[kExitEmitter]
    }
    ObjectDefineProperty(global, kExitEmitter, {
      value: this,
      writable: false,
      enumerable: false,
      configurable: false,
    })
  }

  on(ev: ExitEvent, fn: Handler) {
    this.listeners[ev].push(fn)
  }

  removeListener(ev: ExitEvent, fn: Handler) {
    const list = this.listeners[ev]
    const i = list.indexOf(fn)
    /* c8 ignore start */
    if (i === -1) {
      return
    }
    /* c8 ignore stop */
    if (i === 0 && list.length === 1) {
      list.length = 0
    } else {
      list.splice(i, 1)
    }
  }

  emit(
    ev: ExitEvent,
    code: number | null | undefined,
    signal: NodeJS.Signals | null
  ): boolean {
    if (this.emitted[ev]) {
      return false
    }
    this.emitted[ev] = true
    let ret: boolean = false
    for (const fn of this.listeners[ev]) {
      ret = fn(code, signal) === true || ret
    }
    if (ev === 'exit') {
      ret = this.emit('afterExit', code, signal) || ret
    }
    return ret
  }
}

abstract class SignalExitBase {
  abstract onExit(cb: Handler, opts?: { alwaysLast?: boolean }): () => void
  abstract load(): void
  abstract unload(): void
}

const signalExitWrap = <T extends SignalExitBase>(handler: T) => {
  return {
    onExit(cb: Handler, opts?: { alwaysLast?: boolean }) {
      return handler.onExit(cb, opts)
    },
    load() {
      return handler.load()
    },
    unload() {
      return handler.unload()
    },
  }
}

class SignalExitFallback extends SignalExitBase {
  onExit() {
    return () => {}
  }
  load() {}
  unload() {}
}

class SignalExit extends SignalExitBase {
  // "SIGHUP" throws an `ENOSYS` error on Windows,
  // so use a supported signal instead
  /* c8 ignore start */
  #hupSig = process.platform === 'win32' ? 'SIGINT' : 'SIGHUP'
  /* c8 ignore stop */
  #emitter = new Emitter()
  #process: ProcessRE
  #originalProcessEmit: ProcessRE['emit']
  #originalProcessReallyExit: ProcessRE['reallyExit']

  #sigListeners: { [k in NodeJS.Signals]?: () => void } = {}
  #loaded: boolean = false

  constructor(process: ProcessRE) {
    super()
    this.#process = process
    // { <signal>: <listener fn>, ... }
    this.#sigListeners = {}
    for (const sig of signals) {
      this.#sigListeners[sig] = () => {
        // If there are no other listeners, an exit is coming!
        // Simplest way: remove us and then re-send the signal.
        // We know that this will kill the process, so we can
        // safely emit now.
        const listeners = this.#process.listeners(sig)
        let { count } = this.#emitter
        // This is a workaround for the fact that signal-exit v3 and signal
        // exit v4 are not aware of each other, and each will attempt to let
        // the other handle it, so neither of them do. To correct this, we
        // detect if we're the only handler *except* for previous versions
        // of signal-exit, and increment by the count of listeners it has
        // created.
        /* c8 ignore start */
        const p = process as unknown as {
          __signal_exit_emitter__?: { count: number }
        }
        if (
          typeof p.__signal_exit_emitter__ === 'object' &&
          typeof p.__signal_exit_emitter__.count === 'number'
        ) {
          count += p.__signal_exit_emitter__.count
        }
        /* c8 ignore stop */
        if (listeners.length === count) {
          this.unload()
          const ret = this.#emitter.emit('exit', null, sig)
          /* c8 ignore start */
          const s = sig === 'SIGHUP' ? this.#hupSig : sig
          if (!ret) process.kill(process.pid, s)
          /* c8 ignore stop */
        }
      }
    }

    this.#originalProcessReallyExit = process.reallyExit
    this.#originalProcessEmit = process.emit
  }

  onExit(cb: Handler, opts?: { alwaysLast?: boolean }) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return () => {}
    }
    /* c8 ignore stop */

    if (this.#loaded === false) {
      this.load()
    }

    const ev = opts?.alwaysLast ? 'afterExit' : 'exit'
    this.#emitter.on(ev, cb)
    return () => {
      this.#emitter.removeListener(ev, cb)
      if (
        this.#emitter.listeners['exit'].length === 0 &&
        this.#emitter.listeners['afterExit'].length === 0
      ) {
        this.unload()
      }
    }
  }

  load() {
    if (this.#loaded) {
      return
    }
    this.#loaded = true

    // This is the number of onSignalExit's that are in play.
    // It's important so that we can count the correct number of
    // listeners on signals, and don't wait for the other one to
    // handle it instead of us.
    this.#emitter.count += 1

    for (const sig of signals) {
      try {
        const fn = this.#sigListeners[sig]
        if (fn) this.#process.on(sig, fn)
      } catch (_) {}
    }

    this.#process.emit = (ev: string, ...a: any[]) => {
      return this.#processEmit(ev, ...a)
    }
    this.#process.reallyExit = (code?: number | null | undefined) => {
      return this.#processReallyExit(code)
    }
  }

  unload() {
    if (!this.#loaded) {
      return
    }
    this.#loaded = false

    signals.forEach(sig => {
      const listener = this.#sigListeners[sig]
      /* c8 ignore start */
      if (!listener) {
        throw new Error('Listener not defined for signal: ' + sig)
      }
      /* c8 ignore stop */
      try {
        this.#process.removeListener(sig, listener)
        /* c8 ignore start */
      } catch (_) {}
      /* c8 ignore stop */
    })
    this.#process.emit = this.#originalProcessEmit
    this.#process.reallyExit = this.#originalProcessReallyExit
    this.#emitter.count -= 1
  }

  #processReallyExit(code?: number | null | undefined) {
    /* c8 ignore start */
    if (!processOk(this.#process)) {
      return 0
    }
    this.#process.exitCode = code || 0
    /* c8 ignore stop */

    this.#emitter.emit('exit', this.#process.exitCode, null)
    return this.#originalProcessReallyExit.call(
      this.#process,
      this.#process.exitCode
    )
  }

  #processEmit(ev: string, ...args: any[]): any {
    const og = this.#originalProcessEmit
    if (ev === 'exit' && processOk(this.#process)) {
      if (typeof args[0] === 'number') {
        this.#process.exitCode = args[0]
        /* c8 ignore start */
      }
      /* c8 ignore start */
      const ret = og.call(this.#process, ev, ...args)
      /* c8 ignore start */
      this.#emitter.emit('exit', this.#process.exitCode, null)
      /* c8 ignore stop */
      return ret
    } else {
      return og.call(this.#process, ev, ...args)
    }
  }
}

const process = globalThis.process
// wrap so that we call the method on the actual handler, without
// exporting it directly.
export const {
  /**
   * Called when the process is exiting, whether via signal, explicit
   * exit, or running out of stuff to do.
   *
   * If the global process object is not suitable for instrumentation,
   * then this will be a no-op.
   *
   * Returns a function that may be used to unload signal-exit.
   */
  onExit,

  /**
   * Load the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  load,

  /**
   * Unload the listeners.  Likely you never need to call this, unless
   * doing a rather deep integration with signal-exit functionality.
   * Mostly exposed for the benefit of testing.
   *
   * @internal
   */
  unload,
} = signalExitWrap(
  processOk(process) ? new SignalExit(process) : new SignalExitFallback()
)

import { fileURLToPath } from 'url'
import { ProcessInfoNodeData } from './index.js'
import { getSources } from './lookup-sources.js'
import { urlToPath } from './url-to-path.js'

let sourcesCache: Map<string, string[]>

// set the processInfo.sources for a given file, but don't clobber
// if called multiple times, or create duplicate entries.
// Should only be called *after* the file in question has been loaded.
export const setSources = (pi: ProcessInfoNodeData) => {
  sourcesCache ??= getSources()
  for (const [url, sources] of sourcesCache.entries()) {
    const file = fileURLToPath(url)
    const s = pi.sources[file] || []
    s.push(...sources.map(s => urlToPath(s)))
    pi.sources[file] = [...new Set(s)]
  }
}

// Take a line from a stack trace and pull out its relevant parts.

const isCompiled = Symbol('compiled call site line')

export interface LineRef {
  fileName: string
  lineNumber: number
  columnNumber: number
  [isCompiled]: true
}

export interface NativeLineRef {
  isNative: true
  [isCompiled]: true
}

export const isLineRef = (lr?: Compiled | undefined): lr is LineRef =>
  !!lr &&
  typeof (lr as LineRef).lineNumber === 'number' &&
  typeof (lr as LineRef).columnNumber === 'number'

export interface Compiled {
  fname?: string
  fileName?: string
  lineNumber?: number
  columnNumber?: number
  generated?: LineRef
  evalOrigin?: Compiled
  isEval?: boolean
  isNative?: boolean
  [isCompiled]: true
}

export const isCompiledCallSiteLine = (c: any) =>
  !!c && typeof c === 'object' && c[isCompiled] === true

const lineRef =
  '(?:((?:node:|file:)?[^:]+):([1-9][0-9]*):([1-9][0-9]*)|(native))'
const lineRefLoose = '(?:(.+):([1-9][0-9]*):([1-9][0-9]*))'
const bareLineRef = `^(?:${lineRef})$`
const bareLineRefRe = new RegExp(bareLineRef)
const bareLineRefLoose = `^${lineRefLoose}$`
const bareLineRefLooseRe = new RegExp(bareLineRefLoose)
const parenLineRef = `\\(${lineRef}\\)`
const parenLineRefExec = new RegExp(parenLineRef, 'g')
const parenLineRefLoose = `([^(]+) \\(${lineRefLoose}\\)$`
const parenLineRefLooseRe = new RegExp(parenLineRefLoose)
const trailingLineRefs = `((?: ${parenLineRef})+)$`
const trailingLineRefsRe = new RegExp(trailingLineRefs)
// $1 - evalOrigin fname
// $2 - evalOrigin linerefs (origin src)
// $3 - lineref (generated if $4)
// $4 - src lineref ($3 is generated if present)
const evalOrigin = `eval at(.*?)((?: ${parenLineRef})+)(?:, (${lineRef}(?: \\((${lineRef})\\))?))?`
const bareEvalOrigin = `^${evalOrigin}$`
const bareEvalOriginRe = new RegExp(bareEvalOrigin)
const withEvalOrigin = `(.*?) \\((${evalOrigin})\\)$`
const withEvalOriginRe = new RegExp(withEvalOrigin)

// this is the first-phase parse, which might be confused by a line like
// Cls.[foo (parens) bar] (file:1:2)
// If we get a filename containing (, then chop off the bit from that
// point and put it back on fname.
//
// This heuristic assumes that parens are more likely to appear in
// method names than filenames, which has been a safe assumption so far.
export const parseCallSiteLine = (line: string): Compiled => {
  const c = parseCallSiteLine_(line)
  const paren = c.fileName?.lastIndexOf(' (')
  if (c.fileName && paren && paren !== -1) {
    const s = c.fileName.substring(0, paren)
    const f = c.fileName.substring(paren + 2)
    c.fname += ` (${s}`
    c.fileName = f
  }
  // if we ended up with an fname and nothing else, try a more liberal approach
  if (c.fname && !c.fileName && !c.columnNumber && !c.lineNumber) {
    const bl = c.fname.match(bareLineRefLooseRe) as
      | null
      | [string, string, string, string]
    if (bl) {
      c.fname = undefined
      c.fileName = bl[1]
      c.lineNumber = +bl[2]
      c.columnNumber = +bl[3]
    } else {
      const pl = c.fname.match(parenLineRefLooseRe) as
        | null
        | [string, string, string, string, string]
      if (pl) {
        c.fname = pl[1]
        c.fileName = pl[2]
        c.lineNumber = +pl[3]
        c.columnNumber = +pl[4]
      }
    }
  }
  return c
}

const parseCallSiteLine_ = (line: string): Compiled => {
  line = line.replace(/^\s+at /, '')

  // just a lineref, nothing else:
  const bm = line.match(bareLineRefRe) as
    | null
    | [string, string, string, string, string]
  if (bm && bm[4] && !bm[1] && !bm[2] && !bm[3]) {
    return { fileName: bm[4], [isCompiled]: true }
  }
  if (bm) {
    return {
      fileName: bm[1],
      lineNumber: +bm[2],
      columnNumber: +bm[3],
      [isCompiled]: true,
    }
  }

  // an eval origin subsection
  const em = line.match(bareEvalOriginRe) as
    | null
    | [string, string, string]
  if (em) {
    // we ignore the part that comes after the `,` because that is part of
    // the parent's call site, not the evalOrigin
    return {
      ...parseLineRefs(em[2]),
      fname: em[1],
    }
  }

  const wem = line.match(withEvalOriginRe) as
    | null
    | [
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
      ]
  if (wem) {
    const evalOrigin = parseCallSiteLine(wem[2])
    return {
      ...parseLineRefs(wem[9]),
      fname: wem[1],
      isEval: true,
      evalOrigin,
      [isCompiled]: true,
    }
  }

  // now we know it is either a lineref with fname, or multiple linerefs
  // if we have two linerefs, that meaks it's a toString of a callsite
  // with a sourcemap.
  // if it's one, it might be either a fname, or a fname-less callsite with
  // a sourcemap.
  return parseLineRefs(line)
}

const compileLineRefParse = (
  file: string | undefined,
  line: string | undefined,
  col: string | undefined,
  other: string | undefined,
): LineRef | NativeLineRef | Compiled => {
  if (other === 'native') {
    return { isNative: true, [isCompiled]: true }
    // no other types defined, but could be eventually?
    /* c8 ignore start */
  } else if (other) {
    return { fileName: other, [isCompiled]: true }
    /* c8 igore stop */
  } else if (file && line && col) {
    return {
      fileName: file,
      lineNumber: +line,
      columnNumber: +col,
      [isCompiled]: true,
    }
  } else {
    return { fname: file, [isCompiled]: true }
  }
}

// parse a set of trailing line refs or a bare lineref and 1 or more trailing
const parseLineRefs = (line: string): Compiled => {
  const tm = line.match(trailingLineRefsRe)
  if (!tm) {
    const bm = line.match(bareLineRefRe)
    return bm ?
        compileLineRefParse(bm[1], bm[2], bm[3], bm[4])
      : { fname: line, [isCompiled]: true }
  }

  const lineRefs: Compiled[] = []
  const tms = tm[0]
  let pre = line.substring(0, tm.index)
  let m: RegExpExecArray | null
  while ((m = parenLineRefExec.exec(tms))) {
    lineRefs.push(compileLineRefParse(m[1], m[2], m[3], m[4]))
  }
  // figure out if the bit before the paren linerefs was also a lineref
  if (lineRefs.length === 1) {
    const bm = pre.match(bareLineRefRe)
    if (bm && bm[1] && bm[2] && bm[3]) {
      pre = pre.substring(0, bm.index)
      // pre = ''
      lineRefs.unshift({
        fileName: bm[1],
        lineNumber: +bm[2],
        columnNumber: +bm[3],
        [isCompiled]: true,
      })
    }
  }
  // at this point the LAST lineref is the one we care about, and any
  // that preceed it are the generated result.
  const lastLR = lineRefs.pop()
  /* c8 ignore start */
  // very impossible, but TS is afraid of pop()
  if (!lastLR) return { fname: line, [isCompiled]: true }
  /* c8 ignore stop */
  const generated = lineRefs.pop()
  // shouldn't be any left, but possible if you had a function name
  // that looked like a line ref, though V8 puts [] around those
  /* c8 ignore start */
  pre += lineRefs
    .map(lr =>
      isLineRef(lr) ?
        ` (${lr.fileName}:${lr.lineNumber}:${lr.columnNumber})`
      : lr.isNative ? ` (native)`
      : lr.fileName ? ` (${lr.fileName})`
      : '',
    )
    .join('')
  /* c8 ignore stop */

  const r = {
    fname: pre,
    generated: isLineRef(generated) ? generated : undefined,
    ...lastLR,
    [isCompiled]: true,
  } as Compiled
  return r
}

/**
 * Exported as `'resolve-import/get-conditional-values-list'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

export type ConditionalValuesList = [
  submodulePath: string,
  conditions: Set<string>,
  resolvedValue: string | null
][]

/**
 * Given an `exports` or `imports` value from a package, return the list of all
 * possible conditional values that it might potentially resolve to, for any
 * possible set of import conditions, along with the `Set<string>` of
 * conditions, any superset of which will result in the condition.
 *
 * The list includes null results, since while these are not a valid resolution
 * per se, they do *prevent* valid resolutions that match the same conditions.
 */
export const getConditionalValuesList = (
  importsExports: Imports | Exports
): ConditionalValuesList => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: ConditionalValuesList = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getConditionalValuesListFromCond(
            importsExports as ConditionalValue
          ).map(s => ['.', ...s])
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(
        ...getConditionalValuesListFromCond(v).map(
          s => [k, ...s] as [string, Set<string>, string | null]
        )
      )
    }
    return conditions
  }
  return getConditionalValuesListFromCond(
    importsExports as ConditionalValue
  ).map(s => ['.', ...s])
}

const isSubset = (maybeSub: Set<string>, sup: Set<string>) => {
  if (maybeSub.size > sup.size) return false
  for (const c of maybeSub) {
    if (!sup.has(c)) return false
  }
  return true
}

// walk down the tree, creating a list of [Set<Condition>, value]
// if a subset of the current set is already present in the list, then omit it
const getConditionalValuesListFromCond = (
  cond?: ConditionalValue,
  path: string[] = [], // path of conditions that got here
  list: [Set<string>, string | null][] = []
): [Set<string>, string | null][] => {
  /* c8 ignore start */
  if (cond === undefined) return list
  /* c8 ignore stop */
  if (cond === null || typeof cond === 'string') {
    // reached a resolution value.
    // if we got here, we know it has not yet been seen.
    list.push([new Set(path), cond])
    return list
  }
  if (Array.isArray(cond)) {
    for (const c of cond) {
      getConditionalValuesListFromCond(c, path, list)
      // if we hit a default condition, break
      if (!c || typeof c === 'string') break
    }
    return list
  }
  // ConditionalValueObject
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    const p = k === 'default' ? path : path.concat(k)
    // if no subset seen, then recurse
    const ps = new Set(p)
    const seen = list.some(([s]) => isSubset(s, ps))
    if (!seen) {
      getConditionalValuesListFromCond(v, p, list)
    }
    if (k === 'default') break
  }
  return list
}

/**
 * Errors raised by resolve failures.
 * @module
 */

import { resolveImport } from './resolve-import.js'

// TODO: move "caller" to an options object. Then the same options object can
// be used that handles conditions, parentURL, etc., and the relevant
// top-level function can set it explicitly at the start of the process and
// just pass it along.

export const invalidImportSpecifier = (
  url: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error('invalid import() specifier: ' + url)
  Error.captureStackTrace(er, caller)
  return er
}

export const invalidPackage = (
  pj: string | URL,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error(`Not a valid package: ${pj}`)
  Error.captureStackTrace(er, caller)
  return er
}

export const relativeImportWithoutParentURL = (
  url: string,
  parentURL: any,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error('relative import without parentURL'),
    {
      url,
      parentURL,
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const subpathNotExported = (
  sub: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const p =
    sub === '.'
      ? 'No "exports" main defined'
      : `Package subpath '${sub}' is not defined by "exports"`
  const er = Object.assign(
    new Error(`${p} in ${pj} imported from ${from}`),
    {
      code: 'ERR_PACKAGE_PATH_NOT_EXPORTED',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageNotFound = (
  path: string | null,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find package '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const moduleNotFound = (
  path: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find module '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageImportNotDefined = (
  path: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(
      `Package import specifier "${path}" is not defined in package ` +
        `${pj} imported from ${from}`
    ),
    { code: 'ERR_PACKAGE_IMPORT_NOT_DEFINED' }
  )
  Error.captureStackTrace(er, caller)
  return er
}

import { Exports, ExportsSubpaths } from './index.js'
/**
 * Get the condition-resolved targets of all exports
 *
 * Stars are not expanded.
 */
export const getNamedExportsList = (exports?: Exports): string[] => {
  if (!exports) return []
  if (!isExportSubpaths(exports)) return ['.']
  return Object.keys(exports).filter(e => e === '.' || e.startsWith('./'))
}

const isExportSubpaths = (e: Exports): e is ExportsSubpaths => {
  if (!e || typeof e !== 'object' || Array.isArray(e)) return false
  for (const p in e) {
    if (p !== '.' && !p.startsWith('./')) return false
  }
  return true
}

import { subpathNotExported } from './errors.js'
import { findStarMatch } from './find-star-match.js'
import {
  ConditionalValue,
  Exports,
  ExportsSubpaths,
  ResolveImportOpts,
} from './index.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'

/**
 * Resolve an export that might be a string, subpath exports, exports value
 * object, or array of strings and exports value objects
 */
export const resolveExport = (
  sub: string,
  exp: Exports,
  pj: string,
  from: string,
  options: ResolveImportOpts
): string => {
  const s = !sub
    ? '.'
    : sub === '.' || sub.startsWith('./')
    ? sub
    : `./${sub}`

  if (typeof exp === 'string' || Array.isArray(exp)) {
    const res = s === '.' && resolveConditionalValue(exp, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  // now it must be a set of named exports or an export value object
  // first try to resolve as a value object, if that's allowed
  if (s === '.') {
    const res = resolveConditionalValue(exp, options)
    if (res) return res
  }

  // otherwise the only way to match is with subpaths
  const es = exp as ExportsSubpaths

  // if we have an exact match, use that
  const e = es[s]
  if (e !== undefined) {
    const res = resolveConditionalValue(e, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  const sm = findStarMatch(s, es)
  if (sm) {
    const [key, mid] = sm
    const res = resolveConditionalValue(
      es[key] as ConditionalValue,
      options
    )
    if (!res) throw subpathNotExported(s, pj, from)
    return res.replace(/\*/g, mid)
  }

  // did not find a match
  throw subpathNotExported(s, pj, from)
}

import { escape, glob } from 'glob'
import { resolve } from 'path'

export const starGlob = async (
  star: [string, string], // actually [string,string]
  dir: string
): Promise<[string, string][]> => {
  const pattern =
    escape(star[0]) +
    (star[0].endsWith('/') ? '' : '*/') +
    '**' +
    (star[1].startsWith('/') ? '' : '/*') +
    escape(star[1])
  const matches = await glob(pattern, {
    posix: true,
    absolute: false,
    nodir: true,
    cwd: dir,
    dotRelative: true,
  })
  return matches.map(match => {
    const rep = match.substring(
      star[0].length,
      match.length - star[1].length
    )
    return [rep, resolve(dir, match)]
  })
}

export const nodeOptionsToArgv = (no: string | undefined): string[] => {
  if (!no) return []
  const argv = []
  let escaping = false
  let inquote = false
  let tokStart = 0
  let tok = ''
  for (let i = 0; i < no.length; i++) {
    const c = no.charAt(i)
    switch (c) {
      case '"':
        if (escaping) {
          tok += no.slice(tokStart, i - 1) + '"'
          tokStart = i + 1
          escaping = false
        } else if (inquote) {
          tok += no.slice(tokStart, i)
          tokStart = i + 1
          inquote = false
        } else {
          inquote = true
          tok += no.slice(tokStart, i)
          tokStart = i + 1
        }
        continue

      case '\\':
        if (inquote) {
          escaping = true
        }
        continue

      case ' ':
        if (inquote) continue
        tok += no.slice(tokStart, i)
        tokStart = i + 1
        argv.push(tok)
        tok = ''
        continue

      default:
        escaping = false
        continue
    }
  }
  if (inquote) {
    return []
  }
  tok += no.slice(tokStart)
  argv.push(tok)
  return argv
}

import { resolve } from 'path'
import { getImportMetaURL } from './get-import-meta-url.js'

import { fileURLToPath } from 'url'

export const legacyLoader = getImportMetaURL('../esm/loader-legacy.mjs')
export const importLoader = getImportMetaURL('../esm/import.mjs')
const legacyLoaderPath = fileURLToPath(legacyLoader)
const importLoaderPath = fileURLToPath(importLoader)

export const legacyMatch = (p: string): boolean => {
  const d = p.startsWith('file://')
    ? fileURLToPath(p)
    : decodeURIComponent(p)
  return (
    d === '@tapjs/processinfo/loader' ||
    p === legacyLoader ||
    resolve(d) === legacyLoaderPath
  )
}

export const importMatch = (p: string): boolean => {
  const d = p.startsWith('file://')
    ? fileURLToPath(p)
    : decodeURIComponent(p)
  return (
    d === '@tapjs/processinfo/import' ||
    p === importLoader ||
    resolve(d) === importLoaderPath
  )
}

import { realpath, stat } from 'fs/promises'
import { dirname, resolve, sep } from 'path'
import { walkUp } from 'walk-up-path'

const dirExists = async (f: string): Promise<boolean> =>
  stat(f).then(
    st => st.isDirectory(),
    () => false
  )

export const findDepPackage = async (
  pkgName: string | null,
  parentPath: string
) => {
  // starting from the dirname, try to find the nearest node_modules
  for (const dir of walkUp(dirname(parentPath))) {
    const nm = resolve(dir, 'node_modules') + sep
    // if it's null, then we need the node_modules itself
    // if it's '' then we use node_modules with an extra / on it
    // thisis only relevant when generating the error message, since
    // of course node_modules// is never going to be a valid package.
    const ppath =
      pkgName === null ? nm : (!pkgName ? nm : resolve(nm, pkgName)) + sep
    if (await dirExists(ppath)) {
      try {
        return (await realpath(ppath)) + sep
        // the direxists stat will avoid almost all throws that could
        // occur here, but just in case.
        /* c8 ignore start */
      } catch {}
        /* c8 ignore stop */
    }
  }
}

import { resolve } from 'path'
import { pathToFileURL } from 'url'
import { moduleNotFound, packageNotFound } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'

/**
 * Resolve a dependency like '@dep/name/sub/module' where
 * '@dep/name' is in node_modules somewhere and exports './sub/module'
 */
export const resolveDependencyExports = async (
  url: string | null,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL> => {
  const { originalParent } = options
  const parts = url?.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/)
  const [, pkgName, sub] =
    url === null ? [, null, ''] : parts || ['', '', '']
  const ppath = await findDepPackage(pkgName, parentPath)
  if (!ppath) {
    throw packageNotFound(pkgName, originalParent)
  }

  const indexjs = resolve(ppath, 'index.js')
  const pj = resolve(ppath, 'package.json')
  const pkg = await readPkg(pj)
  const subpath = sub ? resolve(ppath, sub) : false
  // if not a package, then the sub can still be a direct path
  // if no sub, then resolves to index.js if available.
  if (!pkg) {
    if (!subpath) {
      // try index.js, otherwise fail
      if (await fileExists(indexjs)) return pathToFileURL(indexjs)
      else throw packageNotFound(ppath, originalParent)
    } else {
      if (await fileExists(subpath)) {
        return pathToFileURL(subpath)
      } else throw moduleNotFound(subpath, originalParent)
    }
  }

  // ok, have a package, look up the export if present.
  // otherwise, use main, otherwise index.js
  if (pkg.exports) {
    const subPath = resolveExport(
      sub,
      pkg.exports,
      pj,
      originalParent,
      options
    )
    const resolved = resolve(ppath, subPath)
    if (await fileExists(resolved)) return pathToFileURL(resolved)
    else throw moduleNotFound(resolved, originalParent)
  } else if (subpath) {
    if (await fileExists(subpath)) return pathToFileURL(subpath)
    else throw moduleNotFound(subpath, originalParent)
  } else if (pkg.main) {
    // fall back to index.js if main is missing
    const rmain = resolve(ppath, pkg.main)
    if (await fileExists(rmain)) return pathToFileURL(rmain)
    else if (await fileExists(indexjs)) return pathToFileURL(indexjs)
    else throw packageNotFound(ppath, originalParent)
  } else if (await fileExists(indexjs)) {
    return pathToFileURL(indexjs)
  } else {
    throw packageNotFound(ppath, originalParent)
  }
}

import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { walkUp } from 'walk-up-path'
import {
  invalidImportSpecifier,
  moduleNotFound,
  packageImportNotDefined,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { findStarMatch } from './find-star-match.js'
import { ConditionalValue, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolveExport } from './resolve-export.js'
import { resolveImport } from './resolve-import.js'

/**
 * Resolve an import like '@package/name/sub/module', where
 * './sub/module' appears in the exports of the local package.
 */
export const resolvePackageImport = async (
  url: string,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL | string> => {
  const { originalParent } = options
  const parts = url.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/) as
    | null
    | (RegExpMatchArray & [string, string, string])
  // impossible
  /* c8 ignore start */
  if (!parts) throw invalidImportSpecifier(url)
  /* c8 ignore stop */

  for (const dir of walkUp(dirname(parentPath))) {
    const pj = resolve(dir, 'package.json')
    const pkg = await readPkg(pj)
    if (!pkg) continue
    if (pkg.name && pkg.exports) {
      // can import from this package name if exports is defined
      const [, pkgName, sub] = parts
      if (pkgName === pkg.name) {
        // ok, see if sub is a valid export then
        const subPath = resolveExport(
          sub,
          pkg.exports,
          pj,
          originalParent,
          options
        )
        const resolved = resolve(dir, subPath)
        if (await fileExists(resolved)) return pathToFileURL(resolved)
        else throw moduleNotFound(resolved, originalParent)
      }
    }

    if (url.startsWith('#')) {
      if (!pkg.imports) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const exact = pkg.imports[url]
      if (exact !== undefined) {
        const res = resolveConditionalValue(exact, options)
        if (!res) {
          throw packageImportNotDefined(url, pj, originalParent)
        }
        // kind of weird behavior, but it's what node does
        if (res.startsWith('#')) {
          return resolveDependencyExports(null, parentPath, options)
        }
        return resolveImport(res, pj, options)
      }

      const sm = findStarMatch(url, pkg.imports)
      if (!sm) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const [key, mid] = sm
      const match = pkg.imports[key] as ConditionalValue
      const res = resolveConditionalValue(match, options)
      if (!res) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      if (res.startsWith('#')) {
        return resolveDependencyExports(null, parentPath, options)
      }
      const expand = res.replace(/\*/g, mid)

      // start over with the resolved import
      return resolveImport(expand, pj, options)
    }

    break
  }

  return resolveDependencyExports(url, parentPath, options)
}

/**
 * Given an object with string keys possibly containing *, and a test
 * string, return the matching key, and the section that the star should
 * expand to when matching against the test string.
 */
export const findStarMatch = (
  s: string,
  obj: Record<string, any>
): [string, string] | null => {
  // longest pattern matches take priority
  const patterns = Object.keys(obj)
    .filter(p => p.length <= s.length)
    .sort((a, b) => b.length - a.length)
    .map(p => [p, p.split('*')])
    .filter(([, p]) => (p as string[]).length === 2) as [
    string,
    [string, string]
  ][]

  for (const [key, [before, after]] of patterns) {
    if (s.startsWith(before) && s.endsWith(after)) {
      const mid = s.substring(before.length, s.length - after.length)
      return [key, mid]
    }
  }

  return null
}

import { dirname, resolve } from 'path'
export const walkUp = function* (path: string) {
  for (path = resolve(path); path;) {
    yield path
    const pp = dirname(path)
    if (pp === path) {
      break
    } else {
      path = pp
    }
  }
}

