/**
 * The main tap export, CJS style
 *
 * The only different between this and the ESM export is that, because a
 * CJS default export cannot co-exist with exported types, we have to make
 * the types available as a global namespace. Which, isn't exactly the most
 * elegant thing in the world, since it can conflict with any other module
 * that defines a `tap` global namespace, but at least it's common enough
 * that it doesn't read as too strange or unintuitive.
 *
 * @module
 */
//@ts-ignore
import * as items from './main.js'
//@ts-ignore
import { t, TAP } from './main.js'

// this has to be done in a somewhat tricky way, because Test objects are
// actually proxies, in order to host all of their plugins. Those proxies
// are necessarily clever about providing methods that are bound to the
// appropriate plugin object, so we have to define them here as getters.
// This is all to prevent the need for `const t = require('tap').default`,
// which just offends me esthetically.
//
// The unfortunate side effect of this hybrid approach with a default
// export is that TS does not provide a way to export both types AND
// a default defined with `export =`
for (const [key, val] of Object.entries(items)) {
  Object.defineProperty(t, key, {
    get: () => val,
    enumerable: true,
    configurable: true,
  })
}
Object.defineProperty(t, 'default', {
  get: () => t,
  enumerable: true,
  configurable: true,
})

// Can't export types along with a default export, so this conventional hack
// Just dump em into the global in the 'tap' namespace.
declare global {
  /**
   * All exported types from the `@tapjs/core` module are exported
   * here into the global `tap` namespace.
   */
  namespace tap {
    export type Base = items.Base
    export type BaseOpts = items.BaseOpts
    export type Counts = items.Counts
    export type Extra = items.Extra
    export type Lists = items.Lists
    export type Minimal = items.Minimal
    export type Spawn = items.Spawn
    export type SpawnEvents = items.SpawnEvents
    export type SpawnOpts = items.SpawnOpts
    export type Stdin = items.Stdin
    export type StdinOpts = items.StdinOpts
    export type TapFile = items.TapFile
    export type TapFileEvents = items.TapFileEvents
    export type TapFileOpts = items.TapFileOpts
    export type TapBaseEvents = items.TapBaseEvents
    export type TestBase = items.TestBase
    export type TestBaseEvents = items.TestBaseEvents
    export type TestBaseOpts = items.TestBaseOpts
    export type Worker = items.Worker
    export type WorkerEvents = items.WorkerEvents
    export type WorkerOpts = items.WorkerOpts

    export type TapPlugin<
      T extends Object,
      O extends unknown = unknown,
    > = items.TapPlugin<T, O>
  }
}
export = t as TAP & typeof items

/**
 * This is not very useful in many cases outside of the tap runner itself,
 * where it's used to replay previous stored test results.
 *
 * @module
 */
import { Minipass } from 'minipass'
import { createReadStream } from 'node:fs'
import { relative, sep } from 'node:path'
import { Base, BaseOpts, TapBaseEvents } from './base.js'
import { cwd as procCwd } from './proc.js'
import { throwToParser } from './throw-to-parser.js'

/**
 * Options that may be provided to the {@link @tapjs/core!tap-file.TapFile}
 * class
 */
export interface TapFileOpts extends BaseOpts {
  /**
   * Optionally provide a stream of content to treat as the file's TAP data
   */
  tapStream?:
    | NodeJS.ReadableStream
    | Minipass<Buffer>
    | Minipass<string>
  /**
   * The file to read, required if tapStream not provided
   */
  filename?: string
  /**
   * just used to calculate the default name from the filename
   * ignored otherwise
   */
  cwd?: string
}

export interface TapFileEvents extends TapBaseEvents {}

/**
 * Class representing a file as a TAP stream
 *
 * @internal
 */
export class TapFile extends Base<TapFileEvents> {
  caughtName: string = 'tapFileError'
  emitName: string = 'tapFile'
  cwd?: string
  filename?: string
  tapStream?:
    | NodeJS.ReadableStream
    | Minipass<Buffer>
    | Minipass<string>
  constructor(options: TapFileOpts) {
    const { filename, tapStream, cwd = procCwd } = options
    const name = TapFile.getName(options.name, filename, cwd)
    super({ ...options, name })

    this.filename = filename
    this.cwd = cwd
    this.tapStream = tapStream
    this.tapStream?.pause()
  }

  static getName(
    name?: string,
    filename?: string,
    cwd: string = procCwd,
  ) {
    if (name) return name
    if (!filename) return 'file input'
    const rel = relative(cwd, filename)
    return (rel.startsWith('..' + sep) ? filename : rel).replace(
      /\.tap$/,
      '',
    )
  }

  main(cb: () => void) {
    const { tapStream, filename } = this
    if (filename && !tapStream) {
      this.tapStream = createReadStream(filename)
    } else if (!this.tapStream) {
      throw new Error('either tapStream or filename must be provided')
    }
    ;(this.tapStream as Minipass<Buffer>).on('error', er => {
      ;(er as Error & { tapCaught: string }).tapCaught =
        this.caughtName
      this.threw(er)
    })
    if (this.options.timeout) {
      this.setTimeout(this.options.timeout)
    }
    const s = this.tapStream as Minipass
    s.pipe(this.parser)
    if (this.parent) {
      this.parent.emit(this.emitName, this)
    }
    s.once('end', cb)
    s.once('error', cb)
    this.tapStream.resume()
  }

  threw(er: any, extra?: any) {
    return throwToParser(this.parser, super.threw(er, extra))
  }
}

import {
  AsyncHook,
  createHook,
  executionAsyncId,
  HookCallbacks,
} from 'async_hooks'

// grab a reference to this right away, in case the user changes it
// weird thing to do, but this is used in tests a lot, where weird
// things are quite common. Set a dummy process if we don't have it.
const proc =
  typeof process === 'object' && process
    ? process
    : ({
        _handler: null,
        env: {},
        execArgv: [],
        /* c8 ignore start */
        hasUncaughtExceptionCaptureCallback: () => {
          const p = proc as NodeJS.Process & { _handler: any }
          return !!p._handler
        },
        setUncaughtExceptionCaptureCallback: (fn: Function) => {
          const p = proc as NodeJS.Process & { _handler: any }
          p._handler = fn
        },
        /* c8 ignore end */
        listeners: () => ({}),
        emit: () => false,
        once: () => proc,
        on: () => proc,
        removeListener: () => proc,
      } as unknown as NodeJS.Process)

import { writeSync } from 'fs'
import { format } from 'util'
const debugAlways = (() => {
  return (...args: any[]) => writeSync(2, format(...args) + '\n')
})()
const debug = proc.env.ASYNC_HOOK_DOMAIN_DEBUG !== '1' ? () => {} : debugAlways

const domains = new Map()

// possible values here:
// throw (default)
//    we let our rejection handler call the domain handler
// none, warn-with-error-code
//    same as default
// warn
//    same as default (no way to make it any less noisy, sadly)
// strict
//    set the uncaughtExceptionMonitor, because it will throw,
//    but do NOT set our rejection handler, or it'll double-handle
const unhandledRejectionMode = (() => {
  let mode = 'throw'
  for (let i = 0; i < proc.execArgv.length; i++) {
    const m = process.execArgv[i]
    if (m.startsWith('--unhandled-rejections=')) {
      mode = m.substring('--unhandled-rejections='.length)
    } else if (m === '--unhandled-rejections') {
      mode = proc.execArgv[i + 1]
    }
  }
  return mode
})()

// the async hook activation and deactivation
let domainHook: AsyncHook | null = null
const activateDomains = () => {
  if (!domainHook) {
    debug('ACTIVATE')
    domainHook = createHook(hookMethods)
    domainHook.enable()
    proc.on('uncaughtExceptionMonitor', domainErrorHandler)
    if (unhandledRejectionMode !== 'strict') {
      proc.emit = domainProcessEmit as NodeJS.Process['emit']
    }
  }
}
const deactivateDomains = () => {
  if (domainHook) {
    debug('DEACTIVATE')
    domainHook.disable()
    domainHook = null
    proc.removeListener('uncaughtExceptionMonitor', domainErrorHandler)
    proc.emit = originalProcessEmit
  }
}

// monkey patch to silently listen on unhandledRejection, without
// marking the event as 'handled' unless we handled it.
// Do nothing if there's a user handler for the event, though.
const originalProcessEmit = proc.emit
const domainProcessEmit = (ev: string | symbol, ...args: any[]) => {
  if (
    ev !== 'unhandledRejection' ||
    proc.listeners('unhandledRejection').length
  ) {
    //@ts-ignore
    return originalProcessEmit.call(proc, ev, ...args)
  }
  const er = args[0]
  return domainErrorHandler(er, 'unhandledRejection', true)
}

const domainErrorHandler = (
  er: unknown,
  ev?: string,
  rejectionHandler: boolean = false
) => {
  debug('AHD MAYBE HANDLE?', ev, er)
  // if anything else attached a handler, then it's their problem,
  // not ours.  get out of the way.
  if (
    proc.hasUncaughtExceptionCaptureCallback() ||
    proc.listeners('uncaughtException').length > 0
  ) {
    debug('OTHER HANDLER ALREADY SET')
    return false
  }
  const domain = currentDomain()
  if (domain) {
    debug('HAVE DOMAIN')
    try {
      domain.onerror(er, ev)
    } catch (e) {
      debug('ONERROR THREW', e)
      domain.destroy()
      // this is pretty bad.  treat it as a fatal exception, which
      // may or may not be caught in the next domain up.
      // We drop 'from promise', because now it's a throw.
      if (domainErrorHandler(e)) {
        return true
      }
      throw e
    }
    // at this point, we presumably handled the error, and attach a
    // no-op one-time handler to just prevent the crash from happening.
    if (!rejectionHandler) {
      proc.setUncaughtExceptionCaptureCallback(() => {
        debug('UECC ONCE')
        proc.setUncaughtExceptionCaptureCallback(null)
      })
      // in strict mode, node raises the error *before* the uR event,
      // and it warns if the uR event is not handled.
      if (unhandledRejectionMode === 'strict') {
        process.once('unhandledRejection', () => {})
      }
    }
    return true
  }
  return false
}

// the hook callbacks
const hookMethods: HookCallbacks = {
  init(id, type, triggerId) {
    debug('INIT', id, type, triggerId)
    const current = domains.get(triggerId)
    if (current) {
      debug('INIT have current', current)
      current.ids.add(id)
      domains.set(id, current)
      debug('POST INIT', id, type, current)
    }
  },

  destroy(id) {
    const domain = domains.get(id)
    debug('DESTROY', id)
    if (!domain) {
      return
    }
    domains.delete(id)
    domain.ids.delete(id)
    if (!domain.ids.size) {
      domain.destroy()
    }
  },
}

const currentDomain = () => domains.get(executionAsyncId())

let id = 1
export class Domain {
  eid: number
  id: number
  ids: Set<number>
  onerror: (
    er: unknown,
    event: 'uncaughtException' | 'unhandledRejection'
  ) => any
  parent?: Domain
  destroyed: boolean

  constructor(
    onerror: (
      er: unknown,
      event: 'uncaughtException' | 'unhandledRejection'
    ) => any
  ) {
    if (typeof onerror !== 'function') {
      // point at where the wrong thing was actually done
      const er = new TypeError('onerror must be a function')
      Error.captureStackTrace(er, this.constructor)
      throw er
    }
    const eid = executionAsyncId()
    this.eid = eid
    this.id = id++
    this.ids = new Set([eid])
    this.onerror = onerror
    this.parent = domains.get(eid)
    this.destroyed = false
    domains.set(eid, this)
    debug('NEW DOMAIN', this.id, this.eid, this.ids)
    activateDomains()
  }

  destroy() {
    if (this.destroyed) {
      return
    }
    debug('DESTROY DOMAIN', this.id, this.eid, this.ids)
    this.destroyed = true
    // find the nearest non-destroyed parent, assign all ids to it
    let parent = this.parent
    while (parent && parent.destroyed) {
      parent = parent.parent
    }
    this.parent = parent
    if (parent) {
      for (const id of this.ids) {
        domains.set(id, parent)
        parent.ids.add(id)
      }
    } else {
      for (const id of this.ids) {
        domains.delete(id)
      }
    }
    this.ids = new Set()
    if (!domains.size) {
      deactivateDomains()
    }
  }
}

const proc =
  typeof process === 'object' && process
    ? process
    : {
        stdout: null,
        stderr: null,
      }
import { EventEmitter } from 'node:events'
import Stream from 'node:stream'
import { StringDecoder } from 'node:string_decoder'

/**
 * Same as StringDecoder, but exposing the `lastNeed` flag on the type
 */
type SD = StringDecoder & { lastNeed: boolean }

export type { SD, Pipe, PipeProxyErrors }

/**
 * Return true if the argument is a Minipass stream, Node stream, or something
 * else that Minipass can interact with.
 */
export const isStream = (
  s: any
): s is Minipass.Readable | Minipass.Writable =>
  !!s &&
  typeof s === 'object' &&
  (s instanceof Minipass ||
    s instanceof Stream ||
    isReadable(s) ||
    isWritable(s))

/**
 * Return true if the argument is a valid {@link Minipass.Readable}
 */
export const isReadable = (s: any): s is Minipass.Readable =>
  !!s &&
  typeof s === 'object' &&
  s instanceof EventEmitter &&
  typeof (s as Minipass.Readable).pipe === 'function' &&
  // node core Writable streams have a pipe() method, but it throws
  (s as Minipass.Readable).pipe !== Stream.Writable.prototype.pipe

/**
 * Return true if the argument is a valid {@link Minipass.Writable}
 */
export const isWritable = (s: any): s is Minipass.Readable =>
  !!s &&
  typeof s === 'object' &&
  s instanceof EventEmitter &&
  typeof (s as Minipass.Writable).write === 'function' &&
  typeof (s as Minipass.Writable).end === 'function'

const EOF = Symbol('EOF')
const MAYBE_EMIT_END = Symbol('maybeEmitEnd')
const EMITTED_END = Symbol('emittedEnd')
const EMITTING_END = Symbol('emittingEnd')
const EMITTED_ERROR = Symbol('emittedError')
const CLOSED = Symbol('closed')
const READ = Symbol('read')
const FLUSH = Symbol('flush')
const FLUSHCHUNK = Symbol('flushChunk')
const ENCODING = Symbol('encoding')
const DECODER = Symbol('decoder')
const FLOWING = Symbol('flowing')
const PAUSED = Symbol('paused')
const RESUME = Symbol('resume')
const BUFFER = Symbol('buffer')
const PIPES = Symbol('pipes')
const BUFFERLENGTH = Symbol('bufferLength')
const BUFFERPUSH = Symbol('bufferPush')
const BUFFERSHIFT = Symbol('bufferShift')
const OBJECTMODE = Symbol('objectMode')
// internal event when stream is destroyed
const DESTROYED = Symbol('destroyed')
// internal event when stream has an error
const ERROR = Symbol('error')
const EMITDATA = Symbol('emitData')
const EMITEND = Symbol('emitEnd')
const EMITEND2 = Symbol('emitEnd2')
const ASYNC = Symbol('async')
const ABORT = Symbol('abort')
const ABORTED = Symbol('aborted')
const SIGNAL = Symbol('signal')
const DATALISTENERS = Symbol('dataListeners')
const DISCARDED = Symbol('discarded')

const defer = (fn: (...a: any[]) => any) => Promise.resolve().then(fn)
const nodefer = (fn: (...a: any[]) => any) => fn()

// events that mean 'the stream is over'
// these are treated specially, and re-emitted
// if they are listened for after emitting.
type EndishEvent = 'end' | 'finish' | 'prefinish'
const isEndish = (ev: any): ev is EndishEvent =>
  ev === 'end' || ev === 'finish' || ev === 'prefinish'

const isArrayBufferLike = (b: any): b is ArrayBufferLike =>
  b instanceof ArrayBuffer ||
  (!!b &&
    typeof b === 'object' &&
    b.constructor &&
    b.constructor.name === 'ArrayBuffer' &&
    b.byteLength >= 0)

const isArrayBufferView = (b: any): b is ArrayBufferView =>
  !Buffer.isBuffer(b) && ArrayBuffer.isView(b)

/**
 * Options that may be passed to stream.pipe()
 */
export interface PipeOptions {
  /**
   * end the destination stream when the source stream ends
   */
  end?: boolean
  /**
   * proxy errors from the source stream to the destination stream
   */
  proxyErrors?: boolean
}

/**
 * Internal class representing a pipe to a destination stream.
 *
 * @internal
 */
class Pipe<T extends unknown> {
  src: Minipass<T>
  dest: Minipass<any, T>
  opts: PipeOptions
  ondrain: () => any
  constructor(
    src: Minipass<T>,
    dest: Minipass.Writable,
    opts: PipeOptions
  ) {
    this.src = src
    this.dest = dest as Minipass<any, T>
    this.opts = opts
    this.ondrain = () => src[RESUME]()
    this.dest.on('drain', this.ondrain)
  }
  unpipe() {
    this.dest.removeListener('drain', this.ondrain)
  }
  // only here for the prototype
  /* c8 ignore start */
  proxyErrors(_er: any) {}
  /* c8 ignore stop */
  end() {
    this.unpipe()
    if (this.opts.end) this.dest.end()
  }
}

/**
 * Internal class representing a pipe to a destination stream where
 * errors are proxied.
 *
 * @internal
 */
class PipeProxyErrors<T> extends Pipe<T> {
  unpipe() {
    this.src.removeListener('error', this.proxyErrors)
    super.unpipe()
  }
  constructor(
    src: Minipass<T>,
    dest: Minipass.Writable,
    opts: PipeOptions
  ) {
    super(src, dest, opts)
    this.proxyErrors = er => dest.emit('error', er)
    src.on('error', this.proxyErrors)
  }
}

export namespace Minipass {
  /**
   * Encoding used to create a stream that outputs strings rather than
   * Buffer objects.
   */
  export type Encoding = BufferEncoding | 'buffer' | null

  /**
   * Any stream that Minipass can pipe into
   */
  export type Writable =
    | Minipass<any, any, any>
    | NodeJS.WriteStream
    | (NodeJS.WriteStream & { fd: number })
    | (EventEmitter & {
        end(): any
        write(chunk: any, ...args: any[]): any
      })

  /**
   * Any stream that can be read from
   */
  export type Readable =
    | Minipass<any, any, any>
    | NodeJS.ReadStream
    | (NodeJS.ReadStream & { fd: number })
    | (EventEmitter & {
        pause(): any
        resume(): any
        pipe(...destArgs: any[]): any
      })

  /**
   * Utility type that can be iterated sync or async
   */
  export type DualIterable<T> = Iterable<T> & AsyncIterable<T>

  type EventArguments = Record<string | symbol, unknown[]>

  /**
   * The listing of events that a Minipass class can emit.
   * Extend this when extending the Minipass class, and pass as
   * the third template argument.  The key is the name of the event,
   * and the value is the argument list.
   *
   * Any undeclared events will still be allowed, but the handler will get
   * arguments as `unknown[]`.
   */
  export interface Events<RType extends any = Buffer>
    extends EventArguments {
    readable: []
    data: [chunk: RType]
    error: [er: unknown]
    abort: [reason: unknown]
    drain: []
    resume: []
    end: []
    finish: []
    prefinish: []
    close: []
    [DESTROYED]: [er?: unknown]
    [ERROR]: [er: unknown]
  }

  /**
   * String or buffer-like data that can be joined and sliced
   */
  export type ContiguousData =
    | Buffer
    | ArrayBufferLike
    | ArrayBufferView
    | string
  export type BufferOrString = Buffer | string

  /**
   * Options passed to the Minipass constructor.
   */
  export type SharedOptions = {
    /**
     * Defer all data emission and other events until the end of the
     * current tick, similar to Node core streams
     */
    async?: boolean
    /**
     * A signal which will abort the stream
     */
    signal?: AbortSignal
    /**
     * Output string encoding. Set to `null` or `'buffer'` (or omit) to
     * emit Buffer objects rather than strings.
     *
     * Conflicts with `objectMode`
     */
    encoding?: BufferEncoding | null | 'buffer'
    /**
     * Output data exactly as it was written, supporting non-buffer/string
     * data (such as arbitrary objects, falsey values, etc.)
     *
     * Conflicts with `encoding`
     */
    objectMode?: boolean
  }

  /**
   * Options for a string encoded output
   */
  export type EncodingOptions = SharedOptions & {
    encoding: BufferEncoding
    objectMode?: false
  }

  /**
   * Options for contiguous data buffer output
   */
  export type BufferOptions = SharedOptions & {
    encoding?: null | 'buffer'
    objectMode?: false
  }

  /**
   * Options for objectMode arbitrary output
   */
  export type ObjectModeOptions = SharedOptions & {
    objectMode: true
    encoding?: null
  }

  /**
   * Utility type to determine allowed options based on read type
   */
  export type Options<T> =
    | ObjectModeOptions
    | (T extends string
        ? EncodingOptions
        : T extends Buffer
        ? BufferOptions
        : SharedOptions)
}

const isObjectModeOptions = (
  o: Minipass.SharedOptions
): o is Minipass.ObjectModeOptions => !!o.objectMode

const isEncodingOptions = (
  o: Minipass.SharedOptions
): o is Minipass.EncodingOptions =>
  !o.objectMode && !!o.encoding && o.encoding !== 'buffer'

/**
 * Main export, the Minipass class
 *
 * `RType` is the type of data emitted, defaults to Buffer
 *
 * `WType` is the type of data to be written, if RType is buffer or string,
 * then any {@link Minipass.ContiguousData} is allowed.
 *
 * `Events` is the set of event handler signatures that this object
 * will emit, see {@link Minipass.Events}
 */
export class Minipass<
    RType extends unknown = Buffer,
    WType extends unknown = RType extends Minipass.BufferOrString
      ? Minipass.ContiguousData
      : RType,
    Events extends Minipass.Events<RType> = Minipass.Events<RType>
  >
  extends EventEmitter
  implements Minipass.DualIterable<RType>
{
  [FLOWING]: boolean = false;
  [PAUSED]: boolean = false;
  [PIPES]: Pipe<RType>[] = [];
  [BUFFER]: RType[] = [];
  [OBJECTMODE]: boolean;
  [ENCODING]: BufferEncoding | null;
  [ASYNC]: boolean;
  [DECODER]: SD | null;
  [EOF]: boolean = false;
  [EMITTED_END]: boolean = false;
  [EMITTING_END]: boolean = false;
  [CLOSED]: boolean = false;
  [EMITTED_ERROR]: unknown = null;
  [BUFFERLENGTH]: number = 0;
  [DESTROYED]: boolean = false;
  [SIGNAL]?: AbortSignal;
  [ABORTED]: boolean = false;
  [DATALISTENERS]: number = 0;
  [DISCARDED]: boolean = false

  /**
   * true if the stream can be written
   */
  writable: boolean = true
  /**
   * true if the stream can be read
   */
  readable: boolean = true

  /**
   * If `RType` is Buffer, then options do not need to be provided.
   * Otherwise, an options object must be provided to specify either
   * {@link Minipass.SharedOptions.objectMode} or
   * {@link Minipass.SharedOptions.encoding}, as appropriate.
   */
  constructor(
    ...args:
      | [Minipass.ObjectModeOptions]
      | (RType extends Buffer
          ? [] | [Minipass.Options<RType>]
          : [Minipass.Options<RType>])
  ) {
    const options: Minipass.Options<RType> = (args[0] ||
      {}) as Minipass.Options<RType>
    super()
    if (options.objectMode && typeof options.encoding === 'string') {
      throw new TypeError(
        'Encoding and objectMode may not be used together'
      )
    }
    if (isObjectModeOptions(options)) {
      this[OBJECTMODE] = true
      this[ENCODING] = null
    } else if (isEncodingOptions(options)) {
      this[ENCODING] = options.encoding
      this[OBJECTMODE] = false
    } else {
      this[OBJECTMODE] = false
      this[ENCODING] = null
    }
    this[ASYNC] = !!options.async
    this[DECODER] = this[ENCODING]
      ? (new StringDecoder(this[ENCODING]) as SD)
      : null

    //@ts-ignore - private option for debugging and testing
    if (options && options.debugExposeBuffer === true) {
      Object.defineProperty(this, 'buffer', { get: () => this[BUFFER] })
    }
    //@ts-ignore - private option for debugging and testing
    if (options && options.debugExposePipes === true) {
      Object.defineProperty(this, 'pipes', { get: () => this[PIPES] })
    }

    const { signal } = options
    if (signal) {
      this[SIGNAL] = signal
      if (signal.aborted) {
        this[ABORT]()
      } else {
        signal.addEventListener('abort', () => this[ABORT]())
      }
    }
  }

  /**
   * The amount of data stored in the buffer waiting to be read.
   *
   * For Buffer strings, this will be the total byte length.
   * For string encoding streams, this will be the string character length,
   * according to JavaScript's `string.length` logic.
   * For objectMode streams, this is a count of the items waiting to be
   * emitted.
   */
  get bufferLength() {
    return this[BUFFERLENGTH]
  }

  /**
   * The `BufferEncoding` currently in use, or `null`
   */
  get encoding() {
    return this[ENCODING]
  }

  /**
   * @deprecated - This is a read only property
   */
  set encoding(_enc) {
    throw new Error('Encoding must be set at instantiation time')
  }

  /**
   * @deprecated - Encoding may only be set at instantiation time
   */
  setEncoding(_enc: Minipass.Encoding) {
    throw new Error('Encoding must be set at instantiation time')
  }

  /**
   * True if this is an objectMode stream
   */
  get objectMode() {
    return this[OBJECTMODE]
  }

  /**
   * @deprecated - This is a read-only property
   */
  set objectMode(_om) {
    throw new Error('objectMode must be set at instantiation time')
  }

  /**
   * true if this is an async stream
   */
  get ['async'](): boolean {
    return this[ASYNC]
  }
  /**
   * Set to true to make this stream async.
   *
   * Once set, it cannot be unset, as this would potentially cause incorrect
   * behavior.  Ie, a sync stream can be made async, but an async stream
   * cannot be safely made sync.
   */
  set ['async'](a: boolean) {
    this[ASYNC] = this[ASYNC] || !!a
  }

  // drop everything and get out of the flow completely
  [ABORT]() {
    this[ABORTED] = true
    this.emit('abort', this[SIGNAL]?.reason)
    this.destroy(this[SIGNAL]?.reason)
  }

  /**
   * True if the stream has been aborted.
   */
  get aborted() {
    return this[ABORTED]
  }
  /**
   * No-op setter. Stream aborted status is set via the AbortSignal provided
   * in the constructor options.
   */
  set aborted(_) {}

  /**
   * Write data into the stream
   *
   * If the chunk written is a string, and encoding is not specified, then
   * `utf8` will be assumed. If the stream encoding matches the encoding of
   * a written string, and the state of the string decoder allows it, then
   * the string will be passed through to either the output or the internal
   * buffer without any processing. Otherwise, it will be turned into a
   * Buffer object for processing into the desired encoding.
   *
   * If provided, `cb` function is called immediately before return for
   * sync streams, or on next tick for async streams, because for this
   * base class, a chunk is considered "processed" once it is accepted
   * and either emitted or buffered. That is, the callback does not indicate
   * that the chunk has been eventually emitted, though of course child
   * classes can override this function to do whatever processing is required
   * and call `super.write(...)` only once processing is completed.
   */
  write(chunk: WType, cb?: () => void): boolean
  write(
    chunk: WType,
    encoding?: Minipass.Encoding,
    cb?: () => void
  ): boolean
  write(
    chunk: WType,
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void
  ): boolean {
    if (this[ABORTED]) return false
    if (this[EOF]) throw new Error('write after end')

    if (this[DESTROYED]) {
      this.emit(
        'error',
        Object.assign(
          new Error('Cannot call write after a stream was destroyed'),
          { code: 'ERR_STREAM_DESTROYED' }
        )
      )
      return true
    }

    if (typeof encoding === 'function') {
      cb = encoding
      encoding = 'utf8'
    }

    if (!encoding) encoding = 'utf8'

    const fn = this[ASYNC] ? defer : nodefer

    // convert array buffers and typed array views into buffers
    // at some point in the future, we may want to do the opposite!
    // leave strings and buffers as-is
    // anything is only allowed if in object mode, so throw
    if (!this[OBJECTMODE] && !Buffer.isBuffer(chunk)) {
      if (isArrayBufferView(chunk)) {
        //@ts-ignore - sinful unsafe type changing
        chunk = Buffer.from(
          chunk.buffer,
          chunk.byteOffset,
          chunk.byteLength
        )
      } else if (isArrayBufferLike(chunk)) {
        //@ts-ignore - sinful unsafe type changing
        chunk = Buffer.from(chunk)
      } else if (typeof chunk !== 'string') {
        throw new Error(
          'Non-contiguous data written to non-objectMode stream'
        )
      }
    }

    // handle object mode up front, since it's simpler
    // this yields better performance, fewer checks later.
    if (this[OBJECTMODE]) {
      // maybe impossible?
      /* c8 ignore start */
      if (this[FLOWING] && this[BUFFERLENGTH] !== 0) this[FLUSH](true)
      /* c8 ignore stop */

      if (this[FLOWING]) this.emit('data', chunk as unknown as RType)
      else this[BUFFERPUSH](chunk as unknown as RType)

      if (this[BUFFERLENGTH] !== 0) this.emit('readable')

      if (cb) fn(cb)

      return this[FLOWING]
    }

    // at this point the chunk is a buffer or string
    // don't buffer it up or send it to the decoder
    if (!(chunk as Minipass.BufferOrString).length) {
      if (this[BUFFERLENGTH] !== 0) this.emit('readable')
      if (cb) fn(cb)
      return this[FLOWING]
    }

    // fast-path writing strings of same encoding to a stream with
    // an empty buffer, skipping the buffer/decoder dance
    if (
      typeof chunk === 'string' &&
      // unless it is a string already ready for us to use
      !(encoding === this[ENCODING] && !this[DECODER]?.lastNeed)
    ) {
      //@ts-ignore - sinful unsafe type change
      chunk = Buffer.from(chunk, encoding)
    }

    if (Buffer.isBuffer(chunk) && this[ENCODING]) {
      //@ts-ignore - sinful unsafe type change
      chunk = this[DECODER].write(chunk)
    }

    // Note: flushing CAN potentially switch us into not-flowing mode
    if (this[FLOWING] && this[BUFFERLENGTH] !== 0) this[FLUSH](true)

    if (this[FLOWING]) this.emit('data', chunk as unknown as RType)
    else this[BUFFERPUSH](chunk as unknown as RType)

    if (this[BUFFERLENGTH] !== 0) this.emit('readable')

    if (cb) fn(cb)

    return this[FLOWING]
  }

  /**
   * Low-level explicit read method.
   *
   * In objectMode, the argument is ignored, and one item is returned if
   * available.
   *
   * `n` is the number of bytes (or in the case of encoding streams,
   * characters) to consume. If `n` is not provided, then the entire buffer
   * is returned, or `null` is returned if no data is available.
   *
   * If `n` is greater that the amount of data in the internal buffer,
   * then `null` is returned.
   */
  read(n?: number | null): RType | null {
    if (this[DESTROYED]) return null
    this[DISCARDED] = false

    if (
      this[BUFFERLENGTH] === 0 ||
      n === 0 ||
      (n && n > this[BUFFERLENGTH])
    ) {
      this[MAYBE_EMIT_END]()
      return null
    }

    if (this[OBJECTMODE]) n = null

    if (this[BUFFER].length > 1 && !this[OBJECTMODE]) {
      // not object mode, so if we have an encoding, then RType is string
      // otherwise, must be Buffer
      this[BUFFER] = [
        (this[ENCODING]
          ? this[BUFFER].join('')
          : Buffer.concat(
              this[BUFFER] as Buffer[],
              this[BUFFERLENGTH]
            )) as RType,
      ]
    }

    const ret = this[READ](n || null, this[BUFFER][0] as RType)
    this[MAYBE_EMIT_END]()
    return ret
  }

  [READ](n: number | null, chunk: RType) {
    if (this[OBJECTMODE]) this[BUFFERSHIFT]()
    else {
      const c = chunk as Minipass.BufferOrString
      if (n === c.length || n === null) this[BUFFERSHIFT]()
      else if (typeof c === 'string') {
        this[BUFFER][0] = c.slice(n) as RType
        chunk = c.slice(0, n) as RType
        this[BUFFERLENGTH] -= n
      } else {
        this[BUFFER][0] = c.subarray(n) as RType
        chunk = c.subarray(0, n) as RType
        this[BUFFERLENGTH] -= n
      }
    }

    this.emit('data', chunk)

    if (!this[BUFFER].length && !this[EOF]) this.emit('drain')

    return chunk
  }

  /**
   * End the stream, optionally providing a final write.
   *
   * See {@link Minipass#write} for argument descriptions
   */
  end(cb?: () => void): this
  end(chunk: WType, cb?: () => void): this
  end(chunk: WType, encoding?: Minipass.Encoding, cb?: () => void): this
  end(
    chunk?: WType | (() => void),
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void
  ): this {
    if (typeof chunk === 'function') {
      cb = chunk as () => void
      chunk = undefined
    }
    if (typeof encoding === 'function') {
      cb = encoding
      encoding = 'utf8'
    }
    if (chunk !== undefined) this.write(chunk, encoding)
    if (cb) this.once('end', cb)
    this[EOF] = true
    this.writable = false

    // if we haven't written anything, then go ahead and emit,
    // even if we're not reading.
    // we'll re-emit if a new 'end' listener is added anyway.
    // This makes MP more suitable to write-only use cases.
    if (this[FLOWING] || !this[PAUSED]) this[MAYBE_EMIT_END]()
    return this
  }

  // don't let the internal resume be overwritten
  [RESUME]() {
    if (this[DESTROYED]) return

    if (!this[DATALISTENERS] && !this[PIPES].length) {
      this[DISCARDED] = true
    }
    this[PAUSED] = false
    this[FLOWING] = true
    this.emit('resume')
    if (this[BUFFER].length) this[FLUSH]()
    else if (this[EOF]) this[MAYBE_EMIT_END]()
    else this.emit('drain')
  }

  /**
   * Resume the stream if it is currently in a paused state
   *
   * If called when there are no pipe destinations or `data` event listeners,
   * this will place the stream in a "discarded" state, where all data will
   * be thrown away. The discarded state is removed if a pipe destination or
   * data handler is added, if pause() is called, or if any synchronous or
   * asynchronous iteration is started.
   */
  resume() {
    return this[RESUME]()
  }

  /**
   * Pause the stream
   */
  pause() {
    this[FLOWING] = false
    this[PAUSED] = true
    this[DISCARDED] = false
  }

  /**
   * true if the stream has been forcibly destroyed
   */
  get destroyed() {
    return this[DESTROYED]
  }

  /**
   * true if the stream is currently in a flowing state, meaning that
   * any writes will be immediately emitted.
   */
  get flowing() {
    return this[FLOWING]
  }

  /**
   * true if the stream is currently in a paused state
   */
  get paused() {
    return this[PAUSED]
  }

  [BUFFERPUSH](chunk: RType) {
    if (this[OBJECTMODE]) this[BUFFERLENGTH] += 1
    else this[BUFFERLENGTH] += (chunk as Minipass.BufferOrString).length
    this[BUFFER].push(chunk)
  }

  [BUFFERSHIFT](): RType {
    if (this[OBJECTMODE]) this[BUFFERLENGTH] -= 1
    else
      this[BUFFERLENGTH] -= (
        this[BUFFER][0] as Minipass.BufferOrString
      ).length
    return this[BUFFER].shift() as RType
  }

  [FLUSH](noDrain: boolean = false) {
    do {} while (
      this[FLUSHCHUNK](this[BUFFERSHIFT]()) &&
      this[BUFFER].length
    )

    if (!noDrain && !this[BUFFER].length && !this[EOF]) this.emit('drain')
  }

  [FLUSHCHUNK](chunk: RType) {
    this.emit('data', chunk)
    return this[FLOWING]
  }

  /**
   * Pipe all data emitted by this stream into the destination provided.
   *
   * Triggers the flow of data.
   */
  pipe<W extends Minipass.Writable>(dest: W, opts?: PipeOptions): W {
    if (this[DESTROYED]) return dest
    this[DISCARDED] = false

    const ended = this[EMITTED_END]
    opts = opts || {}
    if (dest === proc.stdout || dest === proc.stderr) opts.end = false
    else opts.end = opts.end !== false
    opts.proxyErrors = !!opts.proxyErrors

    // piping an ended stream ends immediately
    if (ended) {
      if (opts.end) dest.end()
    } else {
      // "as" here just ignores the WType, which pipes don't care about,
      // since they're only consuming from us, and writing to the dest
      this[PIPES].push(
        !opts.proxyErrors
          ? new Pipe<RType>(this as Minipass<RType>, dest, opts)
          : new PipeProxyErrors<RType>(this as Minipass<RType>, dest, opts)
      )
      if (this[ASYNC]) defer(() => this[RESUME]())
      else this[RESUME]()
    }

    return dest
  }

  /**
   * Fully unhook a piped destination stream.
   *
   * If the destination stream was the only consumer of this stream (ie,
   * there are no other piped destinations or `'data'` event listeners)
   * then the flow of data will stop until there is another consumer or
   * {@link Minipass#resume} is explicitly called.
   */
  unpipe<W extends Minipass.Writable>(dest: W) {
    const p = this[PIPES].find(p => p.dest === dest)
    if (p) {
      if (this[PIPES].length === 1) {
        if (this[FLOWING] && this[DATALISTENERS] === 0) {
          this[FLOWING] = false
        }
        this[PIPES] = []
      } else this[PIPES].splice(this[PIPES].indexOf(p), 1)
      p.unpipe()
    }
  }

  /**
   * Alias for {@link Minipass#on}
   */
  addListener<Event extends keyof Events>(
    ev: Event,
    handler: (...args: Events[Event]) => any
  ): this {
    return this.on(ev, handler)
  }

  /**
   * Mostly identical to `EventEmitter.on`, with the following
   * behavior differences to prevent data loss and unnecessary hangs:
   *
   * - Adding a 'data' event handler will trigger the flow of data
   *
   * - Adding a 'readable' event handler when there is data waiting to be read
   *   will cause 'readable' to be emitted immediately.
   *
   * - Adding an 'endish' event handler ('end', 'finish', etc.) which has
   *   already passed will cause the event to be emitted immediately and all
   *   handlers removed.
   *
   * - Adding an 'error' event handler after an error has been emitted will
   *   cause the event to be re-emitted immediately with the error previously
   *   raised.
   */
  on<Event extends keyof Events>(
    ev: Event,
    handler: (...args: Events[Event]) => any
  ): this {
    const ret = super.on(
      ev as string | symbol,
      handler as (...a: any[]) => any
    )
    if (ev === 'data') {
      this[DISCARDED] = false
      this[DATALISTENERS]++
      if (!this[PIPES].length && !this[FLOWING]) {
        this[RESUME]()
      }
    } else if (ev === 'readable' && this[BUFFERLENGTH] !== 0) {
      super.emit('readable')
    } else if (isEndish(ev) && this[EMITTED_END]) {
      super.emit(ev)
      this.removeAllListeners(ev)
    } else if (ev === 'error' && this[EMITTED_ERROR]) {
      const h = handler as (...a: Events['error']) => any
      if (this[ASYNC]) defer(() => h.call(this, this[EMITTED_ERROR]))
      else h.call(this, this[EMITTED_ERROR])
    }
    return ret
  }

  /**
   * Alias for {@link Minipass#off}
   */
  removeListener<Event extends keyof Events>(
    ev: Event,
    handler: (...args: Events[Event]) => any
  ) {
    return this.off(ev, handler)
  }

  /**
   * Mostly identical to `EventEmitter.off`
   *
   * If a 'data' event handler is removed, and it was the last consumer
   * (ie, there are no pipe destinations or other 'data' event listeners),
   * then the flow of data will stop until there is another consumer or
   * {@link Minipass#resume} is explicitly called.
   */
  off<Event extends keyof Events>(
    ev: Event,
    handler: (...args: Events[Event]) => any
  ) {
    const ret = super.off(
      ev as string | symbol,
      handler as (...a: any[]) => any
    )
    // if we previously had listeners, and now we don't, and we don't
    // have any pipes, then stop the flow, unless it's been explicitly
    // put in a discarded flowing state via stream.resume().
    if (ev === 'data') {
      this[DATALISTENERS] = this.listeners('data').length
      if (
        this[DATALISTENERS] === 0 &&
        !this[DISCARDED] &&
        !this[PIPES].length
      ) {
        this[FLOWING] = false
      }
    }
    return ret
  }

  /**
   * Mostly identical to `EventEmitter.removeAllListeners`
   *
   * If all 'data' event handlers are removed, and they were the last consumer
   * (ie, there are no pipe destinations), then the flow of data will stop
   * until there is another consumer or {@link Minipass#resume} is explicitly
   * called.
   */
  removeAllListeners<Event extends keyof Events>(ev?: Event) {
    const ret = super.removeAllListeners(ev as string | symbol | undefined)
    if (ev === 'data' || ev === undefined) {
      this[DATALISTENERS] = 0
      if (!this[DISCARDED] && !this[PIPES].length) {
        this[FLOWING] = false
      }
    }
    return ret
  }

  /**
   * true if the 'end' event has been emitted
   */
  get emittedEnd() {
    return this[EMITTED_END]
  }

  [MAYBE_EMIT_END]() {
    if (
      !this[EMITTING_END] &&
      !this[EMITTED_END] &&
      !this[DESTROYED] &&
      this[BUFFER].length === 0 &&
      this[EOF]
    ) {
      this[EMITTING_END] = true
      this.emit('end')
      this.emit('prefinish')
      this.emit('finish')
      if (this[CLOSED]) this.emit('close')
      this[EMITTING_END] = false
    }
  }

  /**
   * Mostly identical to `EventEmitter.emit`, with the following
   * behavior differences to prevent data loss and unnecessary hangs:
   *
   * If the stream has been destroyed, and the event is something other
   * than 'close' or 'error', then `false` is returned and no handlers
   * are called.
   *
   * If the event is 'end', and has already been emitted, then the event
   * is ignored. If the stream is in a paused or non-flowing state, then
   * the event will be deferred until data flow resumes. If the stream is
   * async, then handlers will be called on the next tick rather than
   * immediately.
   *
   * If the event is 'close', and 'end' has not yet been emitted, then
   * the event will be deferred until after 'end' is emitted.
   *
   * If the event is 'error', and an AbortSignal was provided for the stream,
   * and there are no listeners, then the event is ignored, matching the
   * behavior of node core streams in the presense of an AbortSignal.
   *
   * If the event is 'finish' or 'prefinish', then all listeners will be
   * removed after emitting the event, to prevent double-firing.
   */
  emit<Event extends keyof Events>(
    ev: Event,
    ...args: Events[Event]
  ): boolean {
    const data = args[0]
    // error and close are only events allowed after calling destroy()
    if (
      ev !== 'error' &&
      ev !== 'close' &&
      ev !== DESTROYED &&
      this[DESTROYED]
    ) {
      return false
    } else if (ev === 'data') {
      return !this[OBJECTMODE] && !data
        ? false
        : this[ASYNC]
        ? (defer(() => this[EMITDATA](data as RType)), true)
        : this[EMITDATA](data as RType)
    } else if (ev === 'end') {
      return this[EMITEND]()
    } else if (ev === 'close') {
      this[CLOSED] = true
      // don't emit close before 'end' and 'finish'
      if (!this[EMITTED_END] && !this[DESTROYED]) return false
      const ret = super.emit('close')
      this.removeAllListeners('close')
      return ret
    } else if (ev === 'error') {
      this[EMITTED_ERROR] = data
      super.emit(ERROR, data)
      const ret =
        !this[SIGNAL] || this.listeners('error').length
          ? super.emit('error', data)
          : false
      this[MAYBE_EMIT_END]()
      return ret
    } else if (ev === 'resume') {
      const ret = super.emit('resume')
      this[MAYBE_EMIT_END]()
      return ret
    } else if (ev === 'finish' || ev === 'prefinish') {
      const ret = super.emit(ev)
      this.removeAllListeners(ev)
      return ret
    }

    // Some other unknown event
    const ret = super.emit(ev as string, ...args)
    this[MAYBE_EMIT_END]()
    return ret
  }

  [EMITDATA](data: RType) {
    for (const p of this[PIPES]) {
      if (p.dest.write(data as RType) === false) this.pause()
    }
    const ret = this[DISCARDED] ? false : super.emit('data', data)
    this[MAYBE_EMIT_END]()
    return ret
  }

  [EMITEND]() {
    if (this[EMITTED_END]) return false

    this[EMITTED_END] = true
    this.readable = false
    return this[ASYNC]
      ? (defer(() => this[EMITEND2]()), true)
      : this[EMITEND2]()
  }

  [EMITEND2]() {
    if (this[DECODER]) {
      const data = this[DECODER].end()
      if (data) {
        for (const p of this[PIPES]) {
          p.dest.write(data as RType)
        }
        if (!this[DISCARDED]) super.emit('data', data)
      }
    }

    for (const p of this[PIPES]) {
      p.end()
    }
    const ret = super.emit('end')
    this.removeAllListeners('end')
    return ret
  }

  /**
   * Return a Promise that resolves to an array of all emitted data once
   * the stream ends.
   */
  async collect(): Promise<RType[] & { dataLength: number }> {
    const buf: RType[] & { dataLength: number } = Object.assign([], {
      dataLength: 0,
    })
    if (!this[OBJECTMODE]) buf.dataLength = 0
    // set the promise first, in case an error is raised
    // by triggering the flow here.
    const p = this.promise()
    this.on('data', c => {
      buf.push(c)
      if (!this[OBJECTMODE])
        buf.dataLength += (c as Minipass.BufferOrString).length
    })
    await p
    return buf
  }

  /**
   * Return a Promise that resolves to the concatenation of all emitted data
   * once the stream ends.
   *
   * Not allowed on objectMode streams.
   */
  async concat(): Promise<RType> {
    if (this[OBJECTMODE]) {
      throw new Error('cannot concat in objectMode')
    }
    const buf = await this.collect()
    return (
      this[ENCODING]
        ? buf.join('')
        : Buffer.concat(buf as Buffer[], buf.dataLength)
    ) as RType
  }

  /**
   * Return a void Promise that resolves once the stream ends.
   */
  async promise(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.on(DESTROYED, () => reject(new Error('stream destroyed')))
      this.on('error', er => reject(er))
      this.on('end', () => resolve())
    })
  }

  /**
   * Asynchronous `for await of` iteration.
   *
   * This will continue emitting all chunks until the stream terminates.
   */
  [Symbol.asyncIterator](): AsyncGenerator<RType, void, void> {
    // set this up front, in case the consumer doesn't call next()
    // right away.
    this[DISCARDED] = false
    let stopped = false
    const stop = async (): Promise<IteratorReturnResult<void>> => {
      this.pause()
      stopped = true
      return { value: undefined, done: true }
    }
    const next = (): Promise<IteratorResult<RType, void>> => {
      if (stopped) return stop()
      const res = this.read()
      if (res !== null) return Promise.resolve({ done: false, value: res })

      if (this[EOF]) return stop()

      let resolve!: (res: IteratorResult<RType>) => void
      let reject!: (er: unknown) => void
      const onerr = (er: unknown) => {
        this.off('data', ondata)
        this.off('end', onend)
        this.off(DESTROYED, ondestroy)
        stop()
        reject(er)
      }
      const ondata = (value: RType) => {
        this.off('error', onerr)
        this.off('end', onend)
        this.off(DESTROYED, ondestroy)
        this.pause()
        resolve({ value, done: !!this[EOF] })
      }
      const onend = () => {
        this.off('error', onerr)
        this.off('data', ondata)
        this.off(DESTROYED, ondestroy)
        stop()
        resolve({ done: true, value: undefined })
      }
      const ondestroy = () => onerr(new Error('stream destroyed'))
      return new Promise<IteratorResult<RType>>((res, rej) => {
        reject = rej
        resolve = res
        this.once(DESTROYED, ondestroy)
        this.once('error', onerr)
        this.once('end', onend)
        this.once('data', ondata)
      })
    }

    return {
      next,
      throw: stop,
      return: stop,
      [Symbol.asyncIterator]() {
        return this
      },
    }
  }

  /**
   * Synchronous `for of` iteration.
   *
   * The iteration will terminate when the internal buffer runs out, even
   * if the stream has not yet terminated.
   */
  [Symbol.iterator](): Generator<RType, void, void> {
    // set this up front, in case the consumer doesn't call next()
    // right away.
    this[DISCARDED] = false
    let stopped = false
    const stop = (): IteratorReturnResult<void> => {
      this.pause()
      this.off(ERROR, stop)
      this.off(DESTROYED, stop)
      this.off('end', stop)
      stopped = true
      return { done: true, value: undefined }
    }

    const next = (): IteratorResult<RType, void> => {
      if (stopped) return stop()
      const value = this.read()
      return value === null ? stop() : { done: false, value }
    }

    this.once('end', stop)
    this.once(ERROR, stop)
    this.once(DESTROYED, stop)

    return {
      next,
      throw: stop,
      return: stop,
      [Symbol.iterator]() {
        return this
      },
    }
  }

  /**
   * Destroy a stream, preventing it from being used for any further purpose.
   *
   * If the stream has a `close()` method, then it will be called on
   * destruction.
   *
   * After destruction, any attempt to write data, read data, or emit most
   * events will be ignored.
   *
   * If an error argument is provided, then it will be emitted in an
   * 'error' event.
   */
  destroy(er?: unknown) {
    if (this[DESTROYED]) {
      if (er) this.emit('error', er)
      else this.emit(DESTROYED)
      return this
    }

    this[DESTROYED] = true
    this[DISCARDED] = true

    // throw away all buffered data, it's never coming out
    this[BUFFER].length = 0
    this[BUFFERLENGTH] = 0

    const wc = this as Minipass<RType, WType, Events> & {
      close?: () => void
    }
    if (typeof wc.close === 'function' && !this[CLOSED]) wc.close()

    if (er) this.emit('error', er)
    // if no error to emit, still reject pending promises
    else this.emit(DESTROYED)

    return this
  }

  /**
   * Alias for {@link isStream}
   *
   * Former export location, maintained for backwards compatibility.
   *
   * @deprecated
   */
  static get isStream() {
    return isStream
  }
}

import { EventEmitter } from 'events'
import yaml from 'tap-yaml'

import { FinalResults } from './final-results.js'
import { lineType, lineTypes } from './line-type.js'
import { parseDirective } from './parse-directive.js'
import { Plan } from './plan.js'
import { Result, TapError } from './result.js'

/* c8 ignore start */
export type { FinalPlan } from './final-plan.js'
export { FinalResults } from './final-results.js'
export { lineType, lineTypes } from './line-type.js'
export type { ParsedLine } from './line-type.js'
export { parseDirective } from './parse-directive.js'
export type { Directive } from './parse-directive.js'
export { Plan } from './plan.js'
export { Result } from './result.js'
export type { TapError } from './result.js'
export type { EventLog }
/* c8 ignore stop */

import type etoa from 'events-to-array'
import { parse, stringify } from './statics.js'
type EventLog = ReturnType<typeof etoa>

import { unesc } from './escape.js'

export interface ParserOptions {
  name?: string
  passes?: boolean
  parent?: Parser
  level?: number
  closingTestPoint?: Result
  bail?: boolean
  omitVersion?: boolean
  buffered?: boolean
  preserveWhitespace?: boolean
  strict?: boolean
  flat?: boolean
}

export interface Pragmas {
  [pragma: string]: boolean
}

// TODO: declare event signatures

export class Parser
  extends EventEmitter
  implements NodeJS.WritableStream
{
  #child: Parser | null = null
  #current: Result | null = null
  #extraQueue: [string, string][] = []
  #maybeChild: string | null = null
  #postPlan: boolean = false
  #previousChild: Parser | null = null
  #yamlish: string = ''
  #yind: string = ''
  #sawVersion: boolean = false

  aborted: boolean = false
  bail: boolean = false
  bailedOut: boolean | string = false
  #bailingOut: boolean | string = false
  braceLevel: number = 0
  buffer: string = ''
  buffered: boolean
  #closingTestPoint: Result | null = null
  comments: string[] = []
  count: number = 0
  fail: number = 0
  failures: TapError[] = []
  skips: (Result & { skip: string | true })[] = []
  todos: (Result & { todo: string | true })[] = []
  level: number
  name: string
  ok: boolean = true
  omitVersion: boolean
  parent: Parser | null = null
  pass: number = 0
  passes: Result[] | null
  planComment: string = ''
  planEnd: number = -1
  planStart: number = -1
  pointsSeen: Map<number, Result> = new Map()
  pragmas: Pragmas
  preserveWhitespace: boolean
  results: FinalResults | null = null
  root: Parser
  skip: number = 0
  strict: boolean
  syntheticBailout: boolean = false
  syntheticPlan: boolean = false
  time: number | null = null
  todo: number = 0

  get closingTestPoint() {
    return this.#closingTestPoint
  }
  set closingTestPoint(res) {
    this.#closingTestPoint = res
    if (res) res.closingTestPoint = true
  }

  /* c8 ignore start */
  get readable(): false {
    return false
  }
  get writable(): true {
    return true
  }
  /* c8 ignore stop */

  constructor(onComplete?: (results: FinalResults) => any)
  constructor(
    options?: ParserOptions,
    onComplete?: (results: FinalResults) => any,
  )
  constructor(
    options?: ParserOptions | ((results: FinalResults) => any),
    onComplete?: (results: FinalResults) => any,
  ) {
    if (typeof options === 'function') {
      onComplete = options
      options = {} as ParserOptions
    }

    options = options || {}
    super()

    if (onComplete) this.on('complete', onComplete)

    this.name = options.name || ''
    this.parent = options.parent || null
    this.closingTestPoint =
      (this.parent && options.closingTestPoint) || null
    this.root = options.parent ? options.parent.root : this
    this.passes = options.passes ? [] : null
    this.level = options.level || 0

    this.bail = !!options.bail
    this.omitVersion = !!options.omitVersion
    this.buffered = !!options.buffered
    this.preserveWhitespace = options.preserveWhitespace || false

    this.strict = !!options.strict
    this.pragmas = { strict: this.strict }
  }

  get fullname(): string {
    const n: string[] = []
    const pn = (this.parent?.fullname ?? '').trim()
    const mn = (this.name || '').trim()
    if (pn) n.push(pn)
    if (mn) n.push(mn)
    return n.join(' > ')
  }

  tapError(
    error: Result | { tapError: string; [k: string]: any } | string,
    line: string,
  ) {
    if (line) this.emit('line', line)
    this.ok = false
    this.fail++
    if (typeof error === 'string') {
      error = {
        tapError: error,
      }
    }
    this.failures.push(error)
  }

  parseTestPoint(testPoint: RegExpMatchArray, line: string) {
    // need to hold off on this when we have a child so we can
    // associate the closing test point with the test.
    if (!this.#child) this.emitResult()

    if (this.bailedOut) return

    const resId = testPoint[2]
    const res = new Result(testPoint, this)

    if (resId && this.planStart !== -1) {
      const lessThanStart = res.id < this.planStart
      const greaterThanEnd = res.id > this.planEnd
      if (lessThanStart || greaterThanEnd) {
        if (lessThanStart) res.tapError = 'id less than plan start'
        else res.tapError = 'id greater than plan end'
        res.plan = new Plan(this.planStart, this.planEnd)
        this.tapError(res, line)
      }
    }

    if (resId && this.pointsSeen.has(res.id)) {
      res.tapError =
        'test point id ' + resId + ' appears multiple times'
      /* c8 ignore start */
      res.previous = this.pointsSeen.get(res.id) || null
      /* c8 ignore stop */
      this.tapError(res, line)
    } else if (resId) {
      this.pointsSeen.set(res.id, res)
    }

    if (this.#child) {
      if (!this.#child.closingTestPoint)
        this.#child.closingTestPoint = res
      this.emitResult()
      // can only bail out here in the case of a child with broken diags
      // anything else would have bailed out already.
      if (this.bailedOut) return
    }

    this.emit('line', line)

    if (!res.skip && !res.todo) this.ok = this.ok && res.ok

    // hold onto it, because we might get yamlish diagnostics
    this.#current = res
  }

  nonTap(data: string, didLine: boolean = false) {
    if (this.#bailingOut && /^( {4})*\}\n$/.test(data)) return

    if (this.strict) {
      const err = {
        tapError: 'Non-TAP data encountered in strict mode',
        data: data,
      }
      this.tapError(err, data)
      if (this.parent) this.parent.tapError(err, data)
    }

    // emit each line, then the extra as a whole
    if (!didLine)
      data
        .split('\n')
        .slice(0, -1)
        .forEach(line => {
          line += '\n'
          if (this.#current || this.#extraQueue.length)
            this.#extraQueue.push(['line', line])
          else this.emit('line', line)
        })

    this.emitExtra(data)
  }

  emitExtra(data: string, fromChild: boolean = false) {
    if (this.parent)
      this.parent.emitExtra(
        data.replace(/\n$/, '').replace(/^/gm, '    ') + '\n',
        true,
      )
    else if (!fromChild && (this.#current || this.#extraQueue.length))
      this.#extraQueue.push(['extra', data])
    else this.emit('extra', data)
  }

  plan(start: number, end: number, comment: string, line: string) {
    // not allowed to have more than one plan
    if (this.planStart !== -1) {
      this.nonTap(line)
      return
    }

    // can't put a plan in a child.
    if (this.#child || this.#yind) {
      this.nonTap(line)
      return
    }

    this.emitResult()
    if (this.bailedOut) return

    // 1..0 is a special case. Otherwise, end must be >= start
    if (end < start && end !== 0 && start !== 1) {
      if (this.strict)
        this.tapError(
          {
            tapError: 'plan end cannot be less than plan start',
            plan: { start, end },
          },
          line,
        )
      else this.nonTap(line)
      return
    }

    this.planStart = start
    this.planEnd = end
    const p: Plan = new Plan(start, end, comment)
    if (p.comment) this.planComment = p.comment = comment

    // This means that the plan is coming at the END of all the tests
    // Plans MUST be either at the beginning or the very end.  We treat
    // plans like '1..0' the same, since they indicate that no tests
    // will be coming.
    if (this.count !== 0 || this.planEnd === 0) {
      const seen = new Set()
      for (const [id, res] of this.pointsSeen.entries()) {
        const tapError =
          id < start ? 'id less than plan start'
          : id > end ? 'id greater than plan end'
          : null
        if (tapError) {
          seen.add(tapError)
          res.tapError = tapError
          res.plan = new Plan(start, end)
          this.tapError(res, line)
        }
      }
      this.#postPlan = true
    }

    this.emit('line', line)
    this.emit('plan', p)
  }

  resetYamlish() {
    this.#yind = ''
    this.#yamlish = ''
  }

  // that moment when you realize it's not what you thought it was
  yamlGarbage() {
    const yamlGarbage = this.#yind + '---\n' + this.#yamlish
    this.emitResult()
    if (this.bailedOut) return
    this.nonTap(yamlGarbage, true)
  }

  yamlishLine(line: string) {
    if (line === this.#yind + '...\n') {
      // end the yaml block
      this.processYamlish()
    } else {
      this.#yamlish += line
    }
  }

  processYamlish() {
    /* c8 ignore start */
    if (!this.#current) {
      throw new Error(
        'called processYamlish without a current test point',
      )
    }
    /* c8 ignore stop */
    const yamlish = this.#yamlish
    this.resetYamlish()

    let diags: any
    try {
      diags = yaml.parse(yamlish)
    } catch (er) {
      this.nonTap(
        this.#yind + '---\n' + yamlish + this.#yind + '...\n',
        true,
      )
      return
    }

    if (
      typeof diags.duration_ms === 'number' &&
      this.#current.time === null
    ) {
      this.#current.time = diags.duration_ms
      delete diags.duration_ms
    }

    this.#current.diag = diags
    // we still don't emit the result here yet, to support diags
    // that come ahead of buffered subtests.
  }

  write(
    chunk: string | Uint8Array | Buffer,
    cb?: (...x: any[]) => any,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding,
    cb?: (...x: any[]) => any,
  ): boolean
  write(
    chunk: string | Uint8Array | Buffer,
    encoding?: BufferEncoding | ((...a: any[]) => any),
    cb?: (...x: any[]) => any,
  ): boolean {
    if (this.aborted) {
      return false
    }

    if (
      typeof encoding === 'string' &&
      encoding !== 'utf8' &&
      typeof chunk === 'string'
    ) {
      chunk = Buffer.from(chunk, encoding)
    }

    if (Buffer.isBuffer(chunk)) {
      chunk = chunk.toString('utf8')
    }

    if (typeof encoding === 'function') {
      cb = encoding
      encoding = undefined
    }

    this.buffer += chunk
    do {
      const match = this.buffer.match(/^.*\r?\n/)
      if (!match) break

      this.buffer = this.buffer.substring(match[0].length)
      this.parse(match[0])
    } while (this.buffer.length)

    if (cb) process.nextTick(cb)

    return true
  }

  end(
    chunk?: string | Buffer | Uint8Array,
    encoding?: BufferEncoding,
    cb?: (...a: any[]) => any,
  ): this
  end(
    chunk?: string | Buffer | Uint8Array,
    cb?: (...a: any[]) => any,
  ): this
  end(cb?: (...a: any[]) => any): this
  end(
    chunk?: string | Buffer | Uint8Array | ((...a: any[]) => any),
    encoding?: BufferEncoding | ((...a: any[]) => any),
    cb?: (...a: any[]) => any,
  ): this {
    if (chunk && typeof chunk !== 'function') {
      if (typeof encoding === 'function') {
        cb = encoding
        this.write(chunk)
      } else {
        this.write(chunk, encoding)
      }
    }

    if (this.buffer) {
      this.write('\n')
    }

    // if we have yamlish, means we didn't finish with a ...
    if (this.#yamlish) this.yamlGarbage()

    this.emitResult()

    if (this.syntheticBailout && this.level === 0) {
      this.syntheticBailout = false
      const reason =
        this.bailedOut === true ? '' : ' ' + this.bailedOut
      this.emit('line', 'Bail out!' + reason + '\n')
    }

    let skipAll: boolean = false

    if (this.planEnd === 0 && this.planStart === 1) {
      skipAll = true
      if (this.count === 0) {
        this.ok = true
      } else {
        this.tapError('Plan of 1..0, but test points encountered', '')
      }
    } else if (!this.bailedOut && this.planStart === -1) {
      if (this.count === 0 && !this.syntheticPlan) {
        this.syntheticPlan = true
        if (this.buffered) {
          this.planStart = 1
          this.planEnd = 0
        } else {
          if (!this.#sawVersion) {
            this.version(14, 'TAP version 14\n')
          }
          this.plan(1, 0, 'no tests found', '1..0 # no tests found\n')
        }
        skipAll = true
      } else {
        this.tapError('no plan', '')
      }
    } else if (
      this.ok &&
      this.count !== this.planEnd - this.planStart + 1
    ) {
      this.tapError('incorrect number of tests', '')
    }

    this.emitComplete(skipAll)
    if (cb) process.nextTick(cb)

    return this
  }

  emitComplete(skipAll: boolean) {
    if (!this.results) {
      const res = (this.results = new FinalResults(!!skipAll, this))

      if (!res.bailout) {
        // comment a bit at the end so we know what happened.
        // but don't repeat these comments if they're already present.
        if (res.plan.end !== res.count) {
          this.emitComment(
            'test count(' +
              res.count +
              ') != plan(' +
              res.plan.end +
              ')',
            false,
            true,
          )
        }
      }

      this.emit('complete', this.results)
      this.emit('finish')
      this.emit('close')
    }
  }

  version(version: number, line: string) {
    // If version is specified, must be at the very beginning.
    if (
      version >= 13 &&
      this.planStart === -1 &&
      this.count === 0 &&
      !this.#current
    ) {
      this.#sawVersion = true
      this.emit('line', line)
      this.emit('version', version)
    } else this.nonTap(line)
  }

  pragma(key: string, value: boolean, line: string) {
    // can't put a pragma in a child or yaml block
    if (this.#child) {
      this.nonTap(line)
      return
    }

    this.emitResult()
    if (this.bailedOut) return
    // only the 'strict' pragma is currently relevant
    if (key === 'strict') {
      this.strict = value
    }
    this.pragmas[key] = value
    this.emit('line', line)
    this.emit('pragma', key, value)
  }

  bailout(reason: string, synthetic: boolean = false) {
    this.syntheticBailout = synthetic

    if (this.#bailingOut) return

    // Guard because emitting a result can trigger a forced bailout
    // if the harness decides that failures should be bailouts.
    this.#bailingOut = reason || true

    if (!synthetic) this.emitResult()
    else this.#current = null

    this.bailedOut = this.#bailingOut
    this.ok = false
    if (!synthetic) {
      // synthetic bailouts get emitted on end
      let line = 'Bail out!'
      if (reason) line += ' ' + reason
      this.emit('line', line + '\n')
    }
    this.emit('bailout', reason)
    if (this.parent) {
      this.end()
      this.parent.bailout(reason, true)
    }
  }

  clearExtraQueue() {
    for (const [ev, data] of this.#extraQueue) {
      this.emit(ev, data)
    }
    this.#extraQueue.length = 0
  }

  endChild() {
    if (this.#child && (!this.#bailingOut || this.#child.count)) {
      this.#child.time = this.#child.closingTestPoint?.time || null
      this.#previousChild = this.#child
      this.#child.end()
      this.#child = null
    }
  }

  emitResult() {
    if (this.bailedOut) return

    this.endChild()
    this.resetYamlish()

    if (!this.#current) return this.clearExtraQueue()

    const res = this.#current
    this.#current = null

    this.count++
    const { skip, todo } = res
    if (skip) this.skips.push({ ...res, skip })
    if (todo) this.todos.push({ ...res, todo })
    if (res.ok) {
      this.pass++
      if (!skip && !todo && this.passes) this.passes.push(res)
    } else {
      this.fail++
      if (!res.todo && !res.skip) {
        this.ok = false
        this.failures.push(res)
      }
    }

    if (res.skip) this.skip++
    if (res.todo) this.todo++

    this.emitAssert(res)
    if (
      this.bail &&
      !res.ok &&
      !res.todo &&
      !res.skip &&
      !this.#bailingOut
    ) {
      this.#maybeChild = null
      const ind = new Array(this.level + 1).join('    ')
      let p: Parser
      for (p = this; p.parent; p = p.parent);
      const bailName = res.name ? ' ' + res.name : ''
      p.parse(ind + 'Bail out!' + bailName + '\n')
    }
    this.clearExtraQueue()
  }

  // TODO: We COULD say that any "relevant tap" line that's indented
  // by 4 spaces starts a child test, and just call it 'unnamed' if
  // it does not have a prefix comment.  In that case, any number of
  // 4-space indents can be plucked off to try to find a relevant
  // TAP line type, and if so, start the unnamed child.
  startChild(line: string) {
    const maybeBuffered = this.#current && this.#current.buffered
    const unindentStream = !maybeBuffered && this.#maybeChild
    const indentStream =
      !maybeBuffered &&
      !unindentStream &&
      lineTypes.subtestIndent.test(line)

    // If we have any other result waiting in the wings, we need to emit
    // that now.  A buffered test emits its test point at the *end* of
    // the child subtest block, so as to match streamed test semantics.
    if (!maybeBuffered) this.emitResult()

    if (this.bailedOut) return

    this.#child = new Parser({
      bail: this.bail,
      parent: this,
      level: this.level + 1,
      buffered: maybeBuffered || undefined,
      closingTestPoint: (maybeBuffered && this.#current) || undefined,
      preserveWhitespace: this.preserveWhitespace,
      omitVersion: true,
      strict: this.strict,
    })

    this.#child.on('complete', results => {
      if (!results.ok) this.ok = false
    })

    this.#child.on('line', l => {
      if (l.trim() || this.preserveWhitespace) l = '    ' + l
      this.emit('line', l)
    })

    // Canonicalize the parsing result of any kind of subtest
    // if it's a buffered subtest or a non-indented Subtest directive,
    // then synthetically emit the Subtest comment
    line = line.substring(4)
    let subtestComment: string
    if (indentStream) {
      subtestComment = line
      line = ''
    } else if (maybeBuffered && this.#current) {
      subtestComment = '# Subtest: ' + this.#current.name + '\n'
    } else {
      subtestComment = this.#maybeChild || '# Subtest\n'
    }

    this.#maybeChild = null
    this.#child.name = subtestComment
      .substring('# Subtest: '.length)
      .trim()

    // at some point, we may wish to move 100% to preferring
    // the Subtest comment on the parent level.  If so, uncomment
    // this line, and remove the child.emitComment below.
    // this.emit('comment', subtestComment)
    if (!this.#child.buffered) this.emit('line', subtestComment)
    this.emit('child', this.#child)
    this.#child.emitComment(subtestComment, true)
    if (line) this.#child.parse(line)
  }

  destroy(er?: Error) {
    this.abort('destroyed', er)
  }

  abort(message: string = '', extra?: any) {
    if (this.#child) {
      const b = this.#child.buffered
      this.#child.abort(message, extra)
      extra = null
      if (b) this.write('\n}\n')
    }

    let dump: string = ''
    if (extra && Object.keys(extra).length) {
      try {
        dump = yaml.stringify(extra).trimEnd()
        /* c8 ignore start */
      } catch (er) {}
      /* c8 ignore stop */
    }

    const y: string =
      dump ?
        '  ---\n  ' + dump.split('\n').join('\n  ') + '\n  ...\n'
      : '\n'

    const n = (this.count || 0) + 1 + (this.#current ? 1 : 0)

    if (this.planEnd !== -1 && this.planEnd < n && this.parent) {
      // skip it, let the parent do this.
      this.aborted = true
      return
    }

    message = message.replace(/[\n\r\s\t]/g, ' ')
    let point = '\nnot ok ' + n + ' - ' + message + '\n' + y

    if (this.planEnd === -1) point += '1..' + n + '\n'

    if (!this.#sawVersion) this.write('TAP version 14\n')
    this.write(point)
    this.aborted = true
    this.end()
  }

  emitAssert(res: Result) {
    this.emit('assert', res)

    // see if we need to surface to the top level
    const c = this.#child || this.#previousChild
    if (c) {
      this.#previousChild = null
      if (
        res.name === c.name &&
        c.results &&
        res.ok === c.results.ok &&
        c.results.count &&
        !res.todo &&
        !res.skip
      ) {
        // just procedural, ignore it
        return
      }
    }

    // surface result to the top level parser
    this.root.emit('result', res)
    if (res.skip) this.root.emit('skip', res)
    else if (res.todo) this.root.emit('todo', res)
    else if (!res.ok) this.root.emit('fail', res)
    else this.root.emit('pass', res)
  }

  emitComment(
    line: string,
    skipLine: boolean = false,
    noDuplicate: boolean = false,
  ) {
    if (line.trim().charAt(0) !== '#') line = '# ' + line

    if (line.slice(-1) !== '\n') line += '\n'

    // XXX: is this still needed?
    /* c8 ignore start */
    if (noDuplicate && this.comments.indexOf(line) !== -1) return
    /* c8 ignore stop */

    this.comments.push(line)
    const dir = parseDirective(line.replace(/^\s*#\s*/, '').trim())
    if (dir && dir[0] === 'time' && typeof dir[1] === 'number')
      this.time = dir[1]

    if (this.#current || this.#extraQueue.length) {
      // no way to get here with skipLine being true
      this.#extraQueue.push(['line', line])
      this.#extraQueue.push(['comment', line])
    } else {
      if (!skipLine) this.emit('line', line)
      this.emit('comment', line)
    }
  }

  parse(line: string) {
    // normalize line endings
    line = line.replace(/\r\n$/, '\n')

    // sometimes empty lines get trimmed, but are still part of
    // a subtest or a yaml block.  Otherwise, nothing to parse!
    if (line === '\n') {
      if (this.#child) line = '    ' + line
      else if (this.#yind) line = this.#yind + line
    }

    // If we're bailing out, then the only thing we want to see is the
    // end of a buffered child test.  Anything else should be ignored.
    // But!  if we're bailing out a nested child, and ANOTHER nested child
    // comes after that one, then we don't want the second child's } to
    // also show up, or it looks weird.
    if (this.#bailingOut) {
      if (!/^\s*}\n$/.test(line)) return
      else if (!this.braceLevel || line.length < this.braceLevel)
        this.braceLevel = line.length
      else return
    }

    // This allows omitting even parsing the version if the test is
    // an indented child test.  Several parsers get upset when they
    // see an indented version field.
    if (
      this.omitVersion &&
      lineTypes.version.test(line) &&
      !this.#yind
    ) {
      return
    }

    // check to see if the line is indented.
    // if it is, then it's either a subtest, yaml, or garbage.
    const indent = line.match(/^[ \t]*/)
    if (indent && indent[0]) {
      this.parseIndent(line, indent[0])
      return
    }

    // In any case where we're going to emitResult, that can trigger
    // a bailout, so we need to only emit the line once we know that
    // isn't happening, to prevent cases where there's a bailout, and
    // then one more line of output.  That'll also prevent the case
    // where the test point is emitted AFTER the line that follows it.

    // buffered subtests must end with a }
    if (this.#child && this.#child.buffered && line === '}\n') {
      this.endChild()
      this.emit('line', line)
      this.emitResult()
      return
    }

    // just a \n, emit only if we care about whitespace
    const validLine =
      this.preserveWhitespace || line.trim() || this.#yind
    if (line === '\n') return validLine && this.emit('line', line)

    // buffered subtest with diagnostics
    if (
      this.#current &&
      line === '{\n' &&
      this.#current.diag &&
      !this.#current.buffered &&
      !this.#child
    ) {
      this.emit('line', line)
      this.#current.buffered = true
      return
    }

    // now we know it's not indented, so if it's either valid tap
    // or garbage.  Get the type of line.
    const type = lineType(line)
    if (!type) {
      this.nonTap(line)
      return
    }

    if (type[0] === 'comment') {
      this.emitComment(line)
      return
    }

    // if we have any yamlish, it's garbage now.  We tolerate non-TAP and
    // comments in the midst of yaml (though, perhaps, that's questionable
    // behavior), but any actual TAP means that the yaml block was just
    // not valid.
    if (this.#yind) this.yamlGarbage()

    // If it's anything other than a comment or garbage, then any
    // maybeChild is just an unsatisfied promise.
    if (this.#maybeChild) {
      this.emitComment(this.#maybeChild)
      this.#maybeChild = null
    }

    // nothing but comments can come after a trailing plan
    if (this.#postPlan) {
      this.nonTap(line)
      return
    }

    // ok, now it's maybe a thing
    if (type[0] === 'bailout') {
      const msg = type[1]?.[1] || ''
      this.bailout(unesc(msg), false)
      return
    }

    if (type[0] === 'pragma') {
      const [_, posNeg, key] = type[1]
      this.pragma(String(key), posNeg === '+', line)
      return
    }

    if (type[0] === 'version') {
      const version = type[1]
      this.version(parseInt(String(version[1]), 10), line)
      return
    }

    if (type[0] === 'plan') {
      const plan = type[1]
      this.plan(
        +String(plan[1]),
        +String(plan[2]),
        unesc(plan[3] || '').trim(),
        line,
      )
      return
    }

    // streamed subtests will end when this test point is emitted
    if (type[0] === 'testPoint') {
      // note: it's weird, but possible, to have a testpoint ending in
      // { before a streamed subtest which ends with a test point
      // instead of a }.  In this case, the parser gets confused, but
      // also, even beginning to handle that means doing a much more
      // involved multi-line parse.  By that point, the subtest block
      // has already been emitted as a 'child' event, so it's too late
      // to really do the optimal thing.  The only way around would be
      // to buffer up everything and do a multi-line parse.  This is
      // rare and weird, and a multi-line parse would be a bigger
      // rewrite, so I'm allowing it as it currently is.
      this.parseTestPoint(type[1], line)
      return
    }

    // We already detected nontap up above, so the only case left
    // should be a `# Subtest:` comment.  Ignore for coverage, but
    // include the error here just for good measure.
    if (type[0] === 'subtest') {
      // this is potentially a subtest.  Not indented.
      // hold until later.
      this.#maybeChild = line
      /* c8 ignore start */
    } else {
      throw new Error('Unhandled case: ' + type[0])
    }
    /* c8 ignore stop */
  }

  parseIndent(line: string, indent: string) {
    // still belongs to the child, so pass it along.
    if (this.#child && line.substring(0, 4) === '    ') {
      line = line.substring(4)
      this.#child.write(line)
      return
    }

    // one of:
    // - continuing yaml block
    // - starting yaml block
    // - ending yaml block
    // - body of a new child subtest that was previously introduced
    // - An indented subtest directive
    // - A comment, or garbage

    // continuing/ending yaml block
    if (this.#yind) {
      if (line.indexOf(this.#yind) === 0) {
        this.emit('line', line)
        this.yamlishLine(line)
        return
      } else {
        // oops!  that was not actually yamlish, I guess.
        // this is a case where the indent is shortened mid-yamlish block
        // treat existing yaml as garbage, continue parsing this line
        this.yamlGarbage()
      }
    }

    // start a yaml block under a test point
    if (this.#current && !this.#yind && line === indent + '---\n') {
      this.#yind = indent
      this.emit('line', line)
      return
    }

    // at this point, not yamlish, and not an existing child test.
    // We may have already seen an unindented Subtest directive, or
    // a test point that ended in { indicating a buffered subtest
    // Child tests are always indented 4 spaces.
    if (line.substring(0, 4) === '    ') {
      if (
        this.#maybeChild ||
        (this.#current && this.#current.buffered) ||
        lineTypes.subtestIndent.test(line)
      ) {
        this.startChild(line)
        return
      }

      // It's _something_ indented, if the indentation is divisible by
      // 4 spaces, and the result is actual TAP of some sort, then do
      // a child subtest for it as well.
      //
      // This will lead to some ambiguity in cases where there are multiple
      // levels of non-signaled subtests, but a Subtest comment in the
      // middle of them, which may or may not be considered "indented"
      // See the subtest-no-comment-mid-comment fixture for an example
      // of this.  As it happens, the preference is towards an indented
      // Subtest comment as the interpretation, which is the only possible
      // way to resolve this, since otherwise there's no way to distinguish
      // between an anonymous subtest with a non-indented Subtest comment,
      // and an indented Subtest comment.
      const s = line.match(/( {4})+(.*\n)$/)
      if (s && s[2]?.charAt(0) !== ' ') {
        // integer number of indentations.
        const type = lineType(String(s[2]))
        if (type) {
          if (type[0] === 'comment') {
            this.emit('line', line)
            this.emitComment(line)
          } else {
            // it's relevant!  start as an "unnamed" child subtest
            this.startChild(line)
          }
          return
        }
      }
    }

    // at this point, it's either a non-subtest comment, or garbage.

    if (lineTypes.comment.test(line)) {
      this.emitComment(line)
      return
    }

    this.nonTap(line)
  }

  static parse(str: string, options: ParserOptions = {}): EventLog {
    return parse(str, options)
  }

  static stringify(msg: EventLog, options = {}): string {
    return stringify(msg, options)
  }
}

import { parse } from './parse.js'
import { stringify } from './stringify.js'
export { parse, stringify }
export default { parse, stringify }

import { ScalarTag } from 'yaml'

export const date: ScalarTag = {
  tag: '!date',

  // use !!timestamp instead, this is for parsing only
  /* c8 ignore start */
  identify() {
    return false
  },
  /* c8 ignore stop */

  resolve(src) {
    const d = new Date(src)
    const t = d.getTime()
    if (t !== t) {
      throw new Error(`Invalid date string: ${src}`)
    }
    return d
  },
}

// this just sets the !!timestamp tag to be not considered a default,
// so that we don't confuse date strings and actual dates.
// See: https://github.com/eemeli/yaml/issues/475
import { ScalarTag, Schema } from 'yaml'
const schema = new Schema({ resolveKnownTags: true })
export const timestamp = schema.knownTags[
  'tag:yaml.org,2002:timestamp'
] as ScalarTag
timestamp.default = false

/**
 * Collection of the various types of lines encountered in a TAP stream
 */
export const lineTypes = {
  testPoint: /^(not )?ok(?: ([0-9]+))?(?:(?: -)?( .*?))?(\{?)\n$/,
  pragma: /^pragma ([+-])([a-zA-Z0-9_-]+)\n$/,
  bailout: /^bail out!(.*)\n$/i,
  version: /^TAP version ([0-9]+)\n$/i,
  childVersion: /^(    )+TAP version ([0-9]+)\n$/i,
  plan: /^([0-9]+)\.\.([0-9]+)(?:\s+(?:#\s*(.*)))?\n$/,
  subtest: /^# Subtest(?:: (.*))?\n$/,
  subtestIndent: /^    # Subtest(?:: (.*))?\n$/,
  comment: /^\s*#.*\n$/,
} as const

/**
 * The type of a line, and its constituent parsed pieces
 */
export type ParsedLine = [string, RegExpMatchArray]

/**
 * Determine the type of line, and parse it into a {@link ParsedLine}
 */
export const lineType = (line: string): ParsedLine | null => {
  for (const [t, pattern] of Object.entries(lineTypes)) {
    const match = line.match(pattern)
    if (match) return [t, match]
  }
  return null
}

import { OPEN_BRACE_EOL } from './brace-patterns.js'

/**
 * Directive types supported by this library.
 *
 * `'time'` is not officially in the specification, but node-tap has
 * supported it for a long time, and uses it to report how long
 * subtests take to complete.
 */
export type Directive = 'todo' | 'skip' | 'time'

/**
 * Parse a "directive", the bit that follows the `#` character
 * on a TestPoint line.
 */
export const parseDirective = (
  line: string,
): [Directive, any] | false => {
  if (!line.trim()) return false

  line = line
    .replace(OPEN_BRACE_EOL, '')
    .trim()
    .replace(/^duration_ms ([0-9.]+)$/, 'time=$1ms')
  const time = line.match(
    /^time=((?:[1-9][0-9]*|0)(?:\.[0-9]+)?)(ms|s)$/i,
  )
  const t = time?.[1]
  const s = time?.[2]
  if (typeof t === 'string') {
    let n = +t
    if (s === 's') {
      // JS does weird things with floats.  Round it off a bit.
      n *= 1000000
      n = Math.round(n)
      n /= 1000
    }
    return ['time', n]
  }

  const ts = line.match(/^(todo|skip)(?:\S*)\b(.*)$/i)
  const type = ts?.[1]?.toLowerCase()
  const msg = ts?.[2]?.trim()
  if (!type) return false

  // we know at this point it must be either 'todo' or 'skip',
  // in unknown upper/lower case
  return [type as Directive, msg || true]
}

/**
 * A class representing the TAP plan line
 */
export class Plan {
  public start: number
  public end: number
  public comment: string
  constructor(start: number, end: number, comment: string = '') {
    this.start = start
    this.end = end
    this.comment = comment
  }
}

import { OPEN_BRACE_EOL } from './brace-patterns.js'
import { Parser } from './index.js'
import { Directive, parseDirective } from './parse-directive.js'
import { Plan } from './plan.js'

/**
 * An indication that a violation of the TAP specification has occurred
 *
 * This can indicate a test point that exceeds the plan, a test point
 * encountered after a trailing plan, or in the case of `pragma +strict`,
 * any non-TAP data.
 */
export type TapError = Result | { tapError: string; [k: string]: any }

/**
 * A representation of a TestPoint result, with diagnostics if present.
 */
export class Result {
  public ok: boolean
  public name: string = ''
  public id: number = 0
  public buffered: boolean = false
  public tapError: string | null = null
  public skip: boolean | string = false
  public todo: boolean | string = false
  public previous: Result | null = null
  public plan: Plan | null = null
  public diag: any | null = null
  public time: number | null = null
  public fullname: string = ''
  public closingTestPoint: boolean = false

  constructor(parsed: string[], parser: Parser) {
    const ok = !parsed[1]
    const id = +(parsed[2] || 0)
    let buffered = parsed[4]
    this.ok = ok
    if (parsed[2]) this.id = id

    let rest = parsed[3] || ''
    let name: string
    // We know at this point the parsed result cannot contain \n,
    // so we can leverage that as a placeholder.
    // first, replace any PAIR of \ chars with \n
    // then, split on any # that is not preceeded by \
    // the first of these is definitely the description
    // the rest is the directive, if recognized, otherwise
    // we just lump it onto the description, but escaped.
    // then any \n chars in either are turned into \ (just one)

    // escape \ with \
    // swap out escaped \ with \n, then swap back
    rest = rest.replace(/(\\\\)/g, '\n')
    const [h, ...r] = rest.split(/(?<=\s|^)(?<!\\)#/g)
    name = (h || '').replace(/\\#/g, '#').replace(/\n/g, '\\')
    rest = r.join('#').replace(/\\#/g, '#').replace(/\n/g, '\\')

    // now, let's see if there's a directive in there.
    const dir = parseDirective(rest.trim())
    if (!dir) name += (rest ? '#' + rest : '') + buffered
    else {
      // handle buffered subtests with todo/skip on them, like
      // ok 1 - bar # todo foo {\n
      const dirKey: Directive = dir[0]
      const dirValue = dir[1]
      if (dirKey === 'todo' || dirKey === 'skip') {
        this[dirKey] = dirValue
      } else {
        if (dirKey === 'time') {
          this.time = parseFloat(dirValue)
          /* c8 ignore start */
        }
        /* c8 ignore stop */
      }
    }

    if (OPEN_BRACE_EOL.test(name)) {
      name = name.replace(OPEN_BRACE_EOL, '')
      buffered = '{'
    }

    if (buffered === '{') this.buffered = true

    if (name) this.name = name.trim()
    const n: string[] = []
    if (parser.fullname) n.push(parser.fullname)
    if (this.name) n.push(this.name)
    this.fullname = n.join(' > ').trim()
  }
}

/**
 * Static `parse()` and `stringify()` methods
 */
import etoa from 'events-to-array'
type EventLog = ReturnType<typeof etoa>
import { Parser, ParserOptions } from './index.js'
import { esc } from './escape.js'
import yaml from 'tap-yaml'
import { SPACE_OPEN_BRACE_EOL } from './brace-patterns.js'

// used in flattening mode
const getId = (): { (): number; current: number } => {
  const id = () => id.current++
  id.current = 1
  return id
}

/**
 * Parse a TAP text stream into a log of all the events encountered
 */
export const parse = (
  str: string,
  options: ParserOptions,
): EventLog => {
  const { flat = false } = options
  const ignore = ['line', 'pass', 'fail', 'todo', 'skip', 'result']
  if (flat) ignore.push('assert', 'child', 'plan', 'complete')
  const parser = new Parser(options)
  const events = etoa(parser, ignore)
  if (flat) {
    const id = getId()
    parser.on('result', res => {
      res.name = res.fullname
      res.id = id()
      events.push(['assert', res])
    })
    parser.on('complete', res => {
      if (!res.bailout)
        events.push(['plan', { end: id.current - 1, start: 1 }])
      events.push(['complete', res])
    })
  }

  parser.end(str)
  return events
}

/**
 * Turn an EventLog from {@link tap-parser!statics.parse} into a TAP string
 */
export const stringify = (
  msg: EventLog,
  { flat = false, indent = '', id = getId() },
): string => {
  const ind = flat ? '' : indent
  return (
    ind +
    msg
      .map(item => {
        switch (item[0]) {
          case 'child':
            const comment = item[1][0]
            const child = item[1].slice(1)
            return (
              Parser.stringify([comment], {
                flat,
                indent: '',
                id,
              }) +
              Parser.stringify(child, {
                flat,
                indent: '    ',
                id,
              })
            )

          case 'version':
            return 'TAP version ' + item[1] + '\n'

          case 'plan':
            if (flat) {
              if (indent !== '') return ''
              item[1].start = 1
              item[1].end = id.current - 1
            }
            return (
              item[1].start +
              '..' +
              item[1].end +
              (item[1].comment ? ' # ' + esc(item[1].comment) : '') +
              '\n'
            )

          case 'pragma':
            return 'pragma ' + (item[2] ? '+' : '-') + item[1] + '\n'

          case 'bailout':
            return (
              'Bail out!' + (item[1] ? ' ' + esc(item[1]) : '') + '\n'
            )

          case 'assert':
            const res = item[1]
            if (flat) {
              res.id = id()
              res.name = res.fullname
            }
            return (
              (res.ok ? '' : 'not ') +
              'ok' +
              (res.id ? ' ' + res.id : '') +
              (res.name ?
                ' - ' +
                esc(res.name).replace(SPACE_OPEN_BRACE_EOL, '')
              : '') +
              (res.skip ?
                ' # SKIP' +
                (res.skip === true ? '' : ' ' + esc(res.skip))
              : '') +
              (res.todo ?
                ' # TODO' +
                (res.todo === true ? '' : ' ' + esc(res.todo))
              : '') +
              (res.time ? ' # time=' + res.time + 'ms' : '') +
              '\n' +
              (res.diag ?
                '  ---\n  ' +
                yaml
                  .stringify(res.diag)
                  .split('\n')
                  .join('\n  ')
                  .trim() +
                '\n  ...\n'
              : '')
            )

          case 'extra':
          case 'comment':
            return item[1]
        }
      })
      .join('')
      .split('\n')
      .join('\n' + ind)
      .trim() +
    '\n'
  )
}

/**
 * JSON object representation of {@link @tapjs/core!counts.Counts}
 */
export interface CountsJSON {
  total: number
  pass: number
  fail?: number
  skip?: number
  todo?: number
  complete?: number
}

/**
 * Class representing a count of all the assertions in a test
 *
 * The {@link @tapjs/core!counts.Counts#complete} field is only used when counting tests and
 * tracking their completion status.
 *
 * @internal
 */
export class Counts {
  total: number = 0
  pass: number = 0
  fail: number = 0
  skip: number = 0
  todo: number = 0
  complete: number = 0
  constructor(c?: Counts | CountsJSON) {
    if (c) Object.assign(this, c)
  }
  toJSON(): CountsJSON {
    const c: CountsJSON = {
      total: this.total,
      pass: this.pass,
    }
    if (this.fail) c.fail = this.fail
    if (this.todo) c.todo = this.todo
    if (this.skip) c.skip = this.skip
    if (this.complete) c.complete = this.complete
    return c
  }
}

import yaml from 'tap-yaml'
import { cleanYamlObject } from './clean-yaml-object.js'
import { Extra } from './index.js'

const hasDefinedKey = (o: Extra) => {
  for (const v of Object.values(o)) {
    if (v !== undefined && v !== null) return true
  }
  return false
}

/**
 * Print the YAML diagnostics based on the {@link @tapjs/core!index.Extra}
 * object received
 */
export const diags = (obj: Extra) => {
  const clean = cleanYamlObject(obj)
  if (!clean || typeof clean !== 'object' || !hasDefinedKey(clean)) {
    return ''
  }
  return (
    '  ---\n' +
    yaml
      .stringify(clean)
      .split('\n')
      .map(l => (l.trim() ? '  ' + l : l.trim()))
      .join('\n') +
    '  ...\n'
  )
}

import * as stack from '@tapjs/stack'
import { createTwoFilesPatch } from 'diff'
import { readFileSync } from 'node:fs'
import { format, strict } from 'tcompare'
import { extraFromError } from './extra-from-error.js'
import { Extra } from './index.js'

const tryReadFile = (path: string) => {
  try {
    return readFileSync(path, 'utf8')
  } catch (_) {
    return
  }
}

const hasOwn = (obj: { [k: string]: any }, key: string) =>
  Object.prototype.hasOwnProperty.call(obj, key)

/**
 * Prepare an object for printing to YAML diagnostics.
 *
 * Looks up source, calculates diffs of actual/expected values, and so on.
 */
export const cleanYamlObject = (
  obj: { [k: string]: any },
  seen: Set<any> = new Set(),
) => {
  seen.add(obj)
  const res = { ...obj }
  seen.add(res)
  if (hasOwn(res, 'stack') && !hasOwn(res, 'at')) {
    const st =
      Array.isArray(res.stack) ?
        res.stack.map(s => String(s).trimEnd() + '\n').join('')
      : String(res.stack)
    const p = stack.parseStack(st)
    res.at = p[0]
    res.stack = p.map(c => String(c) + '\n').join('')
  }
  if (
    typeof res.stack === 'string' &&
    res.stack &&
    !res.stack.endsWith('\n')
  ) {
    res.stack = res.stack.trimEnd() + '\n'
  }

  /* c8 ignore start - old pre-Error.cause custom decorator */
  if (res.errorOrigin && typeof res.errorOrigin === 'object') {
    res.errorOrigin = cleanYamlObject(res.errorOrigin, seen)
  }
  /* c8 ignore stop */

  if (
    res.cause &&
    typeof res.cause === 'object' &&
    !seen.has(res.cause)
  ) {
    seen.add(res.cause)
    const { message } = res.cause
    const ex = extraFromError(res.cause)
    const clean = cleanYamlObject(ex, seen)
    if (message !== undefined) clean.message = message
    res.cause = clean
  }
  if (Array.isArray(res.errors)) {
    const cleaned: Extra[] = []
    for (const e of res.errors) {
      if (!!e && typeof e === 'object') {
        seen.add(e)
        const { message } = e
        const ex = extraFromError(e)
        const clean = cleanYamlObject(ex, seen)
        if (message !== undefined) clean.message = message
        cleaned.push(clean)
      } else {
        cleaned.push(e)
      }
    }
    res.errors = cleaned
  }

  if (
    res.at &&
    res.at instanceof stack.CallSiteLike &&
    res.at.fileName &&
    res.at.absoluteFileName &&
    res.at.lineNumber &&
    !res.source
  ) {
    const file = res.at.absoluteFileName
    const content = tryReadFile(file)
    if (content) {
      const lines = content.split('\n')
      if (res.at.lineNumber <= lines.length) {
        const startLine = Math.max(res.at.lineNumber - 3, 0)
        const endLine = Math.min(res.at.lineNumber + 2, lines.length)
        const line = lines[res.at.lineNumber - 1]
        const caret =
          (
            res.at.columnNumber &&
            line &&
            res.at.columnNumber <= line.length
          ) ?
            ['-'.repeat(res.at.columnNumber - 1) + '^']
          : []
        const context = lines
          .slice(startLine, res.at.lineNumber)
          .concat(caret)
          .concat(lines.slice(res.at.lineNumber, endLine))
        const csplit = context.join('\n').trimEnd()
        if (csplit) res.source = csplit + '\n'
      }
    }
  }
  if (res.at && res.at instanceof stack.CallSiteLike) {
    res.at = res.at.toJSON()
  }

  // show a line by line string diff
  // diff the yaml, to make it more humane, especially
  // when strings or buffers are very large or multi-line
  // the shipped compare methods will generally supply
  // their own diff, which is much nicer.
  if (
    'found' in res &&
    'wanted' in res &&
    res.found !== res.wanted &&
    !res.diff
  ) {
    const f = res.found
    const w = res.wanted
    if (typeof f === 'string' && typeof w === 'string')
      res.diff = createTwoFilesPatch(
        'expected',
        'actual',
        w + '\n',
        f + '\n',
      ).replace(/^=+\n/, '')
    else if (
      f &&
      w &&
      typeof f === 'object' &&
      typeof w === 'object'
    ) {
      const s = strict(f, w)
      if (!s.match) {
        res.diff = s.diff
      } else {
        res.note = 'object identities differ'
      }
    } else {
      // some mixed stringly bits
      const ff = format(f)
      const fw = format(w)
      const fs = (typeof f === 'string' ? f : ff) + '\n'
      const ws = (typeof w === 'string' ? w : fw) + '\n'
      if (fw !== ff) {
        res.diff = createTwoFilesPatch(
          'expected',
          'actual',
          ws,
          fs,
        ).replace(/^=+\n/, '')
      }
    }
    if (res.diff === '--- expected\n+++ actual\n') {
      delete res.diff
    }
    if (res.diff) {
      delete res.found
      delete res.wanted
    }
  }

  for (const [key, value] of Object.entries(res)) {
    if (shouldDeleteKey(key, value)) {
      delete res[key]
    }
  }

  // if the 'message' is a string, then we print it on the
  // test point, so no need to repeat in the diags
  if (typeof res.message === 'string') delete res.message

  // worker: remove inline code
  if (
    res.eval === true &&
    typeof res.filename === 'string' &&
    res.filename.includes('\n')
  ) {
    res.filename = '<inline code>'
  }
  // always put cause at the end.
  if (res.cause) {
    const { cause, ...props } = res
    return { ...props, cause }
  }

  return res
}

/**
 * Properties that are *always* removed from the diagnostics, either because
 * they are internal (eg, `time`), overly noisy (eg, `parent`), or captured
 * elsewhere in the TAP output (eg, `skip`).
 */
export const deleteAlways = new Set([
  'todo',
  'time',
  'childId',
  'cb',
  'name',
  'indent',
  'skip',
  'bail',
  'diagnostic',
  'buffered',
  'parent',
  // only relevant if activated, a failedTodo or failedSkip will be added
  'failSkip',
  'failTodo',
  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'grep',
  'grepInvert',
  'only',
  'saveFixture',
  'env',
])

/**
 * Fields on this list are removed from YAML diagnostics if they are empty
 * (ie, falsey, empty array, or object with no keys)
 */
export const deleteIfEmpty = new Set([
  'at',
  'stack',
  'context',
  'debug',

  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'runOnly',
  'compareOptions',
])

/**
 * Fields are removed from YAML diagnostics if they match any of these
 * patterns.
 */
export const deleteIfMatch = [
  /^_?tapChild/,
  /^tapStream/,
  // TODO: create a @tapjs/mocha plugin
  /^tapMochaTest/,
]

const shouldDeleteKey = (key: string, value: any) =>
  deleteAlways.has(key) ||
  (deleteIfEmpty.has(key) && isEmpty(value)) ||
  deleteIfMatch.some(r => r.test(key))

// return true if object is empty, including inherited properties
const isEmpty = (obj: any): obj is {} => {
  if (!obj) {
    return true
  }
  if (typeof obj !== 'object') {
    return false
  }
  if (Array.isArray(obj)) {
    return obj.length === 0
  }
  for (const _ in obj) {
    return false
  }
  return true
}

import { builtinModules } from 'node:module'
import { resolve } from 'node:path'
import { CallSiteLike } from './call-site-like.js'
import { requireResolve } from './require-resolve.js'
export { CallSiteLike, CallSiteLikeJSON } from './call-site-like.js'
export type { GeneratedResult } from './call-site-like.js'
export type { Compiled, LineRef } from './parse.js'

const regExpEscape = (s: string) =>
  s.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&')

// platform portability accommodation
/* c8 ignore start */
let cwd =
  (
    typeof process === 'object' &&
    process &&
    typeof process.cwd === 'function'
  ) ?
    process.cwd()
  : undefined
/* c8 ignore stop */

/**
 * Set the effective cwd for shortening filenames in stack traces
 * Set to `undefined` to show full absolute paths.
 */
export const setCwd = (c: string | undefined) => (cwd = c)

/**
 * Get the effective cwd for shortening filenames in stack traces
 * If set to `undefined`, then will show full absolute paths.
 */
export const getCwd = () => cwd

let filterNodeInternals = true
/**
 * Get the current value indicating whether node internals should be
 * filtered out. (Defaults to true)
 */
export const getFilterNodeInternals = () => filterNodeInternals
/**
 * Set whether node internals should be filtered out.
 */
export const setFilterNodeInternals = (s: boolean) =>
  (filterNodeInternals = s)

// these are packages that function somewhat like node internals,
// for tap's purposes, and `@tapjs` packages themselves, when loaded
// from node_modules.
const ignoredPackages: string[] = [
  '@tapjs',
  '@isaacs/ts-node-temp-fork-for-pr-2009',
  'pirates',
  'function-loop',
  '@cspotcode/source-map-support',
  'signal-exit',
  'async-hook-domain',
]
let dirty: boolean = false

/**
 * Add a package name to the list of deps that should be excluded
 * from stack traces.
 */
export const addIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i === -1) {
    ignoredPackages.push(s)
    dirty = true
  }
}

/**
 * Remove a package name from the list of deps that should be excluded
 * from stack traces.
 */
export const removeIgnoredPackage = (s: string) => {
  const i = ignoredPackages.indexOf(s)
  if (i !== -1) {
    ignoredPackages.splice(i, 1)
    dirty = true
  }
}

/**
 * Get a read-only copy of the list of deps that should be excluded
 * from stack traces.
 */
export const getIgnoredPackages = () =>
  Object.freeze(ignoredPackages.slice(0))

const getTestBuiltPath = () => {
  const p = requireResolve('@tapjs/test')
  // we'll always find the test class in this project
  /* c8 ignore start */
  if (!p) return ''
  /* c8 ignore stop */
  return resolve(p, '../../../test-built')
}

const buildIgnoredPackages = () => {
  // just a safety precaution, no reason to ever do this
  /* c8 ignore start */
  if (!ignoredPackages.length) return undefined
  /* c8 ignore stop */
  const p = ignoredPackages.map(s => regExpEscape(s)).join('|')
  const nm = `[/\\\\]node_modules[/\\\\](?:${p})([/\\\\]|$)`
  // if we are ignoring @tapjs/test, then also ignore its built
  // plugged-in implementation. This is only relevant when developing
  // this project, or other cases where @tapjs/test may be linked,
  // because when it's loaded from node_modules, it'll be excluded
  // by virtue of being in that folder anyhow.
  const built =
    (
      ignoredPackages.includes('@tapjs') ||
      ignoredPackages.includes('@tapjs/test')
    ) ?
      getTestBuiltPath()
    : ''
  const re = built ? `${built}([/\\\\].*|$)|${nm}` : nm
  return new RegExp(re)
}

/**
 * exported for testing, no real purpose, but also no harm in looking
 */
export const getIgnoredPackagesRE = () =>
  !dirty ? ignoredPackagesRE : (
    (ignoredPackagesRE = buildIgnoredPackages())
  )

let ignoredPackagesRE: RegExp | undefined = buildIgnoredPackages()
let filterIgnoredPackages = true

/**
 * Set whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const setFilterIgnoredPackages = (s: boolean) =>
  (filterIgnoredPackages = s)

/**
 * Get whether or not the list of ignored packages should
 * be excluded from stack traces.
 */
export const getFilterIgnoredPackages = () => filterIgnoredPackages

// detect the first line of Error.stack, 'Error: blah'
const isErrorStackHead = (c?: CallSiteLike): boolean =>
  !!c &&
  c.lineNumber === null &&
  c.columnNumber === null &&
  c.this === undefined &&
  c.evalOrigin === undefined &&
  c.function === undefined &&
  c.typeName === null &&
  c.methodName === null &&
  (typeof c.functionName === 'string' || !c.functionName) &&
  c.isEval === false &&
  c.isNative === false &&
  c.isToplevel === false &&
  c.isConstructor === false &&
  c.generated === undefined

const filter = (c: CallSiteLike): boolean => {
  const s = c.fileName
  // technically this is possible, but super unlikely
  /* c8 ignore start */
  if (!s) return true
  /* c8 ignore stop */
  if (dirty && filterIgnoredPackages) {
    ignoredPackagesRE = buildIgnoredPackages()
    dirty = false
  }
  return (
    (!filterNodeInternals ||
      !(s.startsWith('node:') || builtinModules.includes(s))) &&
    (!filterIgnoredPackages || !ignoredPackagesRE?.test(s))
  )
}

const clean = (c: CallSiteLike[]): CallSiteLike[] => {
  const filtered = c.filter(filter)
  while (isErrorStackHead(filtered[0])) filtered.shift()
  if (cwd !== undefined) {
    for (const c of filtered) {
      c.cwd = cwd
    }
  }
  return filtered
}

let capturing = false
/**
 * Get an array of {@link @tapjs/stack!call-site-like.CallSiteLike} objects for
 * the current location, from the call to the `fn` argument supplied, limited
 * to the number of frames specified by `limit`.
 *
 * If the `limit` argument is 0, then the current `Error.stackTraceLimit`
 * value will be used.
 *
 * This method is not re-entry safe, due to the fact that it relies on
 * temporarily overriding the global Error.prepareStackTrace. As a result,
 * if a capture() is triggered in any of the methods used by the
 * CallSiteLike constructor (for example, if `@tapjs/intercept` is used to
 * capture the process.cwd() method, which is used by path.resolve()),
 * then that will cause problems. To work around this, if a re-entry is
 * detected, then an empty stack of [] is returned.
 *
 * Even if it was made re-entry safe, it would be easy to accidentally
 * trigger an infinite recursion and stack overflow in such a scenario, so
 * returning an empty stack in the case of re-entry is the best workaround.
 */
export function capture(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(limit: number): CallSiteLike[]
export function capture(
  fn: Function | ((...a: any[]) => any),
): CallSiteLike[]
export function capture(): CallSiteLike[]
export function capture(
  limit: number | Function | ((...a: any[]) => any) = 0,
  fn: Function | ((...a: any[]) => any) = capture,
): CallSiteLike[] {
  // should be impossible to hit, but can if we have to look up
  // a sourcemap for a file we haven't seen before, and the filename
  // happens to come through as a url or relative path.
  /* c8 ignore start */
  if (capturing) return []
  /* c8 ignore stop */
  capturing = true
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const { prepareStackTrace, stackTraceLimit } = Error
  Error.prepareStackTrace = CallSiteLike.prepareStackTrace
  if (limit) {
    // we always get an extra few frames to account for internals
    // or proxy frames that might be filtered out of the top.
    Error.stackTraceLimit = limit + 10
  }
  const obj: { stack: CallSiteLike[] } = { stack: [] }
  Error.captureStackTrace(obj, fn)
  const { stack } = obj
  Object.assign(Error, { prepareStackTrace, stackTraceLimit })
  const st = clean(stack)
  capturing = false
  return limit === 0 ? st : st.slice(0, limit)
}

/**
 * Get the call site in the stack either where `at()` is called, or
 * where the supplied `fn` function is called.
 *
 * If `fn` is provided, and is not in the current call stack, then
 * `undefined` will be returned.
 */
export const at: (
  fn?: Function | ((...a: any[]) => any),
) => CallSiteLike | undefined = (fn = at) => {
  const [site] = capture(1, fn)
  return site
}

/**
 * Same as {@link @tapjs/stack!index.capture}, but return the `toString()`
 * values of the {@link @tapjs/stack!call-site-like.CallSiteLike} objects
 */
export function captureString(
  limit: number,
  fn: Function | ((...a: any[]) => any),
): string
export function captureString(limit: number): string
export function captureString(
  fn?: Function | ((...a: any[]) => any),
): string
export function captureString(): string
export function captureString(
  limit: number | Function | ((...a: any[]) => any) = Infinity,
  fn: Function | ((...a: any[]) => any) = captureString,
): string {
  if (typeof limit === 'function') {
    fn = limit
    limit = 0
  }
  const stack = capture(limit, fn)
  return (limit === 0 ? stack : stack.slice(0, limit))
    .map(c => String(c) + '\n')
    .join('')
}

/**
 * Get an array of {@link CallSiteLike} objects corresponding to the stack
 * trace of the Error object provided.
 *
 * This does _not_ actually look at the current call site, or do anything
 * magical with the V8 engine. It's just parsing a string.
 *
 * While some effort is made to interpret stacks correctly when an Error
 * contains a `name` and `message`, remember that the `Error.stack` property in
 * JavaScript is remarkably sloppy. In some cases, if the `Error.message`
 * contains `\n` and some lines after the first look like stack trace lines,
 * incorrect data may result. It's only as good as the stack you pass to it.
 */
export const captureError = (
  e: Error | NodeJS.ErrnoException,
): CallSiteLike[] => {
  // errors almost always have these fields
  const { message = '', name = '', code } = e as NodeJS.ErrnoException
  const stack = typeof e.stack === 'string' ? e.stack : ''
  const head = name && message ? `${name}: ${message}\n` : ''
  const errnoHead =
    name && message && code ? `${name} [${code}]: ${message}` : ''
  const cleanHead = !!head && stack.startsWith(head)
  const cleanErrnoHead = !!errnoHead && stack.startsWith(errnoHead)

  const s =
    cleanHead ? stack.substring(head.length)
    : cleanErrnoHead ? stack.substring(errnoHead.length)
    : stack
  const cleaned = clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l)
      .map(line => new CallSiteLike(e, line)),
  )

  // if we didn't clean the header cleanly, then sweep the stack for
  // any weird junk it might contain
  return cleanHead ? cleaned : (
      cleaned.filter(c => !isErrorStackHead(c))
    )
}

/**
 * Get a processed string stack corresponding to the stack trace of the Error
 * object provided.
 *
 * This method has all the same caveats as {@link captureError}. If the
 * object provided has a weird looking `stack` property, then you might get
 * weird results.
 */
export const captureErrorString = (e: Error): string =>
  captureError(e)
    .map(c => String(c) + '\n')
    .join('')

/**
 * Parse a stack string and return an array of CallSiteLike objects
 *
 * We use this to get the `at` diagnostic callsite when all we have is
 * a stack, either from a native Error object, or a stringified CallSiteLike
 * stack.
 */
export const parseStack = (s: string): CallSiteLike[] =>
  clean(
    s
      .trimEnd()
      .split('\n')
      .filter(l => !!l.trim())
      .map(line => new CallSiteLike(null, line)),
  )

export const expandStack = (s?: string | CallSiteLike[]): string => {
  if (!s) return ''
  if (typeof s === 'string') return expandStack(parseStack(s))
  return clean(s)
    .map(c => c.toString(true) + '\n')
    .join('')
}

import { findSourceMap, SourceMap } from 'module'
import { isAbsolute, relative, resolve } from 'path'
import { fileURLToPath } from 'url'
import {
  Compiled,
  isCompiledCallSiteLine,
  parseCallSiteLine,
} from './parse.js'

const methodRe = /^(.*?) \[as (.*?)\]$/

export interface CallSiteLikeJSON {
  fileName?: string
  lineNumber?: number
  columnNumber?: number
  evalOrigin?: CallSiteLikeJSON
  typeName?: string
  methodName?: string
  functionName?: string
  isEval?: true
  isNative?: true
  isToplevel?: true
  isConstructor?: true
  generated?: {
    fileName?: string
    lineNumber?: number
    columnNumber?: number
  }
}

export interface GeneratedResult {
  fileName?: string | null
  lineNumber?: number | null
  columnNumber?: number | null
}

const isCallSite = (c: any): c is NodeJS.CallSite =>
  !!c && typeof c === 'object' && c.constructor?.name === 'CallSite'

export class CallSiteLike {
  static prepareStackTrace(e: Error, c: NodeJS.CallSite[]) {
    return c.map(c => new CallSiteLike(e, c))
  }

  #fileName?: string | null
  #cwd?: string
  lineNumber: ReturnType<NodeJS.CallSite['getLineNumber']>
  columnNumber: ReturnType<NodeJS.CallSite['getColumnNumber']>
  this: ReturnType<NodeJS.CallSite['getThis']>
  evalOrigin?: CallSiteLike
  function: ReturnType<NodeJS.CallSite['getFunction']>
  typeName: ReturnType<NodeJS.CallSite['getTypeName']>
  methodName: ReturnType<NodeJS.CallSite['getMethodName']>
  functionName: ReturnType<NodeJS.CallSite['getFunctionName']>
  isEval: ReturnType<NodeJS.CallSite['isEval']>
  isNative: ReturnType<NodeJS.CallSite['isNative']>
  isToplevel: ReturnType<NodeJS.CallSite['isToplevel']>
  isConstructor: ReturnType<NodeJS.CallSite['isConstructor']>
  generated?: GeneratedResult
  #sourceMap?: SourceMap

  // normalize and relativize filename if cwd is set
  get fileName() {
    return this.#relativize(this.#fileName)
  }

  get absoluteFileName() {
    if (!this.#fileName) return this.#fileName
    else if (this.#fileName.startsWith('file://')) {
      return fileURLToPath(this.#fileName)
    } else {
      return this.#fileName
    }
  }

  get cwd(): string | undefined {
    return this.#cwd
  }

  set cwd(cwd: string | undefined) {
    if (cwd === undefined) {
      if (this.generated) {
        this.generated.fileName = this.#derelativize(
          this.generated?.fileName,
        )
      }
    }
    this.#cwd = cwd?.replace(/\\/g, '/')
    if (cwd !== undefined) {
      if (this.generated) {
        this.generated.fileName = this.#relativize(
          this.generated?.fileName,
        )
      }
    }
    if (this.evalOrigin) this.evalOrigin.cwd = cwd
  }

  constructor(
    e: Error | null,
    c: NodeJS.CallSite | string | Compiled,
  ) {
    if (typeof c === 'string') {
      c = parseCallSiteLine(c)
    }

    if (isCallSite(c)) {
      const fileName = c.getFileName()
      this.#fileName = typeof fileName === 'string' ? fileName : null
      this.lineNumber = c.getLineNumber()
      this.columnNumber = c.getColumnNumber()
      this.this = c.getThis()
      const evalOrigin = c.getEvalOrigin()
      if (evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, evalOrigin)
      }
      this.function = c.getFunction()
      this.typeName = c.getTypeName()
      this.methodName = c.getMethodName()
      this.functionName = c.getFunctionName()
      this.isEval = c.isEval()
      this.isNative = c.isNative()
      this.isToplevel = c.isToplevel()
      this.isConstructor = c.isConstructor()
    } else if (isCompiledCallSiteLine(c)) {
      // compiled object from stack line
      this.isEval = !!c.isEval
      this.isToplevel = false
      if (c.evalOrigin) {
        this.evalOrigin = new CallSiteLike(e, c.evalOrigin)
      }

      this.lineNumber =
        c.lineNumber === undefined ? null : c.lineNumber
      this.columnNumber =
        c.columnNumber === undefined ? null : c.columnNumber
      const fileName = c.fileName
      this.#fileName = typeof fileName === 'string' ? fileName : null
      const { generated } = c
      if (generated) {
        this.generated = generated
        this.generated.fileName = this.#relativize(generated.fileName)
      }
      let fname = c.fname?.trim()
      let method: null | string = null
      this.isNative = !!c.isNative

      if (fname) {
        if (fname.startsWith('new ')) {
          this.isConstructor = true
          fname = fname.substring('new '.length).trim()
        } else {
          this.isConstructor = false
        }
        this.methodName = null
        const methodMatch = fname.match(methodRe) as [
          string,
          string,
          string,
        ]
        if (methodMatch) {
          fname = methodMatch[1]
          method = methodMatch[2]
        }
        const dots = fname.split('.')
        const m = dots.pop()
        if (m !== undefined) {
          this.typeName = dots.join('.').trim() || null
          this.methodName = method || m
          if (this.methodName.match(/^get |set /)) {
            this.methodName = this.methodName.substring(4)
          }
          this.functionName = m
          // we know it's not undefined, but TS is afraid of pop()
          /* c8 ignore start */
        } else {
          this.functionName = null
          this.typeName = null
        }
        /* c8 ignore stop */
      } else {
        this.isConstructor = false
        this.typeName = null
        this.functionName = null
        this.methodName = null
      }
    } else {
      throw new Error('invalid call site information provided')
    }

    // This is a slight deviation from the CallSite API, but it's very useful
    // to have a field that's the actual function with type and method name.
    if (
      this.typeName &&
      this.functionName &&
      !this.functionName.startsWith(this.typeName)
    ) {
      this.functionName = `${this.typeName}.${this.functionName}`
    } else if (this.functionName === this.methodName) {
      this.methodName = null
    }

    // We only do the sourcemap lookup if we're parsing from a CallSite
    // If we get it from an Error stack line, then Node has already done
    // the mapping for us, and when ts-node (and other in-place
    // transpilers) create JavaScript, they use the same filename, leading
    // to an incorrect double offset,.
    if (
      this.#fileName &&
      isCallSite(c) &&
      !this.#sourceMap &&
      !this.#fileName.startsWith('node:')
    ) {
      // Passing an object that isn't an actual Error object to
      // findSourceMap causes problems in node 16
      /* c8 ignore start */
      const sme = e && e instanceof Error ? e : undefined
      /* c8 ignore stop */
      this.#sourceMap = findSourceMap(this.#fileName, sme)
      if (this.#sourceMap && typeof this.lineNumber === 'number') {
        // SourceMap.findEntry doesn't actually return the line/column
        // number, despite the property names, but rather the zero-indexed
        // line/column start of a mapping range, and must be looked up
        // using the zero-indexed line and column.
        // To find the mapping, we look it up with the zero-indexed
        // line/col, then figure out how far our line/col is from the
        // mapping, and apply that same offset to the start of the origin
        // in the mapping.
        const payload = this.#sourceMap.findEntry(
          // safety bounds around numbers here, impossible to hit
          // if we're looking up actual call sites though.
          /* c8 ignore start */
          Math.max(0, this.lineNumber - 1),
          Math.max(0, (this.columnNumber || 0) - 1),
          /* c8 ignore stop */
        )
        if (payload) {
          const offset: [number, number] = [
            this.lineNumber - payload.generatedLine,
            /* c8 ignore start */
            (this.columnNumber || 1) - payload.generatedColumn,
            /* c8 ignore stop */
          ]
          const originalLine = payload.originalLine + offset[0]
          const originalColumn = payload.originalColumn + offset[1]
          const genFilename = this.#relativize(this.#fileName)
          this.generated = {
            /* c8 ignore start */
            fileName: this.#relativize(genFilename || null),
            /* c8 ignore stop */
            lineNumber: this.lineNumber,
            columnNumber: this.columnNumber,
          }

          this.#fileName = payload.originalSource
          this.lineNumber = originalLine
          this.columnNumber = originalColumn
        }
      }
    }
  }

  #relativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    else f = f.replace(/\\/g, '/')
    try {
      const rel = relative(this.#cwd, f)
      return rel.length < f.length ? rel : f
    } catch {
      return f
    }
  }
  #derelativize(fileName?: string | null) {
    let f = fileName
    if (!f) return f
    if (f.startsWith('node:')) return f
    if (f.startsWith('file://')) f = fileURLToPath(f)
    if (this.#cwd === undefined) return f
    try {
      return resolve(this.#cwd, f)
    } catch {
      return f
    }
  }

  toString(jsStyle = false): string {
    // in js style mode, use the origin source file if it is within
    // our cwd. Otherwise, use the generated source location.
    const useGen =
      jsStyle &&
      this.generated?.fileName &&
      this.fileName &&
      this.generated.fileName !== this.fileName &&
      (isAbsolute(this.fileName) || this.fileName.startsWith('..'))
    const { fileName, lineNumber, columnNumber, generated } =
      useGen && this.generated ?
        {
          fileName: this.#derelativize(this.generated.fileName),
          lineNumber: this.generated.lineNumber,
          columnNumber: this.generated.columnNumber,
          generated: undefined,
        }
      : jsStyle ?
        {
          fileName: this.#derelativize(this.fileName),
          lineNumber: this.lineNumber,
          columnNumber: this.columnNumber,
          generated: undefined,
        }
      : this
    const loc = { fileName, lineNumber, columnNumber, generated }
    for (const l of [loc, loc.generated]) {
      if (l?.fileName) {
        l.fileName =
          jsStyle ?
            this.#derelativize(l.fileName)
          : this.#relativize(l.fileName)
      }
    }

    let fname = this.functionName || ''
    let tn = ''
    let tnGet = ''
    let tnSet = ''
    if (this.typeName) {
      const mn = this.methodName || '<anonymous>'
      tn = this.typeName + '.' + mn
      tnGet = this.typeName + '.get ' + mn
      tnSet = this.typeName + '.set ' + mn
    }
    if (!fname && tn) {
      fname = tn
    }
    const mn = this.methodName
    if (
      fname &&
      tn &&
      mn &&
      fname !== tn &&
      fname !== tnGet &&
      fname !== tnSet
    ) {
      fname += ` [as ${mn}]`
    }
    if (this.isConstructor && fname) {
      fname = `new ${fname}`
    }
    let ev = ''
    const nat = this.isNative ? 'native' : ''
    let file = loc.fileName || ''
    const hasLC = loc.lineNumber && loc.columnNumber
    if (this.evalOrigin) {
      ev = `eval at ${this.evalOrigin.toString(jsStyle)}`
      if (hasLC) {
        const f = loc.fileName || '<anonymous>'
        let lr = `${f}:${loc.lineNumber}:${loc.columnNumber}`
        if (loc.generated && loc.generated.fileName) {
          const f = loc.generated.fileName
          const { lineNumber: l, columnNumber: c } = loc.generated
          lr = `${f}:${l}:${c} (${lr})`
        }
        ev += `, ${lr}`
      }
      // should always have an fname at this point
      /* c8 ignore start */
      ev = fname ? ` (${ev})` : ev
      /* c8 ignore stop */
      return `${fname}${ev}`
    }

    if (file || hasLC) {
      if (hasLC) {
        /* c8 ignore start */
        file = file || '<anonymous>'
        /* c8 ignore stop */
        file += `:${loc.lineNumber}:${loc.columnNumber}`
      }
    } else if (nat) {
      file = 'native'
      // impossible in normal cases
      /* c8 ignore start */
    } else {
      file = ''
    }
    /* c8 ignore stop */
    let g = ''
    if (loc.generated && loc.generated.fileName) {
      const { fileName, lineNumber, columnNumber } = loc.generated
      g = fileName
      /* c8 ignore start */
      if (!g) g = ''
      /* c8 ignore stop */
      if (g === loc.fileName) g = ''
      if (g) {
        if (
          typeof lineNumber === 'number' &&
          typeof columnNumber === 'number'
        ) {
          g += `:${lineNumber}:${columnNumber}`
        }
        if (ev || fname) {
          g = ` (${g})`
        }
      }
    }
    if (file && (ev || fname || g)) {
      file = ` (${file})`
    }
    const pre = jsStyle ? '    at ' : ''
    return `${pre}${fname}${ev}${g}${file}`
  }

  toJSON(): CallSiteLikeJSON {
    const {
      fileName,
      lineNumber,
      columnNumber,
      evalOrigin,
      typeName,
      methodName,
      functionName,
      isEval,
      isNative,
      isToplevel,
      isConstructor,
      generated,
    } = this
    const json: CallSiteLikeJSON = {}
    if (fileName !== null) json.fileName = fileName
    if (lineNumber || lineNumber === 0) json.lineNumber = lineNumber
    if (columnNumber || columnNumber === 0)
      json.columnNumber = columnNumber
    if (evalOrigin) json.evalOrigin = evalOrigin.toJSON()

    if (typeName !== null) {
      json.typeName = typeName
    }

    if (methodName !== null) json.methodName = methodName
    if (functionName !== null) json.functionName = functionName
    if (isEval) json.isEval = isEval
    if (isNative) json.isNative = isNative
    if (isToplevel) json.isToplevel = isToplevel
    if (isConstructor) json.isConstructor = isConstructor
    if (generated && generated.fileName) {
      const f = this.#relativize(generated.fileName)
      if (
        f &&
        typeof f === 'string' &&
        (f !== json.fileName || f === '<anonymous>')
      ) {
        const gen: Record<string, string | number> = {}
        gen.fileName = f
        if (generated.lineNumber)
          gen.lineNumber = generated.lineNumber
        if (generated.columnNumber)
          gen.columnNumber = generated.columnNumber
        if (Object.keys(gen).length > 0) {
          json.generated = gen
        }
      }
    }
    return json
  }
}

// Take a line from a stack trace and pull out its relevant parts.

const isCompiled = Symbol('compiled call site line')

export interface LineRef {
  fileName: string
  lineNumber: number
  columnNumber: number
  [isCompiled]: true
}

export interface NativeLineRef {
  isNative: true
  [isCompiled]: true
}

export const isLineRef = (lr?: Compiled | undefined): lr is LineRef =>
  !!lr &&
  typeof (lr as LineRef).lineNumber === 'number' &&
  typeof (lr as LineRef).columnNumber === 'number'

export interface Compiled {
  fname?: string
  fileName?: string
  lineNumber?: number
  columnNumber?: number
  generated?: LineRef
  evalOrigin?: Compiled
  isEval?: boolean
  isNative?: boolean
  [isCompiled]: true
}

export const isCompiledCallSiteLine = (c: any) =>
  !!c && typeof c === 'object' && c[isCompiled] === true

const lineRef =
  '(?:((?:node:|file:)?[^:]+):([1-9][0-9]*):([1-9][0-9]*)|(native))'
const lineRefLoose = '(?:(.+):([1-9][0-9]*):([1-9][0-9]*))'
const bareLineRef = `^(?:${lineRef})$`
const bareLineRefRe = new RegExp(bareLineRef)
const bareLineRefLoose = `^${lineRefLoose}$`
const bareLineRefLooseRe = new RegExp(bareLineRefLoose)
const parenLineRef = `\\(${lineRef}\\)`
const parenLineRefExec = new RegExp(parenLineRef, 'g')
const parenLineRefLoose = `([^(]+) \\(${lineRefLoose}\\)$`
const parenLineRefLooseRe = new RegExp(parenLineRefLoose)
const trailingLineRefs = `((?: ${parenLineRef})+)$`
const trailingLineRefsRe = new RegExp(trailingLineRefs)
// $1 - evalOrigin fname
// $2 - evalOrigin linerefs (origin src)
// $3 - lineref (generated if $4)
// $4 - src lineref ($3 is generated if present)
const evalOrigin = `eval at(.*?)((?: ${parenLineRef})+)(?:, (${lineRef}(?: \\((${lineRef})\\))?))?`
const bareEvalOrigin = `^${evalOrigin}$`
const bareEvalOriginRe = new RegExp(bareEvalOrigin)
const withEvalOrigin = `(.*?) \\((${evalOrigin})\\)$`
const withEvalOriginRe = new RegExp(withEvalOrigin)

// this is the first-phase parse, which might be confused by a line like
// Cls.[foo (parens) bar] (file:1:2)
// If we get a filename containing (, then chop off the bit from that
// point and put it back on fname.
//
// This heuristic assumes that parens are more likely to appear in
// method names than filenames, which has been a safe assumption so far.
export const parseCallSiteLine = (line: string): Compiled => {
  const c = parseCallSiteLine_(line)
  const paren = c.fileName?.lastIndexOf(' (')
  if (c.fileName && paren && paren !== -1) {
    const s = c.fileName.substring(0, paren)
    const f = c.fileName.substring(paren + 2)
    c.fname += ` (${s}`
    c.fileName = f
  }
  // if we ended up with an fname and nothing else, try a more liberal approach
  if (c.fname && !c.fileName && !c.columnNumber && !c.lineNumber) {
    const bl = c.fname.match(bareLineRefLooseRe) as
      | null
      | [string, string, string, string]
    if (bl) {
      c.fname = undefined
      c.fileName = bl[1]
      c.lineNumber = +bl[2]
      c.columnNumber = +bl[3]
    } else {
      const pl = c.fname.match(parenLineRefLooseRe) as
        | null
        | [string, string, string, string, string]
      if (pl) {
        c.fname = pl[1]
        c.fileName = pl[2]
        c.lineNumber = +pl[3]
        c.columnNumber = +pl[4]
      }
    }
  }
  return c
}

const parseCallSiteLine_ = (line: string): Compiled => {
  line = line.replace(/^\s+at /, '')

  // just a lineref, nothing else:
  const bm = line.match(bareLineRefRe) as
    | null
    | [string, string, string, string, string]
  if (bm && bm[4] && !bm[1] && !bm[2] && !bm[3]) {
    return { fileName: bm[4], [isCompiled]: true }
  }
  if (bm) {
    return {
      fileName: bm[1],
      lineNumber: +bm[2],
      columnNumber: +bm[3],
      [isCompiled]: true,
    }
  }

  // an eval origin subsection
  const em = line.match(bareEvalOriginRe) as
    | null
    | [string, string, string]
  if (em) {
    // we ignore the part that comes after the `,` because that is part of
    // the parent's call site, not the evalOrigin
    return {
      ...parseLineRefs(em[2]),
      fname: em[1],
    }
  }

  const wem = line.match(withEvalOriginRe) as
    | null
    | [
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
        string,
      ]
  if (wem) {
    const evalOrigin = parseCallSiteLine(wem[2])
    return {
      ...parseLineRefs(wem[9]),
      fname: wem[1],
      isEval: true,
      evalOrigin,
      [isCompiled]: true,
    }
  }

  // now we know it is either a lineref with fname, or multiple linerefs
  // if we have two linerefs, that meaks it's a toString of a callsite
  // with a sourcemap.
  // if it's one, it might be either a fname, or a fname-less callsite with
  // a sourcemap.
  return parseLineRefs(line)
}

const compileLineRefParse = (
  file: string | undefined,
  line: string | undefined,
  col: string | undefined,
  other: string | undefined,
): LineRef | NativeLineRef | Compiled => {
  if (other === 'native') {
    return { isNative: true, [isCompiled]: true }
    // no other types defined, but could be eventually?
    /* c8 ignore start */
  } else if (other) {
    return { fileName: other, [isCompiled]: true }
    /* c8 igore stop */
  } else if (file && line && col) {
    return {
      fileName: file,
      lineNumber: +line,
      columnNumber: +col,
      [isCompiled]: true,
    }
  } else {
    return { fname: file, [isCompiled]: true }
  }
}

// parse a set of trailing line refs or a bare lineref and 1 or more trailing
const parseLineRefs = (line: string): Compiled => {
  const tm = line.match(trailingLineRefsRe)
  if (!tm) {
    const bm = line.match(bareLineRefRe)
    return bm ?
        compileLineRefParse(bm[1], bm[2], bm[3], bm[4])
      : { fname: line, [isCompiled]: true }
  }

  const lineRefs: Compiled[] = []
  const tms = tm[0]
  let pre = line.substring(0, tm.index)
  let m: RegExpExecArray | null
  while ((m = parenLineRefExec.exec(tms))) {
    lineRefs.push(compileLineRefParse(m[1], m[2], m[3], m[4]))
  }
  // figure out if the bit before the paren linerefs was also a lineref
  if (lineRefs.length === 1) {
    const bm = pre.match(bareLineRefRe)
    if (bm && bm[1] && bm[2] && bm[3]) {
      pre = pre.substring(0, bm.index)
      // pre = ''
      lineRefs.unshift({
        fileName: bm[1],
        lineNumber: +bm[2],
        columnNumber: +bm[3],
        [isCompiled]: true,
      })
    }
  }
  // at this point the LAST lineref is the one we care about, and any
  // that preceed it are the generated result.
  const lastLR = lineRefs.pop()
  /* c8 ignore start */
  // very impossible, but TS is afraid of pop()
  if (!lastLR) return { fname: line, [isCompiled]: true }
  /* c8 ignore stop */
  const generated = lineRefs.pop()
  // shouldn't be any left, but possible if you had a function name
  // that looked like a line ref, though V8 puts [] around those
  /* c8 ignore start */
  pre += lineRefs
    .map(lr =>
      isLineRef(lr) ?
        ` (${lr.fileName}:${lr.lineNumber}:${lr.columnNumber})`
      : lr.isNative ? ` (native)`
      : lr.fileName ? ` (${lr.fileName})`
      : '',
    )
    .join('')
  /* c8 ignore stop */

  const r = {
    fname: pre,
    generated: isLineRef(generated) ? generated : undefined,
    ...lastLR,
    [isCompiled]: true,
  } as Compiled
  return r
}

// commonjs style, just pull the resolve function from our require
const { resolve } = require
export const requireResolve = (s: string) => {
  try {
    return resolve(s)
  } catch {
    return null
  }
}

