  printArrayEmpty(): void {
    this.memo += this.style.arrayEmpty(this.getClass())
  }

  printArrayHead(): void {
    this.memo += this.style.arrayHead(this.getClass())
  }

  printArrayBody(): void {
    if (this.objectAsArray) {
      this.objectAsArray.forEach((val, key) =>
