        SpawnOptionsWithStdioTuple<
          StdioNull | StdioPipe,
          StdioNull | StdioPipe,
          StdioNull | StdioPipe
        >
      >,
  options?:
    | WithExternalID<SpawnOptions>
    | WithExternalID<SpawnOptionsWithoutStdio>
    | WithExternalID<
        SpawnOptionsWithStdioTuple<
          StdioNull | StdioPipe,
          StdioNull | StdioPipe,
          StdioNull | StdioPipe
        >
      >
