export type ExecArgs =
  | [
      command: string,
      callback?: (
        error: ExecException | null,
        stdout: string,
        stderr: string
      ) => void
    ]
  | [
      command: string,
      options: WithExternalID<
        {
          encoding: 'buffer' | null
        } & ExecOptions
      >,
      callback?: (
        error: ExecException | null,
        stdout: Buffer,
        stderr: Buffer
      ) => void
    ]
  | [
      command: string,
      options: WithExternalID<
        {
          encoding: BufferEncoding
        } & ExecOptions
      >,
      callback?: (
        error: ExecException | null,
        stdout: string,
        stderr: string
      ) => void
    ]
  | [
      command: string,
      options: WithExternalID<
        {
          encoding: BufferEncoding
        } & ExecOptions
      >,
      callback?: (
        error: ExecException | null,
        stdout: string | Buffer,
        stderr: string | Buffer
      ) => void
    ]
  | [
      command: string,
      options: WithExternalID<ExecOptions>,
      callback?: (
        error: ExecException | null,
        stdout: string,
        stderr: string
      ) => void
    ]
  | [
      command: string,
      options:
        | WithExternalID<ObjectEncodingOptions & ExecOptions>
        | undefined
        | null,
      callback?: (
        error: ExecException | null,
        stdout: string | Buffer,
        stderr: string | Buffer
      ) => void
    ]
export function exec(...args: ExecArgs): ChildProcess {
  const [cmd, options, callback] = args
  if (typeof options === 'function') {
    return cpExec(cmd, spawnOpts<ExecOptions>({}, getExclude(k), []), options)
  } else if (!options) {
    return cpExec(
      cmd,
      spawnOpts<ExecOptions>({}, getExclude(k), []),
      callback as (
        error: ExecException | null,
        stdout: string | Buffer,
        stderr: string | Buffer
      ) => void
    )
  }

  return cpExec(
    cmd,
    spawnOpts(options, getExclude(k), []),
    callback as (
      error: ExecException | null,
      stdout: string | Buffer,
      stderr: string | Buffer
    ) => void
  )
}

