export const defaultExclude =
  /(^|[\\\/])(node_modules|\.tap|tap-testdir-.*?|tap-snapshots|tests?|[^\\\/]+\.test\.([cm]?[jt]s|[jt]sx?)|__tests?__)([\\\/]|$)/i

const parseExclude = (src: string | undefined): RegExp | undefined => {
  if (!src) return
  const parsed = src.match(/^\/(.*)\/([a-z]*)$/) as
    | null
    | [string, string, string]
  if (parsed) {
    try {
      return new RegExp(parsed[1], parsed[2])
    } catch (e) {}
  }
}

const proc = process

export const getExclude = (k: string, useDefault = true) => {
  const src = proc.env[k]
  const exclude = parseExclude(src) || (useDefault ? defaultExclude : /$./)
  proc.env[k] = String(exclude)
  return exclude
}

import { ProcessEnvOptions } from 'child_process'
import { nodeOptionsEnv } from './node-options-env.js'

export type WithExternalID<T> = T & { externalID?: string }
const p = process

export const spawnOpts = <T extends { [k: string]: any }>(
  options: WithExternalID<T>,
  exclude?: RegExp | string,
  args?: readonly string[]
): Omit<T, 'externalID'> & { env: ProcessEnvOptions } => {
  const { externalID, ...rest } = options
  const env = { ...(options.env || p.env) }
  // only add if lacking in args, but this can come in multiple ways
  const a = [
    ...(options.execArgv ?? []),
    ...(args ?? []),
    ...(options.args ?? []),
  ]
  env.NODE_OPTIONS = nodeOptionsEnv(env, a)
  if (externalID) {
    env._TAPJS_PROCESSINFO_EXTERNAL_ID_ = externalID
  }
  if (exclude) {
    env._TAPJS_PROCESSINFO_EXCLUDE_ = String(exclude)
  }
  return { ...rest, env }
}

import { argvToNodeOptions } from './argv-to-node-options.js'
import {
  importLoader,
  importMatch,
  legacyLoader,
  legacyMatch,
} from './loader-paths.js'
import { nodeOptionsToArgv } from './node-options-to-argv.js'

import Module from 'node:module'

const getKeyValue = (
  args: readonly string[],
  i: number
): [boolean, string, string | undefined] => {
  const arg = args[i]
  /* c8 ignore start */
  if (typeof arg !== 'string') throw new Error('invalid arg')
  /* c8 ignore stop */
  if (arg.includes('=')) {
    const [k, ...rest] = arg.split('=') as [string, ...string[]]
    return [true, k, rest.join('=')]
  } else if (i < args.length - 1) {
    return [false, arg, args[i + 1]]
  } else {
    return [false, arg, undefined]
  }
}

const useImport = !!(Module as { register?: (...a: any[]) => any })
  .register

// JUST test if we need to do anything at all with the env.
// if the loader is set already in the args, even incorrectly, return true
const hasLoader = (args: readonly string[]): boolean => {
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || arg === '--') break
    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      continue
    }
    if (!eq) i++
    if (
      (k === '--experimental-loader' || k === '--loader') &&
      legacyMatch(v)
    ) {
      return true
    }
    if (k === '--import' && importMatch(v)) return true
  }
  return false
}

const rmLoader = (args: string[]) => {
  const doNotWantKeys = ['--experimental-loader', '--loader', '--import']

  const result: string[] = []

  let doubledash = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, remove it
      continue
    }
    // not ours, leave it
    result.push(arg)
    const next = args[i]
    if (!eq && typeof next === 'string') result.push(next)
    continue
  }
  return result
}

const addLoader = (args: string[]) => {
  const addKey = useImport ? '--import' : '--loader'
  const addValue = useImport ? importLoader : legacyLoader
  const doNotWantKeys = [
    '--experimental-loader',
    useImport ? '--loader' : '--import',
  ]
  const test = useImport ? importMatch : legacyMatch

  const result: string[] = []

  let doubledash = false
  let found = false
  for (let i = 0; i < args.length; i++) {
    const arg = args[i]
    /* c8 ignore start */
    if (typeof arg !== 'string') throw new Error('invalid arg')
    /* c8 ignore stop */
    if (!arg.startsWith('--') || doubledash) {
      result.push(arg)
      continue
    }
    if (arg === '--') {
      result.push(arg)
      doubledash = true
      continue
    }

    const [eq, k, v] = getKeyValue(args, i)
    if (!v) {
      // wasn't a key-value pair
      result.push(arg)
      continue
    }
    if (!eq) i++
    if (doNotWantKeys.includes(k) && (importMatch(v) || legacyMatch(v))) {
      // it's ours, but not how we want it, omit
      continue
    }

    if (k === addKey && test(v)) {
      // already present, don't let it be set multiple times.
      if (found) continue
      found = true
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
    } else {
      // not ours
      result.push(arg)
      const next = args[i]
      if (!eq && typeof next === 'string') result.push(next)
      continue
    }
  }
  if (!found) result.push(`${addKey}=${addValue}`)
  return !useImport ? addIgnoreLoadersWarning(result) : result
}

const addIgnoreLoadersWarning = (args: readonly string[]) =>
  args.includes('--no-warnings') ||
  args.includes('--no-warnings=ExperimentalLoader')
    ? args
    : args.concat('--no-warnings')

export const nodeOptionsEnv = (
  env: NodeJS.ProcessEnv,
  args: readonly string[]
) => {
  // if we already have the loader in args, don't add to NODE_OPTIONS
  const no = nodeOptionsToArgv(env.NODE_OPTIONS)
  return argvToNodeOptions(hasLoader(args) ? rmLoader(no) : addLoader(no))
}

import { resolve } from 'path'
import { getImportMetaURL } from './get-import-meta-url.js'

import { fileURLToPath } from 'url'

export const legacyLoader = getImportMetaURL('../esm/loader-legacy.mjs')
export const importLoader = getImportMetaURL('../esm/import.mjs')
const legacyLoaderPath = fileURLToPath(legacyLoader)
const importLoaderPath = fileURLToPath(importLoader)

export const legacyMatch = (p: string): boolean => {
  const d = p.startsWith('file://')
    ? fileURLToPath(p)
    : decodeURIComponent(p)
  return (
    d === '@tapjs/processinfo/loader' ||
    p === legacyLoader ||
    resolve(d) === legacyLoaderPath
  )
}

export const importMatch = (p: string): boolean => {
  const d = p.startsWith('file://')
    ? fileURLToPath(p)
    : decodeURIComponent(p)
  return (
    d === '@tapjs/processinfo/import' ||
    p === importLoader ||
    resolve(d) === importLoaderPath
  )
}

export const nodeOptionsToArgv = (no: string | undefined): string[] => {
  if (!no) return []
  const argv = []
  let escaping = false
  let inquote = false
  let tokStart = 0
  let tok = ''
  for (let i = 0; i < no.length; i++) {
    const c = no.charAt(i)
    switch (c) {
      case '"':
        if (escaping) {
          tok += no.slice(tokStart, i - 1) + '"'
          tokStart = i + 1
          escaping = false
        } else if (inquote) {
          tok += no.slice(tokStart, i)
          tokStart = i + 1
          inquote = false
        } else {
          inquote = true
          tok += no.slice(tokStart, i)
          tokStart = i + 1
        }
        continue

      case '\\':
        if (inquote) {
          escaping = true
        }
        continue

      case ' ':
        if (inquote) continue
        tok += no.slice(tokStart, i)
        tokStart = i + 1
        argv.push(tok)
        tok = ''
        continue

      default:
        escaping = false
        continue
    }
  }
  if (inquote) {
    return []
  }
  tok += no.slice(tokStart)
  argv.push(tok)
  return argv
}

import type { ProcessInfo } from './index.js'

export interface ProcessInfoNodeData {
  // set initially, but deleted before it is written
  hrstart?: [number, number]

  // always set
  date: string
  argv: string[]
  execArgv: string[]
  NODE_OPTIONS?: string
  cwd: string
  pid: number
  ppid: number
  parent: string | null
  uuid: string
  files: string[]
  sources: Record<string, string[]>

  // fields that are only set when the process completes
  root?: string | null
  externalID?: string | null
  code?: number | null
  signal?: NodeJS.Signals | null
  runtime?: number
  globalsAdded?: string[]
}

export class ProcessInfoNode {
  date!: string
  argv!: string[]
  execArgv!: string[]
  NODE_OPTIONS?: string
  cwd!: string
  pid!: number
  ppid!: number
  uuid!: string
  code?: number | null
  signal?: NodeJS.Signals | null
  runtime?: number
  globalsAdded?: string[]
  files: string[] = []
  sources: Record<string, string[]> = Object.create(null)
  parent?: ProcessInfoNode | null = null
  root?: ProcessInfoNode | null = null
  children?: Set<ProcessInfoNode> | null = null
  descendants: Set<ProcessInfoNode> | null = null
  externalID: string | null = null

  #data: ProcessInfoNodeData

  constructor(data: ProcessInfoNodeData) {
    this.#data = data
    Object.assign(this, data)
  }

  toJSON() {
    return Object.fromEntries(
      Object.entries(this)
        .filter(([_, val]) => val !== null && val !== undefined)
        .map(([key, val]) =>
          val instanceof ProcessInfoNode
            ? [key, val.uuid]
            : key === 'children' || key === 'descendants'
            ? [key, [...val].map(c => c.uuid)]
            : [key, val]
        )
    )
  }

  link(db: ProcessInfo) {
    db.uuids.set(this.uuid, this)
    if (this.#data.parent) {
      this.parent = db.uuids.get(this.#data.parent) || this.parent
    }
    if (this.#data.root) {
      this.root = db.uuids.get(this.#data.root) ?? this.root
    }

    if (this.parent === null) {
      this.root = this
      const pendingRoot = db.pendingRoot.get(this.uuid)
      if (pendingRoot) {
        this.descendants = pendingRoot
        for (const n of pendingRoot) {
          n.root = this
        }
        db.pendingRoot.delete(this.uuid)
      }
      db.roots.add(this)
    } else if (typeof this.root === 'string') {
      const pendingRoot = db.pendingRoot.get(this.root)
      if (pendingRoot) {
        pendingRoot.add(this)
      } else {
        db.pendingRoot.set(this.root, new Set([this]))
      }
    } else if (this.root) {
      this.root.descendants ??= new Set()
      this.root.descendants.add(this)
    }

    if (typeof this.parent === 'string') {
      const pendingParent = db.pendingParent.get(this.parent)
      if (pendingParent) {
        pendingParent.add(this)
      } else {
        db.pendingParent.set(this.parent, new Set([this]))
      }
    } else if (this.parent) {
      if (!this.parent.children) {
        this.parent.children = new Set([this])
      } else {
        this.parent.children.add(this)
      }
    }

    const pendingParent = db.pendingParent.get(this.uuid)
    if (pendingParent) {
      this.children = pendingParent
      for (const n of this.children) {
        n.parent = this
      }
      db.pendingParent.delete(this.uuid)
    }

    for (const f of this.files as string[]) {
      const files = db.files.get(f)
      if (!files) {
        db.files.set(f, new Set<ProcessInfoNode>([this]))
      } else {
        files.add(this)
      }
    }

    if (this.externalID) {
      // newer tagged entry wins
      const e = db.externalIDs.get(this.externalID)
      if (e && Date.parse(e.date) > Date.parse(this.date)) {
        return
      }
      db.externalIDs.set(this.externalID, this)
    }
  }
}

