/**
 * Union of {@link @tapjs/core!test-base.TestBase} plus all plugin
 * return values
 */
export type TTest<P extends PluginSet = PluginSet> = TestBase &
  PluginResult<P>

/**
 * Interface that is the assembled result of every loaded plugin.
 *
 * This is extended into an interface because otherwise the code
 * hinting is overwhelmingly extravagant.
 */
export interface BuiltPlugins extends PluginResult<PluginSet> {}

const applyPlugins = <
  Ext extends BuiltPlugins,
  Opts extends TestOpts,
>(
  base: Test<Ext, Opts>,
  plugs: (TapPlugin<any, Opts> | TapPlugin<any>)[] = plugins() as (
    | TapPlugin<any>
    | TapPlugin<any, Opts>
  )[],
): Test<Ext, Opts> & Ext => {
  const ext: Plug<Opts>[] = plugs
    // typecast in case we have *only* option-less plugins.
    .map(p =>
      (p as TapPlugin<Plug<Opts>, TestBaseOpts>)(base, base.options),
    )
    .concat(base)
  const getCache = new Map<any, any>()
  // extend the proxy with Object.create, and then set the toStringTag
  // to 'Test', so we don't get stack frames like `Proxy.<anonymous>`
  const t = Object.create(
    new Proxy(base, {
      has(_, p) {
        for (const t of ext) {
          if (Reflect.has(t, p)) return true
        }
        return false
      },
      ownKeys() {
        const k: PlugKeys[] = []
        for (const t of ext) {
          const keys = Reflect.ownKeys(t) as PlugKeys[]
          k.push(...keys)
        }
        return [...new Set(k)]
      },
      set(_, p, v) {
        // check to see if there's any setters, and if so, set it there
        // otherwise, just set on the base
        let didSet = false
        if (getCache.has(p)) getCache.delete(p)
        for (const t of ext) {
          let o: Object | null = t
          while (o) {
            // assign to the all plugs that can receive it
            const prop = Reflect.getOwnPropertyDescriptor(o, p)
            if (prop) {
              if (prop.set || prop.writable) {
                //@ts-ignore
                t[p] = v
                didSet = true
              }
              break
            }
            o = Reflect.getPrototypeOf(o)
          }
        }
        if (!didSet) {
          // if nothing has that field, assign to the base
          //@ts-ignore
          base[p] = v
        }
        return true
      },
      get(_, p) {
        if (p === 'parent') {
          return base.parent?.t
        }
        // cache get results so t.blah === t.blah
        // we only cache functions, so that getters aren't memoized
        // Of course, a getter that returns a function will be broken,
        // at least when accessed from outside the plugin, but that's
        // a pretty narrow caveat, and easily documented.
        if (getCache.has(p)) return getCache.get(p)
        for (const plug of ext) {
          if (p in plug) {
            //@ts-ignore
            const v = plug[p]
            // Functions need special handling so that they report
            // the correct toString and are called on the correct object
            // Otherwise attempting to access #private props will fail.
            if (typeof v === 'function') {
              if (getCache.has(v)) return getCache.get(v)
              const vv: Function = copyFunction<Ext, Opts>(t, plug, v)
              getCache.set(p, vv)
              // aliases remain aliases
              getCache.set(v, vv)
              return vv
            } else {
              return v
            }
          }
        }
      },
    }),
  )

  // assign a reference to the extended Test for use in plugin at run-time
  Object.assign(base, { t })
  // put the .t self-ref and plugin inspection on top of the stack
  const top = {
    t,
    get pluginLoaded() {
      return <T extends any = any>(
        plugin: (t: any, opts?: any) => T,
      ) => {
        return plugs.includes(plugin)
      }
    },
    get plugins() {
      return [...plugs]
    },
  }
  ext.unshift(top)

  //@ts-ignore
  const tst: string = base[Symbol.toStringTag]
  Object.defineProperty(t, Symbol.toStringTag, {
    value: tst,
    configurable: true,
  })
  Object.defineProperty(top, Symbol.toStringTag, {
    value: tst,
    configurable: true,
  })
  return t
}

const kPluginSet = Symbol('@tapjs/test construction plugin set')
const kClass = Symbol('@tapjs/test construction class')
/**
 * Option object used when extending the `Test` class via
 * {@link @tapjs/test!index.Test.applyPlugin}
 *
 * @internal
 */
export type PluginExtensionOption<
  E extends BuiltPlugins = BuiltPlugins,
  O extends TestOpts = TestOpts,
> = {
  [kPluginSet]: TapPlugin<any, O>[]
  [kClass]?: typeof Test<E, O>
}

/**
 * interface defining the fully extended {@link @tapjs/test!index.Test} class.
 */
export interface Test<
  Ext extends BuiltPlugins = BuiltPlugins,
  Opts extends TestOpts = TestOpts,
> extends TTest {
  /**
   * Explicitly mark the test as completed, outputting the TAP plan line if
   * needed.
   *
   * This is not required to be called if the test function returns a promise,
   * or if a plan is explicitly declared and eventually fulfilled.
   *
   * @group Test Lifecycle Management
   */
  end(): this
  /**
   * Specify the number of Test Points expected by this test.
   * Outputs a TAP plan line.
   *
   * @group Test Lifecycle Management
   */
  plan(n: number, comment?: string): void
}

/**
 * This is the class that is extended for the root {@link @tapjs/core!tap.TAP}
 * test, and used to instantiate test objects in its child tests. It extends
 * {@link @tapjs/core!test-base.TestBase}, and implements the union of return
 * values of all loaded plugins via a Proxy.
 */
export class Test<
    Ext extends BuiltPlugins = BuiltPlugins,
    Opts extends TestOpts = TestOpts,
  >
  extends TestBase
  implements TTest
{
  #Class: typeof Test<Ext, Opts>
  #pluginSet: TapPlugin<any, Opts>[]

  /**
   * @param opts Test options for this instance
   *
   * @param __INTERNAL Extension option used by the subclasses created in
   * {@link @tapjs/test!index.Test#applyPlugin}.
   *
   * @internal
   */
  constructor(
    opts: Opts,
    __INTERNAL: PluginExtensionOption<Ext, Opts> = {
      [kPluginSet]: plugins() as TapPlugin<any, Opts>[],
      [kClass]: Test,
    },
  ) {
    super(opts)
    this.#Class = __INTERNAL[kClass] as typeof Test<Ext, Opts>
    const pluginSet = __INTERNAL[kPluginSet]
    this.#pluginSet = pluginSet
    type T = Test<Ext, Opts> & Ext
    // need to ignore this because it's a ctor that returns a value.
    /* c8 ignore start */
    return applyPlugins(this, pluginSet) as T
  }
  /* c8 ignore stop */

  /**
   * The string signature of the plugins built into this Test class
   */
  get pluginSignature() {
    return signature
  }

  /**
   * Add a plugin at run-time.
   *
   * Creates a subclass of {@link @tapjs/test!index.Test} which has the
   * specified plugin, and which applies the plugin to all child tests it
   * creates.
   *
   * Typically, it's best to load plugins using configuration, set via the
   * `tap plugin <add|rm>` command.
   *
   * However, in some cases, for example while developing plugins or if a
   * certain plugin is only needed in a small number of tests, it can be
   * useful to apply it after the fact.
   *
   * This is best used sparingly, as it may result in poor typescript
   * compilation performance, which can manifest in slower test start-up times
   * and lag loading autocomplete in editors. If you find yourself calling
   * applyPlugin often, consider whether it'd be better to just add the plugin
   * to the entire test suite, so that it can be built up front.
   *
   * @group Plugin Management
   */
  applyPlugin<B extends Object, O extends unknown = unknown>(
    plugin: TapPlugin<B, O>,
  ): Test<Ext & B, Opts & O> & Ext & B {
    if (this.printedOutput) {
      throw new Error(
        'Plugins must be applied prior to any test output',
      )
    }

    if (this.#pluginSet.includes(plugin as TapPlugin<B, Opts>)) {
      throw new Error('Plugin already applied')
    }

    type ExtExt = Ext & B
    type ExtOpts = Opts & O
    const p = plugin as TapPlugin<B, ExtOpts>
    const pluginSetExtended: TapPlugin<any, ExtOpts>[] = (
      this.#pluginSet as TapPlugin<any, ExtOpts>[]
    ).concat([p])
    const extended = this as unknown as Test<ExtExt, ExtOpts> & ExtExt
    class TestExtended extends Test<ExtExt, ExtOpts> {
      constructor(
        opts: ExtOpts,
        __INTERNAL: PluginExtensionOption<ExtExt, ExtOpts> = {
          [kPluginSet]: pluginSetExtended,
          [kClass]: TestExtended,
        },
      ) {
        super(opts, __INTERNAL)
      }
    }
    extended.#pluginSet = pluginSetExtended
    extended.#Class = TestExtended
    Object.defineProperty(TestExtended, 'name', {
      value: 'Test',
      configurable: true,
    })
    return applyPlugins<ExtExt, ExtOpts>(extended, pluginSetExtended)
  }

  // NB: this isn't ever actually called, because we add a pluginLoaded
  // method in the applyPlugins proxy, but it's here to establish the
  // type interface.
  /**
   * Return true if the specified plugin is loaded. Asserts that the
   * test object in question implements the return value of the plugin.
   *
   * @group Plugin Management
   */
  pluginLoaded<T extends any = any>(
    plugin: (t: any, opts?: any) => T,
  ): this is TestBase & T {
    plugin
    return false
  }
  /**
   * Return the set of plugins loaded by this Test
   *
   * @group Plugin Management
   */
  get plugins(): TapPlugin<any, Opts>[] {
    return []
  }

  /**
   * Create a child Test object and parse its output as a subtest
   *
   * @group Subtest Methods
   */
  test(
    name: string,
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  test(
    name: string,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  test(
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  test(
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  test(
    ...args: TestArgs<Test<Ext, Opts> & Ext, Opts>
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext> {
    const extra = parseTestArgs<Test<Ext, Opts> & Ext, Opts>(...args)
    return this.sub(
      this.#Class,
      extra,
      this.test,
    ) as PromiseWithSubtest<Test<Ext, Opts> & Ext>
  }

  /**
   * Create a subtest which is marked as `todo`
   *
   * @group Subtest Methods
   */
  todo(
    name: string,
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  todo(
    name: string,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  todo(
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  todo(
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  todo(
    ...args: TestArgs<Test<Ext, Opts> & Ext, Opts>
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext> {
    const extra = parseTestArgs<Test<Ext, Opts> & Ext, Opts>(...args)
    extra.todo = true
    return this.sub(
      this.#Class,
      extra,
      this.todo,
    ) as PromiseWithSubtest<Test<Ext, Opts> & Ext>
  }

  /**
   * Create a subtest which is marked as `skip`
   *
   * @group Subtest Methods
   */
  skip(
    name: string,
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  skip(
    name: string,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  skip(
    extra: Opts,
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  skip(
    cb: (t: Test<Ext, Opts> & Ext) => any,
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext>
  skip(
    ...args: TestArgs<Test<Ext, Opts> & Ext, Opts>
  ): PromiseWithSubtest<Test<Ext, Opts> & Ext> {
    const extra = parseTestArgs<Test<Ext, Opts> & Ext, Opts>(...args)
    extra.skip = true
    return this.sub(
      this.#Class,
      extra,
      this.skip,
    ) as PromiseWithSubtest<Test<Ext, Opts> & Ext>
  }
}

/**
 * Plugin class providing {@link @tapjs/after!After#after} and
 * {@link @tapjs/after!After#teardown} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import { isPromise } from 'is-actual-promise'

/**
 * Implementation class returned by plugin function
 */
export class After {
  #t: TestBase
  #onTeardown: (() => any)[] = []
  #didOnEOF: boolean = false

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Alias for {@link @tapjs/after!After#after}
   *
   * @group Test Lifecycle Management
   */
  teardown(fn: () => any) {
    return this.after(fn)
  }

  /**
   * Runs the supplied function after the test is completely finished, and
   * before the next test starts.
   *
   * @group Test Lifecycle Management
   */
  after(fn: () => any) {
    this.#onTeardown.push(fn)

    if (!this.#didOnEOF) {
      this.#didOnEOF = true
      const onEOF = this.#t.onEOF
      this.#t.onEOF = () => {
        const ret = onEOF()
        if (isPromise(ret)) {
          return ret.then(() => this.#callTeardown())
        }
        return this.#callTeardown()
      }
    }
  }

  /**
   * call the teardown functions
   *
   * @internal
   */
  #callTeardown(): void | Promise<void> {
    let fn: (() => any) | undefined
    while ((fn = this.#onTeardown.pop())) {
      try {
        const ret = fn.call(this.#t.t)
        if (isPromise(ret)) {
          return ret.then(() => this.#callTeardown())
        }
      } catch (e) {
        this.#onTeardown.length = 0
        this.#t.threw(e)
        return
      }
    }
  }
}

/**
 * Plugin method that creates the {@link @tapjs/after!After} instance
 */
export const plugin: TapPlugin<After> = (t: TestBase) => new After(t)

/**
 * Plugin class providing {@link @tapjs/after-each!AfterEach#afterEach}
 * on the {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import type { Test } from '@tapjs/test'
import { loop } from 'function-loop'

/**
 * Implementation class returned by plugin function
 */
export class AfterEach {
  static #refs = new Map<TestBase, AfterEach>()
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
    AfterEach.#refs.set(t, this)
    const pae = t.parent && AfterEach.#refs.get(t.parent)
    if (
      pae &&
      !t.silent &&
      (pae.#onAfterEach.length || pae.#parentOnAfterEach.length)
    ) {
      this.#parentOnAfterEach = [
        ...pae.#onAfterEach,
        ...pae.#parentOnAfterEach,
      ]
    } else {
      this.#parentOnAfterEach = []
    }
    if (this.#parentOnAfterEach.length) {
      const runMain = t.runMain
      t.runMain = (cb: () => void) => {
        runMain.call(t, () => this.#runAfterEach(cb))
      }
    }
  }
  #onAfterEach: ((t: Test) => any)[] = []
  #parentOnAfterEach: ((t: Test) => any)[] = []

  /**
   * Run the supplied function after every *child* test, and any of those
   * child tests' children, and so on.
   *
   * The test that has just completed is passed in as an argument to the
   * function. Note that at this point, the test is fully ended, so attempting
   * to call assertion methods on it will raise an error.
   *
   * @group Test Lifecycle Management
   */
  afterEach(fn: (t: Test) => any) {
    this.#onAfterEach.push(fn)
  }

  #runAfterEach(cb: () => void) {
    const onerr = (er: any) => {
      this.#t.threw(er)
      cb()
    }
    loop(
      this.#parentOnAfterEach.map(f => () => f(this.#t.t)),
      cb,
      onerr,
    )
  }
}

/**
 * Plugin method that creates the {@link @tapjs/after-each!AfterEach} instance
 */
export const plugin: TapPlugin<AfterEach> = (t: TestBase) =>
  new AfterEach(t)

export const loop = (
  arr: ((...a: any) => any)[],
  cb: () => any,
  onerr: (er: any) => any
) => loop_(arr, cb, onerr, 0)

const loop_ = <T extends () => any>(
  arr: ((...a: any[]) => any)[],
  cb: T,
  onerr: (er: any) => any,
  i: number
): ReturnType<T> | Promise<ReturnType<T>> => {
  while (i < arr.length) {
    const fn = arr[i]
    let ret: any = null
    try {
      ret = fn()
    } catch (er) {
      return onerr(er)
    }
    i++
    if (ret && typeof ret.then === 'function') {
      return ret.then(() => loop_(arr, cb, onerr, i), onerr)
    }
  }

  return cb()
}

import {
  Extra,
  extraFromError,
  MessageExtra,
  normalizeMessageExtra,
  TapPlugin,
  TestBase,
} from '@tapjs/core'
import * as stack from '@tapjs/stack'
import EventEmitter from 'events'
import { isPromise } from 'is-actual-promise'
import {
  CompareOptions,
  has,
  hasStrict,
  match,
  matchOnly,
  matchOnlyStrict,
  matchStrict,
  same,
  strict,
} from 'tcompare'
import { Deferred } from 'trivial-deferred'

export interface AssertOptions {
  compareOptions?: CompareOptions
}

import { normalizeThrowsArgs } from './normalize-throws-args.js'

export type ErrorMessageMatch = {
  message: string | RegExp
  [k: string]: any
}

export type ErrorNameMatch = {
  name: string | RegExp | null | undefined
  [k: string]: any
}

export type ErrorCodeMatch = {
  code: string | RegExp | number | null | undefined
  [k: string]: any
}

export type ErrorCauseMatch<C extends {}> = {
  cause: C
  [k: string]: any
}

export type ErrorErrorsMatch<C extends {}> = {
  errors: C[]
  [k: string]: any
}

export type ErrorMatch =
  | Error
  | typeof Error
  | ErrorMessageMatch
  | ErrorNameMatch
  | ErrorCodeMatch
  | ErrorCauseMatch<{}>
  | ErrorErrorsMatch<{}>
  | RegExp

export type ThrowsArgs =
  | []
  | [msg: string, extra?: Extra]
  | [wanted: ErrorMatch, ...messageExtra: MessageExtra]

export type ExpectedEmit = [
  emitted: boolean,
  emitter: EventEmitter | EventTarget,
  event: string,
  handler: (...a: any) => void,
  msg: string,
  extra: Extra,
]

// return true of every argument is an object
const objects = (...a: any[]): boolean =>
  !a.some(o => !o || typeof o !== 'object')

const hasOwn = <T extends {}>(
  obj: T,
  key: string | number | symbol,
) => Object.prototype.hasOwnProperty.call(obj, key)

export class Assertions {
  #t: TestBase
  #opts: CompareOptions
  #pendingEmits: ExpectedEmit[] = []
  #setOnBeforeEnd: boolean = false
  constructor(t: TestBase, { compareOptions }: AssertOptions) {
    this.#t = t
    // TODO: this is a pita to do in each plugin
    // Either there should be a straightforward interface for declaring
    // which fields get inherited, or maybe add some logic in TestBase
    // to inherit all but the select few that can't be.
    if (!compareOptions) {
      for (let p = t.parent; p && !compareOptions; p = p.parent) {
        compareOptions = p.options.compareOptions
      }
      if (compareOptions) t.options.compareOptions = compareOptions
    }
    this.#opts = compareOptions || {}
  }

  #onBeforeEnd() {
    for (const [emitted, emitter, event, handler, msg, extra] of this
      .#pendingEmits) {
      if (emitted) {
        this.#t.pass(msg, extra)
      } else {
        if (emitter instanceof EventEmitter) {
          emitter.removeListener(event, handler)
        } else {
          emitter.removeEventListener(event, handler)
        }
        this.#t.fail(msg, extra)
      }
    }
  }

  /**
   * Verify that the value is truthy
   *
   * @group Assertion Methods
   */
  ok(obj: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.ok
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    return obj ? this.#t.pass(...me) : this.#t.fail(...me)
  }

  /**
   * Verify that the value is not truthy
   *
   * @group Assertion Methods
   */
  notOk(obj: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notOk
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    return !obj ? this.#t.pass(...me) : this.#t.fail(...me)
  }

  /**
   * Verify that the values are equal
   *
   * @group Assertion Methods
   */
  equal<T extends unknown>(
    found: any,
    wanted: T,
    ...[msg, extra]: MessageExtra
  ): found is T {
    this.#t.currentAssert = this.#t.t.equal
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equal', args)
    if (found === wanted) return this.#t.pass(...me)
    if (objects(found, wanted)) {
      const { match, diff } = strict(found, wanted, this.#opts)
      if (!match) {
        Object.assign(me[1], { diff, compare: '===' })
      } else {
        Object.assign(me[1], {
          found,
          wanted,
          note: 'object identities differ',
          compare: '===',
        })
      }
    } else {
      Object.assign(me[1], { found, wanted, compare: '===' })
    }
    return this.#t.fail(...me)
  }

  /**
   * Verify that the values are not equal
   *
   * @group Assertion Methods
   */
  not(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.not
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not be equal', args)
    if (found !== doNotWant) {
      return this.#t.pass(...me)
    }
    Object.assign(me[1], {
      found,
      doNotWant,
      compare: '!==',
    })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is of the type specified
   * Type can be either a string, or a constructor.
   *
   * If a string, then it can match either the `typeof` result
   * or 'null' for `null` values, or the `name` property of the
   * object's constructor.
   *
   * @group Assertion Methods
   */
  type(
    obj: any,
    klass: string | Function,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.type

    const name =
      typeof klass === 'function' ?
        klass.name || '(anonymous constructor)'
      : klass

    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(`type is ${name}`, args)

    // simplest case, it literally is the same thing
    if (obj === klass) {
      return this.#t.pass(me[0], me[1])
    }

    const tof = typeof obj
    const type =
      !obj && tof === 'object' ? 'null'
        // treat as object, but not Object
        // t.type(() => {}, Function)
      : (
        tof === 'function' &&
        typeof klass === 'function' &&
        klass !== Object
      ) ?
        'object'
      : tof

    if (
      (type === 'number' && klass === Number) ||
      (type === 'string' && klass === String) ||
      (type === 'bigint' && klass === BigInt) ||
      (klass === 'array' && Array.isArray(obj)) ||
      (type === 'symbol' && klass === Symbol)
    ) {
      return this.#t.pass(...me)
    }

    if (type === 'object' && klass !== 'object') {
      if (typeof klass === 'function') {
        me[1].found = Object.getPrototypeOf(obj).constructor.name
        me[1].wanted = name
        return this.ok(obj instanceof klass, ...me)
      }

      // check prototype chain for name
      // at this point, we already know klass is not a function
      // if the klass specified is an obj in the proto chain, pass
      // if the name specified is the name of a ctor in the chain, pass
      for (let p = obj; p; p = Object.getPrototypeOf(p)) {
        const ctor = p.constructor && p.constructor.name
        if (p === klass || ctor === name) {
          return this.#t.pass(...me)
        }
      }
    }

    return this.equal(type, name, ...me)
  }

  /**
   * Verify that the value is loosely equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  same(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.same
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should be equivalent', args)
    const { match, diff } = same(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is not loosely equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  notSame(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not be equivalent', args)
    const { match } = same(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is strictly equivalent to the supplied pattern
   *
   * @group Assertion Methods
   */
  strictSame<T extends unknown>(
    found: any,
    wanted: T,
    ...[msg, extra]: MessageExtra
  ): found is T {
    this.#t.currentAssert = this.#t.t.strictSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should be equivalent strictly',
      args,
    )
    const { match, diff } = strict(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value is not strictly equivalent to the supplied
   * pattern object
   *
   * @group Assertion Methods
   */
  strictNotSame(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.strictNotSame
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should not be equivalent strictly',
      args,
    )
    const { match } = strict(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object has all of the properties and values in the
   * pattern, matching loosely.
   *
   * @group Assertion Methods
   */
  has(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.has
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should be equivalent',
      args,
    )
    const { match, diff } = has(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object does NOT have all of the properties and values
   * in the pattern, matching loosely.
   *
   * @group Assertion Methods
   */
  notHas(found: any, doNotWant: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.notHas
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should not be equivalent',
      args,
    )
    const { match } = has(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value has all of the properties and values in the
   * pattern, matching strictly.
   *
   * @group Assertion Methods
   */
  hasStrict(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.hasStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should be equivalent strictly',
      args,
    )
    const { match, diff } = hasStrict(found, wanted, this.#opts)
    if (match) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does NOT contain all of the properties and
   * values in the test pattern, comparing strictly.
   *
   * Note that this will pass if the value has *some* of the listed properties,
   * or if they do not match the same type.
   *
   * @group Assertion Methods
   */
  notHasStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notHasStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'all provided fields should not be equivalent strictly',
      args,
    )
    const { match } = hasStrict(found, doNotWant, this.#opts)
    if (!match) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided
   *
   * @group Assertion Methods
   */
  match(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.match
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = match(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does NOT match the pattern provided.
   *
   * @group Assertion Methods
   */
  notMatch(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatch
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = match(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, with no
   * extra properties.
   *
   * @group Assertion Methods
   */
  matchOnly(found: any, wanted: any, ...[msg, extra]: MessageExtra) {
    this.#t.currentAssert = this.#t.t.matchOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchOnly(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, with no
   * extra properties. Ie, it might either not match, or have extra props.
   *
   * @group Assertion Methods
   */
  notMatchOnly(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatchOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchOnly(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, with no
   * extra properties.
   *
   * @group Assertion Methods
   */
  matchOnlyStrict(
    found: any,
    wanted: any,
    ...[msg, extra]: MessageExtra
  ) {
    // this.#t.currentAssert = this.#t.t.matchOnlyStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchOnlyStrict(
      found,
      wanted,
      this.#opts,
    )
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, with no
   * extra properties. Ie, it might either not match, or have extra props.
   *
   * @group Assertion Methods
   */
  notMatchOnlyStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    // this.#t.currentAssert = this.#t.t.notMatchOnlyStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchOnlyStrict(
      found,
      doNotWant,
      this.#opts,
    )
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value matches the pattern provided, but fail if any
   * fields *only* match via type coercion.
   *
   * For example,
   *
   * ```ts
   * t.matchStrict({ a: 1 }, { a: Number }, 'this passes')
   * t.matchStrict({ a: 1 }, { a: '1' }, 'this fails')
   * ```
   *
   * @group Assertion Methods
   */
  matchStrict(
    found: any,
    wanted: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.matchStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should match pattern', args)
    const { match: ok, diff } = matchStrict(found, wanted, this.#opts)
    if (ok) return this.#t.pass(...me)
    Object.assign(me[1], { diff })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the value does not match the pattern provided, without
   * type coercion.
   *
   * @group Assertion Methods
   */
  notMatchStrict(
    found: any,
    doNotWant: any,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.notMatchStrict
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('should not match pattern', args)
    const { match: ok } = matchStrict(found, doNotWant, this.#opts)
    if (!ok) return this.#t.pass(...me)
    Object.assign(me[1], { found, doNotWant })
    return this.#t.fail(...me)
  }

  /**
   * Verify that the object has the wanted property, anywhere in its
   * prototype chain.
   *
   * @group Assertion Methods
   */
  hasProp<T extends {}>(
    found: T,
    wanted: string | number | symbol,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasProp
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'specified property should be defined',
      args,
    )
    Object.assign(me[1], { found, wanted })
    try {
      if (
        wanted in found &&
        (found as { [k: typeof wanted]: any })[wanted] !== undefined
      ) {
        return this.#t.pass(...me)
      } else {
        return this.#t.fail(...me)
      }
    } catch (er) {
      return this.#t.fail((er as Error)?.message || me[0], me[1])
    }
  }

  /**
   * Verify that the object has the wanted property, using
   * Object#hasOwnProperty
   *
   * @group Assertion Methods
   */
  hasOwnProp<T extends {}>(
    found: T,
    wanted: string | number | symbol,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnProp
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'specified property should be defined own property',
      args,
    )
    Object.assign(me[1], { found, wanted })
    try {
      if (
        hasOwn(found, wanted) &&
        (found as { [k: typeof wanted]: any })[wanted] !== undefined
      ) {
        return this.#t.pass(...me)
      } else {
        return this.#t.fail(...me)
      }
    } catch (er) {
      return this.#t.fail((er as Error)?.message || me[0], me[1])
    }
  }

  /**
   * Verify that the object has all of the properties in the `wanted`
   * list, anywhere in its prototype chain.
   *
   * @group Assertion Methods
   */
  hasProps<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasProps
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    for (const prop of wanted) {
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        Object.assign(me[1], { invalidProperty: prop })
        return this.#t.fail(
          'invalid property in hasProps assertion',
          me[1],
        )
      }
      try {
        if (
          !(
            prop in found &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the object has all of the properties listed in the
   * `wanted` list, using Object#hasOwnProperties()
   *
   * @group Assertion Methods
   */
  hasOwnProps<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnProps
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    for (const prop of wanted) {
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        Object.assign(me[1], { invalidProperty: prop })
        return this.#t.fail(
          'invalid property in hasOwnProps assertion',
          me[1],
        )
      }
      try {
        if (
          !(
            hasOwn(found, prop) &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the object has all of the properties listed in the
   * `wanted` list, using Object#hasOwnProperties(), and no others
   *
   * @group Assertion Methods
   */
  hasOwnPropsOnly<T extends {}>(
    found: T,
    wanted: Iterable<string | number | symbol>,
    ...[msg, extra]: MessageExtra
  ) {
    this.#t.currentAssert = this.#t.t.hasOwnPropsOnly
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'should have all specified properties',
      args,
    )
    Object.assign(me[1], { found, wanted })
    if (!isIterable(wanted)) {
      return this.#t.fail(
        'property list must be iterable object',
        me[1],
      )
    }
    const seen = new Set<string | symbol | number>()
    for (const prop of wanted) {
      seen.add(prop)
      if (!['string', 'number', 'symbol'].includes(typeof prop)) {
        return this.#t.fail(
          'invalid property in hasOwnPropsOnly assertion',
          { ...me[1], invalidProperty: prop },
        )
      }
      try {
        if (
          !(
            hasOwn(found, prop) &&
            (found as { [k: typeof prop]: any })[prop] !== undefined
          )
        ) {
          return this.#t.fail(...me)
        }
      } catch (er) {
        return this.#t.fail((er as Error)?.message || me[0], me[1])
      }
    }
    for (const prop of Object.keys(found)) {
      if (!seen.has(prop)) {
        return this.#t.fail(me[0], { ...me[1], doNotWant: prop })
      }
    }
    return this.#t.pass(...me)
  }

  /**
   * Verify that the function throws an error.
   * Thrown error is tested against the `wanted` param if provided, using
   * `t.match()`.
   *
   * Returns false on failure, or the error object thrown on success
   *
   * @group Assertion Methods
   */
  throws(
    fn: Function | (() => any),
    ...[wanted, msg, extra]: ThrowsArgs
  ): boolean | Error {
    this.#t.currentAssert = this.#t.t.throws
    const args = [wanted, msg, extra] as ThrowsArgs
    const [w, m, e] = normalizeThrowsArgs(
      fn.name || 'expected to throw',
      args,
    )
    try {
      fn()
      return this.#t.fail(m, e)
    } catch (err) {
      const er = err as Error
      if (er?.name) {
        Object.defineProperty(er, 'name', {
          value: er.name + '',
          enumerable: true,
          configurable: true,
          writable: true,
        })
      }
      return (
        (w ?
          this.match(isRegExp(wanted) ? er.message : er, wanted, m, e)
        : this.#t.pass(m, e)) && er
      )
    }
  }

  /**
   * Returns the error object if it throws and that does not fail the test
   * (by virtue of being marked skip or todo). Otherwise returns the
   * passing status, like other assertions.
   *
   * @group Assertion Methods
   */
  doesNotThrow(
    fn: Function | (() => any),
    ...[msg, extra]: MessageExtra
  ): boolean | Error {
    this.#t.currentAssert = this.#t.t.doesNotThrow
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      fn.name || 'expect to not throw',
      args,
    )

    try {
      fn()
      return this.#t.pass(...me)
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      const res =
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me)
      return (res && (er as Error)) || res
    }
  }

  /**
   * resolves to the error object rejected if it rejects as expected,
   * 'false' if it does not, or 'true' if it fails to reject but is marked
   * as skip/todo.
   *
   * @group Assertion Methods
   */
  async rejects<T extends any = any>(
    fnOrPromise: (() => Promise<T>) | Promise<T>,
    ...[wanted, msg, extra]: ThrowsArgs
  ): Promise<boolean | Error> {
    const args = [wanted, msg, extra] as ThrowsArgs
    const [w, m, e] = normalizeThrowsArgs('expected to reject', args)
    this.#t.currentAssert = this.#t.t.rejects
    e.at = e.at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.rejects',
        e,
      )
    }

    const d = new Deferred<boolean | Error>()
    this.#t.waitOn(d.promise)
    try {
      await p
      d.resolve(this.#t.fail(m, e))
    } catch (err) {
      const er = err as Error
      if (er?.name) {
        Object.defineProperty(er, 'name', {
          value: er.name + '',
          enumerable: true,
          configurable: true,
          writable: true,
        })
      }
      d.resolve(
        (w ?
          this.match(isRegExp(w) ? er.message : er, w, m, e)
        : this.#t.pass(m, e)) &&
          (er || true),
      )
    }
    return d.promise
  }

  /**
   * Resolves to 'true' if the promise resolves successfully, 'false' if
   * it rejects and fails, or the rejection error if it rejects but the
   * failure is accepted by by being marked todo or skip
   *
   * @group Assertion Methods
   */
  async resolves<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    ...[msg, extra]: MessageExtra
  ): Promise<boolean | Error> {
    this.#t.currentAssert = this.#t.t.resolves
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('expected to resolve', args)
    me[1].at = me[1].at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolves',
        me[1],
      )
    }

    const d = new Deferred<boolean | Error>()
    this.#t.waitOn(d.promise)
    try {
      await p
      d.resolve(this.#t.pass(...me))
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      const res =
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me)
      d.resolve((res && (er as Error)) || res)
    }
    return d.promise
  }

  /**
   * Test the resolved promise result with `t.match()`
   *
   * Resolves to true if it passes, false if the promise rejects or the match
   * fails, or the rejection error value if the promise rejects but the
   * assertion passes by being marked todo/skip.
   *
   * @group Assertion Methods
   */
  async resolveMatch<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    wanted: any,
    ...[msg, extra]: MessageExtra
  ): Promise<boolean> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'expected to resolve and match provided pattern',
      args,
    )
    this.#t.currentAssert = this.#t.t.resolveMatch
    me[1].at = me[1].at || stack.at(this.#t.currentAssert)

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolveMatch',
        me[1],
      )
    }

    const d = new Deferred<boolean>()
    this.#t.waitOn(d.promise)
    try {
      d.resolve(this.match(await p, wanted, ...me))
    } catch (er) {
      // pull the errorOrigin from the thrown error, if possible
      d.resolve(
        er !== undefined && er !== null ?
          this.error(er, ...me)
        : this.#t.fail(...me),
      )
    }
    return d.promise
  }

  // TODO: maybe let this be guarded by an AbortSignal or timeout number?
  /**
   * Asserts that the emitter emits the specified event before the test
   * ends. Returns a promise that resolves when the event is emitted.
   * Note that waiting on the returned promise within a test can deadlock
   * the test, if the event never emits, but the returned promise can be
   * a handy way to pause a test until an event happens, if you are
   * reasonably confident that it will fire.
   *
   * @group Assertion Methods
   */
  emits(
    emitter: EventEmitter | EventTarget,
    event: string,
    ...[msg, extra]: MessageExtra
  ): Promise<void> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      `expect ${event} to be emitted`,
      args,
    )
    me[1].at = me[1].at || stack.at(this.#t.t.emits)
    me[1].stack = me[1].stack || stack.captureString(this.#t.t.emits)
    const d = new Deferred<void>()
    const handler = () => {
      pending[0] = true
      d.resolve()
    }
    const pending: ExpectedEmit = [
      false,
      emitter,
      event,
      handler,
      ...me,
    ]
    this.#pendingEmits.push(pending)
    if (emitter instanceof EventEmitter) {
      emitter.once(event, handler)
    } else {
      emitter.addEventListener(event, handler, { once: true })
    }
    if (!this.#setOnBeforeEnd) {
      this.#setOnBeforeEnd = true
      const obe = this.#t.onbeforeend
      this.#t.onbeforeend = () => {
        this.#onBeforeEnd()
        return obe.call(this.#t)
      }
    }
    return d.promise
  }

  /**
   * Assert that an error object is not provided.
   *
   * Like with {@link @tapjs/asserts!index.Assertions#doesNotThrow}, you
   * can also usually just throw the error, and tap will handle that
   * reasonably.
   *
   * This is useful in cases where you just want to assert that a callback did
   * not receive an error, without necessarily aborting the callback function
   * entirely. Both the origin of the error and the location of the failed
   * assertion will be printed in the test results.
   *
   * It is also used internally in
   * {@link @tapjs/asserts!index.Assertions#resolves},
   * {@link @tapjs/asserts!index.Assertions#doesNotThrow},
   * and {@link @tapjs/asserts!index.Assertions#resolveMatch} to show both
   * the source of a raised error as well as the callsite where the assertion
   * failed.
   *
   * @group Assertion Methods
   */
  error(er: unknown, ...[msg, extra]: MessageExtra) {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(`should not error`, args)
    this.#t.currentAssert = this.#t.t.error

    if (er === undefined || er === null) return this.#t.pass(...me)

    if (!(er instanceof Error)) {
      return this.#t.fail(
        ...normalizeMessageExtra('non-Error error encountered', [
          msg,
          { ...me[1], found: er },
        ] as MessageExtra),
      )
    }

    // ok, we got an error, that's a problem
    me[1].message = (er as Error)?.message || me[0]
    const { at, stack } = me[1]
    const ex = extraFromError(er, me[1])
    // put the error's origin on the object, but let the normal
    // diags parsing show us where the failed assertion happened,
    // or respect the at/stack added by another caller.
    // iow, the "main" error is `t.error(...)`, but the "cause"
    // callsite is whatever the error refers to.
    const { cause } = ex
    ex.cause = {
      message: er.message || msg,
      at: ex.at,
      stack: ex.stack,
    }
    if (cause !== undefined) ex.cause.cause = cause

    ex.at = at
    ex.stack = stack
    // always going to have *something* here.
    return this.#t.fail(ex.message as string, ex)
  }
}

const isIterable = (it: any) =>
  typeof it === 'string' ||
  it instanceof String ||
  (!!it &&
    typeof it === 'object' &&
    typeof it[Symbol.iterator] === 'function')

const isRegExp = (re: any): re is RegExp =>
  Object.prototype.toString.call(re) === '[object RegExp]'

export const plugin: TapPlugin<Assertions, AssertOptions> = (
  t: TestBase,
  opts: AssertOptions = {},
) => new Assertions(t, opts)

import { ErrorMatch, ThrowsArgs } from './index.js'
import { Extra } from '@tapjs/core'

export const normalizeThrowsArgs = (
  defaultMessage: string,
  [wanted, message, extra]: ThrowsArgs,
): [ErrorMatch | undefined, string, Extra] => {
  if (typeof wanted === 'string') {
    if (typeof message === 'object' && message) {
      extra = message
    }
    message = wanted
    return [undefined, message || defaultMessage, extra || {}]
  }
  if (message && typeof message === 'object') {
    return [wanted, defaultMessage, message]
  }
  return [wanted, message || defaultMessage, extra || {}]
}

/**
 * Plugin class providing {@link @tapjs/before!Before#before} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import { TapPlugin, TestBase } from '@tapjs/core'
import { isPromise } from 'is-actual-promise'

/**
 * Implementation class returned by plugin function
 */
export class Before {
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Just run the supplied function right away, but do not run any
   * child tests until it has completed.
   *
   * This is handy when some setup may require asynchronous actions, and
   * tests should wait until after it completes.
   *
   * @group Test Lifecycle Management
   */
  before(fn: () => any) {
    this.#t.currentAssert = this.before
    if (this.#t.occupied) {
      // use a silent test as a roadblock so that async subtests
      // scheduled after this will have to wait for it.
      if (this.#t.jobs > 1) {
        this.#t.t.test(
          'pause for before()',
          { silent: true, buffered: false },
          t => t.end(),
        )
      }
      this.#t.queue.push(() => this.#call(fn, true))
    } else {
      this.#call(fn)
    }
  }

  #call(fn: () => any, deferred = false) {
    this.#t.currentAssert = this.before
    // if it throws, we let it kill the test
    const ret = fn.call(this.#t.t)

    // no need to waitOn it if we're already deferred, because the
    // TestBase will already wait on functions that return promises.
    if (isPromise(ret) && !deferred) {
      this.#t.waitOn(ret, w => {
        if (w.rejected) {
          // sort of a mini bailout, just for this one test
          // drop everything from the queue, quit right away
          this.#t.queue.length = 0
          this.#t.threw(w.value)
          this.#t.end()
        }
      })
    }

    return ret
  }
}

/**
 * Plugin method that creates the {@link @tapjs/before!Before} instance
 */
export const plugin: TapPlugin<Before> = (t: TestBase) =>
  new Before(t)

/**
 * Plugin class providing
 * {@link @tapjs/before-each!BeforeEach#beforeEach} on the
 * {@link @tapjs/test!index.Test} class.
 *
 * @module
 */

import type { Test } from '@tapjs/test'
import { loop } from 'function-loop'
import { TestBase } from '@tapjs/core'

/**
 * Implementation class returned by plugin
 */
export class BeforeEach {
  static #refs = new Map<TestBase, BeforeEach>()
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
    BeforeEach.#refs.set(t, this)
    const pbe = t.parent && BeforeEach.#refs.get(t.parent)
    if (pbe && !t.silent) {
      this.#onBeforeEach = [...pbe.#onBeforeEach]
    } else {
      this.#onBeforeEach = []
    }
    if (this.#onBeforeEach.length) {
      const runMain = t.runMain
      // only wrap runMain if there actually is a beforeEach
      // on the parent test. we only do this once, so any beforeEach
      // added between test initiation and test run is ignored.
      t.runMain = (cb: () => void) => {
        this.#runBeforeEach(() => runMain.call(t, cb))
      }
    }
  }

  #onBeforeEach: ((t: Test) => any)[]

  /**
   * Run the supplied function before any child tests, and all of their
   * children, and so on.
   *
   * The test about to run is an argument to the function. While its test
   * method has not yet run, it is safe to call test methods on it, but note
   * that this may potentially be confusing if for example you call `t.plan()`
   * and this conflicts with the `t.plan()` called in the test method.
   *
   * @group Test Lifecycle Management
   */
  beforeEach(fn: (t: Test) => any) {
    this.#onBeforeEach.push(fn)
  }

  #runBeforeEach(cb: () => void) {
    const onerr = (er: any) => {
      this.#t.threw(er)
      cb()
    }
    loop(
      this.#onBeforeEach.map(f => () => f(this.#t.t)),
      cb,
      onerr,
    )
  }
}

/**
 * plugin method that instantiates the {@link @tapjs/before-each!BeforeEach} object
 */
export const plugin = (t: TestBase) => new BeforeEach(t)

import {
  BaseOpts,
  env,
  Extra,
  parseTestArgs,
  PromiseWithSubtest,
  TapPlugin,
  TestArgs,
  TestBase,
  TestBaseOpts,
} from '@tapjs/core'
import type { Test, TestOpts } from '@tapjs/test'

const reRegExpLiteral = /^\/(.*)\/([a-z]*)$/

/**
 * Options added by this plugin
 *
 * @group Test Options
 */
export interface FilterOptions extends TestBaseOpts {
  /**
   * Set to true to run in contexts where `runOnly` is set
   */
  only?: boolean

  /**
   * Only run subtests marked with { only: true }
   */
  runOnly?: boolean

  /**
   * only run subtests whose name matches the supplied pattern.
   * Provide an array of strings/regexps to match down the test
   * heirarchy.
   */
  grep?: string | RegExp | (string | RegExp)[]

  /**
   * only run tests that DON'T match the pattern provided in `grep`
   */
  grepInvert?: boolean
}

/**
 * Class that provides the {@link @tapjs/filter!Filter#only} method, and
 * extends the {@link @tapjs/core!test-base.TestBase#shouldSkipChild} method to
 * respect the grep option
 */
export class Filter {
  #t: TestBase
  #grep: (string | RegExp)[] = []
  #grepInvert: boolean
  #runOnly: boolean
  #filterQuietly: boolean

  get runOnly() {
    return this.#runOnly
  }
  set runOnly(o: boolean) {
    this.#runOnly = o
  }

  constructor(t: TestBase, opts: FilterOptions) {
    this.#t = t
    const eq = env?.TAP_FILTER_QUIETLY
    this.#filterQuietly =
      eq === '1' || (!!t.options.failSkip && eq !== '0')

    // don't filter test files when we're the cli test runner
    const { grep, grepInvert, runOnly } =
      opts.context === Symbol.for('tap.isRunner') ?
        ({
          grep: [],
          grepInvert: false,
          runOnly: false,
        } as FilterOptions)
      : opts

    if (grep !== undefined) {
      this.#grep = !Array.isArray(grep) ? [grep] : grep
    } else if (env.TAP_GREP !== undefined) {
      this.#grep = env.TAP_GREP.split('\n').map(g => {
        const p = g.match(reRegExpLiteral)
        g = p && p[1] ? p[1] : g
        const flags = p ? p[2] : ''
        return new RegExp(g, flags)
      })
    }
    if (grepInvert !== undefined) {
      this.#grepInvert = !!grepInvert
    } else {
      this.#grepInvert = env.TAP_INVERT === '1'
    }
    if (runOnly !== undefined) {
      this.#runOnly = !!runOnly
    } else {
      this.#runOnly = env.TAP_ONLY === '1'
    }
    if (this.#grep?.length) t.options.grep = this.#grep

    const { shouldSkipChild } = t
    t.shouldSkipChild = extra =>
      this.#shouldSkipChild(extra, shouldSkipChild)
  }

  #shouldSkipChild(
    extra: TestBaseOpts | BaseOpts | TestOpts,
    shouldSkipChild: (
      extra: TestBaseOpts | BaseOpts | TestOpts,
    ) => boolean,
  ) {
    const opts = extra as FilterOptions & Extra & TestBaseOpts
    const [pattern, ...rest] = this.#grep
    if (pattern !== undefined) {
      /* c8 ignore start */
      const name = opts.name || ''
      /* c8 ignore stop */
      const m =
        typeof pattern === 'string' ?
          name.includes(pattern)
        : pattern.test(name)
      const match = this.#grepInvert ? !m : m
      if (!match) {
        if (!this.#filterQuietly) {
          opts.skip = `filter${
            this.#grepInvert ? ' out' : ''
          }: ${pattern}`
        }
        shouldSkipChild(opts)
        return true
      } else {
        opts.grep = rest
      }
    } else if (this.#grep) {
      // just set to an empty array so it doesn't try to load from the
      // env once we've exhausted the pattern parts.
      opts.grep = []
    }
    if (this.#runOnly && !opts.only) {
      if (!this.#filterQuietly) {
        opts.skip = 'filter: only'
      }
      shouldSkipChild(opts)
      return true
    }
    if (opts.only && !this.#runOnly) {
      this.#t.comment(
        '%j has `only` set but all tests run',
        extra.name,
      )
    }
    if (typeof opts.runOnly === 'undefined') {
      opts.runOnly = this.#runOnly
    }
    return shouldSkipChild(opts)
  }

  /**
   * Run a child test that will run when the `--only` config is set,
   * or `{ runOnly: true }` is set in the parent test options.
   *
   * @group Subtest Methods
   */
  only(
    name: string,
    extra: TestOpts,
    cb: (t: Test) => any,
  ): PromiseWithSubtest<Test>
  only(name: string, cb: (t: Test) => any): PromiseWithSubtest<Test>
  only(
    extra: TestOpts,
    cb: (t: Test) => any,
  ): PromiseWithSubtest<Test>
  only(cb: (t: Test) => any): PromiseWithSubtest<Test>
  only(...args: TestArgs<Test>): PromiseWithSubtest<Test> {
    const extra = parseTestArgs(...args)
    /* c8 ignore start */
    const name = extra.name || ''
    /* c8 ignore stop */
    return this.#t.t.test(name, { ...extra, only: true }, extra.cb)
  }
}

