  optArg,
  optArgSync,
  RimrafAsyncOptions,
  RimrafSyncOptions,
} from './opt-arg.js'
import pathArg from './path-arg.js'
import { rimrafManual, rimrafManualSync } from './rimraf-manual.js'
import { rimrafMoveRemove, rimrafMoveRemoveSync } from './rimraf-move-remove.js'
