  await rm(path, {
    ...opt,
    force: true,
    recursive: true,
  })
  return true
}

export const rimrafNativeSync = (
  path: string,
  opt: RimrafSyncOptions,
): boolean => {
  rmSync(path, {
    ...opt,
    force: true,
    recursive: true,
  })
  return true
}

import { RimrafAsyncOptions, RimrafOptions } from './index.js'
import platform from './platform.js'

const version = process.env.__TESTING_RIMRAF_NODE_VERSION__ || process.version
const versArr = version.replace(/^v/, '').split('.')

/* c8 ignore start */
const [major = 0, minor = 0] = versArr.map(v => parseInt(v, 10))
/* c8 ignore stop */
const hasNative = major > 14 || (major === 14 && minor >= 14)

// we do NOT use native by default on Windows, because Node's native
// rm implementation is less advanced.  Change this code if that changes.
export const useNative: (opt?: RimrafAsyncOptions) => boolean =
  !hasNative || platform === 'win32' ?
    () => false
  : opt => !opt?.signal && !opt?.filter
export const useNativeSync: (opt?: RimrafOptions) => boolean =
  !hasNative || platform === 'win32' ?
    () => false
  : opt => !opt?.signal && !opt?.filter

import { mkdirpSync } from 'mkdirp'
import {
  linkSync,
  statSync,
  symlinkSync,
  writeFileSync,
} from 'node:fs'
import { dirname, resolve } from 'path'

export type FixtureType = 'file' | 'dir' | 'link' | 'symlink'
export interface FixtureDir {
  [entry: string]: FixtureDirContent
}

export type FixtureDirContent =
  | string
  | Buffer
  | Uint8Array
  | FixtureDir
  | Fixture<'file'>
  | Fixture<'dir'>
  | Fixture<'link'>
  | Fixture<'symlink'>

export type FixtureContent<T> =
  T extends 'file' ? string | Buffer | Uint8Array
  : T extends 'link' | 'symlink' ? string
  : T extends 'dir' ? FixtureDir
  : never

type GetType<T extends FixtureDirContent> =
  T extends Fixture<infer Type> ? Type
  : T extends string | Buffer | Uint8Array ? 'file'
  : T extends number | symbol | bigint ? never
  : T extends {} ? 'dir'
  : never

const validateDirContents = (
  content: Record<string, FixtureDirContent>,
  seen: Set<Record<string, FixtureDirContent>>,
) => {
  for (const [f, v] of Object.entries(content)) {
    const t = rawToType(v)
    if (
      v &&
      typeof v === 'object' &&
      t === 'dir' &&
      !(v instanceof Fixture)
    ) {
      const r = v as Record<string, FixtureDirContent>
      if (seen.has(r)) {
        throw new Error(
          'cycle detected in t.fixture contents at ' + f,
        )
      }
      seen.add(r)
      validateDirContents(r, seen)
    }
  }
}

const assertValidContent: (
  type: FixtureType,
  content: any,
) => void = <T extends FixtureType>(
  type: T,
  content: any,
): asserts content is FixtureContent<T> => {
  switch (type) {
    case 'dir':
      if (!content || typeof content !== 'object') {
        throw new TypeError('dir fixture must have object content')
      }
      validateDirContents(content, new Set([content]))
      break
    case 'file':
      if (
        typeof content !== 'string' &&
        !Buffer.isBuffer(content) &&
        !(content instanceof Uint8Array)
      ) {
        throw new TypeError(
          'file fixture must have string/buffer content',
        )
      }
      break
    case 'link':
    case 'symlink':
      if (typeof content !== 'string') {
        throw new TypeError(type + ' fixture must have string target')
      }
      break
    default:
      throw new Error('invalid fixture type: ' + type)
  }
}

const rawToType = (f: FixtureDirContent): FixtureType => {
  if (f instanceof Fixture) {
    return f.type
  } else if (
    typeof f === 'string' ||
    Buffer.isBuffer(f) ||
    f instanceof Uint8Array
  ) {
    return 'file'
  } else if (f && typeof f === 'object') {
    return 'dir'
  }
  throw new Error('invalid fixture type: ' + f)
}

const rawToFixture = (
  f: FixtureDirContent,
): Fixture<GetType<typeof f>> =>
  f instanceof Fixture ? f : new Fixture(rawToType(f), f)

const isSymlinkF = (
  f: Fixture<FixtureType>,
): f is Fixture<'symlink'> => f.type === 'symlink'
const isLinkF = (f: Fixture<FixtureType>): f is Fixture<'link'> =>
  f.type === 'link'
const isDirF = (f: Fixture<FixtureType>): f is Fixture<'dir'> =>
  f.type === 'dir'
const isFileF = (f: Fixture<FixtureType>): f is Fixture<'file'> =>
  f.type === 'file'

export class Fixture<T extends FixtureType> {
  type: T
  content: FixtureContent<T>
  constructor(type: T, content: FixtureContent<T>) {
    assertValidContent(type, content)
    this.type = type
    this.content = content
  }

  get [Symbol.toStringTag]() {
    return 'Fixture<' + this.type + '>'
  }

  // have to gather up symlinks for the end
  static make(
    abs: string,
    content: FixtureDirContent,
    symlinks: null | { [k: string]: string } = null,
  ) {
    const f = rawToFixture(content)

    const isRoot = symlinks === null
    symlinks = symlinks || {}

    if (isSymlinkF(f)) {
      // have to gather up symlinks for the end, because windows
      symlinks[abs] = f.content
    } else if (isLinkF(f)) {
      linkSync(resolve(dirname(abs), f.content), abs)
    } else if (isDirF(f)) {
      mkdirpSync(abs)
      for (const [name, fixture] of Object.entries(f.content))
        Fixture.make(`${abs}/${name}`, fixture, symlinks)
    } else if (isFileF(f)) {
      writeFileSync(abs, f.content)
      /* c8 ignore start */
    } else {
      // already validated above, impossible here
      throw new Error('uknown fixture type: ' + f.type)
    }
    /* c8 ignore stop */

    // create all those symlinks we were asked for
    if (isRoot) {
      for (const [abs, target] of Object.entries(symlinks)) {
        symlinkSync(
          target,
          abs,
          isDir(abs, target) ? 'junction' : 'file',
        )
      }
    }
  }
}

// check if a symlink target is a directory
const isDir = (abs: string, target: string) => {
  try {
    return statSync(resolve(dirname(abs), target)).isDirectory()
  } catch (er) {
    return false
  }
}

import { mkdirpManual, mkdirpManualSync } from './mkdirp-manual.js'
import { mkdirpNative, mkdirpNativeSync } from './mkdirp-native.js'
import { MkdirpOptions, optsArg } from './opts-arg.js'
import { pathArg } from './path-arg.js'
import { useNative, useNativeSync } from './use-native.js'
/* c8 ignore start */
export { mkdirpManual, mkdirpManualSync } from './mkdirp-manual.js'
export { mkdirpNative, mkdirpNativeSync } from './mkdirp-native.js'
export { useNative, useNativeSync } from './use-native.js'
/* c8 ignore stop */

export const mkdirpSync = (path: string, opts?: MkdirpOptions) => {
  path = pathArg(path)
  const resolved = optsArg(opts)
  return useNativeSync(resolved)
    ? mkdirpNativeSync(path, resolved)
    : mkdirpManualSync(path, resolved)
}

export const sync = mkdirpSync
export const manual = mkdirpManual
export const manualSync = mkdirpManualSync
export const native = mkdirpNative
export const nativeSync = mkdirpNativeSync
export const mkdirp = Object.assign(
  async (path: string, opts?: MkdirpOptions) => {
    path = pathArg(path)
    const resolved = optsArg(opts)
    return useNative(resolved)
      ? mkdirpNative(path, resolved)
      : mkdirpManual(path, resolved)
  },
  {
    mkdirpSync,
    mkdirpNative,
    mkdirpNativeSync,
    mkdirpManual,
    mkdirpManualSync,

    sync: mkdirpSync,
    native: mkdirpNative,
    nativeSync: mkdirpNativeSync,
    manual: mkdirpManual,
    manualSync: mkdirpManualSync,
    useNative,
    useNativeSync,
  }
)

import { dirname } from 'path'
import { MkdirpOptions, optsArg } from './opts-arg.js'

export const mkdirpManualSync = (
  path: string,
  options?: MkdirpOptions,
  made?: string | undefined | void
): string | undefined | void => {
  const parent = dirname(path)
  const opts = { ...optsArg(options), recursive: false }

  if (parent === path) {
    try {
      return opts.mkdirSync(path, opts)
    } catch (er) {
      // swallowed by recursive implementation on posix systems
      // any other error is a failure
      const fer = er as NodeJS.ErrnoException
      if (fer && fer.code !== 'EISDIR') {
        throw er
      }
      return
    }
  }

  try {
    opts.mkdirSync(path, opts)
    return made || path
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer && fer.code === 'ENOENT') {
      return mkdirpManualSync(path, opts, mkdirpManualSync(parent, opts, made))
    }
    if (fer && fer.code !== 'EEXIST' && fer && fer.code !== 'EROFS') {
      throw er
    }
    try {
      if (!opts.statSync(path).isDirectory()) throw er
    } catch (_) {
      throw er
    }
  }
}

export const mkdirpManual = Object.assign(
  async (
    path: string,
    options?: MkdirpOptions,
    made?: string | undefined | void
  ): Promise<string | undefined | void> => {
    const opts = optsArg(options)
    opts.recursive = false
    const parent = dirname(path)
    if (parent === path) {
      return opts.mkdirAsync(path, opts).catch(er => {
        // swallowed by recursive implementation on posix systems
        // any other error is a failure
        const fer = er as NodeJS.ErrnoException
        if (fer && fer.code !== 'EISDIR') {
          throw er
        }
      })
    }

    return opts.mkdirAsync(path, opts).then(
      () => made || path,
      async er => {
        const fer = er as NodeJS.ErrnoException
        if (fer && fer.code === 'ENOENT') {
          return mkdirpManual(parent, opts).then(
            (made?: string | undefined | void) => mkdirpManual(path, opts, made)
          )
        }
        if (fer && fer.code !== 'EEXIST' && fer.code !== 'EROFS') {
          throw er
        }
        return opts.statAsync(path).then(
          st => {
            if (st.isDirectory()) {
              return made
            } else {
              throw er
            }
          },
          () => {
            throw er
          }
        )
      }
    )
  },
  { sync: mkdirpManualSync }
)

import {
  MakeDirectoryOptions,
  mkdir,
  mkdirSync,
  stat,
  Stats,
  statSync,
} from 'fs'

export interface FsProvider {
  stat?: (
    path: string,
    callback: (err: NodeJS.ErrnoException | null, stats: Stats) => any
  ) => any
  mkdir?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean },
    callback: (err: NodeJS.ErrnoException | null, made?: string) => any
  ) => any
  statSync?: (path: string) => Stats
  mkdirSync?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => string | undefined
}

interface Options extends FsProvider {
  mode?: number | string
  fs?: FsProvider
  mkdirAsync?: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => Promise<string | undefined>
  statAsync?: (path: string) => Promise<Stats>
}

export type MkdirpOptions = Options | number | string

export interface MkdirpOptionsResolved {
  mode: number
  fs: FsProvider
  mkdirAsync: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => Promise<string | undefined>
  statAsync: (path: string) => Promise<Stats>
  stat: (
    path: string,
    callback: (err: NodeJS.ErrnoException | null, stats: Stats) => any
  ) => any
  mkdir: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean },
    callback: (err: NodeJS.ErrnoException | null, made?: string) => any
  ) => any
  statSync: (path: string) => Stats
  mkdirSync: (
    path: string,
    opts: MakeDirectoryOptions & { recursive?: boolean }
  ) => string | undefined
  recursive?: boolean
}

export const optsArg = (opts?: MkdirpOptions): MkdirpOptionsResolved => {
  if (!opts) {
    opts = { mode: 0o777 }
  } else if (typeof opts === 'object') {
    opts = { mode: 0o777, ...opts }
  } else if (typeof opts === 'number') {
    opts = { mode: opts }
  } else if (typeof opts === 'string') {
    opts = { mode: parseInt(opts, 8) }
  } else {
    throw new TypeError('invalid options argument')
  }

  const resolved = opts as MkdirpOptionsResolved
  const optsFs = opts.fs || {}

  opts.mkdir = opts.mkdir || optsFs.mkdir || mkdir

  opts.mkdirAsync = opts.mkdirAsync
    ? opts.mkdirAsync
    : async (
        path: string,
        options: MakeDirectoryOptions & { recursive?: boolean }
      ): Promise<string | undefined> => {
        return new Promise<string | undefined>((res, rej) =>
          resolved.mkdir(path, options, (er, made) =>
            er ? rej(er) : res(made)
          )
        )
      }

  opts.stat = opts.stat || optsFs.stat || stat
  opts.statAsync = opts.statAsync
    ? opts.statAsync
    : async (path: string) =>
        new Promise((res, rej) =>
          resolved.stat(path, (err, stats) => (err ? rej(err) : res(stats)))
        )

  opts.statSync = opts.statSync || optsFs.statSync || statSync
  opts.mkdirSync = opts.mkdirSync || optsFs.mkdirSync || mkdirSync

  return resolved
}

import { dirname } from 'path'
import { findMade, findMadeSync } from './find-made.js'
import { mkdirpManual, mkdirpManualSync } from './mkdirp-manual.js'
import { MkdirpOptions, optsArg } from './opts-arg.js'

export const mkdirpNativeSync = (
  path: string,
  options?: MkdirpOptions
): string | void | undefined => {
  const opts = optsArg(options)
  opts.recursive = true
  const parent = dirname(path)
  if (parent === path) {
    return opts.mkdirSync(path, opts)
  }

  const made = findMadeSync(opts, path)
  try {
    opts.mkdirSync(path, opts)
    return made
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer && fer.code === 'ENOENT') {
      return mkdirpManualSync(path, opts)
    } else {
      throw er
    }
  }
}

export const mkdirpNative = Object.assign(
  async (
    path: string,
    options?: MkdirpOptions
  ): Promise<string | void | undefined> => {
    const opts = { ...optsArg(options), recursive: true }
    const parent = dirname(path)
    if (parent === path) {
      return await opts.mkdirAsync(path, opts)
    }

    return findMade(opts, path).then((made?: string | undefined) =>
      opts
        .mkdirAsync(path, opts)
        .then(m => made || m)
        .catch(er => {
          const fer = er as NodeJS.ErrnoException
          if (fer && fer.code === 'ENOENT') {
            return mkdirpManual(path, opts)
          } else {
            throw er
          }
        })
    )
  },
  { sync: mkdirpNativeSync }
)

import { dirname } from 'path'
import { MkdirpOptionsResolved } from './opts-arg.js'

export const findMade = async (
  opts: MkdirpOptionsResolved,
  parent: string,
  path?: string
): Promise<undefined | string> => {
  // we never want the 'made' return value to be a root directory
  if (path === parent) {
    return
  }

  return opts.statAsync(parent).then(
    st => (st.isDirectory() ? path : undefined), // will fail later
    er => {
      const fer = er as NodeJS.ErrnoException
      return fer && fer.code === 'ENOENT'
        ? findMade(opts, dirname(parent), parent)
        : undefined
    }
  )
}

export const findMadeSync = (
  opts: MkdirpOptionsResolved,
  parent: string,
  path?: string
): undefined | string => {
  if (path === parent) {
    return undefined
  }

  try {
    return opts.statSync(parent).isDirectory() ? path : undefined
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    return fer && fer.code === 'ENOENT'
      ? findMadeSync(opts, dirname(parent), parent)
      : undefined
  }
}

const platform = process.env.__TESTING_MKDIRP_PLATFORM__ || process.platform
import { parse, resolve } from 'path'
export const pathArg = (path: string) => {
  if (/\0/.test(path)) {
    // simulate same failure that node raises
    throw Object.assign(
      new TypeError('path must be a string without null bytes'),
      {
        path,
        code: 'ERR_INVALID_ARG_VALUE',
      }
    )
  }

  path = resolve(path)
  if (platform === 'win32') {
    const badWinChars = /[*|"<>?:]/
    const { root } = parse(path)
    if (badWinChars.test(path.substring(root.length))) {
      throw Object.assign(new Error('Illegal characters in path.'), {
        path,
        code: 'EINVAL',
      })
    }
  }

  return path
}

import { mkdir, mkdirSync } from 'fs'
import { MkdirpOptions, optsArg } from './opts-arg.js'

const version = process.env.__TESTING_MKDIRP_NODE_VERSION__ || process.version
const versArr = version.replace(/^v/, '').split('.')
const hasNative = +versArr[0] > 10 || (+versArr[0] === 10 && +versArr[1] >= 12)

export const useNativeSync = !hasNative
  ? () => false
  : (opts?: MkdirpOptions) => optsArg(opts).mkdirSync === mkdirSync

export const useNative = Object.assign(
  !hasNative
    ? () => false
    : (opts?: MkdirpOptions) => optsArg(opts).mkdir === mkdir,
  {
    sync: useNativeSync,
  }
)

import { plugin as TeardownPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import { at, CallSiteLike, captureString } from '@tapjs/stack'

const notConfig = (
  m: string,
  property: PropertyKey,
  caller: ((...a: any[]) => any) | Function,
) => {
  const s =
    typeof property === 'string' ?
      `'${property}'`
    : property.toString()
  const er = new Error(
    `Cannot ${m} ${s}, defined {configurable:false}`,
  )
  Error.captureStackTrace(er, caller)
  return er
}

/**
 * An object type containing all of the methods of the object
 * type provided
 */
export type MethodsObject<O extends object> = {
  [k in keyof O]: O[k] extends (...a: any[]) => any ? k : never
}

/**
 * The union type of all methods in an object
 */
export type Methods<O extends object> =
  MethodsObject<O>[keyof MethodsObject<O>]

/**
 * Base type of result objects generated by
 * {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultBase<F extends (...a: any[]) => any> {
  args: OverloadParams<F>
  receiver: Receiver<F>
  at?: CallSiteLike
  stack?: string
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * returned
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultReturned<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  returned: OverloadReturnType<F>
  threw: false
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * threw
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultThrew<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  threw: true
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export type CaptureResult<F extends (...a: any[]) => any> =
  | CaptureResultReturned<F>
  | CaptureResultThrew<F>
  | CaptureResultBase<F>

/**
 * like ReturnType<F>, but a union of the return types of up to 10 overloads
 *
 * @typeParam F - the (possibly overloaded) function
 */
export type OverloadReturnType<F> = TupleUnion<
  UnarrayArray<FilterTupleUnknown<ORTuple<F>>>
>

/**
 * Get member type from array/tuple type
 */
export type Unarray<A> = A extends (infer V)[] ? V : A

/**
 * Get tuple of member types from array of array types
 */
export type UnarrayArray<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [Unarray<H>, ...UnarrayArray<T>]
      : [Unarray<H>]
    : true
  : L

/**
 * Get overloaded return values as tuple of arrays
 */
export type ORTuple<F> =
  F extends (
    {
      (...a: any[]): infer A0
      (...a: any[]): infer A1
      (...a: any[]): infer A2
      (...a: any[]): infer A3
      (...a: any[]): infer A4
      (...a: any[]): infer A5
      (...a: any[]): infer A6
      (...a: any[]): infer A7
      (...a: any[]): infer A8
      (...a: any[]): infer A9
    }
  ) ?
    [A0[], A1[], A2[], A3[], A4[], A5[], A6[], A7[], A8[], A9[]]
  : never

/**
 * like Parameters<F>, but a union of parameter sets for up to 10 overloads
 */
export type OverloadParams<F> = TupleUnion<
  FilterUnknown<OverloadParamsTuple<F>>
>

/**
 * Get overloaded Parameters types as 10-tuple. When the function has less
 * than 10 overloaded signatures, the remaining param sets will be set to
 * `unknown`.
 *
 * @internal
 */
export type OverloadParamsTuple<F> =
  F extends (
    {
      (...a: infer A0): any
      (...a: infer A1): any
      (...a: infer A2): any
      (...a: infer A3): any
      (...a: infer A4): any
      (...a: infer A5): any
      (...a: infer A6): any
      (...a: infer A7): any
      (...a: infer A8): any
      (...a: infer A9): any
    }
  ) ?
    [A0, A1, A2, A3, A4, A5, A6, A7, A8, A9]
  : never

/**
 * Convert all `unknown[]` types in an array type to `never`
 */
export type NeverUnknown<T extends unknown[]> =
  unknown[] extends T ?
    (T extends {}[] ? true : false) extends true ?
      any[]
    : never
  : T

/**
 * Convert all `unknown[]` types in an array type to `never[]`
 */
export type NeverTupleUnknown<T extends unknown[]> =
  unknown[] extends T ?
    (T extends {}[] ? true : false) extends true ?
      any[]
    : never[]
  : T

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never`
 */
export type FilterUnknown<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [NeverUnknown<H>, ...FilterUnknown<T>]
      : [NeverUnknown<H>]
    : FilterUnknown<T>
  : L

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never[]`
 */
export type FilterTupleUnknown<L> =
  L extends [infer H, ...infer T] ?
    H extends unknown[] ?
      T extends unknown[][] ?
        [NeverTupleUnknown<H>, ...FilterTupleUnknown<T>]
      : [NeverTupleUnknown<H>]
    : FilterTupleUnknown<T>
  : L

/**
 * Create a union from a tuple type
 */
export type TupleUnion<L> =
  L extends [infer H, ...infer T] ? H | TupleUnion<T> : never

/**
 * Infer the `this` target of a function
 */
export type Receiver<F> =
  F extends (this: infer T, ...a: any[]) => any ? T : never

/**
 * The method returned by {@link @tapjs/intercept!Interceptor#capture},
 * which returns the {@link @tapjs/intercept!CaptureResult} array when
 * called, and has methods to restore or get args, and exposes the list of
 * calls
 */
export type CaptureResultsMethod<
  T extends {},
  M extends Methods<T>,
  F = T[M],
> = (() => CaptureResult<
  F extends (...a: any[]) => any ? F : (...a: any[]) => any
>[]) & {
  restore: () => void
  calls: CaptureResult<
    F extends (...a: any[]) => any ? F : (...a: any[]) => any
  >[]
  args: () => OverloadParams<
    F extends { (...a: any[]): any } ? F : (...a: any[]) => any
  >[]
}

/**
 * Base class for objects created by
 * {@link @tapjs/intercept!Interceptor#intercept}
 */
export interface InterceptResultBase {
  receiver: any
  at?: CallSiteLike
  stack?: string
  value: any
  success: boolean
  threw: boolean
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for gets
 */
export interface InterceptResultGet extends InterceptResultBase {
  type: 'get'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for sets
 */
export interface InterceptResultSet extends InterceptResultBase {
  type: 'set'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResult = InterceptResultGet | InterceptResultSet

/**
 * Method returned by {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResultsMethod = (() => InterceptResult[]) & {
  restore: () => void
}

/**
 * Implementation class providing the
 * {@link @tapjs/intercept!Interceptor#intercept},
 * {@link @tapjs/intercept!Interceptor#capture}, and
 * {@link @tapjs/intercept!Interceptor#captureFn} methods.
 */
export class Interceptor {
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Intercept and track object property sets and gets.
   *
   * If a PropertyDescriptor is set, then it will be used as the replacement
   * value. Otherwise, the original descriptor will be used.
   *
   * If the `strictMode` param is set, then attempts to write to read-only
   * properties will throw an error.
   *
   * @group Spies, Mocks, and Fixtures
   */
  intercept<T extends object>(
    obj: T,
    prop: keyof T,
    desc?: PropertyDescriptor,
    strictMode: boolean = true,
  ): InterceptResultsMethod {
    const resList: InterceptResult[] = []

    // find the original property descriptor, if it exists.
    let orig = Object.getOwnPropertyDescriptor(obj, prop)
    let src = obj
    while (!orig && src) {
      if (!(orig = Object.getOwnPropertyDescriptor(src, prop))) {
        src = Object.getPrototypeOf(src)
      }
    }
    if (orig && !orig.configurable) {
      throw notConfig('intercept property', prop, this.#t.t.intercept)
    }

    let restore: () => void

    // if we have an original, then that serves as the basis for
    // the value, if desc isn't set. If we don't have a desc, and
    // don't have an orig, then it's just { value: undefined }
    if (!orig || src !== obj) {
      // either it came from the proto chain, or wasn't found.
      // either way, we're assigning here.
      restore = () => {
        delete obj[prop]
        restore = () => {}
      }
    } else {
      // have an original, on the object itself
      restore = () => {
        Object.defineProperty(obj, prop, orig as PropertyDescriptor)
        restore = () => {}
      }
    }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    if (!desc) {
      desc =
        orig ?
          { ...orig }
        : {
            value: undefined,
            configurable: true,
            writable: true,
            enumerable: true,
          }
    }

    const base = desc as PropertyDescriptor
    const interceptor: PropertyDescriptor & {
      get: () => any
      set: (v: any) => void
    } = {
      enumerable: desc.enumerable,
      configurable: true,

      get() {
        // return the current value, and track
        let threw = true
        let success = false
        let value: any
        try {
          value = base.get ? base.get.call(obj) : base.value
          threw = false
          success = true
        } finally {
          resList.push({
            receiver: this,
            type: 'get',
            at: at(interceptor.get),
            stack: captureString(interceptor.get),
            value,
            threw,
            success,
          })
          if (!threw) return value
        }
      },

      set(value: any) {
        let threw = true
        let success = false
        try {
          if (base.set) {
            base.set.call(obj, value)
            success = true
          } else if (base.get) {
            if (strictMode) {
              const er = new TypeError(
                `Cannot set property '${String(
                  prop,
                )}' of ${Object.prototype.toString.call(
                  obj,
                )} which has only a getter`,
              )
              Error.captureStackTrace(er, interceptor.set)
              throw er
            }
          } else {
            if (base.writable) {
              base.value = value
              success = true
            } else {
              if (strictMode) {
                const er = new TypeError(
                  `Cannot assign to read only property '${String(
                    prop,
                  )}' of ${Object.prototype.toString.call(obj)}`,
                )
                Error.captureStackTrace(er, interceptor.set)
                throw er
              }
            }
          }
          threw = false
        } finally {
          resList.push({
            receiver: this,
            type: 'set',
            at: at(interceptor.set),
            stack: captureString(interceptor.set),
            value,
            success,
            threw,
          })
        }
      },
    }

    Object.defineProperty(obj, prop, interceptor)
    return Object.assign(
      () => {
        const r = resList.slice()
        resList.length = 0
        return r
      },
      { restore: () => restore() },
    )
  }

  /**
   * Intercept calls to a method to track the arguments, call site,
   * and return/throw status, and replace the implementation.
   *
   * By default, the method is set to a no-op. To retain the method behavior,
   * pass the current value of the method as the third argument.  For example:
   *
   * ```
   * const results = t.capture(obj, 'foo', obj.foo)
   * ```
   *
   * Automatically restores at `t.teardown()` if the `@tapjs/after`
   * plugin is not disabled.  Otherwise, it is important to call the
   * `restore()` method on the returned function when you are done capturing.
   *
   * @group Spies, Mocks, and Fixtures
   */
  capture<T extends {}, M extends Methods<T>>(
    obj: T,
    method: M,
    impl: (this: T, ...a: any[]) => any = (..._: any[]) => {},
  ): CaptureResultsMethod<T, M> {
    const prop = Object.getOwnPropertyDescriptor(obj, method)
    if (prop && !prop.configurable) {
      throw notConfig('capture method', method, this.#t.t.capture)
    }

    // if we don't have a prop we can restore by just deleting
    // otherwise, we restore by putting it back as it was
    let restore =
      prop ?
        () => {
          Object.defineProperty(obj, method, prop)
          restore = () => {}
        }
      : () => {
          delete obj[method]
          restore = () => {}
        }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    const fn = Object.assign(this.captureFn(impl), {
      restore: () => restore(),
    })

    Object.defineProperty(obj, method, {
      enumerable: prop ? prop.enumerable : true,
      writable: true,
      value: fn,
      configurable: true,
    })

    return Object.assign(
      () => {
        const r = fn.calls.slice()
        fn.calls.length = 0
        return r
      },
      {
        restore: () => restore(),
        calls: fn.calls,
        args: () => {
          const r = fn.calls.slice()
          fn.calls.length = 0
          return r.map(({ args }) => args) as OverloadParams<
            T[M] extends { (...a: any[]): any } ? T[M]
            : (...a: any[]) => any
          >[]
        },
      },
    )
  }

  /**
   * Just wrap the function and return it.  Does not have any
   * logic to restore, since it's not actually modifying anything.
   * The results hang off the function as the 'calls' property.
   *
   * The added `fn.args()` method will return an array of the arguments
   * passed to each call since the last time it was inspected.
   *
   * @group Spies, Mocks, and Fixtures
   */
  captureFn<F extends (this: any, ...a: any[]) => any>(
    original: F,
  ): F & {
    calls: CaptureResult<F>[]
    args: () => OverloadParams<F>[]
  } {
    const calls: CaptureResult<F>[] = []
    const args = () => calls.map(({ args }) => args)
    return Object.assign(
      function wrapped(
        this: Receiver<F>,
        ...args: OverloadParams<F>
      ) {
        const res: CaptureResultBase<F> = {
          receiver: this,
          args,
          at: at(wrapped),
          stack: captureString(wrapped),
        }
        try {
          const returned = res as CaptureResultReturned<F>
          returned.returned = original.call(this, ...args)
          returned.threw = false
          calls.push(res)
          return returned.returned
        } finally {
          if ((res as CaptureResultReturned<F>).threw !== false) {
            ;(res as CaptureResultThrew<F>).threw = true
            calls.push(res)
          }
        }
      } as F,
      { calls, args },
    )
  }
}

/**
 * plugin method that instantiates an {@link @tapjs/intercept!Interceptor}
 */
export const plugin: TapPlugin<Interceptor> = t => new Interceptor(t)

import { plugin as AfterPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import * as stack from '@tapjs/stack'
import { isBuiltin } from 'node:module'
import { mockRequire } from './mock-require.js'
import { MockService } from './mock-service.js'

/**
 * Implementation class providing the
 * {@link @tapjs/mock!index.TapMock#mockRequire},
 * {@link @tapjs/mock!index.TapMock#mockImport}, and
 * {@link @tapjs/mock!index.TapMock#createMock} methods.
 */
export class TapMock {
  #t: TestBase
  #didTeardown: boolean = false
  #mocks: MockService[] = []

  #allMock: Record<string, any>
  static #refs = new Map<TestBase, TapMock>()

  constructor(t: TestBase) {
    this.#t = t
    TapMock.#refs.set(t, this)
    // inherit #allMock
    const p = t.parent && TapMock.#refs.get(t.parent)
    this.#allMock = Object.assign(
      Object.create(null),
      p ? p.#allMock : {},
    )
  }

  /**
   * Convenience method to create a mock from an existing object by
   * overriding some (possibly deeply nested) methods or properties.
   *
   * Example:
   *
   * ```ts
   * import * as fs from 'node:fs'
   * const mockedThing = t.mockRequire('./module.js', t.createMock(
   *   { fs },
   *   { fs: { statSync: myMockedStatSync }}
   * )
   * ```
   *
   * This can also appear anywhere in the object hierarchy, which may
   * be more convenient in some cases:
   *
   * ```ts
   * import * as blah from '@long-name/blah-api'
   * const mockedThing = t.mockRequire('./module.js', {
   *   fs: t.createMock(fs, { statSync: myMockedStatSync }),
   *   child_process: t.createMock(child_process, { spawn: mockSpawn }),
   *   '@long-name/blah-api': t.createMock(blah, {
   *     some: {
   *       nested: {
   *         prop: true
   *       }
   *     }
   *   })
   * })
   * ```
   *
   * To *remove* a property, set it as undefined in the override.
   *
   * @group Spies, Mocks, and Fixtures
   */
  createMock<
    B extends { [k: PropertyKey]: any } | Array<any>,
    O extends { [k: string]: any } | Array<any>,
  >(bases: B, overrides: O): MockedObject<B, O> {
    if (Array.isArray(overrides))
      return overrides as unknown as MockedObject<B, O>
    const mockedObject = Object.fromEntries(
      Object.entries(bases)
        .map(([k, v]) => {
          if (k in overrides) {
            const bobj = !!v && typeof v === 'object'
            const oobj =
              !!overrides[k] && typeof overrides[k] === 'object'
            if (oobj && bobj) {
              return [k, this.createMock(v, overrides[k])]
            } else {
              return [k, overrides[k]]
            }
          }
          return [k, v]
        })
        .concat(
          Object.entries(overrides).filter(
            ([k]) => !Object.hasOwnProperty.call(bases, k),
          ),
        ),
    ) as MockedObject<B, O>

    if (Object.getPrototypeOf(bases) !== Object.prototype) {
      Object.setPrototypeOf(
        mockedObject,
        Object.getPrototypeOf(bases),
      )
    }

    return mockedObject
  }

  /**
   * Deprecated alias for {@link @tapjs/mock!index.TapMock#mockRequire}
   *
   * Prints a warning to stderr the first time it used, otherwise
   * identical.
   *
   * @group Spies, Mocks, and Fixtures
   *
   * @deprecated
   */
  mock<T = any>(module: string, mocks: { [k: string]: any } = {}): T {
    /* c8 ignore start */
    const at = stack.at(this.#t.t.mock)?.toJSON() || ''
    /* c8 ignore stop */
    console.error(
      't.mock() is now t.mockRequire(). Please update your tests.',
      at,
    )
    return mockRequire(module, mocks, this.#t.t.mock) as T
  }

  /**
   * Load the supplied module asynchronously using import(),
   * replacing any of the referenced modules with the mocks provided.
   *
   * Works with either ESM or CommonJS modules, but as with `import()` of
   * CommonJS modules, the `module.exports` value will be set as the
   * `default` property on the resolved object, making
   * {@link @tapjs/mock!index.TapMock#mockRequire} somewhat more intuitive in those cases.
   *
   * For type info, cast using `as typeof import(...)` or use the type
   * parameter, as TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = await t.mockImport<
   *   typeof import('../my-thing.js')
   * >('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * })
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  async mockImport<T = any>(
    module: string,
    mocks: Record<string, any> = {},
  ): Promise<T> {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockImport
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked',
      )
      return {} as T
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    if (!this.#didTeardown && this.#t.t.pluginLoaded(AfterPlugin)) {
      this.#didTeardown = true
      this.#t.t.teardown(() => this.unmock())
    }
    const service = await MockService.create(
      module,
      mocks,
      this.#t.t.mockImport,
    )
    this.#mocks.push(service)
    return Promise.resolve(service.module).then(s => import(s))
  }

  /**
   * Load the supplied module synchronously using `require()`,
   * replacing any of the referenced modules with the mocks provided.
   *
   * Only works with CommonJS modules.
   *
   * For type info, cast using `as typeof import(...)` or use the type
   * parameter, as TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = t.mockRequire<
   *   typeof import('../my-thing.js')
   * >('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * })
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  mockRequire<T = any>(
    module: string,
    mocks: Record<string, any> = {},
  ): T {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockRequire
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked',
      )
      return {} as T
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    return mockRequire(module, mocks, this.#t.t.mockRequire) as T
  }

  /**
   * Set some mocks that will be used for all subsequent
   * {@link @tapjs/mock!index.TapMock#mockImport} and
   * {@link @tapjs/mock!index.TapMock#mockRequire} calls made by this test.
   *
   * Mocks added with `mockAll` are overridden by any explicit mocks set in the
   * `t.mockRequire` or `t.mockImport` call.
   *
   * Repeated calls to `t.mockAll()` will *add* mocks to the set. If the same
   * name is used again, it will replace the previous value, not merge.
   *
   * If a key is set to `undefined` or `null`, then it will be removed from
   * the `mockAll` set.
   *
   * Reset by calling `t.mockAll(null)`
   *
   * Call with no args to return the current `mockAll` object.
   */
  mockAll(mocks?: Record<string, any> | null): Record<string, any> {
    if (mocks === null) this.#allMock = Object.create(null)
    else if (mocks !== undefined) {
      this.#allMock = Object.assign(this.#allMock, mocks)
      for (const [k, v] of Object.entries(this.#allMock)) {
        if (v === undefined || v === null) delete this.#allMock[k]
      }
    }
    return this.#allMock
  }

  /**
   * Unwind the mocks and free up the memory at the end of the test.
   *
   * Called automatically if the `@tapjs/after` plugin is not disabled.
   *
   * @group Spies, Mocks, and Fixtures
   */
  unmock() {
    for (const m of this.#mocks) {
      m.unmock()
    }
  }
}

/**
 * Utility type, overrides the properties in B with the properties
 * in O, deeply nested.
 */
export type MockedObject<B, O> =
  O extends Array<any> ? O
  : B extends { [k: PropertyKey]: any } ?
    O extends Function ? O
    : O extends { [k: string]: any } ?
      {
        [k in keyof B]: k extends keyof O ? MockedObject<B[k], O[k]>
        : B[k]
      }
    : O
  : O

/**
 * Loader that supports {@link @tapjs/mock!index.TapMock#mockImport}
 */
export const loader = '@tapjs/mock/loader'

/**
 * Importer for use with node --import
 */
export const importLoader = '@tapjs/mock/import'

/**
 * plugin method that instantiates {@link @tapjs/mock!index.TapMock}
 */
export const plugin: TapPlugin<TapMock> = (t: TestBase) =>
  new TapMock(t)

/**
 * Implementation of the {@link TapMock#mockRequire} method
 */
import * as stack from '@tapjs/stack'
import Module, { createRequire, isBuiltin } from 'module'
import { dirname, resolve } from 'path'
import { walkUp } from 'walk-up-path'

// for some reason @types/node incorrectly believes that
// Module.prototype.require is a RequireFunction, but it isn't.
/**
 * Export of the Module class, but with the `require()` and `load()` methods
 * exposed.
 */
export type CorrectModule = Omit<Module, 'require'> & {
  require(id: string): any
  load(id: string): void
}

export type CorrectModuleCtor = Omit<
  typeof Module,
  'new' | 'prototype'
> & {
  new (
    id: string,
    parent: CorrectModule | MockedModule,
  ): CorrectModule
  _cache: { [k: string]: CorrectModule }
}

const CorrectModule: CorrectModuleCtor =
  Module as unknown as CorrectModuleCtor

/**
 * A child class of Module, which loads modules from the mock object
 * if specified, otherwise will load *unmocked* modules in its own
 * mocked environment, so that they will load the mocked modules as well.
 */
export class MockedModule extends CorrectModule {
  #resolve: (id: string) => string
  #mocker: Mocker

  constructor(
    id: string,
    parent: CorrectModule | MockedModule,
    mocker?: Mocker,
  ) {
    super(id, parent)
    this.filename ??= id
    this.#resolve = createRequire(this.filename).resolve
    if (parent instanceof MockedModule && parent.#mocker) {
      this.#mocker = parent.#mocker
    } else if (mocker) {
      this.#mocker = mocker
      /* c8 ignore start */
    } else {
      throw new Error('no mocker provided to MockedModule')
    }
    /* c8 ignore stop */
  }

  /**
   * Override require() to perform our mocked require
   */
  require(id: string): any {
    const requiredFilePath = this.#resolve(id)
    if (this.#mocker.hasMock(requiredFilePath)) {
      return this.#mocker.getMock(requiredFilePath)
    }

    if (isBuiltin(requiredFilePath)) {
      // has to be a builtin module, and we didn't mock it.
      return super.require(id)
    }

    const s = this.#mocker.getSeen(requiredFilePath)
    if (s) return s.exports

    const mod = new MockedModule(requiredFilePath, this)
    this.#mocker.setSeen(requiredFilePath, mod)
    mod.load(requiredFilePath)
    return mod.exports
  }
}

/**
 * Class that handles swapping out real modules for mocks as needed
 */
export class Mocker {
  #mocks: Map<string, any> = new Map()
  #seen: Map<string, MockedModule | Module> = new Map()
  module: MockedModule
  constructor(
    parentFileName: string,
    fileName: string,
    mocks: { [k: string]: any } = {},
  ) {
    const require = createRequire(parentFileName)
    const filePath = require.resolve(fileName)
    for (const [key, mock] of Object.entries(mocks)) {
      const mockFilePath = require.resolve(key)
      this.#mocks.set(mockFilePath, mock)
      // builtins can be either with or without the node: prefix
      if (mockFilePath.startsWith('node:')) {
        const bare = mockFilePath.substring('node:'.length)
        if (!(bare in mocks) && isBuiltin(bare)) {
          this.#mocks.set(bare, mock)
        }
      } else if (isBuiltin(mockFilePath)) {
        const prefixed = `node:${mockFilePath}`
        if (!(prefixed in mocks)) {
          this.#mocks.set(prefixed, mock)
        }
      }
    }

    this.#seen = new Map<string, Module>()
    const callerModule = CorrectModule._cache[parentFileName]
    let p: CorrectModule
    /* c8 ignore start */
    if (callerModule) p = callerModule
    else {
      /* c8 ignore stop */
      // if calling t.mockRequire from esm, you won't get a parent
      // but we should still pretend to have one.
      p = new Module(parentFileName) as unknown as CorrectModule
      p.filename = parentFileName
      p.loaded = true
      p.paths = [...walkUp(dirname(parentFileName))].map(path =>
        resolve(path, 'node_modules'),
      )
    }

    this.module = new MockedModule(filePath, p, this)
    this.module.load(filePath)
  }

  /**
   * Return the module we've previously loaded from the specified filePath,
   * or undefined if it's new.
   */
  getSeen(filePath: string) {
    return this.#seen.get(filePath)
  }

  /**
   * Set the module in the internal cache once loaded
   */
  setSeen(filePath: string, mod: MockedModule) {
    this.#seen.set(filePath, mod)
  }

  /**
   * returns true if the set of mocks includes the specified ID
   */
  hasMock(id: string) {
    return this.#mocks.has(id)
  }

  /**
   * Get the mock at the specified identifier, or undefined if it's not mocked
   */
  getMock(id: string) {
    return this.#mocks.get(id)
  }
}

/**
 * Function that actually performs a mocked require()
 */
export const mockRequire: (
  module: string,
  mocks?: { [k: string]: any },
  caller?: Function | ((...a: any[]) => any),
) => any = (module, mocks = {}, caller = mockRequire) => {
  const needIgnoreTap = !stack.getIgnoredPackages().includes('@tapjs')
  if (needIgnoreTap) stack.addIgnoredPackage('@tapjs')
  const at = stack.at(caller)
  if (needIgnoreTap) stack.removeIgnoredPackage('@tapjs')
  const file = at?.absoluteFileName
  if (!file) {
    throw new Error('could not get current call site')
  }
  return new Mocker(file, module, mocks).module.exports
}

import { dirname, resolve } from 'path'
export const walkUp = function* (path: string) {
  for (path = resolve(path); path;) {
    yield path
    const pp = dirname(path)
    if (pp === path) {
      break
    } else {
      path = pp
    }
  }
}

// This is the machinery that supports the load() and resolve()
// methods used by the loader, returning the appropriate urls
// and source.
// It runs on the main thread, not the loader thread.
//
// The globalPreload/initialize inits the port and grabs a reference to it
// in the loader thread.
// Then the loader asks the service over that port for the mocked url and
// source code, which is sent back.
//
// The mock-import module creates a MockService instance when t.mockImport
// is called, giving it a key and setting up its mocks, and calls
// import(startURL) as the return value.
//
// There should be at most 1 MockService instance for a given tap test,
// with its own unique key that's used for loading.
//
// There are some weird lines ignored for coverage in this file, owing to the
// fact that it is loaded as part of tap's initial startup, prior to
// instrumenting coverage, and loaded very differently in node 20 vs prior
// versions that ran loaders on the main thread..

import type { CallSiteLike, CallSiteLikeJSON } from '@tapjs/stack'
import * as stack from '@tapjs/stack'
import { randomBytes } from 'crypto'
import { dirname, isAbsolute } from 'path'
import { resolveImport } from 'resolve-import'
import { isRelativeRequire } from 'resolve-import/is-relative-require'
import { pathToFileURL } from 'url'
import { MessagePort } from 'worker_threads'
import { exportLine } from './export-line.js'
import { mungeMocks } from './munge-mocks.js'
import { resolveMockEntryPoint } from './resolve-mock-entry-point.js'
import { serviceKey } from './service-key.js'

const { hasOwnProperty } = Object.prototype
const hasOwn = (o: any, k: PropertyKey) => hasOwnProperty.call(o, k)
const getKey = () => randomBytes(8).toString('hex')

/**
 * Build the source code for a mocked module.
 */
const buildSrc = (m: MockService, url: string) => {
  const mock = m?.mocks?.[url]
  /* c8 ignore start */
  if (!mock) return
  /* c8 ignore stop */

  const { key } = m

  const keySrc = `__tapmock${serviceKey}$${key}`
  const mockSrc = `global[${keySrc}].mocks[${JSON.stringify(url)}]`
  const src = Object.keys(mock).map(k => exportLine(k, mockSrc))
  if (!Object.keys(mock).includes('default')) {
    src.push(`const defExp = ${mockSrc}
export default defExp\n`)
  }
  const symFor = JSON.stringify(keySrc)
  return `const ${keySrc} = Symbol.for(${symFor})\n${src.join('\n')}`
}

export type MockServiceLoadRequest = {
  action: 'load'
  url: string
  id: string
}
export type MockServiceResolveRequest = {
  action: 'resolve'
  url: string
  parentURL: string
  id: string
}

export type MockServiceRequest =
  | MockServiceLoadRequest
  | MockServiceResolveRequest

export type MockServiceResponse = MockServiceRequest & {
  // a false response means "not a mocked module"
  response: string | undefined
}

export const isMockServiceResponse = (
  m: any,
): m is MockServiceResponse =>
  isMockServiceRequest(m) &&
  Object.keys(m).includes('response') &&
  (typeof (m as MockServiceResponse).response === 'string' ||
    typeof (m as MockServiceResponse).response === 'undefined')

export const isMockServiceRequest = (
  m: any,
): m is MockServiceRequest =>
  !!m &&
  typeof m === 'object' &&
  ((m.action === 'resolve' && typeof m.parentURL === 'string') ||
    (m.action === 'load' && m.parentURL === undefined)) &&
  typeof m.url === 'string' &&
  typeof m.id === 'string'

// keys and instances across dialects, so we get the right one when
// it's loaded from the commonjs globalPreload, or just a cjs test
const kInstances = Symbol.for(`${serviceKey}.instances`)
const g = globalThis as typeof globalThis & {
  [kInstances]?: Map<string, MockService>
}

// this gets called at startup before coverage is initiated,
// so it never covers the second case where it isn't already set.
/* c8 ignore start */
const instances = g[kInstances] || new Map<string, MockService>()
/* c8 ignore stop */
g[kInstances] = instances

const mockServiceCtorSymbol = Symbol('private')
export class MockService {
  static #port?: MessagePort
  key: string = getKey()
  module?: string | Promise<string>
  mocks?: Record<string, Record<string, any>>
  caller?: {
    path: string
    dir: string
    url: URL
    at: CallSiteLike | CallSiteLikeJSON
    stack?: string
  }

  constructor(priv: typeof mockServiceCtorSymbol) {
    if (priv !== mockServiceCtorSymbol) {
      throw new Error('create MockService with MockService.get()')
    }
    instances.set(this.key, this)
  }

  // pass in the main-thread end of the loader port set, and respond
  // appropriately to messages we can handle
  /* c8 ignore start */
  static async listen(port: MessagePort) {
    this.#port = port
    port.on('message', async msg => {
      /* c8 ignore stop */
      /* c8 ignore start */
      if (!isMockServiceRequest(msg)) return
      /* c8 ignore stop */
      const r = await this.handle(msg).catch(e => console.error(e))
      // typescript handles 'void' weirdly
      const response = r === undefined ? undefined : r
      const msr: MockServiceResponse = { ...msg, response }
      port.postMessage(msr)
      /* c8 ignore start */
    })
    port.unref()
  }
  /* c8 ignore stop */

  static async handle(msg: any) {
    if (!isMockServiceRequest(msg)) return
    return msg.action === 'resolve' ?
        this.resolve(msg)
      : this.load(msg)
  }

  static async resolve(req: MockServiceResolveRequest) {
    const { parentURL } = req
    const p = new URL(parentURL)
    const [sk, k] = (p.searchParams.get('tapmock') || '').split('.')
    if (sk !== serviceKey || !k) return
    return this.get(k).resolve(req)
  }
  async resolve({ url, parentURL }: MockServiceResolveRequest) {
    const resolvedURL =
      hasOwn(this.mocks, url) ? url
      : isRelativeRequire(url) ? String(new URL(url, parentURL))
      : isAbsolute(url) ? String(pathToFileURL(url))
      : url

    if (!hasOwn(this.mocks, resolvedURL)) {
      // parent is mocked, but this module isn't, so the things IT loads
      // should be loaded from the mock, even though it isn't. Need to
      // resolveImport() here so that it doesn't get confused when
      // loading deps out of node_modules.
      //
      // If a node builtin is mocked, and another builtin references it, then
      // the builtin will get the original builtin, not the mock. This is a
      // shortcoming owing to the fact that there's no way here to tack a
      // search param on the "url" for an internal module. If it causes
      // problems, then the solution could be to swap out internal modules with
      // a known url type like tapmockBuiltin://node:fs or something, and use
      // that as the indicator that its builtin deps might need to be mocked.
      // For now, it's just a known design limitation, because that's a bit
      // tricky to get right.
      let mocker = await resolveImport(url, parentURL).catch(() => {})
      // we can't resolve it, but maybe someone else can.
      // if RI gives us a string, then it's a builtin, do nothing
      if (!mocker || typeof mocker !== 'object') return
      mocker.searchParams.set('tapmock', `${serviceKey}.${this.key}`)
      return String(mocker)
    }

    const mockRes = new URL(`tapmock://${serviceKey}.${this.key}/`)
    mockRes.searchParams.set('url', resolvedURL)
    return String(mockRes)
  }

  static async load(req: MockServiceLoadRequest) {
    const { url } = req
    if (!url.startsWith(`tapmock://${serviceKey}.`)) return
    /* c8 ignore start */
    const u = new URL(url)
    /* c8 ignore stop */
    const [_, key] = u.host.split('.')
    /* c8 ignore start */
    if (!key) return
    /* c8 ignore stop */
    return this.get(key).load(req)
  }
  async load({ url }: MockServiceLoadRequest) {
    if (!url.startsWith('tapmock://')) return
    const u = new URL(url)
    const key = u.host
    const mockURL = u.searchParams.get('url')
    if (key !== `${serviceKey}.${this.key}` || !mockURL) return
    return buildSrc(this, mockURL)
  }

  static async create(
    module: string,
    mocks: Record<string, any> = {},
    caller: Function | ((...a: any[]) => any) = MockService.create,
  ): Promise<MockService & { module: string | Promise<string> }> {
    const ms = new MockService(mockServiceCtorSymbol)

    /* c8 ignore start */
    const needIgnoreTap = !stack
      .getIgnoredPackages()
      .includes('@tapjs')
    if (needIgnoreTap) stack.addIgnoredPackage('@tapjs')
    const at = stack.at(caller)
    if (needIgnoreTap) stack.removeIgnoredPackage('@tapjs')

    const path = at?.absoluteFileName
    if (!path) {
      throw new Error('could not get current call site')
    }
    /* c8 ignore stop */

    // tell the loader hooks thread that it's ok to start using it now.
    this.#port?.postMessage({ start: true })

    const dir = dirname(path)
    const url = pathToFileURL(path)

    const resolved = resolveMockEntryPoint(
      url,
      module,
      serviceKey,
      ms.key,
      caller,
    )
    resolved.then(s => (ms.module = s))

    ms.mocks = mungeMocks(mocks, dir)
    ms.caller = {
      path,
      dir,
      url,
      at,
      stack: stack.captureString(caller),
    }
    const sym = Symbol.for(`__tapmock${serviceKey}$${ms.key}`)
    const g = globalThis as typeof globalThis & {
      [sym]?: MockService
    }
    return (g[sym] = Object.assign(ms, { module: resolved }))
  }

  unmock() {
    const sym = Symbol.for(`__tapmock${serviceKey}$${this.key}`)
    const g = globalThis as typeof globalThis & {
      [sym]?: MockService
    }
    delete g[sym]
    instances.delete(this.key)
    this.mocks = undefined
  }

  static get(key: string) {
    const i = instances.get(key)
    if (!i) {
      throw new Error('mock service instance key not found')
    }
    return i
  }
}

/**
 * Exported as `'resolve-import/get-conditional-values-list'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

export type ConditionalValuesList = [
  submodulePath: string,
  conditions: Set<string>,
  resolvedValue: string | null
][]

/**
 * Given an `exports` or `imports` value from a package, return the list of all
 * possible conditional values that it might potentially resolve to, for any
 * possible set of import conditions, along with the `Set<string>` of
 * conditions, any superset of which will result in the condition.
 *
 * The list includes null results, since while these are not a valid resolution
 * per se, they do *prevent* valid resolutions that match the same conditions.
 */
export const getConditionalValuesList = (
  importsExports: Imports | Exports
): ConditionalValuesList => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: ConditionalValuesList = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getConditionalValuesListFromCond(
            importsExports as ConditionalValue
          ).map(s => ['.', ...s])
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(
        ...getConditionalValuesListFromCond(v).map(
          s => [k, ...s] as [string, Set<string>, string | null]
        )
      )
    }
    return conditions
  }
  return getConditionalValuesListFromCond(
    importsExports as ConditionalValue
  ).map(s => ['.', ...s])
}

const isSubset = (maybeSub: Set<string>, sup: Set<string>) => {
  if (maybeSub.size > sup.size) return false
  for (const c of maybeSub) {
    if (!sup.has(c)) return false
  }
  return true
}

// walk down the tree, creating a list of [Set<Condition>, value]
// if a subset of the current set is already present in the list, then omit it
const getConditionalValuesListFromCond = (
  cond?: ConditionalValue,
  path: string[] = [], // path of conditions that got here
  list: [Set<string>, string | null][] = []
): [Set<string>, string | null][] => {
  /* c8 ignore start */
  if (cond === undefined) return list
  /* c8 ignore stop */
  if (cond === null || typeof cond === 'string') {
    // reached a resolution value.
    // if we got here, we know it has not yet been seen.
    list.push([new Set(path), cond])
    return list
  }
  if (Array.isArray(cond)) {
    for (const c of cond) {
      getConditionalValuesListFromCond(c, path, list)
      // if we hit a default condition, break
      if (!c || typeof c === 'string') break
    }
    return list
  }
  // ConditionalValueObject
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    const p = k === 'default' ? path : path.concat(k)
    // if no subset seen, then recurse
    const ps = new Set(p)
    const seen = list.some(([s]) => isSubset(s, ps))
    if (!seen) {
      getConditionalValuesListFromCond(v, p, list)
    }
    if (k === 'default') break
  }
  return list
}

/**
 * Exported as `'resolve-import/get-all-conditions'`
 * @module
 */
import { ConditionalValue, Exports, Imports } from './index.js'

/**
 * Given an `exports` or `imports` value from a package, return the list of
 * conditions that it is sensitive to.
 *
 * `default` is not included in the returned list, since that's always
 * effectively relevant.
 *
 * Note that a condition being returned by this method does not mean
 * that the export/import object actually has a *target* for that condition,
 * since it may map to `null`, be nested under another condition, etc. But it
 * does potentially have some kind of conditional behavior for all the
 * conditions returned.
 *
 * Ordering of returned conditions is arbitrary, and does not imply precedence
 * or object shape.
 */
export const getAllConditions = (
  importsExports: Imports | Exports
): string[] => {
  if (
    !!importsExports &&
    typeof importsExports === 'object' &&
    !Array.isArray(importsExports)
  ) {
    let subs: string | undefined = undefined
    const conditions: string[] = []
    for (const [k, v] of Object.entries(importsExports)) {
      /* c8 ignore start */
      if (!k) continue
      /* c8 ignore stop */
      if (subs === undefined) {
        if (!k.startsWith('#') && k !== '.' && !k.startsWith('./')) {
          return getAllConditionsFromCond(
            importsExports as ConditionalValue
          )
        }
        subs = k.charAt(0)
      }
      if (
        // imports have to be #<something>
        (subs === '#' && (k === '#' || !k.startsWith('#'))) ||
        // exports can be ./<something> or .
        (subs === '.' && k !== '.' && !k.startsWith('./'))
      ) {
        throw new Error(
          `invalid ${
            subs === '.' ? 'exports' : 'imports'
          } object, all keys ` + `must start with ${subs}. Found ${k}.`
        )
      }
      conditions.push(...getAllConditionsFromCond(v))
    }
    return [...new Set(conditions)]
  }

  return getAllConditionsFromCond(importsExports as ConditionalValue)
}

const getAllConditionsFromCond = (cond?: ConditionalValue): string[] => {
  if (!cond || typeof cond === 'string') return []
  if (Array.isArray(cond)) {
    const conditions: string[] = []
    for (const e of cond) {
      if (!e || typeof e === 'string') break
      conditions.push(...getAllConditionsFromCond(e))
    }
    return [...new Set(conditions)]
  }
  const conditions: string[] = []
  for (const [k, v] of Object.entries(cond)) {
    if (k.startsWith('#') || k === '.' || k.startsWith('./')) {
      throw new Error(`Expected valid import condition, got: ${k}`)
    }
    // anything after 'default' isn't relevant
    conditions.push(...getAllConditionsFromCond(v))
    if (k === 'default') break
    else conditions.push(k)
  }
  return [...new Set(conditions)]
}

import { getConditionalValuesList } from './get-conditional-values-list.js'
import { Exports, Imports } from './index.js'

/**
 * Get the minimal set of conditions that can potentially produce different
 * resolution values for a given imports or exports object from a package
 * manifest.
 *
 * For example:
 *
 * ```json
 * {
 *   ".": [{"import":[{"types":"x.d.ts"},"x.mjs"], "require":"y.js"}]
 *   "./a": {"browser":{"require":"./a.js"}},
 *   "./b": {"browser":"./b.js"},
 *   "./c": {"require":{"browser":"./c.js"}}
 * }
 * ```
 *
 * would return:
 * ```js
 * [
 *   ['import','types'],
 *   ['import'],
 *   ['require'],
 *   ['browser'],
 *   ['browser', 'require'],
 * ]
 * ```
 *
 * With the `['require', 'browser']` condition set omitted, as it is already
 * covered by `['browser', 'require']`.
 *
 * Condition ordering is arbitrary and not guaranteed to be consistent.
 */
export const getUniqueConditionSets = (
  importsExports: Imports | Exports
): string[][] => {
  const list = getConditionalValuesList(importsExports)
  let results: string[][] = []
  for (const [_, conditions] of list) {
    if (!results.some(arr => arrayIsEquivalent(arr, conditions))) {
      results.push([...conditions])
    }
  }
  return results
}

const arrayIsEquivalent = (arr: string[], sup: Set<string>) => {
  if (arr.length !== sup.size) return false
  for (const c of arr) {
    if (!sup.has(c)) return false
  }
  return true
}

/**
 * Exported as `'resolve-import/resolve-all-exports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { getNamedExportsList } from './get-named-exports-list.js'
import { Exports, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'
import { starGlob } from './star-glob.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible export path is mapped to the file URL that it would resolve to.
 *
 * Invalid exports are omitted. No errors are raised as long as the file is a
 * valid `package.json`.
 *
 * Note: in cases like `"./x/*": "./file.js"`, where the list of possible
 * import paths is unbounded, the returned object will contain `"./x/*"` as the
 * key, since there's no way to expand that to every possible match.
 */
export const resolveAllExports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllExports)
  }

  const results: Record<string, string | URL> = {}

  const { exports } = pkg
  for (const sub of getNamedExportsList(exports)) {
    let res

    // this can't shouldn't be able to actually throw, because we're
    // pulling the list from the set itself.
    /* c8 ignore start */
    try {
      res = resolveExport(sub, exports as Exports, pjPath, pjPath, options)
    } catch {}
    if (!res) continue
    /* c8 ignore stop */

    // if it contains a *, then we have to glob,
    // in package.json exports * is actually **, but only
    // relevant if there is exactly ONE star
    const sres = res.split('*')
    const ssub = sub.split('*')
    if (sres.length === 2 && ssub.length === 2) {
      for (const [rep, target] of await starGlob(
        sres as [string, string],
        pjDir
      )) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
    } else {
      results[sub] = pathToFileURL(resolve(pjDir, res))
    }
  }

  return results
}

/**
 * Errors raised by resolve failures.
 * @module
 */

import { resolveImport } from './resolve-import.js'

// TODO: move "caller" to an options object. Then the same options object can
// be used that handles conditions, parentURL, etc., and the relevant
// top-level function can set it explicitly at the start of the process and
// just pass it along.

export const invalidImportSpecifier = (
  url: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error('invalid import() specifier: ' + url)
  Error.captureStackTrace(er, caller)
  return er
}

export const invalidPackage = (
  pj: string | URL,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = new Error(`Not a valid package: ${pj}`)
  Error.captureStackTrace(er, caller)
  return er
}

export const relativeImportWithoutParentURL = (
  url: string,
  parentURL: any,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error('relative import without parentURL'),
    {
      url,
      parentURL,
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const subpathNotExported = (
  sub: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const p =
    sub === '.'
      ? 'No "exports" main defined'
      : `Package subpath '${sub}' is not defined by "exports"`
  const er = Object.assign(
    new Error(`${p} in ${pj} imported from ${from}`),
    {
      code: 'ERR_PACKAGE_PATH_NOT_EXPORTED',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageNotFound = (
  path: string | null,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find package '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const moduleNotFound = (
  path: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(`Cannot find module '${path}' imported from ${from}`),
    {
      code: 'ERR_MODULE_NOT_FOUND',
    }
  )
  Error.captureStackTrace(er, caller)
  return er
}

export const packageImportNotDefined = (
  path: string,
  pj: string,
  from: string,
  caller: (...a: any[]) => any = resolveImport
) => {
  const er = Object.assign(
    new Error(
      `Package import specifier "${path}" is not defined in package ` +
        `${pj} imported from ${from}`
    ),
    { code: 'ERR_PACKAGE_IMPORT_NOT_DEFINED' }
  )
  Error.captureStackTrace(er, caller)
  return er
}

/**
 * Exported as `'resolve-import/resolve-import'`
 * @module
 */
import { realpath } from 'fs/promises'
import { isBuiltin } from 'module'
import { basename, dirname, isAbsolute, resolve } from 'path'
import { fileURLToPath, pathToFileURL } from 'url'
import {
  moduleNotFound,
  relativeImportWithoutParentURL,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { ResolveImportOpts } from './index.js'
import { isRelativeRequire } from './is-relative-require.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolvePackageImport } from './resolve-package-import.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

// It's pretty common to resolve against, eg, cwd + '/x', since we might not
// know the actual file that it's being loaded from, and want to resolve what
// a dep WOULD be from a given path. This allows us to realpath that directory,
// without requiring that the file exist.
const realpathParentDir = async (path: string | URL) => {
  path = toPath(path)
  return resolve(await realpath(dirname(path)), basename(path))
}

/**
 * Resolve an import URL or string as if it were coming from the
 * module at parentURL.
 *
 * Returns a string for node builtin modules, and a file:// URL
 * object for anything resolved on disk.
 *
 * If the resolution is impossible, then an error will be raised, which
 * closely matches the errors raised by Node when failing for the same
 * reason.
 */
export const resolveImport = async (
  /** the thing being imported */
  url: string | URL,
  /**
   * the place the import() would be coming from. Required for relative
   * imports.
   */
  parentURL: string | URL | undefined = undefined,
  options: ResolveImportOpts = {}
): Promise<URL | string> => {
  // already resolved, just check that it exists
  if (typeof url === 'string' && url.startsWith('file://')) {
    url = new URL(url)
  }
  if (typeof url === 'object') {
    if (!(await fileExists(url))) {
      throw moduleNotFound(String(url), String(parentURL))
    }
    const rp = await realpath(toPath(url))
    return rp !== fileURLToPath(url) ? pathToFileURL(rp) : url
  }

  const pu = parentURL
    ? toFileURL(await realpathParentDir(parentURL))
    : undefined

  if (isRelativeRequire(url)) {
    if (!pu) {
      throw relativeImportWithoutParentURL(url, parentURL)
    }
    const u = new URL(url, pu)
    if (!(await fileExists(u))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(new URL(url, pu)))
  }

  if (isAbsolute(url)) {
    if (!(await fileExists(url))) {
      throw moduleNotFound(url, String(parentURL))
    }
    return pathToFileURL(await realpath(url))
  }

  if (isBuiltin(String(url))) {
    return String(url)
  }

  // ok, we have to resolve it. some kind of bare dep import,
  // either a package name resolving to module or main, or a named export.
  const parentPath: string = toPath(
    parentURL || resolve(await realpath(process.cwd()), 'x')
  )
  const opts = {
    ...options,
    originalParent: String(options.originalParent || parentPath),
  }
  if (url) {
    return resolvePackageImport(url, parentPath, opts)
  } else {
    return resolveDependencyExports(url, parentPath, opts)
  }
}

import { resolve } from 'path'
import { pathToFileURL } from 'url'
import { moduleNotFound, packageNotFound } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveExport } from './resolve-export.js'

/**
 * Resolve a dependency like '@dep/name/sub/module' where
 * '@dep/name' is in node_modules somewhere and exports './sub/module'
 */
export const resolveDependencyExports = async (
  url: string | null,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL> => {
  const { originalParent } = options
  const parts = url?.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/)
  const [, pkgName, sub] =
    url === null ? [, null, ''] : parts || ['', '', '']
  const ppath = await findDepPackage(pkgName, parentPath)
  if (!ppath) {
    throw packageNotFound(pkgName, originalParent)
  }

  const indexjs = resolve(ppath, 'index.js')
  const pj = resolve(ppath, 'package.json')
  const pkg = await readPkg(pj)
  const subpath = sub ? resolve(ppath, sub) : false
  // if not a package, then the sub can still be a direct path
  // if no sub, then resolves to index.js if available.
  if (!pkg) {
    if (!subpath) {
      // try index.js, otherwise fail
      if (await fileExists(indexjs)) return pathToFileURL(indexjs)
      else throw packageNotFound(ppath, originalParent)
    } else {
      if (await fileExists(subpath)) {
        return pathToFileURL(subpath)
      } else throw moduleNotFound(subpath, originalParent)
    }
  }

  // ok, have a package, look up the export if present.
  // otherwise, use main, otherwise index.js
  if (pkg.exports) {
    const subPath = resolveExport(
      sub,
      pkg.exports,
      pj,
      originalParent,
      options
    )
    const resolved = resolve(ppath, subPath)
    if (await fileExists(resolved)) return pathToFileURL(resolved)
    else throw moduleNotFound(resolved, originalParent)
  } else if (subpath) {
    if (await fileExists(subpath)) return pathToFileURL(subpath)
    else throw moduleNotFound(subpath, originalParent)
  } else if (pkg.main) {
    // fall back to index.js if main is missing
    const rmain = resolve(ppath, pkg.main)
    if (await fileExists(rmain)) return pathToFileURL(rmain)
    else if (await fileExists(indexjs)) return pathToFileURL(indexjs)
    else throw packageNotFound(ppath, originalParent)
  } else if (await fileExists(indexjs)) {
    return pathToFileURL(indexjs)
  } else {
    throw packageNotFound(ppath, originalParent)
  }
}

import { realpath, stat } from 'fs/promises'
import { dirname, resolve, sep } from 'path'
import { walkUp } from 'walk-up-path'

const dirExists = async (f: string): Promise<boolean> =>
  stat(f).then(
    st => st.isDirectory(),
    () => false
  )

export const findDepPackage = async (
  pkgName: string | null,
  parentPath: string
) => {
  // starting from the dirname, try to find the nearest node_modules
  for (const dir of walkUp(dirname(parentPath))) {
    const nm = resolve(dir, 'node_modules') + sep
    // if it's null, then we need the node_modules itself
    // if it's '' then we use node_modules with an extra / on it
    // thisis only relevant when generating the error message, since
    // of course node_modules// is never going to be a valid package.
    const ppath =
      pkgName === null ? nm : (!pkgName ? nm : resolve(nm, pkgName)) + sep
    if (await dirExists(ppath)) {
      try {
        return (await realpath(ppath)) + sep
        // the direxists stat will avoid almost all throws that could
        // occur here, but just in case.
        /* c8 ignore start */
      } catch {}
        /* c8 ignore stop */
    }
  }
}

import { subpathNotExported } from './errors.js'
import { findStarMatch } from './find-star-match.js'
import {
  ConditionalValue,
  Exports,
  ExportsSubpaths,
  ResolveImportOpts,
} from './index.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'

/**
 * Resolve an export that might be a string, subpath exports, exports value
 * object, or array of strings and exports value objects
 */
export const resolveExport = (
  sub: string,
  exp: Exports,
  pj: string,
  from: string,
  options: ResolveImportOpts
): string => {
  const s = !sub
    ? '.'
    : sub === '.' || sub.startsWith('./')
    ? sub
    : `./${sub}`

  if (typeof exp === 'string' || Array.isArray(exp)) {
    const res = s === '.' && resolveConditionalValue(exp, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  // now it must be a set of named exports or an export value object
  // first try to resolve as a value object, if that's allowed
  if (s === '.') {
    const res = resolveConditionalValue(exp, options)
    if (res) return res
  }

  // otherwise the only way to match is with subpaths
  const es = exp as ExportsSubpaths

  // if we have an exact match, use that
  const e = es[s]
  if (e !== undefined) {
    const res = resolveConditionalValue(e, options)
    if (!res) throw subpathNotExported(s, pj, from)
    return res
  }

  const sm = findStarMatch(s, es)
  if (sm) {
    const [key, mid] = sm
    const res = resolveConditionalValue(
      es[key] as ConditionalValue,
      options
    )
    if (!res) throw subpathNotExported(s, pj, from)
    return res.replace(/\*/g, mid)
  }

  // did not find a match
  throw subpathNotExported(s, pj, from)
}

/**
 * Given an object with string keys possibly containing *, and a test
 * string, return the matching key, and the section that the star should
 * expand to when matching against the test string.
 */
export const findStarMatch = (
  s: string,
  obj: Record<string, any>
): [string, string] | null => {
  // longest pattern matches take priority
  const patterns = Object.keys(obj)
    .filter(p => p.length <= s.length)
    .sort((a, b) => b.length - a.length)
    .map(p => [p, p.split('*')])
    .filter(([, p]) => (p as string[]).length === 2) as [
    string,
    [string, string]
  ][]

  for (const [key, [before, after]] of patterns) {
    if (s.startsWith(before) && s.endsWith(after)) {
      const mid = s.substring(before.length, s.length - after.length)
      return [key, mid]
    }
  }

  return null
}

/**
 * Exported as `'resolve-import/resolve-conditional-value'`
 * @module
 */
import { ConditionalValue, ResolveImportOpts } from './index.js'

/**
 * find the first match for string, import, node, or default
 * at this point, we know we're on the right subpath already
 */
export const resolveConditionalValue = (
  exp: ConditionalValue,
  options: ResolveImportOpts
): string | null => {
  const { conditions = ['import', 'node'] } = options
  if (exp === null || typeof exp === 'string') return exp
  if (Array.isArray(exp)) {
    for (const e of exp) {
      const r = resolveConditionalValue(e, options)
      if (r) return r
    }
    return null
  }
  for (const [k, v] of Object.entries(exp)) {
    if (conditions.includes(k) || k === 'default') {
      return resolveConditionalValue(v, options)
    }
  }
  return null
}

import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { walkUp } from 'walk-up-path'
import {
  invalidImportSpecifier,
  moduleNotFound,
  packageImportNotDefined,
} from './errors.js'
import { fileExists } from './file-exists.js'
import { findStarMatch } from './find-star-match.js'
import { ConditionalValue, ResolveImportOpts } from './index.js'
import { readPkg } from './read-pkg.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveDependencyExports } from './resolve-dependency-export.js'
import { resolveExport } from './resolve-export.js'
import { resolveImport } from './resolve-import.js'

/**
 * Resolve an import like '@package/name/sub/module', where
 * './sub/module' appears in the exports of the local package.
 */
export const resolvePackageImport = async (
  url: string,
  parentPath: string,
  options: ResolveImportOpts & { originalParent: string }
): Promise<URL | string> => {
  const { originalParent } = options
  const parts = url.match(/^(@[^\/]+\/[^\/]+|[^\/]+)(?:\/(.*))?$/) as
    | null
    | (RegExpMatchArray & [string, string, string])
  // impossible
  /* c8 ignore start */
  if (!parts) throw invalidImportSpecifier(url)
  /* c8 ignore stop */

  for (const dir of walkUp(dirname(parentPath))) {
    const pj = resolve(dir, 'package.json')
    const pkg = await readPkg(pj)
    if (!pkg) continue
    if (pkg.name && pkg.exports) {
      // can import from this package name if exports is defined
      const [, pkgName, sub] = parts
      if (pkgName === pkg.name) {
        // ok, see if sub is a valid export then
        const subPath = resolveExport(
          sub,
          pkg.exports,
          pj,
          originalParent,
          options
        )
        const resolved = resolve(dir, subPath)
        if (await fileExists(resolved)) return pathToFileURL(resolved)
        else throw moduleNotFound(resolved, originalParent)
      }
    }

    if (url.startsWith('#')) {
      if (!pkg.imports) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const exact = pkg.imports[url]
      if (exact !== undefined) {
        const res = resolveConditionalValue(exact, options)
        if (!res) {
          throw packageImportNotDefined(url, pj, originalParent)
        }
        // kind of weird behavior, but it's what node does
        if (res.startsWith('#')) {
          return resolveDependencyExports(null, parentPath, options)
        }
        return resolveImport(res, pj, options)
      }

      const sm = findStarMatch(url, pkg.imports)
      if (!sm) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      const [key, mid] = sm
      const match = pkg.imports[key] as ConditionalValue
      const res = resolveConditionalValue(match, options)
      if (!res) {
        throw packageImportNotDefined(url, pj, originalParent)
      }
      if (res.startsWith('#')) {
        return resolveDependencyExports(null, parentPath, options)
      }
      const expand = res.replace(/\*/g, mid)

      // start over with the resolved import
      return resolveImport(expand, pj, options)
    }

    break
  }

  return resolveDependencyExports(url, parentPath, options)
}

import { Exports, ExportsSubpaths } from './index.js'
/**
 * Get the condition-resolved targets of all exports
 *
 * Stars are not expanded.
 */
export const getNamedExportsList = (exports?: Exports): string[] => {
  if (!exports) return []
  if (!isExportSubpaths(exports)) return ['.']
  return Object.keys(exports).filter(e => e === '.' || e.startsWith('./'))
}

const isExportSubpaths = (e: Exports): e is ExportsSubpaths => {
  if (!e || typeof e !== 'object' || Array.isArray(e)) return false
  for (const p in e) {
    if (p !== '.' && !p.startsWith('./')) return false
  }
  return true
}

import { escape, glob } from 'glob'
import { resolve } from 'path'

export const starGlob = async (
  star: [string, string], // actually [string,string]
  dir: string
): Promise<[string, string][]> => {
  const pattern =
    escape(star[0]) +
    (star[0].endsWith('/') ? '' : '*/') +
    '**' +
    (star[1].startsWith('/') ? '' : '/*') +
    escape(star[1])
  const matches = await glob(pattern, {
    posix: true,
    absolute: false,
    nodir: true,
    cwd: dir,
    dotRelative: true,
  })
  return matches.map(match => {
    const rep = match.substring(
      star[0].length,
      match.length - star[1].length
    )
    return [rep, resolve(dir, match)]
  })
}

/**
 * Exported as `'resolve-import/resolve-all-local-imports'`
 * @module
 */
import { dirname, resolve } from 'path'
import { pathToFileURL } from 'url'
import { invalidPackage } from './errors.js'
import { fileExists } from './file-exists.js'
import { findDepPackage } from './find-dep-package.js'
import { ResolveImportOpts } from './index.js'
import { Pkg, readPkg } from './read-pkg.js'
import { resolveAllExports } from './resolve-all-exports.js'
import { resolveConditionalValue } from './resolve-conditional-value.js'
import { resolveImport } from './resolve-import.js'
import { starGlob } from './star-glob.js'
import { toFileURL } from './to-file-url.js'
import { toPath } from './to-path.js'

/**
 * Given a path or file URL to a package.json file, return an object where each
 * possible local import path is mapped to the file URL that it would resolve
 * to.
 *
 * Invalid and non-resolving imports are omitted.
 */
export const resolveAllLocalImports = async (
  packageJsonPath: string | URL,
  options: ResolveImportOpts = {}
): Promise<Record<string, string | URL>> => {
  const pjPath = toPath(packageJsonPath)
  const pjDir = dirname(pjPath)
  const pjURL = toFileURL(packageJsonPath)

  const pkg = await readPkg(pjPath)
  if (!pkg) {
    throw invalidPackage(packageJsonPath, resolveAllLocalImports)
  }
  const results: Record<string, URL | string> = {}

  for (const [sub, target] of getNamedImportsList(pkg, options)) {
    // if the import is local, then look it up
    // if it's another package, then look up that package
    // if it's another package with a *, then look up all exports
    // of that package, and filter by the matches.
    const parts = target.match(/^(@[^\/]+\/[^\/]+|[^\/]+)/)
    // make internal package named modules consistently `./`
    const name = pkg.name

    // non-matches already filtered out
    /* c8 ignore start */
    if (!parts) continue
    /* c8 ignore stop */

    const ssub = sub.split('*')
    const starget = target.split('*') as [string, string]
    const star = ssub.length === 2 && starget.length === 2
    if (!star) {
      // simple case, no * replacement
      // if not found, just omit it.
      // do a full resolve, because the target can be anything like
      // './foo/bar' or 'dep/blah', etc.
      try {
        results[sub] = await resolveImport(target, pjURL)
      } catch {}
      continue
    }

    // has a star, have to glob if it's localPath, or look up exports if not
    const localPath = parts[1] === '.'
    if (localPath) {
      for (const [rep, target] of await starGlob(starget, pjDir)) {
        results[ssub[0] + rep + ssub[1]] = pathToFileURL(target)
      }
      continue
    }

    const localName = parts[1] === name
    const dep = !localPath && !localName ? parts[1] : null

    // if we can't find the package, it's not valid.
    const ppath = dep ? await findDepPackage(dep, pjDir) : pjDir
    if (!ppath) continue

    const pj = resolve(ppath, 'package.json')
    if (!(await fileExists(pj))) {
      continue
    }

    const allExports = await resolveAllExports(pj)
    for (const [k, v] of Object.entries(allExports)) {
      if (k === '.' || k === './') continue
      const i = dep + k.substring(1)
      if (i.startsWith(starget[0]) && i.endsWith(starget[1])) {
        const s =
          ssub[0] +
          i.substring(starget[0].length, i.length - starget[1].length) +
          ssub[1]
        // should be impossible to throw, because we're pulling the list
        // from the package itself, and it gets resolved at that point.
        /* c8 ignore start */
        try {
          results[s] = await resolveImport(v, pjURL)
        } catch {}
        /* c8 ignore stop */
      }
    }
  }

  return results
}

/**
 * Get the condition-resolved targets of all imports
 *
 * Stars are not expanded.
 */
const getNamedImportsList = (
  pkg: Pkg,
  options: ResolveImportOpts
): [string, string][] => {
  const results: [string, string][] = []
  const { imports } = pkg
  if (!imports || typeof imports !== 'object') return results
  for (const [k, v] of Object.entries(imports)) {
    const r = resolveConditionalValue(v, options)
    if (r && !r.startsWith('#')) results.push([k, r])
  }
  return results
}

/**
 * utility used in creating the source of a mocked import module
 * @module
 */

const exportIndex = new Map<string, number>()

/**
 * Create the `export` lines in the generated module source
 */
export const exportLine = (k: string, mockSrc: string): string => {
  if (k === 'default') {
    return `const defExp = ${mockSrc}.default
export default defExp
`
  }
  const kSrc = JSON.stringify(k)

  const i = exportIndex.get(mockSrc) || 0
  exportIndex.set(mockSrc, i + 1)
  return `const exp${i} = ${mockSrc}[${kSrc}]
export { exp${i} as ${kSrc} }
`
}

import { isBuiltin } from 'module'
import { isAbsolute, resolve } from 'path'
import { isRelativeRequire } from 'resolve-import/is-relative-require'
import { pathToFileURL } from 'url'

// turn all the mocks we get into either the exports provided,
// if it's an object, or { default: <value> } otherwise.

export const mungeMocks = (
  mocksInput: Record<string, any>,
  dir: string,
): { [k: string]: { [j: string]: any } } => {
  const mocks = Object.create(null)
  for (const [k, v] of Object.entries(mocksInput)) {
    const m =
      v && typeof v === 'object' && !Array.isArray(v) ?
        v
      : { default: v }
    if (isBuiltin(k)) {
      mocks[k] = m
      /* c8 ignore start */
      if (k.startsWith('node:')) {
        /* c8 ignore stop */
        const bare = k.substring('node:'.length)
        if (!(bare in mocksInput)) {
          mocks[bare] = m
        }
      } else {
        const prefixed = `node:${k}`
        if (!(prefixed in mocksInput)) {
          mocks[prefixed] = m
        }
      }
    } else if (/^(file:\/\/\/|https?:\/\/)/.test(k)) {
      mocks[k] = m
    } else if (isRelativeRequire(k) || isAbsolute(k)) {
      mocks[String(pathToFileURL(resolve(dir, k)))] = m
    } else {
      // absolute package name
      mocks[k] = m
    }
  }
  return mocks
}

import { resolveImport } from 'resolve-import'

export const resolveMockEntryPoint = async (
  url: URL,
  module: string,
  serviceKey: string,
  key: string,
  caller: Function | ((...a: any[]) => any),
): Promise<string> => {
  let mockedModuleURL: URL
  if (module.startsWith('./') || module.startsWith('../')) {
    mockedModuleURL = new URL(module, url)
  } else {
    const res = (await resolveImport(module, url)) as URL
    // caught at the exposed API, defense in depth only
    // but the experience if it throws here is unhelpful.
    /* c8 ignore start */
    if (typeof res === 'string') {
      const er = new TypeError(
        'node builtins cannot be mock imported',
      )
      Error.captureStackTrace(er, caller)
      throw er
    }
    /* c8 ignore stop */
    mockedModuleURL = res
  }
  mockedModuleURL.searchParams.set('tapmock', `${serviceKey}.${key}`)
  return String(mockedModuleURL)
}

import { randomBytes } from 'crypto'
const kServiceKey = Symbol.for('@tapjs/mock.' + __filename)
const g = globalThis as typeof globalThis & {
  [kServiceKey]: string
}

export const serviceKey = (g[kServiceKey] =
  g[kServiceKey] ?? randomBytes(8).toString('hex'))

import type { TapPlugin, TAP } from '@tapjs/core'
import { env } from '@tapjs/core'
import { serialize } from './serialize.js'

let serialized = false
export const plugin: TapPlugin<{}> = t => {
  if (
    t.name === 'TAP' &&
    !t.parent &&
    env.NODE_TEST_CONTEXT === 'child-v8' &&
    !serialized
  ) {
    // make sure we don't serialize anything else in this process
    serialized = true
    env.NODE_TEST_CONTEXT = 'child'
    serialize(t as TAP)
  }
  return {}
}

// Serialize the root TAP object, and output a node:test style
// serialized message stream.

import { Base, TAP } from '@tapjs/core'
import {
  TestStreamDeserialize,
  TestStreamSerialize,
} from '@tapjs/error-serdes'

import { proc } from '@tapjs/core'
import { inspect } from 'node:util'
import { commentMethod } from './comment.js'
import { onAddFn } from './on-add.js'
import { printMessagesFn } from './print-messages.js'
import { serializeStdio } from './stdio.js'
import { TestMap } from './test-map.js'

if (!proc) {
  throw new Error('Cannot serialize TAP stream, no process object')
}

const { stdout } = proc

export const serialize = (
  tap: TAP,
): void => {
  if (tap.registered) {
    throw new Error('Cannot serialize TAP stream, already registered')
  }
  tap.register?.()
  tap.options.passes = true

  const subsMap = new TestMap<Base[]>()
  const diagsMap = new TestMap<DiagnosticData[]>([[tap, []]])

  const stream = new TestStreamSerialize()
  // just for inspecting in dev.
  /* c8 ignore start */
  if (process.env.TAP_SERIALIZE_DEBUG === '1') {
    stream.pipe(new TestStreamDeserialize()).on('data', c =>
      stdout.write(
        inspect(JSON.parse(JSON.stringify(c)), {
          colors: true,
          depth: Infinity,
        }) + '\n',
      ),
    )
  } else {
    /* c8 ignore stop */
    stream.pipe(stdout)
  }

  const comment = commentMethod(stream, diagsMap)

  // unpossible, we already checked on module load
  /* c8 ignore start */
  if (!proc) {
    throw new Error('Cannot serialize TAP stream, no process object')
  }
  /* c8 ignore stop */
  if (process.env.TAP_SERIALIZE_DEBUG !== '1') {
    serializeStdio(stream, proc, 'stderr')
    serializeStdio(stream, proc, 'stdout')
  }

  const onAdd = onAddFn(comment, diagsMap, subsMap)

  const printMessages = printMessagesFn(
    tap,
    stream,
    subsMap,
    diagsMap,
  )

  onAdd(tap)
  tap.on('complete', () => {
    printMessages(tap)
    stream.end()
  })
}

// This module is more or less a direct port of the serialization logic
// from node's lib/internal/error_serdes module, with primordials removed.
import {
  errorCtorNames,
  kCustomInspect,
  kCustomInspectedObject,
  kInspectedError,
  kInspectedSymbol,
  kSerializedError,
  kSerializedObject,
} from './constants.js'

import { inspect } from 'node:util'
import { serialize } from 'v8'

const getAllProps = (obj: any, target = obj) => {
  const all = Object.create(null)
  if (obj === null) return all
  Object.assign(all, getAllProps(Object.getPrototypeOf(obj), target))
  const keys = Object.getOwnPropertyNames(obj)
  for (const key of keys) {
    const desc = Object.getOwnPropertyDescriptor(obj, key)
    /* c8 ignore start */
    if (!desc) continue
    /* c8 ignore stop */
    const getter = desc.get
    if (getter) {
      try {
        delete desc.get
        delete desc.set
        desc.value = getter.call(target)
      } catch {}
    }
    if (key === 'cause') {
      desc.value = serializeError(desc.value)
      all.cause = desc
    } else if (
      'value' in desc &&
      typeof desc.value !== 'function' &&
      typeof desc.value !== 'symbol'
    ) {
      all[key] = desc
    }
  }
  return all
}

const isError = (obj: any): obj is Error =>
  typeof obj === 'object' &&
  Object.prototype.toString.call(obj) === '[object Error]'

const getCtors = (obj: any) => {
  const ctors: Function[] = []
  for (
    let cur = obj;
    cur !== null;
    cur = Object.getPrototypeOf(cur)
  ) {
    const desc = Object.getOwnPropertyDescriptor(cur, 'constructor')
    const value = desc?.value
    if (typeof value === 'function') {
      ctors.push(value)
    }
  }
  return ctors
}

const getName = (f: Function): string | undefined => {
  const n = Object.getOwnPropertyDescriptor(f, 'name')?.value
  if (typeof n === 'string') return n
}

export const serializeError = (er: unknown) => {
  if (typeof er === 'symbol') {
    return Buffer.from(
      String.fromCharCode(kInspectedSymbol) + String(er),
      'utf8',
    )
  }
  try {
    if (isError(er)) {
      const ctors = getCtors(er)
      for (const c of ctors) {
        const name = getName(c)
        if (name && errorCtorNames.has(name)) {
          const serialized = serialize({
            constructor: name,
            properties: getAllProps(er),
          })
          return Buffer.concat([
            Buffer.from([kSerializedError]),
            serialized,
          ])
        }
      }
      /* c8 ignore start */
    }
  } catch {}
  /* c8 ignore stop */
  try {
    if (
      er !== null &&
      er !== undefined &&
      kCustomInspect in (er as {})
    ) {
      return Buffer.from(
        String.fromCharCode(kCustomInspectedObject) + inspect(er),
        'utf8',
      )
    }
  } catch {}
  try {
    const serialized = serialize(er)
    return Buffer.concat([
      Buffer.from([kSerializedObject]),
      serialized,
    ])
  } catch {}
  return Buffer.from(
    String.fromCharCode(kInspectedError) + inspect(er),
    'utf8',
  )
}

import { DefaultSerializer } from 'node:v8'

export const kSerializedError = 0
export const kSerializedObject = 1
export const kInspectedError = 2
export const kInspectedSymbol = 3
export const kCustomInspectedObject = 4
export const kCustomInspect = Symbol.for('nodejs.util.inspect.custom')
export const errors = {
  Error,
  TypeError,
  RangeError,
  URIError,
  SyntaxError,
  ReferenceError,
  EvalError,
}
export const errorCtorNames = new Set(Object.keys(errors))

// get the header length that v8 uses
const serializer = new DefaultSerializer()
serializer.writeHeader()
const v8Header = serializer.releaseBuffer()
export const kV8HeaderLength = v8Header.length
// add space for the Uint32 message size
export const kSerializedSizeHeader = 4 + kV8HeaderLength

// This module is more or less a direct port of the deserialization logic
// from node's lib/internal/error_serdes module, with primordials removed.
import { deserialize } from 'v8'
import {
  errors,
  kCustomInspectedObject,
  kInspectedError,
  kInspectedSymbol,
  kSerializedError,
  kSerializedObject,
} from './constants.js'

export const deserializeError = (error: Buffer) => {
  switch (error[0]) {
    case kSerializedError: {
      // serialized Error
      const { properties, constructor } = deserialize(
        error.subarray(1),
      )
      const ctor = errors[constructor as keyof typeof errors]
      if ('cause' in properties && 'value' in properties.cause) {
        properties.cause.value = deserializeError(
          properties.cause.value,
        )
      }
      return Object.create(ctor.prototype, properties)
    }
    case kSerializedObject: {
      return deserialize(error.subarray(1))
    }
    case kInspectedError: {
      return error.subarray(1).toString('utf8')
    }
    case kInspectedSymbol: {
      return Symbol.for(
        error
          .subarray(1 + 'Symbol('.length, error.length - 1)
          .toString(),
      )
    }
    case kCustomInspectedObject: {
      const s = error.subarray(1).toString()
      return Object.assign(Object.create(null), {
        [Symbol.for('nodejs.util.inspect.custom')]: () => s,
      })
    }
  }
}

import { Minipass } from 'minipass'
import { DefaultSerializer } from 'v8'
import {
  kSerializedSizeHeader,
  kV8HeaderLength,
} from './constants.js'
import {
  FailData,
  isMessageFail,
  Message,
  MessageFail,
  NamedNestedLocation,
  PassData,
  PlanData,
  StringData,
} from './messages.js'
import { serializeError } from './serialize.js'
export * from './messages.js'

export class TestStreamSerialize extends Minipass<Buffer> {
  #serializer: DefaultSerializer = new DefaultSerializer()

  constructor() {
    super()
    this.writable = false
  }

  diagnostic(data: DiagnosticData) {
    this.#write({ type: 'test:diagnostic', data })
  }
  dequeue(data: NamedNestedLocation) {
    this.#write({ type: 'test:dequeue', data })
  }
  enqueue(data: NamedNestedLocation) {
    this.#write({ type: 'test:enqueue', data })
  }
  fail(data: FailData) {
    this.#write({ type: 'test:fail', data })
  }
  pass(data: PassData) {
    this.#write({ type: 'test:pass', data })
  }
  plan(data: PlanData) {
    this.#write({ type: 'test:plan', data })
  }
  start(data: NamedNestedLocation) {
    this.#write({ type: 'test:start', data })
  }
  stderr(data: StringData) {
    this.#write({ type: 'test:stderr', data })
  }
  stdout(data: StringData) {
    this.#write({ type: 'test:stdout', data })
  }

  #write(item: Message) {
    let originalError: undefined | (Error & { cause?: any })
    if (
      isMessageFail(item) &&
      item.data.details.error !== undefined
    ) {
      originalError = item.data.details.error
      Object.assign(item.data.details, {
        error: serializeError(originalError),
      })
    }

    this.#serializer.writeHeader()
    // Add 4 bytes, to later populate with message length
    this.#serializer.writeRawBytes(Buffer.allocUnsafe(4))
    this.#serializer.writeHeader()
    this.#serializer.writeValue(item)

    if (originalError !== undefined) {
      Object.assign((item as MessageFail).data.details, {
        error: originalError,
      })
    }
    const serializedMessage = this.#serializer.releaseBuffer()
    const serializedMessageLength =
      serializedMessage.length - kSerializedSizeHeader

    serializedMessage.set(
      [
        (serializedMessageLength >> 24) & 0xff,
        (serializedMessageLength >> 16) & 0xff,
        (serializedMessageLength >> 8) & 0xff,
        serializedMessageLength & 0xff,
      ],
      kV8HeaderLength,
    )
    super.write(serializedMessage)
  }

  //@ts-ignore
  write() {
    throw new Error('not directly writable, use message methods')
  }
}

// These will all be undefined if the t.options.at is null, or not a file
export type Location = {
  file?: string
  line?: number
  column?: number
}

export interface StringData extends Location {
  message: string
}

export interface NestedLocation extends Location {
  nesting: number
}

export type DiagnosticData = StringData & NestedLocation

export interface NamedNestedLocation extends NestedLocation {
  name: string
}

export interface PlanData extends NestedLocation {
  count: number
}

export interface ResultsDataDetails {
  duration_ms: number
  type?: 'suite'
  error?: Buffer | (Error & { cause?: any })
}
export type PassDataDetails = ResultsDataDetails & {
  error?: undefined
}

export interface FailDataDetails extends ResultsDataDetails {
  error: Error & { cause?: any }
}
export interface FailDataDetailsSerialized
  extends ResultsDataDetails {
  error: Buffer
}

export interface TestData extends NamedNestedLocation {
  details: PassDataDetails | FailDataDetails
  testNumber: number
  todo?: string | boolean
  skip?: string | boolean
}
export type PassData = TestData & { details: PassDataDetails }
export type FailData = TestData & {
  details: FailDataDetails
}

export type MessageDequeue = {
  type: 'test:dequeue'
  data: NamedNestedLocation
}

export type MessageDiagnostic = {
  type: 'test:diagnostic'
  data: DiagnosticData
}

export type MessageEnqueue = {
  type: 'test:enqueue'
  data: NamedNestedLocation
}

export type MessageFail = {
  type: 'test:fail'
  data: FailData
}

export type MessagePass = {
  type: 'test:pass'
  data: PassData
}

export type MessagePlan = {
  type: 'test:plan'
  data: PlanData
}

export type MessageStart = {
  type: 'test:start'
  data: NamedNestedLocation
}

export type MessageStderr = {
  type: 'test:stderr'
  data: StringData
}

export type MessageStdout = {
  type: 'test:stdout'
  data: StringData
}

export type Message =
  | MessageDiagnostic
  | MessageEnqueue
  | MessageDequeue
  | MessageStart
  | MessageFail
  | MessagePass
  | MessagePlan
  | MessageStderr
  | MessageStdout

const hasDetails = (m: Message): m is MessagePass | MessageFail =>
  !!(m as MessagePass | MessageFail).data.details
export const isMessageFail = (m: Message): m is MessageFail => {
  return (
    m.type === 'test:fail' &&
    hasDetails(m) &&
    'error' in m.data.details
  )
}

import { Minipass } from 'minipass'
import { DefaultDeserializer } from 'v8'
import {
  kSerializedSizeHeader,
  kV8HeaderLength,
} from './constants.js'
import { deserializeError } from './deserialize.js'
import { Message } from './messages.js'
export * from './messages.js'

export class TestStreamDeserialize extends Minipass<
  Message,
  Minipass.ContiguousData
> {
  #buffer?: Buffer

  constructor() {
    super({ objectMode: true })
  }

  write(chunk: Minipass.ContiguousData, cb?: () => void): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding,
    cb?: () => void,
  ): boolean
  write(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void,
  ): boolean {
    // stream.write types boilerplate
    /* c8 ignore start */
    if (typeof encoding === 'function') {
      cb = encoding
      encoding = undefined
    }
    if (encoding === 'buffer') encoding = undefined
    if (typeof chunk === 'string') {
      chunk = Buffer.from(chunk, encoding ?? 'utf8')
    } else if (!Buffer.isBuffer(chunk)) {
      chunk = Buffer.from(chunk as ArrayBuffer)
    }
    /* c8 ignore stop */

    let buf =
      this.#buffer?.length ?
        Buffer.concat([this.#buffer, chunk as Buffer])
      : (chunk as Buffer)
    this.#buffer = undefined

    while (buf.length >= kSerializedSizeHeader) {
      const size = readSize(buf, kV8HeaderLength)
      if (buf.length < size + kSerializedSizeHeader) {
        break
      }
      const des = new DefaultDeserializer(
        buf.subarray(
          kSerializedSizeHeader,
          size + kSerializedSizeHeader,
        ),
      )
      buf = buf.subarray(size + kSerializedSizeHeader)
      des.readHeader()
      const item = des.readValue()
      if (item?.data?.details?.error) {
        item.data.details.error = deserializeError(
          item.data.details.error,
        )
      }

      //@ts-ignore
      super.write(item as Message)
    }

    if (buf.length) this.#buffer = buf

    /* c8 ignore start */
    if (cb) {
      if (this.flowing) cb()
      else this.once('drain', cb)
    }
    /* c8 ignore stop */
    return this.flowing
  }

  end(cb?: () => void): this
  end(chunk: Minipass.ContiguousData, cb?: () => void): this
  end(
    chunk: Minipass.ContiguousData,
    encoding?: Minipass.Encoding,
    cb?: () => void,
  ): this
  end(
    chunk?: Minipass.ContiguousData | (() => void),
    encoding?: Minipass.Encoding | (() => void),
    cb?: () => void,
  ) {
    // just affordance for TS's persnicketiness
    /* c8 ignore start */
    const ret =
      chunk === undefined || typeof chunk === 'function' ?
        super.end(chunk)
      : typeof encoding === 'function' ? super.end(chunk, encoding)
      : super.end(chunk, encoding, cb)
    /* c8 ignore stop */
    if (this.#buffer) {
      this.emit('error', new Error('deserialize ended mid-message'))
    }
    return ret
  }
}

const readSize = (buf: Buffer, offset: number) => {
  const a = buf[offset]
  const b = buf[offset + 1]
  const c = buf[offset + 2]
  const d = buf[offset + 3]

  // not possible, we check the length
  /* c8 ignore start */
  if (
    a === undefined ||
    b === undefined ||
    c === undefined ||
    d === undefined
  ) {
    throw new Error('Invalid buffer, not long enough to readSize')
  }
  /* c8 ignore stop */

  return (a << 24) | (b << 16) | (c << 8) | d
}

import { Base } from '@tapjs/core'
import { TestStreamSerialize } from '@tapjs/error-serdes'
import { format } from 'util'
import { locFromAt } from './loc-from-at.js'
import { TestMap } from './test-map.js'

export const commentMethod = (
  stream: TestStreamSerialize,
  diagsMap: TestMap<DiagnosticData[]>,
) => {
  return function comment(this: Base, ...args: any[]) {
    const message = format(...args)
    const diags = diagsMap.get(this)
    const m = {
      ...locFromAt(comment),
      nesting: this.nestingLevel,
      message,
    }
    if (diags) diags.push(m)
    else stream.diagnostic(m)
  }
}

import { CallSiteLike, CallSiteLikeJSON } from '@tapjs/stack'
import { resolve } from 'path'

export const locFromCallSite = (
  loc?: CallSiteLike | CallSiteLikeJSON | null,
) => {
  const f = loc?.fileName ?? undefined
  const file = f !== undefined ? resolve(f) : f
  const line = loc?.lineNumber ?? undefined
  const column = loc?.columnNumber ?? undefined
  return { file, line, column }
}

import { Base, TAP } from '@tapjs/core'
import { DiagnosticData } from '@tapjs/error-serdes'
import { TestMap } from './test-map.js'
import { testNestedLocation } from './test-nested-location.js'

export const onAddFn = (
  comment: (this: Base, ...args: any[]) => void,
  diagsMap: TestMap<DiagnosticData[]>,
  subsMap: TestMap<Base[]>,
) => {
  const onAdd = (t: Base) => {
    const tt = t as TAP
    const subs: Base[] = []
    const diags: DiagnosticData[] = diagsMap.get(t) || []
    subsMap.set(t, subs)
    diagsMap.set(t, diags)

    if (typeof tt.comment === 'function') {
      tt.comment = comment
    } else if (!tt.comment) {
      t.parser.on('comment', message => {
        message = message.replace(/^#/, '').trim()
        diags.push({ ...testNestedLocation(t), message })
      })
    }
    tt.on('subtestAdd', onAdd)
    if (t.parent) {
      subsMap.get(t.parent)?.push(t)
    }
  }
  return onAdd
}

import { Base, TAP } from '@tapjs/core'
import {
  FailData,
  PassData,
  TestStreamSerialize,
} from '@tapjs/error-serdes'
import { TestMap } from './test-map.js'
import { testMessageData } from './test-message-data.js'
import { testNestedLocation } from './test-nested-location.js'
import { testPointMessageData } from './test-point-message-data.js'
import { testPointResults } from './test-point-results.js'
import { testResults } from './test-results.js'

const isPass = (pf?: PassData | FailData): pf is PassData =>
  !!pf?.details && !pf.details.error
const isFail = (pf?: PassData | FailData): pf is FailData =>
  !!pf?.details.error

export const printMessagesFn = (
  tap: TAP,
  stream: TestStreamSerialize,
  subsMap: TestMap<Base[]>,
  diagsMap: TestMap<DiagnosticData[]>,
) => {
  const printMessages = (t: Base, testNumber: number = 0) => {
    // enqueue all my subs, then dequeue each one and print them
    const subs = subsMap.get(t)
    if (t.parent) {
      stream.dequeue(testMessageData(t))
      stream.start(testMessageData(t))
    }
    let count = 0

    // print all the test points emitted directly on this test
    const results = t.results
    /* c8 ignore start */
    if (!results)
      throw new Error('printing results before test completion')
    /* c8 ignore stop */
    for (const type of [
      'passes',
      'skips',
      'todos',
      'failures',
    ] as const) {
      for (const res of results[type] ?? []) {
        if (res.closingTestPoint) continue
        stream.enqueue(testPointMessageData(res, t))
        stream.dequeue(testPointMessageData(res, t))
        stream.start(testPointMessageData(res, t))
        const passFail = testPointResults(res, t, ++count)
        if (isPass(passFail)) {
          stream.pass(passFail)
        } else {
          stream.fail(passFail)
        }
      }
    }

    // recurse to all subtests
    if (subs) {
      // determine whether a suite or assertion based on subs.length
      for (const t of subs) {
        // console.error(t.parser)
        stream.enqueue(testMessageData(t))
      }
      for (const t of subs) {
        printMessages(t, ++count)
      }
    }

    if (count && t !== tap) {
      stream.plan({ ...testNestedLocation(t), count })
    }

    const passFail = testResults(t, count, testNumber)
    if (isPass(passFail)) {
      stream.pass(passFail)
    } else if (isFail(passFail)) {
      stream.fail(passFail)
    }

    /* c8 ignore start */
    const diags = diagsMap.get(t) || []
    /* c8 ignore stop */
    for (const d of diags) {
      stream.diagnostic(d)
    }
  }
  return printMessages
}

import { Base } from '@tapjs/core'
import { NamedNestedLocation } from '@tapjs/error-serdes'
import { testNestedLocation } from './test-nested-location.js'

export const testMessageData = (t: Base): NamedNestedLocation => {
  const { name } = t
  return { name, ...testNestedLocation(t) }
}

import { Base } from '@tapjs/core'
import { FailData, PassData } from '@tapjs/error-serdes'
import { expandStack } from '@tapjs/stack'
import { Result, TapError } from 'tap-parser'
import { locFromCallSite } from './loc-from-callsite.js'
import { resultToError } from './result-to-error.js'

export const testPointResults = (
  res: Result | TapError,
  t: Base,
  testNumber: number,
): PassData | FailData => {
  const p: PassData = {
    name: res.name,
    testNumber,
    ...(res.diag?.at ?
      locFromCallSite(res.diag.at)
    : locFromCallSite(t.options.at)),
    nesting: t.nestingLevel + 1,
    details: {
      duration_ms: res.time ?? 0,
    },
  }
  if (res.skip) p.skip = res.skip
  if (res.todo) p.todo = res.todo
  if (res.ok || res.skip || res.todo) return p

  const fe = resultToError(res)
  // console.error(res, fe)
  return {
    ...p,
    details: {
      ...p.details,
      error: Object.assign(new Error(fe.message), {
        cause: fe,
        code: 'ERR_TEST_FAILURE',
        failureType: 'testCodeFailure',
        stack: expandStack(
          res.diag?.stack ?? t.options.stack,
        ).trimEnd(),
      }),
    },
  }
}

// turn a tap-parser Result object into an Error
// Put some of the diagnostic info into the error message,
// because that is the string the node --test reporter shows.

import { Base } from '@tapjs/core'
import {
  CallSiteLike,
  CallSiteLikeJSON,
  expandStack,
} from '@tapjs/stack'
import { Result, TapError } from 'tap-parser'
import { locFromCallSite } from './loc-from-callsite.js'
import { prettyDiff } from './pretty-diff.js'

export const resultToError = (
  result:
    | Result
    | TapError
    | {
        ok?: boolean
        tapError?: string
        name?: string
        diag?: Result['diag']
      },
  test?: Base,
): Error => {
  const {
    stack: diagStack,
    diff: rawDiff,
    source,
    passes: _,
    ...diag
  } = result.diag || {}
  const at: CallSiteLike | CallSiteLikeJSON =
    diag.at ?? test?.options.at
  const lfa = !!at ? locFromCallSite(at) : undefined
  const stack =
    diagStack ||
    (lfa?.file ? `${lfa.file}:${lfa.line}:${lfa.column}` : undefined)
  delete diag.at

  const diff = prettyDiff(rawDiff)
  if (diff) {
    delete diag.actual
    delete diag.expected
    delete diag.found
    delete diag.wanted
  } else if (result.diag) {
    if ('found' in diag) {
      diag.actual = diag.found
    }
    if ('wanted' in diag) {
      diag.expected = diag.wanted
    }
    delete diag.found
    delete diag.wanted
  }

  const name =
    (result.name || result.tapError || 'unnamed test') +
    (diff ? '\n' + diff : '') +
    (source ? '\n| ' + source.trimEnd().split('\n').join('\n| ') : '')

  return Object.assign(new Error(name), {
    ...diag,
    stack: `Error: ${name}\n` + expandStack(stack).trimEnd(),
  })
}

// This is a WAY less advanced version of the diff highlighting
// that the reporter does.
//
// We use this when serializing the diagnostics for node --test,
// because it all has to be concatenated into one big string.

const theme = {
  end: '\x1B[39m\x1B[49m\x1b[m',
  green: '\x1B[48;2;58;117;0m\x1B[38;2;242;255;229m',
  red: '\x1B[48;2;172;62;163m\x1B[38;2;255;229;241m',
  ctx: '\x1B[48;2;34;34;34m\x1B[38;2;117;158;239m',
  white: '\x1B[48;2;51;51;51m\x1B[38;2;204;204;204m',
} as const

import { proc } from '@tapjs/core'

const cols = Math.max(25, Math.min(proc?.stdout.columns || 75, 75))

export const prettyDiff = (diff: any) => {
  if (!diff || typeof diff !== 'string') return ''
  return diff
    .trimEnd()
    .replace(/^.*$/gm, s => s.trimEnd().padEnd(cols))
    .replace(/^\-.+$/gm, s => theme.red + s + theme.end)
    .replace(/^\+.+$/gm, s => theme.green + s + theme.end)
    .replace(/^\@.+$/gm, s => theme.ctx + s + theme.end)
    .replace(/^ .+$/gm, s => theme.white + s + theme.end)
}

// a bunch of lines are ignored for coverage in this file, because
// the situations they cover ought to be impossible, but are covered
// for type safety, and in case there's a bug in tapjs/core that allows
// that incorrect behavior.
import { Base } from '@tapjs/core'
import { FailData, PassData } from '@tapjs/error-serdes'
import { expandStack } from '@tapjs/stack'
import { resultToError } from './result-to-error.js'
import { testMessageData } from './test-message-data.js'

export const testResults = (
  t: Base,
  count: number,
  testNumber: number,
): PassData | FailData | undefined => {
  if (!t.parent) return

  const results = t.results
  /* c8 ignore start */
  if (!results) {
    throw new Error('printing messages before results available')
  }
  /* c8 ignore stop */
  const suite = count > 0
  const p: PassData = {
    ...testMessageData(t),
    details: {
      duration_ms: t.time,
    },
    testNumber,
  }

  const skip =
    results.plan.skipAll ?
      results.plan.skipReason || true
    : t.options.skip
  if (skip) p.skip = skip
  const todo = t.options.todo
  if (todo) p.todo = todo
  if (suite) p.details.type = 'suite'

  if (results.ok) return p

  if (suite) {
    const nf = results.failures.length
    const m = `${nf} subtest${nf === 1 ? '' : 's'} failed`
    return {
      ...p,
      details: {
        ...p.details,
        error: Object.assign(new Error(m), {
          code: 'ERR_TEST_FAILURE',
          failureType: 'subtestsFailed',
          cause: m,
          /* c8 ignore start */
          failures: results.failures.map(f => f.name || f.tapError),
          stack:
            t.options.stack ?
              expandStack(t.options.stack)
            : undefined,
          /* c8 ignore stop */
        }),
      },
    }
  }

  // a failure here indicates a strange test that had no failing test points,
  // but then was marked as not ok.
  const fe = resultToError(
    t.lists.fail[0] ??
      results.failures[0] ?? {
        ok: false,
        name: 'Unknown test failure',
        diag: {},
      },
    t,
  )

  return {
    ...p,
    details: {
      ...p.details,
      error: Object.assign(new Error(fe.message), {
        cause: fe,
        code: 'ERR_TEST_FAILURE',
        failureType: 'testCodeFailure',
        /* c8 ignore start */
        failures: results.failures.map(f => f.name || f.tapError),
        /* c8 ignore stop */
        stack: expandStack(t.options.stack).trimEnd(),
      }),
    },
  }
}

import { TestStreamSerialize } from '@tapjs/error-serdes'
import { locFromAt } from './loc-from-at.js'

export const serializeStdio = (
  stream: TestStreamSerialize,
  process: NodeJS.Process,
  which: 'stdout' | 'stderr',
) => {
  const write = (m: Buffer | string) => {
    const message = String(m)
    stream[which]({ ...locFromAt(write), message })
  }
  const orig = process[which]
  Object.defineProperty(process, which, {
    configurable: true,
    enumerable: true,
    writable: true,
    // create a new object that inherits from the original, but has
    // a write() method that captures a test:stderr/test:stdout message
    value: Object.defineProperties(Object.create(orig), {
      write: { value: write },
    }),
  })
}

// A map of test objects, but make sure that we don't get confused
// if we get the Test proxy or the underlying TestBase.

import { Base } from '@tapjs/core'

const kSerializationKey = Symbol.for('@tapjs/node-serialize.key')
const getKey = (
  t: Base & { [kSerializationKey]?: string },
): string => {
  const k = t[kSerializationKey]
  if (k) return k
  const n = String(Math.random())
  Object.defineProperty(t, kSerializationKey, {
    value: n,
    writable: false,
    configurable: true,
    enumerable: false,
  })
  return n
}

export class TestMap<T extends {}> extends Map<Base, T> {
  constructor(items?: [Base, T][]) {
    super()
    if (items) {
      for (const [t, v] of items) {
        this.set(t, v)
      }
    }
  }
  get(t: Base): T | undefined {
    return super.get(getKey(t) as unknown as Base)
  }
  has(t: Base) {
    return super.has(getKey(t) as unknown as Base)
  }
  set(t: Base, v: T) {
    return super.set(getKey(t) as unknown as Base, v)
  }
}

import type { TapPlugin, TestBase } from '@tapjs/core'
import {
  argv,
  cwd,
  env,
  mainScript,
  MessageExtra,
  normalizeMessageExtra,
} from '@tapjs/core'
import { isPromise } from 'is-actual-promise'
import { relative, resolve } from 'path'
import { CompareOptions, format, strict } from 'tcompare'
import { Deferred } from 'trivial-deferred'
import { fileURLToPath } from 'url'
import { cleanCWD } from './clean-cwd.js'
import { SnapshotProviderDefault } from './provider.js'

const defaultFormatSnapshot =
  (co: CompareOptions = {}) =>
  (obj: any) =>
    format(obj, { sort: true, ...co })

/**
 * Interface provided by the class set in the `snapshotProvider` option.
 * `save()` may be an async method, but `read()` must be synchronous.
 */
export interface SnapshotProvider {
  file: string
  read(msg: string): string
  snap(data: string, msg: string): void
  save(): void | Promise<void>
}

export interface SnapshotOptions {
  compareOptions?: CompareOptions

  /**
   * Class to use to store and load snapshot data.
   * Defaults to SnapshotProviderDefault, which writes
   * to files in ./tap-snapshots
   * There's no hard requirement that "file" be a file on
   * disk of course. Could easily be in a database, localStorage,
   * whatever.
   */
  snapshotProvider?: {
    new (file: string): SnapshotProvider
  }

  /**
   * the "file" used to store snapshots.
   * Defaults to a filename based on the test file name and arguments,
   * in ./tap-snapshots.  Specifying the same filename will result in
   * getting the same SnapshotProvider.
   */
  snapshotFile?: string

  /**
   * whether or not to write the snapshot file.
   * Defaults true if TAP_SNAPSHOT=1 in the environment.
   */
  writeSnapshot?: boolean

  /**
   * Function used to serialize snapshotted objects to a string.
   * If a non-string is returned, then the default formatting will
   * be used, so this can also transform the object, if needed.
   */
  formatSnapshot?: (obj: any) => any

  /**
   * Function called on the string snapshot result, can be used to
   * remove changeable data, platform-specific stuff, etc.
   */
  cleanSnapshot?: (snapshotData: string) => string
}

export const plugin: TapPlugin<SnapshotPlugin, SnapshotOptions> = (
  t: TestBase,
  opts: SnapshotOptions = {},
) => new SnapshotPlugin(t, opts)

export class SnapshotPlugin {
  static #refs = new Map<TestBase, SnapshotPlugin>()
  #t: TestBase
  #provider: Exclude<SnapshotOptions['snapshotProvider'], undefined>
  #cleanSnapshot: SnapshotOptions['cleanSnapshot']
  #formatSnapshot: SnapshotOptions['formatSnapshot']
  #snapshot: SnapshotProvider
  writeSnapshot: boolean = false
  #compareOptions: CompareOptions

  constructor(t: TestBase, opts: SnapshotOptions) {
    SnapshotPlugin.#refs.set(t, this)
    this.#compareOptions = opts.compareOptions || {}
    this.#t = t
    const snapshotFile = SnapshotPlugin.#getFilename(t, opts)
    if (typeof opts.cleanSnapshot === 'function') {
      this.#cleanSnapshot = opts.cleanSnapshot
    }
    if (typeof opts.formatSnapshot === 'function') {
      this.#formatSnapshot = opts.formatSnapshot
    }
    // if the filename matches, and the provider type matches,
    // use the parent's snapshot provider object.  Otherwise,
    // create a new one.
    const p = t.parent && SnapshotPlugin.#refs.get(t.parent)
    const pp = p && p.#provider
    const pf = p && p.snapshotFile
    this.#provider =
      opts.snapshotProvider || pp || SnapshotProviderDefault

    if (typeof opts.writeSnapshot === 'boolean') {
      this.writeSnapshot = opts.writeSnapshot
    } else {
      if (p) {
        this.writeSnapshot = p.writeSnapshot
      } else {
        this.writeSnapshot =
          env.TAP_SNAPSHOT === '1' ||
          env.npm_lifecycle_event === 'snap' ||
          env.npm_lifecycle_event === 'snapshot'
      }
    }

    if (p && this.#provider === pp && snapshotFile === pf) {
      this.#snapshot = p.#snapshot
    } else {
      this.#snapshot = this.#newSnapshot(snapshotFile)
    }
  }

  #newSnapshot(f: string): SnapshotProvider {
    const snapshot = new this.#provider(f)
    if (this.writeSnapshot) {
      const onEOF = this.#t.onEOF
      this.#t.onEOF = () => {
        onEOF.call(this.#t)
        return snapshot.save()
      }
    }
    return snapshot
  }

  /**
   * Options that will be used when formatting snapshots and diffing/comparing
   * objects using any assertion methods.
   */
  get compareOptions(): Exclude<
    SnapshotOptions['compareOptions'],
    undefined
  > {
    return this.#compareOptions
  }
  set compareOptions(
    cmt: Exclude<SnapshotOptions['compareOptions'], undefined>,
  ) {
    this.#compareOptions = cmt
    this.#t.options.compareOptions = cmt
  }

  /**
   * Method that will be called on snapshot strings. This can be used
   * to remove transient run-specific data from snapshots using simple
   * string transforms.
   */
  get cleanSnapshot(): SnapshotOptions['cleanSnapshot'] {
    return this.#cleanSnapshot
  }
  set cleanSnapshot(clean: SnapshotOptions['cleanSnapshot']) {
    this.#cleanSnapshot = clean
    this.#t.options.cleanSnapshot = clean
  }

  /**
   * Function that turns an object into a snapshot string.
   *
   * By default {@link tcompare!format} is used. If a string is returned,
   * then that string is the snapshot string. If any other type is returned,
   * then the returned value will be formatted using {@link tcompare!format}.
   */
  get formatSnapshot(): SnapshotOptions['formatSnapshot'] {
    return this.#formatSnapshot
  }
  set formatSnapshot(
    format: SnapshotOptions['formatSnapshot'] | undefined,
  ) {
    format ??= defaultFormatSnapshot(this.#compareOptions)
    this.#formatSnapshot = format
    this.#t.options.formatSnapshot = format
  }

  /**
   * The file where snapshots will be written to and read from
   */
  get snapshotFile(): string {
    return this.#snapshot.file
  }
  set snapshotFile(f: string | URL) {
    const p =
      this.#t.parent && SnapshotPlugin.#refs.get(this.#t.parent)
    if (f instanceof URL || f.startsWith('file://')) {
      f = fileURLToPath(f)
    }
    if (p && this.#snapshot === p.#snapshot) {
      this.#snapshot = this.#newSnapshot(f)
    } else {
      this.#snapshot.file = f
    }
  }

  /**
   * In `--snapshot` mode, takes a snapshot of the object provided, and writes
   * to the snapshot file.
   *
   * Otherwise, reads the snapshot file, and verifies that a snapshot of the
   * object provided matches the stored snapshot.
   *
   * @group Assertion Methods
   */
  matchSnapshot(found: any, ...[msg, extra]: MessageExtra): boolean {
    this.#t.currentAssert = this.#t.t.matchSnapshot
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('must match snapshot', args)
    const m = this.#t.fullname + ' > ' + me[0]
    if (typeof found !== 'string') {
      if (!this.#formatSnapshot) {
        for (
          let p = this.#t.parent;
          p && p.t.pluginLoaded(plugin);
          p = p.parent
        ) {
          const fs = p.t.formatSnapshot
          if (fs) {
            this.#formatSnapshot = fs
            break
          }
        }
      }
      const format =
        this.#formatSnapshot ||
        defaultFormatSnapshot(this.#compareOptions)
      found = format(found)
      if (typeof found !== 'string') {
        found = defaultFormatSnapshot(this.#compareOptions)(found)
      }
    }

    // see if a parent had defined it if we don't.
    // pretty common to define once on the root t for the whole test.
    if (!this.#cleanSnapshot) {
      for (
        let p = this.#t.parent;
        p && p.t.pluginLoaded(plugin);
        p = p.parent
      ) {
        const cs = p.t.cleanSnapshot
        if (cs) {
          this.#cleanSnapshot = cs
        }
      }
    }

    if (this.#cleanSnapshot) {
      found = this.#cleanSnapshot(found)
    }

    // \r\n line endings are annoying with git and other systems that
    // will "helpfully" make them into \n instead. Plus, when loaded
    // as JavaScript, they're just turned into \n anyway.
    if (found.includes('\r\n')) {
      found = defaultFormatSnapshot({
        ...this.compareOptions,
        bufferChunkSize: 16,
      })(Buffer.from(found))
    }

    // for the cwd, probably the most common error in snapshot
    // testing, see https://github.com/tapjs/tapjs/issues/885
    if (process.env.TAP_SNAPSHOT_CLEAN_CWD !== '0') {
      found = cleanCWD(found)
    }

    if (this.writeSnapshot) {
      this.#snapshot.snap(found, m)
      return this.#t.pass(...me)
    }
    const wanted = this.#snapshot.read(m)
    if (found === wanted) {
      return this.#t.pass(...me)
    }
    const { diff } = strict(found, wanted, this.#compareOptions)

    Object.assign(me[1], {
      found,
      wanted,
      diff,
      compare: '===',
    })
    return this.#t.fail(...me)
  }

  /**
   * Resolve a promise, and verify that the resulting value matches the
   * snapshot.
   *
   * @group Assertion Methods
   */
  async resolveMatchSnapshot<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    ...[msg, extra]: MessageExtra
  ): Promise<boolean> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'promise must resolve to match snapshot',
      args,
    )

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function' ? fnOrPromise() : (
          fnOrPromise
        )
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolveMatchSnapshot',
      )
    }

    const d = new Deferred<boolean>()
    this.#t.waitOn(d.promise)
    this.#t.currentAssert = this.#t.t.resolveMatchSnapshot
    try {
      d.resolve(this.matchSnapshot(await p, ...me))
    } catch (er) {
      d.resolve(this.#t.fail(...me))
    }
    return d.promise
  }

  static #getFilename(t: TestBase, opts: SnapshotOptions): string {
    if (opts.snapshotFile) {
      return opts.snapshotFile
    }
    const p = t.parent && SnapshotPlugin.#refs.get(t.parent)
    if (p) {
      return p.snapshotFile
    }
    // get name from main file and args
    const main = mainScript()
    const args = argv.slice(2)
    const head = relative(cwd, resolve(main))
    const tail =
      args.length === 0 ?
        ''
      : '-' + args.join(' ').replace(/[^a-zA-Z0-9\._\-]/g, '-')
    return resolve(cwd, 'tap-snapshots', head + tail + '.test.cjs')
  }
}

